mapper.select:
select 
    T1.user_id userId,T1.type,T1.contact_condition contactCondition,T1.contact_members contactMembers,
    T1.healthcheck_member healthcheckMember,T1.time,T1.remark, T1.end_time endTime, T1.id,
    T1.project, T1.whys, T1.tjjg
from examination_tbl T1
left join company_view T2 on T2.user_id=T1.user_id

   T2.is_del = 0

     and T2.user_id=#{userId}

   and T2.village_id = #{villageId}
   and T2.town_id = #{townId}
   and T2.district_id = #{districtId}

  order by T1.id desc

SELECT T1.type, T1.time, T1.healthcheck_member healthcheckMember, T1.end_time endTime, T1.remark, T2.name companyName, T2.user_id userId, T1.id, T1.project, T1.whys, T1.tjjg, T2.village_name villageName FROM examination_tbl T1 LEFT JOIN company_view T2 ON T1.user_id = T2.user_id LEFT JOIN village_tbl T3 on T2.village_id = T3.user_id
T2.is_del= 0
and T1.user_id = #{userId}
and T2.village_id = #{villageId} and T2.town_id = #{townId} and T2.district_id = #{districtId}
and T1.user_id IN ( SELECT uid FROM trade_company_tbl WHERE tid = #{tradeId} )
and T2.name LIKE CONCAT('%', #{companyName},'%')
and T2.village_name LIKE CONCAT('%', #{villageName},'%')
and T1.end_time <= #{isTime}
and T1.end_time >= #{now}
order by T1.id desc

	select
		T1.type,T1.time,T1.healthcheck_member healthcheckMember,T1.end_time endTime,T1.remark,
      T1.id,T1.project, T1.whys, T1.tjjg
  from examination_tbl T1

  	T1.user_id = #{tradeId}

      	and T1.end_time <= #{isTime}


      	and T1.end_time >= #{now}


  order by T1.id desc
