mapper.insert:

insert into fc_temporarydanger_study_t (id, uid, checker, 
  tresult, del, ctime
  )
values (#{id,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, #{checker,jdbcType=VARCHAR}, 
  #{tresult,jdbcType=INTEGER}, #{del,jdbcType=INTEGER}, #{ctime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT T1.uid, T1.checker, T1.tresult, T1.ctime, T2.id Tdangeritemid, T2.Tdangerid, T2.Tdangerstudyid, T2.checkret, T3.id, T3.sslcmc, T3.zyzmc, T3.khxm FROM fc_temporarydanger_study_t T1 LEFT JOIN fc_temporarydanger_item_t T2 ON T1.id = T2.Tdangerstudyid LEFT JOIN fc_temporarydanger_manual_t T3 ON T2.Tdangerid = T3.id WHERE T1.del = 0
and T1.id = #{id}
and T1.uid = #{uid}
and T2.checkret = #{checkret}
