mapper.insert:


  SELECT LAST_INSERT_ID()

insert into sperson_tbl (user_id, name, flag, 
  type, certificate_number, forensic_time, 
  audit_time, review_time, remark
  )
values (#{userId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{flag,jdbcType=INTEGER}, 
  #{type,jdbcType=VARCHAR}, #{certificateNumber,jdbcType=VARCHAR}, #{forensicTime,jdbcType=VARCHAR}, 
  #{auditTime,jdbcType=VARCHAR}, #{reviewTime,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:
select
  T1.id, T1.user_id userId, T1.name, T1.flag, T1.type, T1.certificate_number certificateNumber, 
  T1.forensic_time forensicTime, T1.audit_time auditTime, T1.review_time reviewTime, 
  T1.remark, T2.name companyName, T2.village_name villageName
from sperson_tbl T1
left join company_view T2 on T1.user_id = T2.user_id
 T2.is_del = 0

        and T1.user_id = #{userId} 

     and T2.village_id = #{villageId}
     and T2.town_id = #{townId}
     and T2.district_id = #{districtId}

   		and T1.user_id IN ( SELECT uid FROM trade_company_tbl WHERE tid = #{tradeId} )


       and T2.village_name LIKE CONCAT('%', #{villageName},'%')


       and T2.name LIKE CONCAT('%', #{companyName},'%')


       and T1.name LIKE CONCAT('%', #{spName},'%')


       and T1.flag = #{spFlag}


       and T1.type = #{spType}


     and T1.review_time <= #{isTime}


     and T1.review_time >= #{now}


order by T1.id desc

	select
		T1.id, T1.user_id userId, T1.name, T1.flag, T1.type, T1.certificate_number certificateNumber, 
        T1.forensic_time forensicTime, T1.audit_time auditTime, T1.review_time reviewTime, 
        T1.remark
  from sperson_tbl T1

  	T1.user_id = #{tradeId}

         and T1.name LIKE CONCAT('%', #{spName},'%')


         and T1.flag = #{spFlag}


         and T1.type = #{spType}


       and T1.review_time <= #{isTime}


       and T1.review_time >= #{now}


  order by T1.id desc
