mapper.insert:


  SELECT LAST_INSERT_ID()

insert into t_contingency_plan_tbl (user_id, name, create_time, 
  filing_time, active_time, doc_name, 
  doc_url, djb_name, djb_url
  )
values (#{userId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, 
  #{filingTime,jdbcType=VARCHAR}, #{activeTime,jdbcType=VARCHAR}, #{docName,jdbcType=VARCHAR}, 
  #{docUrl,jdbcType=VARCHAR}, #{djbName,jdbcType=VARCHAR}, #{djbUrl,jdbcType=VARCHAR}
  )

mapper.select:
select 
    T1.id, T1.user_id userId, T1.name, T1.create_time createTime, T1.filing_time filingTime, T2.name companyName,
    T1.active_time activeTime, T1.doc_name docName, T1.doc_url docUrl, T1.djb_name djbName, T1.djb_url djbUrl
from t_contingency_plan_tbl T1
left join company_view T2 on T2.user_id=T1.user_id


 	T1.user_id = #{ownerId}


       T2.is_del = 0


       and T1.user_id = #{userId} 

  and T2.village_id = #{villageId}
  and T2.town_id = #{townId}
  and T2.district_id = #{districtId}

     and T1.user_id IN ( SELECT uid FROM trade_company_tbl WHERE tid = #{tradeId} )


       and T1.name LIKE CONCAT('%', #{name},'%')


       and T2.name LIKE CONCAT('%', #{companyName},'%')


   and T1.active_time <= #{isTime}


    and T1.active_time >= #{now}


 order by T1.id DESC
