INSERT INTO `t_item_tbl` VALUES ('1', '依法设立且取得工商营业执照或者工商核准文件从事生产最终产品或者中间产品列入《危险化学品目录》的企业。', '1', '《中华人民共和国公司法》第7条');
INSERT INTO `t_item_tbl` VALUES ('2', ' 企业应当依照本办法的规定取得危险化学品安全生产许可证（以下简称安全生产许可证）。未取得安全生产许可证的企业，不得从事危险化学品的生产活动。\n非煤矿矿山企业必须依照本实施办法的规定取得安全生产许可证。未取得安全生产许可证的，不得从事生产活动。', '2', '《冶金企业安全生产监督管理规定》');
INSERT INTO `t_item_tbl` VALUES ('3', '非煤矿矿山企业申请领取安全生产许可证，应当提交采矿许可证复印件等文件、资料。', '3', '《非煤矿矿山企业安全生产许可证实施办法》第8条');
INSERT INTO `t_item_tbl` VALUES ('4', '申请从事爆破作业的单位，应当按照国务院公安部门的规定，向有关人民政府公安机关申领《爆破作业单位许可证》。\n营业性爆破作业单位持《爆破作业单位许可证》到工商行政管理部门办理工商登记后，方可从事营业性爆破作业活动。\n爆破作业单位应当在办理工商登记后3日内，向所在地县级人民政府公安机关备案。\n', '4', '《民用爆炸物品安全管理条例》第32条');
INSERT INTO `t_item_tbl` VALUES ('5', '企业应当具备印刷经营的条件，并取得《印刷经营许可证》', '5', '《中华人民共和国印刷业管理条例》第7条');
INSERT INTO `t_item_tbl` VALUES ('6', '非煤矿山企业应对本企业的安全安全标准化内部评审每年至少1次,每三年请有资质的中介机构进行1次安全标准化审核。', '6', '');
INSERT INTO `t_item_tbl` VALUES ('7', '使用单位必须持有《人民防空工程平时使用证》，方可使用人民防空工程', '7', '《人民防空工程平时开发利用管理办法》第9条');
INSERT INTO `t_item_tbl` VALUES ('8', '产权人或者管理单位出租普通地下室的，应当与承租人签订书面租赁合同，并自合同签订之日起15日内向普通地下室所在地的建设（房屋）行政主管部门登记备案。', '8', '《人民防空工程平时开发利用管理办法》第9条');
INSERT INTO `t_item_tbl` VALUES ('9', '（1）生产厂房、罐区的防火间距、耐火等级是否符合GB50016等标准及规范的规定，与民用建筑的防火间距不应小于25m。\n（2）应与生活区、办公区分开布置，严禁与员工宿舍在同一座建筑物内。\n（3）氨管线严禁通过有人员办公、休息和居住的建筑物。\n（4）变配电室与涉氨厂房贴邻共用的隔墙应为防火墙，该墙应只穿过与配电室有关的管道、沟道，穿过部位周围应采用不燃材料严密封塞。\n（5）作业车间安全出口应分散布置；安全出口的数量≧2个。\n（6）保持消防通道畅通。\n', '9', '');
INSERT INTO `t_item_tbl` VALUES ('10', '建设工程未经消防验收或者消防验收不合格的，禁止投入使用。', '10', '《中华人民共和国消防法》第13条');
INSERT INTO `t_item_tbl` VALUES ('11', '企业应当依法设置安全生产管理机构，配备专职安全生产管理人员。配备的专职安全生产管理人员必须能够满足安全生产的需要。金属冶炼企业主要负责人和安全生产管理人员依法经考核合格。', '11', '冶金企业安全生产监督管理规定》第7条');
INSERT INTO `t_item_tbl` VALUES ('12', '（一）建立、健全本单位安全生产责任制；\r\n（二）组织制定本单位安全生产规章制度和操作规程；\r\n（三）组织制定并实施本单位安全生产教育和培训计划； \r\n（四）保证本单位安全生产投入的有效实施；\r\n（五）督促、检查本单位的安全生产工作，及时消除生产安全事故隐患；\r\n（六）组织制定并实施本单位的生产安全事故应急救援预案；\r\n（七）及时、如实报告生产安全事故。', '12', '《中华人民共和国安全生产法》第十七条');
INSERT INTO `t_item_tbl` VALUES ('13', '明确各岗位的责任人员、责任内容和考核要求。', '13', '《江苏省安全生产条例》第十条');
INSERT INTO `t_item_tbl` VALUES ('14', '明确考核奖惩的适用范围、内容和要求，以及考核及奖惩的实施部门及其职责范围。', '14', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('15', '明确会议的频次，会议的主持人和参加人员，会议的主要议程和内容', '15', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('16', '明确安全教育培训的对象、类型、内容、形式、时间等以及考核要求；明确年度安全培训教育计划；明确新员工、新工程开工前的安全教育；明确新工艺、新技术、新设备、新材料等投入使用前的安全操作规程教育；明确主要负责人、安全管理人员、特种作业人员以及特种设备操作人员，应按国家有关规定经培训、考核合格持相关资格证书上岗的规定等。', '16', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('17', '明确安全检查方式、内容和频次；明确检查牵头部门和人员配置；明确检查时发现的问题做到“五落实”进行整改；明确隐患统计、上报及台账管理要求。', '17', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('18', '明确火灾危险程度，固定动火区域和禁火区域；划分防火防爆等级；明确应急预案，建立应急队伍；落实防火检查制度；明确机动车进入防火区域的相关规定。明确管理职责，明确消防设施、器材维护管理的范围、周期及责任单位和责任人等要求；明确消防通道应保持畅通，并定期开展消防演练。', '18', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('19', '明确危化品的经营、储存、运输、充装（分装）、装卸作业、托运等环节，以及危化品及其包装物（容器）、设备（设施）报废、安全处理的安全管理要求，购买、运输、储存剧毒化学品、易制毒化学品、易制爆危化品的管理要求，重点监管危化品、危化品输送管道、重复使用危化品包装容器的管理要求，定期安全评价的要求，生产的危险化学品应按规定进行登记的要求等。', '19', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('20', '明确维护周期、责任人；严格执行相关安全作业规程；确认管线、工艺，电路等是否正确；有无可靠的防护措施；明确试车、验收；明确相关台账记录、归档要求。', '20', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('21', '明确执行《用电安全导则》和《电业安全工作规程》流程；明确检查周期和消除隐患的期限；明确易燃易爆场所用电管理规定；明确防雷电定期检测要求；明确特种作业人员持证作业规定；明确劳动防护用品穿戴规定；明确作业票制度；明确用电区域警示标志规定；明确电气试验应有两人以上进行作业，并采取相关安全措施。', '21', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('22', '明确事故分类，根据类别统计各类事故上报情况和记录台账；明确事故等级和损失计算；事故发生后应及时上报，根据各岗位职责明确事故责任的划分；明确事故调查和处理程序；明确“四不放过”原则。', '22', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('23', '明确按照《危险化学品重大危险源暂行规定》的规定，对重大危险源辨识、分级、评估、登记建档、备案、核销等管理要求，对设施监测监控系统进行检测、检验并进行经常性维护保养的要求，制定专项预案并定期组织预案演练、培训、修订的要求，关键装置、重点部位的责任人或责任机构及定期检查的要求，设置警示标志的要求等。', '23', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('24', '包括动火作业、进入受限空间作业、临时用电作业、高处作业、起重吊装作业、破土作业、断路作业、设备检维修作业、高温作业、抽堵盲板作业管理等制度', '24', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('25', '工艺管理、开停车管理、建（构）筑物管理、电气管理、公用工程管理、领导干部轮流现场带班制度、变更管理制度、劳动防护用品使用维护管理制度、承包商管理制度等；制定粉尘清扫制度，作业现场积尘未及时规范清理。', '25', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('26', '生产经营单位应当在有较大危险因素的生产经营场所和有关设施、设备上，设置明显的安全警示标志。', '26', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('27', '生产经营单位新建、改建、扩建工程项目（以下统称建设项目）：安全设施必须与主体工程同时设计、同时施工、同时投入生产和使用。安全设施投资应当纳入建设项目概算。', '27', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('29', '（一）明确本单位值班的部门和岗位；（二）明确各级值班人员的职责和要求；（三）明确值班人员值班工作程序和要求；（四）明确值班人员遇到特殊情况的处置步骤和要求；（五）明确值班记录要求。', '28', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('30', '1、物品存放区与墙距、梁距、灯距、柱距、以及物品之间应符合安全距离的要求；\n2、车行道、人行道宽度符合标准；\n3、作业点和安全通道采光符合标准；\n4、按规定采取防火防爆措施；\n5、消防设施标识及防火安全标志准确、齐全；\n6、按规定的数量和种类配备消防器材，且灵敏可靠；\n7、照明灯具完好率100%。\n8、库房管理实施门禁制度。\n', '29', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('31', '(一)部门职责(二)作业种类(三)作业安全措施(四)人员培训', '30', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('32', '（一）部门职责（二）防雷、防静电相关场所（三）安全措施（四）定期检测', '31', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('33', '（一）部门职责（二）安全防护的范围（三）安全防护装置的管理', '32', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('34', '爆破作业必须有设计和作业规程,建立并执行爆破管理制度,有防止危及人身安全和中毒窒息的安全预防措施。', '33', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('35', '制定安全生产费用管理制度，明确安全费用提取和使用的程序、职责及权限，按规定提取和使用安全费用。', '34', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('36', '明确安全管理制度及安全操作规程的编制、审核、批准、修订以及发放和培训的职能部门、程序、要求；至少每3年一次组织对安全生产规章制度和操作规程进行评审和修订的要求，当工艺、技术路线和装置设备发生变更等情形时应及时进行评审、修订。', '35', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('37', '企业应当根据危险化学品的生产工艺、技术、设备特点和原辅料、产品的危险性编制岗位操作安全规程,并发放到相关岗位。', '36', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('38', '新工艺、新技术、新装置、新产品投产或投用前，应组织编制新的安全操作规程。', '36', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('39', '主要负责人、分管安全负责人和安全管理人员必须具备与其从事的生产经营活动相适应的安全生产知识和管理能力，接受专门的安全培训教育，经安监部门考核合格，取得安全资格证书方可任职；并按规定每年参加再培训。', '37', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('40', '企业分管安全负责人、分管生产负责人、分管技术负责人应当具有一定的化工专业知识或者相应的专业学历，专职安全生产管理人员应当具备国民教育化工化学类（或安全工程）中等职业教育以上学历或者化工化学类中级以上专业技术职称，或者具备危险物品安全类注册安全工程师资格。', '37', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('41', '新上岗从业人员在上岗前必须经过厂（矿）、车间（工段、区、队）、班组三级安全培训教育，安全培训时间不得少于72学时，每年接受再培训的时间不得少于20学时。', '38', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('42', '从业人员在本生产经营单位内调整工作岗位或离岗一年以上重新上岗时，应当重新接受车间（工段、区、队）和班组级的安全培训。', '38', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('43', '生产经营单位实施新工艺、新技术或者使用新设备、新材料时，应当对有关从业人员重新进行有针对性的安全培训。', '38', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('44', '特种作业人员必须经专门的安全技术培训并考核合格，取得《中华人民共和国特种作业操作证》（以下简称特种作业操作证）后，方可上岗作业。', '39', '《中华人民共和国安全生产法》第二十三条；《特种作业人员安全技术培训考核管理规定》（安监总局令第30号）第五、二十一条');
INSERT INTO `t_item_tbl` VALUES ('45', '特种作业操作证每3年复审1次。', '39', '《中华人民共和国安全生产法》第二十三条；《特种作业人员安全技术培训考核管理规定》（安监总局令第30号）第五、二十一条');
INSERT INTO `t_item_tbl` VALUES ('46', '外来人员（包括承包商人员）入厂前，进行专门的安全注意事项的教育。', '40', '《生产经营单位安全培训规定》（安监总局令第3号）第二十四条');
INSERT INTO `t_item_tbl` VALUES ('47', '建立主要负责人和安全管理人员、特种作业人员的教育培训、取证、复审等，以及其他从业人员、新员工、外来人员等教育培训、考核情况的档案；从业人员安全生产教育和培训记录至少保存2年。', '41', '《生产经营单位安全培训规定》（安监总局令第3号）第二十四条');
INSERT INTO `t_item_tbl` VALUES ('48', '应当建立安全检查、隐患治理整改、重大隐患台账，并按照规定的期限保存。\n检查和巡查是否做好记录。', '42', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('49', '重大隐患项目档案，应包括：\n(1)评价报告与技术结论；(2)评审意见；(3)隐患治理方案，包括资金概预算情况等；(4)治理时间表和责任人；(5)竣工验收报告；(6)备案文件。', '42', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('50', '根据安全生产奖惩制度，建立对各级、各部门、各岗位及员工考核及奖惩情况台账。', '43', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('51', '应当建立安全生产例会制度，定期研究本单位安全生产工作；制定有效的安全生产措施，并对措施的落实情况进行检查。', '44', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('52', '维护、保养、检测记录应当包括安全设备的名称和维护、保养、检测的时间、人员等内容。', '45', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('53', '包括生产安全事故报告、事故调查和处理过程中形成的具有保存价值的各种文字、图表、声像、电子等不同形式的历史记录。', '46', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('54', '危险作业时间、地点、类别、作业人员、作业审批、作业票证编号等内容的记录，与动火等危险作业票证一并归档。', '47', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('55', '包括：1）名称，包括别名、英文名等； 2) 存放、生产、使用地点； 3）数量；4）危险性分类、危规号、包装类别、登记号；5）安全技术说明书与安全标签，危险化学品登记证。', '48', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('56', '1、对相关方进行资质审查；2、与相关方签订安全协议；3、对相关方作业人员进行安全教育；4、向相关方进行作业现场安全交底；5、对相关方的安全作业规程、施工方案和应急预案进行审查；6、办理作业许可手续；7、建立相关方名录和档案。8.水泥工厂筒型储存库人工清库作业外包给不具备高空作业工程专业承包资质的承包方且作业前未进行风险分析。', '49', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('57', '建立安全管理机构或专兼职管理人员、特种设备使用登记证档案记录、检验报告、保养记录、特种设备台帐、特种设备安全附件检验报告、包养记录等', '50', '《特种设备安全监察条例》(国务院令第549号）第五条、二十五条、二十六条、三十三条、三十八条、三十九条；《中华人民共和国特种设备安全法》第十三条、十四条、三十三条、三十四条、三十五条、三十六条');
INSERT INTO `t_item_tbl` VALUES ('58', '用于生产、储存危险物品的建设项目，涉及高危作业的企业等，应当按国家有关规定进行安全条件论证和安全评价鞥', '51', '');
INSERT INTO `t_item_tbl` VALUES ('59', '建设项目安全设施竣工后，应当委托具有相应资质的中介机构进行安全验收评价。建设项目安全设施经验收合格后，方可投入生产和使用。\n安全预评价报告、安全专篇、安全验收评价报告应当报安全生产监督管理部门备案。\n', '52', '');
INSERT INTO `t_item_tbl` VALUES ('60', '重大危险源档案应当包括下列文件、资料：\n（一）辨识、分级记录；\n（二）重大危险源基本特征表；\n（三）涉及的所有化学品安全技术说明书；\n（四）区域位置图、平面布置图、工艺流程图和主要设备一览表；\n（五）重大危险源安全管理规章制度及安全操作规程；\n（六）安全监测监控系统、措施说明、检测、检验结果；\n（七）重大危险源事故应急预案、评审意见、演练计划和评估报告；\n（八）安全评估报告或者安全评价报告；\n（九）重大危险源关键装置、重点部位的责任人、责任机构名称；\n（十）重大危险源场所安全警示标志的设置情况；\n（十一）其他文件、资料。\n生产经营单位对重大危险源应当登记建档，进行定期检测、评估、监控，并制定应急预案，告知从业人员和相关人员在紧急情况下应当采取的应急措施。生产经营单位应当按照国家有关规定将本单位重大危险源及有关安全措施、应急措施报有关地方人民政府负责安全生产监督管理的部门和有关部门备案。', '53', '《危险化学品重大危险源监督管理暂行规定》（安监总局令第40号）第二十二条');
INSERT INTO `t_item_tbl` VALUES ('61', '必须依法参加工伤社会保险，为从业人员缴纳保险费。 ', '54', '《中华人民共和国安全生产法》第四十三条');
INSERT INTO `t_item_tbl` VALUES ('62', '存储风险抵押金或参加企业安全生产责任保险', '55', '《江苏省安全生产条例》第三十五条、《市政府关于印发无锡市企业安全生产风险抵押金管理暂行办法和无锡市企业安全生产责任保险实施方法的通知》（锡政发﹝2010﹞152号）');
INSERT INTO `t_item_tbl` VALUES ('63', '应专户存储、专项用于完善、改造和维护安全防护设施设备、人员培训等支出，并建立专门台账。', '56', '《江苏省安全生产条例》第十四条、《企业安全生产费用提取和使用管理办法》（财企〔2012〕16号）第二十条、第三十一条');
INSERT INTO `t_item_tbl` VALUES ('64', '根据AQ/T9002的规定要求制定应急预案。存在重大危险源的，还应制定重大危险源专项应急预案。对于危险性较大的重点岗位，应当制定重点工作岗位的现场处置方案。', '57', '《生产安全事故应急预案管理办法》（安监总局令第17号）第二章、第三章，《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('65', '按照规定要求经评审后到安监部门备案，并取得应急预案备案登记表', '58', '《生产安全事故应急预案管理办法》（安监总局令第17号）第四章');
INSERT INTO `t_item_tbl` VALUES ('66', '应当至少每三年修订一次，并及时向有关部门或者单位报告应急预案的修订情况，按照有关应急预案报备程序重新备案；当生产工艺和技术发生变化等情形时应及时修订。', '59', '《生产安全事故应急预案管理办法》（安监总局令第17号）第二十九条、三十条、三十一条。');
INSERT INTO `t_item_tbl` VALUES ('67', '每年至少组织一次综合应急预案演练或者专项应急预案演练，每半年至少组织一次现场处置方案演练，撰写应急预案演练评估报告。', '60', '《生产安全事故应急预案管理办法》（安监总局令第17号）第二十六条、第二十七条');
INSERT INTO `t_item_tbl` VALUES ('68', '建立应急救援组织或者制定兼职的应急救援人员。', '61', '《中华人民共和国安全生产法》第六十九条');
INSERT INTO `t_item_tbl` VALUES ('69', '配备必要的应急救援器材、设备，并进行经常性维护、保养，保证正常运转。', '62', '《中华人民共和国安全生产法》第六十九条');
INSERT INTO `t_item_tbl` VALUES ('70', '应建立健全职业卫生管理制度或操作规程：1、职业病危害防治责任制度；2、职业病危害警示与告知制度；3、职业病危害项目申报制度；4、职业病防治宣传教育培训制度；5、职业病防护设施维护检修制度；6、职业病防护用品管理制度；7、职业病危害监测及评价管理制度；8、建设项目职业卫生“三同时”管理制度；9、劳动者职业健康监护及其档案管理制度；10、职业病危害事故处置与报告制度；11、职业病危害应急救援与管理制度；12、岗位职业卫生操作规程；13、法律、法规、规章规定的其他职业病防治制度。', '63', '《中华人民共和国职业病防治法》第二十一条；《工作场所职业卫生监督管理规定》（安监总局令第47号）第十一条');
INSERT INTO `t_item_tbl` VALUES ('71', '应当制定职业病防治计划或实施方案。', '64', '《中华人民共和国职业病防治法》第二十一条');
INSERT INTO `t_item_tbl` VALUES ('72', '对于新建、改建、扩建建设项目和技术改造、技术引进项目，要委托职业卫生技术服务机构和有资质的设计单位，编制预评价报告、设计专篇、控制效果评价报告，并根据建设项目职业病危害程度，报安监部门备案、审核、审查和防护设施竣工验收。', '65', '');
INSERT INTO `t_item_tbl` VALUES ('73', '存在职业病危害的用人单位，应当委托具有相应资质的职业卫生技术服务机构，每年至少进行一次职业病危害因素检测。职业病危害严重的用人单位，除遵守前款规定外，应当委托具有相应资质的职业卫生技术服务机构，每三年至少进行一次职业病危害现状评价。检测、评价结果应当存入本单位职业卫生档案，并向安全生产监督管理部门报告和劳动者公布。', '66', '《中华人民共和国职业病防治法》第二十七条；《工作场所职业卫生监督管理规定》（安监总局令第47号）第二十条');
INSERT INTO `t_item_tbl` VALUES ('74', '对产生严重职业病危害的作业岗位，应当在其醒目位置，设置警示标识和中文警示说明。警示说明应当载明产生职业病危害的种类、后果、预防以及应急救治措施等内容', '67', '《中华人民共和国职业病防治法》第二十五条');
INSERT INTO `t_item_tbl` VALUES ('75', '应当在劳动合同中告知劳动者职业病危害真实情况。工作岗位或工作内容变更，从事与所订立劳动合同中未告知的存在职业病危害的作业时，应告知劳动者职业病危害的真实情况。', '68', '《中华人民共和国职业病防治法》第三十四条');
INSERT INTO `t_item_tbl` VALUES ('76', '用人单位工作场所存在职业病目录所列职业病的危害项目的，应当及时、如实向安全生产监管部门申报，接受监督。进行新建、改建、扩建、技术改造或者技术引进的，在建设项目竣工验收之日起30日内进行申报。', '69', '《中华人民共和国职业病防治法》第十六条，《作业场所职业危害申报管理办法》（安监总局令第27号）第八条');
INSERT INTO `t_item_tbl` VALUES ('77', '应当为劳动者建立职业健康监护档案，并按照规定的期限妥善保存。 职业健康监护档案应当包括劳动者的职业史、职业病危害接触史、职业健康检查结果和职业病诊疗等有关个人健康资料。劳动者离开用人单位时，有权索取本人职业健康监护档案复印件，用人单位应当如实、无偿提供，并在所提供的复印件上签章。', '70', '《中华人民共和国职业病防治法》第三十七条');
INSERT INTO `t_item_tbl` VALUES ('78', '有害作业与无害作业分开（粉尘、高毒物质岗位与其他岗位隔离，接触有毒有害岗位与无危害岗位隔开；有毒物品和粉尘的发生源布置在操作岗位下风侧），工作场所与生活场所分开，工作场所不得住人。', '71', '');
INSERT INTO `t_item_tbl` VALUES ('79', '用人单位必须采用有效的职业病防护设施，职业病防护设施配备齐全、有效，通风、排毒、除尘、减震、降噪等职业病防护设施应当处于正常适用状态，并得到及时维护和定期检测。并为劳动者提供个人使用符合国家标准的职业病防护用品，“LA”标识。', '72', '《劳动防护用品监督管理规定》（安监总局令第1号）第十四条');
INSERT INTO `t_item_tbl` VALUES ('80', '向用人单位提供可能产生职业病危害的设备或可能产生职业病危害的化学品材料，应当提供中文说明书，并在设备的醒目位置设置警示标识和中文警示说明。', '73', '《中华人民共和国职业病防治法》第二十九、三十条');
INSERT INTO `t_item_tbl` VALUES ('81', '按照规定为接触职业病危害的劳动者进行上岗前、在岗期间、离岗前和应急时的职业健康检查；发现疑似职业病病人应当及时安排其进行医学观察或者职业病诊断。不得安排有职业禁忌的劳动者从事其所禁忌的作业。', '74', '');
INSERT INTO `t_item_tbl` VALUES ('82', '1、职业病防治责任制文件；2、职业卫生管理规章制度、操作规程；3、工作场所职业病危害因素种类清单、岗位分布以及作业人员接触情况等资料；4、职业病防护设施、应急救援设施基本信息，以及其配置、使用、维护、检修与更换等记录；5、工作场所职业病危害因素检测、评价报告与记录；6、职业病防护用品配备、发放、维护与更换等记录；7、主要负责人、职业卫生管理人员和职业病危害严重工作岗位的劳动者等相关人员职业卫生培训资料；8、职业病危害事故报告与应急处置记录；9、劳动者职业健康检查结果汇总资料，存在职业禁忌证、职业健康损害或者职业病的劳动者处理和安置情况记录；10、建设项目职业卫生“三同时”有关技术资料，以及其备案、审核、审查或者验收等有关回执或者批复文件；11、职业卫生安全许可证申领、职业病危害项目申报等有关回执或者批复文件；12、其他有关职业卫生管理的资料或者文件。', '75', '《工作场所职业卫生监督管理规定》（安监总局令第47号）第三十四条');
INSERT INTO `t_item_tbl` VALUES ('83', '依法设立且取得工商营业执照或者工商核准文件从事生产最终产品或者中间产品列入《危险化学品目录》的企业。', '951', '《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第二条');
INSERT INTO `t_item_tbl` VALUES ('84', '企业应当依照本办法的规定取得危险化学品安全生产许可证。未取得安全生产许可证的企业，不得从事危险化学品的生产活动。\n', '952', '《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第三条');
INSERT INTO `t_item_tbl` VALUES ('85', '国家对非药品类易制毒化学品的生产、经营实行许可制度。对第一类非药品类易制毒化学品的生产、经营实行许可证管理，对第二类、第三类易制毒化学品的生产、经营实行备案证明管理。', '953', '《非药品类易制毒化学品生产、经营许可办法》（安监总局令第5号）第三条');
INSERT INTO `t_item_tbl` VALUES ('86', '应对本企业的安全安全标准化内部评审每年至少1次,每三年请有资质的中介机构进行1次安全标准化审核。', '954', '');
INSERT INTO `t_item_tbl` VALUES ('87', '企业应当依法设置安全生产管理机构，配备专职安全生产管理人员。配备的专职安全生产管理人员必须能够满足安全生产的需要。危险化学品生产、经营单位主要负责人和安全生产管理人员依法经考核合格。', '955', '《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十二条');
INSERT INTO `t_item_tbl` VALUES ('88', '生产、储存剧毒化学品、易制爆危险化学品的单位，应当设置治安保卫机构,配备专职治安保卫人员。', '955', '《危险化学品安全管理条例》第二十三条');
INSERT INTO `t_item_tbl` VALUES ('89', '（一）建立、健全本单位安全生产责任制；\n（二）组织制定本单位安全生产规章制度和操作规程；\n（三）组织制定并实施本单位安全生产教育和培训计划； \n（四）保证本单位安全生产投入的有效实施；\n（五）督促、检查本单位的安全生产工作，及时消除生产安全事故隐患；\n（六）组织制定并实施本单位的生产安全事故应急救援预案；\n（七）及时、如实报告生产安全事故。', '956', '《中华人民共和国安全生产法》第十七条');
INSERT INTO `t_item_tbl` VALUES ('90', '明确各岗位的责任人员、责任内容和考核要求。', '957', '《江苏省安全生产条例》第十条');
INSERT INTO `t_item_tbl` VALUES ('91', '明确考核奖惩的适用范围、内容和要求，以及考核及奖惩的实施部门及其职责范围。\n', '958', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('92', '明确会议的频次，会议的主持人和参加人员，会议的主要议程和内容', '959', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('93', '明确安全教育培训的对象、类型、内容、形式、时间等以及考核要求；明确年度安全培训教育计划；明确新员工、新工程开工前的安全教育；明确新工艺、新技术、新设备、新材料等投入使用前的安全操作规程教育；明确主要负责人、安全管理人员、特种作业人员以及特种设备操作人员，应按国家有关规定经培训、考核合格持相关资格证书上岗的规定等。', '960', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('94', '明确安全检查方式、内容和频次；明确检查牵头部门和人员配置；明确检查时发现的问题做到“五落实”进行整改；明确隐患统计、上报及台账管理要求。', '961', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('95', '明确火灾危险程度，固定动火区域和禁火区域；划分防火防爆等级；明确应急预案，建立应急队伍；落实防火检查制度；明确机动车进入防火区域的相关规定。明确管理职责，明确消防设施、器材维护管理的范围、周期及责任单位和责任人等要求；明确消防通道应保持畅通，并定期开展消防演练。', '962', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('96', '明确危化品的经营、储存、运输、充装（分装）、装卸作业、托运等环节，以及危化品及其包装物（容器）、设备（设施）报废、安全处理的安全管理要求，购买、运输、储存剧毒化学品、易制毒化学品、易制爆危化品的管理要求，重点监管危化品、危化品输送管道、重复使用危化品包装容器的管理要求，定期安全评价的要求等。', '963', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('97', '明确维护周期、责任人；严格执行相关安全作业规程；确认管线、工艺，电路等是否正确；有无可靠的防护措施；明确试车、验收；明确相关台账记录、归档要求。', '964', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('98', '明确执行《用电安全导则》和《电业安全工作规程》流程；明确检查周期和消除隐患的期限；明确易燃易爆场所用电管理规定；明确防雷电定期检测要求；明确特种作业人员持证作业规定；明确劳动防护用品穿戴规定；明确作业票制度；明确用电区域警示标志规定；明确电气试验应有两人以上进行作业，并采取相关安全措施。', '965', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('99', '明确事故分类，根据类别统计各类事故上报情况和记录台账；明确事故等级和损失计算；事故发生后应及时上报，根据各岗位职责明确事故责任的划分；明确事故调查和处理程序；明确“四不放过”原则。', '966', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('100', '明确按照《危险化学品重大危险源暂行规定》的规定，对重大危险源辨识、分级、评估、登记建档、备案、核销等管理要求，对设施监测监控系统进行检测、检验并进行经常性维护保养的要求，制定专项预案并定期组织预案演练、培训、修订的要求，关键装置、重点部位的责任人或责任机构及定期检查的要求，设置警示标志的要求等。', '967', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('101', '包括动火作业、进入受限空间作业、临时用电作业、高处作业、起重吊装作业、破土作业、断路作业、设备检维修作业、高温作业、抽堵盲板作业管理等制度', '968', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('102', '制定安全生产费用管理制度，明确安全费用提取和使用的程序、职责及权限，按规定提取和使用安全费用。', '969', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('103', '制定岗位责任、隐患治理、应急救援等安全管理制度，以及操作规程。', '970', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('104', '工艺管理、开停车管理、建（构）筑物管理、电气管理、公用工程管理、领导干部轮流现场带班制度、变更管理制度、劳动防护用品使用维护管理制度、承包商管理制度等', '971', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('105', '明确职责；明确交接班的要求、内容；明确交接班的检查、考核和奖惩规定。', '972', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('106', '明确维护周期、责任人；严格执行相关安全作业规程；确认管线、工艺、电路等是否正确；有无可靠的防护措施；明确试车、验收的相关规定；明确相关台账记录、归档要求。', '973', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('107', '明确安全管理组织及其安全管理的职责。', '974', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('108', '明确现金保管规定和领用使用票据管理规定等相关制度。', '975', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('109', '明确安全管理制度及安全操作规程的编制、审核、批准、修订以及发放和培训的职能部门、程序、要求；至少每3年一次组织对安全生产规章制度和操作规程进行评审和修订的要求，当工艺、技术路线和装置设备发生变更等情形时应及时进行评审、修订。', '976', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('110', '企业应当根据危险化学品的生产工艺、技术、设备特点和原辅料、产品的危险性编制岗位操作安全规程,并发放到相关岗位。制定操作规程和工艺控制指标。', '977', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('111', '新工艺、新技术、新装置、新产品投产或投用前，应组织编制新的安全操作规程。', '977', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('112', '主要负责人、分管安全负责人和安全管理人员必须具备与其从事的生产经营活动相适应的安全生产知识和管理能力，接受专门的安全培训教育，经安监部门考核合格，取得安全资格证书方可任职；并按规定每年参加再培训。', '978', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('113', '企业分管安全负责人、分管生产负责人、分管技术负责人应当具有一定的化工专业知识或者相应的专业学历，专职安全生产管理人员应当具备国民教育化工化学类（或安全工程）中等职业教育以上学历或者化工化学类中级以上专业技术职称，或者具备危险物品安全类注册安全工程师资格。', '978', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('114', '新上岗从业人员在上岗前必须经过厂（矿）、车间（工段、区、队）、班组三级安全培训教育，安全培训时间不得少于72学时，每年接受再培训的时间不得少于20学时。', '979', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('115', '从业人员在本生产经营单位内调整工作岗位或离岗一年以上重新上岗时，应当重新接受车间（工段、区、队）和班组级的安全培训。', '979', '《中华人民共和国安全生产法》第二十、二十一、二十二条；《生产经营单位安全培训规定》（安监总局令第3号）第二章、第三章，《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第十六条');
INSERT INTO `t_item_tbl` VALUES ('116', '生产经营单位实施新工艺、新技术或者使用新设备、新材料时，应当对有关从业人员重新进行有针对性的安全培训。', '979', '');
INSERT INTO `t_item_tbl` VALUES ('117', '特种作业人员必须经专门的安全技术培训并考核合格，取得《中华人民共和国特种作业操作证》（以下简称特种作业操作证）后，方可上岗作业。特种作业人员持证上岗。', '980', '《中华人民共和国安全生产法》第二十三条；《特种作业人员安全技术培训考核管理规定》（安监总局令第30号）第五、二十一条');
INSERT INTO `t_item_tbl` VALUES ('118', '特种作业操作证每3年复审1次。', '980', '');
INSERT INTO `t_item_tbl` VALUES ('119', '外来人员（包括承包商人员）入厂前，进行专门的安全注意事项的教育。', '981', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('120', '建立主要负责人和安全管理人员、特种作业人员的教育培训、取证、复审等，以及其他从业人员、新员工、外来人员等教育培训、考核情况的档案；从业人员安全生产教育和培训记录至少保存2年。', '982', '《生产经营单位安全培训规定》（安监总局令第3号）第二十四条');
INSERT INTO `t_item_tbl` VALUES ('121', '应当建立安全检查、隐患治理整改、重大隐患台账，并按照规定的期限保存。建立与岗位相匹配的全员安全生产责任制或者制定实施生产安全事故隐患排查治理制度。', '983', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('122', '重大隐患项目档案，应包括：\n(1)评价报告与技术结论；(2)评审意见；(3)隐患治理方案，包括资金概预算情况等；(4)治理时间表和责任人；(5)竣工验收报告；(6)备案文件。', '983', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('123', '建立健全劳动防护用品的采购、验收、保管、发放、使用、报废等管理台账。', '984', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('124', '根据安全生产奖惩制度，建立对各级、各部门、各岗位及员工考核及奖惩情况台账。', '985', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('125', '应当建立安全生产例会制度，定期研究本单位安全生产工作；制定有效的安全生产措施，并对措施的落实情况进行检查。', '986', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('126', '包括生产安全事故报告、事故调查和处理过程中形成的具有保存价值的各种文字、图表、声像、电子等不同形式的历史记录。', '987', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('127', '危险作业时间、地点、类别、作业人员、作业审批、作业票证编号等内容的记录，与动火等危险作业票证一并归档。', '988', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('128', '包括：1）名称，包括别名、英文名等； 2) 存放、生产、使用地点； 3）数量；4）危险性分类、危规号、包装类别、登记号；5）安全技术说明书与安全标签。', '989', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('129', '生产经营单位将生产经营项目、场所、设备发包或者出租的，应当与承包单位、租赁单位签订专门的安全生产管理协议，或者在承包、租赁合同中约定各自的安全生产管理职责。 ', '990', '《危险化学品从业单位安全标准化通用规范》(AQ3013-2008)');
INSERT INTO `t_item_tbl` VALUES ('130', '企业应当根据情况设置特种设备安全管理机构或者配备专职、兼职的特种设备安全管理人员。特种设备及安全附件投入使用前或者投入使用后三十日内，向负责特种设备安全监督管理的部门办理使用登记，取得使用登记证书。\n特种设备及安全附件台帐和档案\n（一）特种设备的设计文件、产品质量合格证明、安装及使用维护保养说明、监督检验证明等相关技术资料和文件；\n（二）特种设备及安全附件的定期检验和定期自行检查记录；\n（三）特种设备及安全附件的日常使用状况记录；\n（四）特种设备及其附属仪器仪表的维护保养记录；\n（五）特种设备的运行故障和事故记录。', '991', '《特种设备安全监察条例》(国务院令第549号）第五条、二十五条、二十六条、三十三条、三十八条、三十九条；《中华人民共和国特种设备安全法》第十三条、十四条、三十三条、三十四条、三十五条、三十六条');
INSERT INTO `t_item_tbl` VALUES ('131', '重大危险源档案应当包括下列文件、资料：\n（一）辨识、分级记录；\n（二）重大危险源基本特征表；\n（三）涉及的所有化学品安全技术说明书；\n（四）区域位置图、平面布置图、工艺流程图和主要设备一览表；\n（五）重大危险源安全管理规章制度及安全操作规程；\n（六）安全监测监控系统、措施说明、检测、检验结果；\n（七）重大危险源事故应急预案、评审意见、演练计划和评估报告；\n（八）安全评估报告或者安全评价报告；\n（九）重大危险源关键装置、重点部位的责任人、责任机构名称；\n（十）重大危险源场所安全警示标志的设置情况；\n（十一）其他文件、资料。', '992', '《危险化学品重大危险源监督管理暂行规定》（安监总局令第40号）第二十二条');
INSERT INTO `t_item_tbl` VALUES ('132', '经营单位应当建立易制毒化学品销售台账，如实记录销售的品种、数量、日期、购买方等情况。销售台账和证明资料复印件应当保存2年备查', '993', '《易制毒化学品管理条例》第十九条');
INSERT INTO `t_item_tbl` VALUES ('133', '必须依法参加工伤社会保险，为从业人员缴纳保险费。 ', '994', '《中华人民共和国安全生产法》第四十三条');
INSERT INTO `t_item_tbl` VALUES ('134', '存储风险抵押金或参加企业安全生产责任保险', '995', '《江苏省安全生产条例》第三十五条、《市政府关于印发无锡市企业安全生产风险抵押金管理暂行办法和无锡市企业安全生产责任保险实施方法的通知》（锡政发﹝2010﹞152号）');
INSERT INTO `t_item_tbl` VALUES ('135', '应专户存储、专项用于完善、改造和维护安全防护设施设备、人员培训等支出，并建立专门台账。', '996', '《江苏省安全生产条例》第十四条、《企业安全生产费用提取和使用管理办法》（财企〔2012〕16号）第二十条、第三十一条');
INSERT INTO `t_item_tbl` VALUES ('136', '根据AQ/T9002的规定要求制定应急预案。存在重大危险源的，还应制定重大危险源专项应急预案。对于危险性较大的重点岗位，应当制定重点工作岗位的现场处置方案。', '997', '《生产安全事故应急预案管理办法》（安监总局令第17号）第二章、第三章，《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('137', '按照规定要求经评审后到安监部门备案，并取得应急预案备案登记表', '998', '《生产安全事故应急预案管理办法》（安监总局令第17号）第四章');
INSERT INTO `t_item_tbl` VALUES ('138', '应当至少每三年修订一次，并及时向有关部门或者单位报告应急预案的修订情况，按照有关应急预案报备程序重新备案；当生产工艺和技术发生变化等情形时应及时修订。', '999', '《生产安全事故应急预案管理办法》（安监总局令第17号）第二十九条、三十条、三十一条。');
INSERT INTO `t_item_tbl` VALUES ('139', '每年至少组织一次综合应急预案演练或者专项应急预案演练，每半年至少组织一次现场处置方案演练，撰写应急预案演练评估报告。', '1000', '《生产安全事故应急预案管理办法》（安监总局令第17号）第二十六条、第二十七条');
INSERT INTO `t_item_tbl` VALUES ('140', '建立应急救援组织或者制定兼职的应急救援人员。', '1001', '《中华人民共和国安全生产法》第六十九条');
INSERT INTO `t_item_tbl` VALUES ('141', '配备必要的应急救援器材、设备，并进行经常性维护、保养，保证正常运转。', '1002', '《中华人民共和国安全生产法》第六十九条');
INSERT INTO `t_item_tbl` VALUES ('142', '应建立健全职业卫生管理制度或操作规程：1、职业病危害防治责任制度；2、职业病危害警示与告知制度；3、职业病危害项目申报制度；4、职业病防治宣传教育培训制度；5、职业病防护设施维护检修制度；6、职业病防护用品管理制度；7、职业病危害监测及评价管理制度；8、建设项目职业卫生“三同时”管理制度；9、劳动者职业健康监护及其档案管理制度；10、职业病危害事故处置与报告制度；11、职业病危害应急救援与管理制度；12、岗位职业卫生操作规程；13、法律、法规、规章规定的其他职业病防治制度。', '1003', '《中华人民共和国职业病防治法》第二十一条；《工作场所职业卫生监督管理规定》（安监总局令第47号）第十一条');
INSERT INTO `t_item_tbl` VALUES ('143', '应当制定职业病防治计划或实施方案。', '1004', '《中华人民共和国职业病防治法》第二十一条');
INSERT INTO `t_item_tbl` VALUES ('144', '对于新建、改建、扩建建设项目和技术改造、技术引进项目，要委托职业卫生技术服务机构和有资质的设计单位，编制预评价报告、设计专篇、控制效果评价报告，并根据建设项目职业病危害程度，报安监部门备案、审核、审查和防护设施竣工验收。', '1005', '');
INSERT INTO `t_item_tbl` VALUES ('145', '存在职业病危害的用人单位，应当委托具有相应资质的职业卫生技术服务机构，每年至少进行一次职业病危害因素检测。职业病危害严重的用人单位，除遵守前款规定外，应当委托具有相应资质的职业卫生技术服务机构，每三年至少进行一次职业病危害现状评价。检测、评价结果应当存入本单位职业卫生档案，并向安全生产监督管理部门报告和劳动者公布。', '1006', '《中华人民共和国职业病防治法》第二十七条；《工作场所职业卫生监督管理规定》（安监总局令第47号）第二十条');
INSERT INTO `t_item_tbl` VALUES ('146', '对产生严重职业病危害的作业岗位，应当在其醒目位置，设置警示标识和中文警示说明。警示说明应当载明产生职业病危害的种类、后果、预防以及应急救治措施等内容', '1007', '《中华人民共和国职业病防治法》第二十五条');
INSERT INTO `t_item_tbl` VALUES ('147', '应当在劳动合同中告知劳动者职业病危害真实情况。工作岗位或工作内容变更，从事与所订立劳动合同中未告知的存在职业病危害的作业时，应告知劳动者职业病危害的真实情况。', '1008', '《中华人民共和国职业病防治法》第三十四条');
INSERT INTO `t_item_tbl` VALUES ('148', '用人单位工作场所存在职业病目录所列职业病的危害项目的，应当及时、如实向安全生产监管部门申报，接受监督。进行新建、改建、扩建、技术改造或者技术引进的，在建设项目竣工验收之日起30日内进行申报。', '1009', '《中华人民共和国职业病防治法》第十六条，《作业场所职业危害申报管理办法》（安监总局令第27号）第八条');
INSERT INTO `t_item_tbl` VALUES ('149', '应当为劳动者建立职业健康监护档案，并按照规定的期限妥善保存。 职业健康监护档案应当包括劳动者的职业史、职业病危害接触史、职业健康检查结果和职业病诊疗等有关个人健康资料。劳动者离开用人单位时，有权索取本人职业健康监护档案复印件，用人单位应当如实、无偿提供，并在所提供的复印件上签章。', '1010', '《中华人民共和国职业病防治法》第三十七条');
INSERT INTO `t_item_tbl` VALUES ('150', '用人单位必须采用有效的职业病防护设施，职业病防护设施配备齐全、有效，通风、排毒、除尘、减震、降噪等职业病防护设施应当处于正常适用状态，并得到及时维护和定期检测。并为劳动者提供个人使用符合国家标准的职业病防护用品，“LA”标识。', '1011', '《劳动防护用品监督管理规定》（安监总局令第1号）第十四条');
INSERT INTO `t_item_tbl` VALUES ('151', '向用人单位提供可能产生职业病危害的设备或可能产生职业病危害的化学品材料，应当提供中文说明书，并在设备的醒目位置设置警示标识和中文警示说明。', '1012', '《中华人民共和国职业病防治法》第二十九、三十条');
INSERT INTO `t_item_tbl` VALUES ('152', '按照规定为接触职业病危害的劳动者进行上岗前、在岗期间、离岗前和应急时的职业健康检查；发现疑似职业病病人应当及时安排其进行医学观察或者职业病诊断。不得安排有职业禁忌的劳动者从事其所禁忌的作业。', '1013', '');
INSERT INTO `t_item_tbl` VALUES ('153', '1、职业病防治责任制文件；2、职业卫生管理规章制度、操作规程；3、工作场所职业病危害因素种类清单、岗位分布以及作业人员接触情况等资料；4、职业病防护设施、应急救援设施基本信息，以及其配置、使用、维护、检修与更换等记录；5、工作场所职业病危害因素检测、评价报告与记录；6、职业病防护用品配备、发放、维护与更换等记录；7、主要负责人、职业卫生管理人员和职业病危害严重工作岗位的劳动者等相关人员职业卫生培训资料；8、职业病危害事故报告与应急处置记录；9、劳动者职业健康检查结果汇总资料，存在职业禁忌证、职业健康损害或者职业病的劳动者处理和安置情况记录；10、建设项目职业卫生“三同时”有关技术资料，以及其备案、审核、审查或者验收等有关回执或者批复文件；11、职业卫生安全许可证申领、职业病危害项目申报等有关回执或者批复文件；12、其他有关职业卫生管理的资料或者文件。', '1014', '《工作场所职业卫生监督管理规定》（安监总局令第47号）第三十四条');
INSERT INTO `t_item_tbl` VALUES ('154', '传动部位防护要求:\n（一）如有下列情况之一的，应安装防护罩、盖或栏：\n1、以操作人员站立平面为基准，高度在2米以下的外露传动部位；\n2、旋转的键、销、楔等突出大于3毫米的部位；\n3、产生切屑、磨屑、冷却液等飞溅，可能触及人体或造成设备与环境污染的部位\n4、产生射线或弧光的部位\n5、伸入通道的超长工件\n6、超出设备后端300毫米以上的工件\n7、容易伤人的设备往复运动部位\n8、悬挂输送装置跨越通道的下部\n9、高于地面0.7米的操作平台\n（二）防护罩、盖、栏应满足以下要求：\n1、有足够强度和刚度，无明显锈蚀和变形\n2、安装牢固', '894', '《机械加工设备一般安全要求》（GB12266-90）');
INSERT INTO `t_item_tbl` VALUES ('155', '各种限位装置状况:\n（一）各类行程限位装置应安全可靠，运动机构的行程限位应在规定的范围内\n（二）凡限制运动件范围的，应装设限位开关，且固定牢靠有效', '895', '《机械安全  安全标准的起草与表述规则》（GB／T 16755—2008）');
INSERT INTO `t_item_tbl` VALUES ('156', '劳动防护用品要求：\n(一）操作旋转机械禁止戴手套，如车床、钻床等要扣紧袖口，不要戴围巾等，在操作旋转机械时一定要做到工作服的“三紧”，即：袖口紧、下摆紧、裤脚紧；不要戴手套、围巾等                                                                                                                                              （二）工作中戴好护目镜                                                                                                                                                              \n 保险机构要求：(1)破坏式剪切销。这种带剪切销的保险机构是在连接两个轴的部位有两个圆盘，两圆盘靠孔中用销子连接，销子强度小于传动系统中最弱的零件，当设备超负荷传动时，销子首先切断，使被动轴停止转动。\n (2)自动保险机构。在机床传动中，摩擦离合器、齿爪式离合器、安全阀、脱落蜗杆等已成为机床本身的传动部件。一般情况下，这些机构随机床正常工作，当由于某种原因机床出现过载时，这些保险机构就会由于摩擦力不足而打滑，使动力中断传输，被动轴停止转动。当负荷恢复正常时，能自动复位。', '896', '《机械安全  安全标准的起草与表述规则》（GB／T 16755—2008）');
INSERT INTO `t_item_tbl` VALUES ('157', '制动机构要求：在大批量生产中多使用气动或液压夹具，由于工作紧张，往往发生夹具未夹紧工件即开车加工，以致工件飞出而造成事故。', '896', '《机械安全  安全标准的起草与表述规则》（GB／T 16755—2008）');
INSERT INTO `t_item_tbl` VALUES ('158', '自动停车机构的主要作用是在机床受到阻碍或在预定位置碰到挡块时，有关部件能自动停止运动。', '896', '《机械安全  安全标准的起草与表述规则》（GB／T 16755—2008）');
INSERT INTO `t_item_tbl` VALUES ('159', '互锁机构是一种更完善的安全装置，能直接防止操作失误而引起的伤害。', '896', '《机械安全  安全标准的起草与表述规则》（GB／T 16755—2008）');
INSERT INTO `t_item_tbl` VALUES ('160', '机械的安全要求和措施：（一）可能触及到的外露部分应尽量平整、光滑，不应有可能导致刺伤或划伤人员的尖棱、尖角、凸出部分和开口。（二） 溜板纵向运动应设有可靠的限位装置。\n（三）机床飞轮应采取切实可靠的防护措施，确保在运动中不会因其松脱、甩出及缠绕而出现危险。\n（四）外露带和带轮应设置防护罩。应在遮蔽危险部位的防护罩内表面、挂轮箱内表面涂上安全色，以提醒操作、调整和维护人员注意危险的存在。\n（五）因位置原因，在运动中可能造成缠绕危险的花键轴、丝杠应采取切实可靠的安全防护措施。\n（六）控制系统不应有导致危险情况的出现。\n（七） 电力偶然中断，不应给人员造成危险；电力重新恢复后，机床不应自行起动。\n（八） 按钮、指示灯、开关等控制器件的位置应确保操作时不会引起误操作和附加的危险。\n（九）控制器件应清晰可辨，易于区别，必要时应设置表示其功能或用途的标志。\n（十）在每个“起动”控制器件附近应设置一个“停止”控制器件。在每个操作位置应设置一个紧急停止控制器件，紧急停止控制器件的动作不应影响保护操作者或机床装置的功能。\n（十二）冷却系统应能保证操作者的人身安全。 \n（十三）机床应能有效地回收冷却液，应设置防止冷却液飞溅的防护挡板。                                                                                    \n（十四）应设置防止切屑飞溅的防护挡板。\n（十五）冷却液喷嘴应能方便、安全、可靠地固定在所需位置上。', '896', '《机械安全  安全标准的起草与表述规则》（GB／T 16755—2008）');
INSERT INTO `t_item_tbl` VALUES ('161', '（一）刚性离合器的转键、键柄和直键无裂纹\n（二）操纵器的杆件、销钉或弹簧无裂纹、折断\n（三）离合器安装紧固，动作可靠，没有连冲\n（四）滑块从上死点至上死点25毫米处行程范围内需要制动时，离合器应对滑块制动', '897', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('162', '制动器要求：\n（一）制动器与离合器必须相互协调与连锁，开机时动器先松闸，离合器稍滞后再结合\n（二）连锁应准确、紧固，工作稳定、协调\n（三）行程限位装置、控制装置正确，动作灵敏有效', '897', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('163', '紧停装置：（一）大型冲压机械前后都应安装急停按钮，位置在人手可迅速触及的地方\n（二）剪板机剪板长度大于2500毫米时，两侧应安装紧停装置\n（三）紧停装置应有红色标记', '897', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('164', '距作业平台2米以上旋转部位应装设防护罩或防护栏', '897', '《机械设备防护罩安全要求》（GB8196-87）');
INSERT INTO `t_item_tbl` VALUES ('165', '脚踏开关要求：（一）外露在工作台面的脚踏开关，只要有物体撞压可能的，应在开关上部和两侧装设防护罩\n（二）剪板机脚踏杠在整个长度上应装设防护罩\n（三）脚踏开关的踏面应用防滑的花纹钢板制作\n（四）防护罩有足够的强度，无棱角毛边，便于开关检修', '897', '《机械设备防护罩安全要求》（GB8196-87）、《剪板机安全技术要求》5.2.6（JB8781-1998）');
INSERT INTO `t_item_tbl` VALUES ('166', '冲、剪、压、钻机械的金属结构件、电机、变压器、穿线金属管等都应有接地保护', '897', '《用电安全导则》');
INSERT INTO `t_item_tbl` VALUES ('167', '安全防护附件要求：（一）必须采取各种安全措施，确保在滑块下落时，操作者任何部位不可能进入作业危险区\n（二）使用双按钮之间的内侧距离必须大于25厘米', '897', '《冷冲压安全规程》（GB13887—2008）\n《可移动式电动工具的安全 第二部分：台式砂轮机的专用要求》（GB13960.5-2008）');
INSERT INTO `t_item_tbl` VALUES ('168', '（一）安装地点应保证人员和设备安全\n（二）朝向不得对着通道、门口和操作岗位', '898', '');
INSERT INTO `t_item_tbl` VALUES ('169', '防护罩应符合国家安全标准\n挡屑板应有足够强度\n砂轮无裂纹、无破损\n法兰盘使用软垫连接\n托架安装牢固可靠\n运行平稳，砂轮磨损量符合要求\n接地可靠，控制电器符合规定', '898', '');
INSERT INTO `t_item_tbl` VALUES ('170', '应采用橡皮护套绝缘软线。与电源联接，应采用开关、插头座。严禁用导线直接插入插座，或挂在电源线上使用。', '899', '《手持式电动工具的管理、使用、检查和维修安全技术规程》第4.7条');
INSERT INTO `t_item_tbl` VALUES ('171', '（一）工具必须由专职人员每年至少检查一次；\n（二）在湿热和常有温度变化的地区或使用条件恶劣的地方还应相应缩短检查周期；\n（三）在梅雨季节前应及时进行检查。', '899', '《手持式电动工具的管理、使用、检查和维修安全技术规程》第5.3条');
INSERT INTO `t_item_tbl` VALUES ('172', 'I类设备使用时，应先确认其金属外壳或构架已经可靠接地，或已与插头插座内接地效果良好的保护接地极可靠连接，同时应根据环境条件加装合适的点击保护装置。', '899', '《用电安全导则》第6.20条');
INSERT INTO `t_item_tbl` VALUES ('173', '露天使用的用电设备、配电装置应采取防雨、防雪、防雾和防尘的措施。', '899', '《用电安全导则》第6.23条');
INSERT INTO `t_item_tbl` VALUES ('174', '电源线、焊接电缆与焊机连接处的裸露接线板，应采取安全防护罩或防护板隔离，以防止人员或金属物体接触', '900', '《电弧焊机通用技术条件》（GB8118-2010）');
INSERT INTO `t_item_tbl` VALUES ('175', '焊机外壳防漏电保护要求：\n（一）焊机外壳必须接地或接零保护，接地或接零装置连接良好，并定期检查\n（二）严禁使用易燃易爆气体管道作为接地装置\n（三）在有接地或接零装置的焊件上进行弧焊操作，或焊接与地面密切连接的焊件时，应注意避免焊机和工件的双重接地', '900', '《电弧焊机通用技术条件》（GB8118-2010）');
INSERT INTO `t_item_tbl` VALUES ('176', '绝缘电阻要求：\n（一）焊接变压器一、二次绕组、绕组与外壳之间的绝缘电阻值不小于1兆欧\n（二）每半年应对焊机绝缘电阻遥测一次，且记录完整', '900', '《电弧焊机通用技术条件》（GB8118-2010）');
INSERT INTO `t_item_tbl` VALUES ('177', '焊机一次线使用要求：\n（一）焊机一次侧电源线长度不超过3米\n（二）一次线不得在地面拖拽使用，不得在地面跨越通道使用\n（三）电焊机应接在专用开关上使用，不得与其它设备共用一个开关\n（四）在高处或金属容器内工作时应设专人监护', '900', '《电弧焊机通用技术条件》（GB8118-2010）');
INSERT INTO `t_item_tbl` VALUES ('178', '焊机二次线使用要求：\n（一）焊机二次线必须连接紧固，无松动\n（二）二次线接头不超过3个\n（三）二次线截面选用正确，避免长期过载使用造成绝缘老化\n（四）严禁使用厂房金属结构、管道、轨道等作为焊接二次回路使用', '900', '《电弧焊机通用技术条件》（GB8118-2010）');
INSERT INTO `t_item_tbl` VALUES ('179', '焊钳夹紧力好，绝缘良好，手柄隔热层完整，焊钳与导线连接可靠', '900', '《电弧焊机通用技术条件》（GB8118-2010）');
INSERT INTO `t_item_tbl` VALUES ('180', '焊机使用场所要求：\n（一）焊机应安放在通风、干燥、无碰撞、无剧烈震动、无高温、无易燃品存在的地方\n（二）在室外或特殊环境下使用，应采取防护措施保证其正常使用。雨、雪、雾天，无防护措施不准作业\n（三）焊机使用场所应清洁，无严重粉尘\n（四）操作人员应穿绝缘鞋、防护服、面罩（护镜）、电焊手套', '900', '《电弧焊机通用技术条件》（GB8118-2010）');
INSERT INTO `t_item_tbl` VALUES ('181', '旋转部位防护要求：\n（一）炊事机械的传动部位的皮带轮、齿轮、链轮与链条、联轴器等，应加防护罩或防护盖，不便安装的应加设防护栏\n（二）防护罩、盖和栏完好可靠，强度符合要求', '901', '《家用及类似用途电器的安全》（IEC 60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('182', '电气部分要求：\n（一）炊事机械的金属外壳、电动机外壳的PE线均应可靠\n（二）电源线最好穿管敷设，若采用橡胶电缆时，应敷设在无浸泡、无高温和无压砸的沿墙壁面\n（三）线路不得有接头、破损、老化或过载\n（四）随机配备的电气箱柜应符合要求', '901', '《家用及类似用途电器的安全》（IEC 60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('183', '控制开关要求：（一）炊事机械电源控制开关应单机单设，不得几台设备共用一个开关或用距离较远的闸刀控制\n（二）有尘、水可能进入的按钮开关应有防护装置，且不防碍操作', '901', '《家用及类似用途电器的安全》（IEC 60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('184', '搅拌、合面机要求：（一）应加盖密封，容器盖应采用规定的材料制作\n（二）必须安装机盖联锁安全装置，联锁开关应固定在机器本体上，启盖应即刻断电', '901', '《家用及类似用途电器的安全》（IEC 60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('185', '绞肉、绞菜机要求：\n（一）加料口护手装置应保证操作人员手指不能触及刀口或螺旋部位，加料口较大的其高度应适当增加，否则应设小口径加料托盘\n（二）护手装置、加高的加料口和加料托盘应用耐腐蚀的材料制作，具有足够的强度，与加料口连接固定，应能翻转清洗（三）应配备送料的辅助工具', '901', '《家用及类似用途电器的安全》（IEC 60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('186', '压面机要求：\n（一）轧辊应便于装拆，调整灵活、定位可靠\n（二）加料处应有防护装置，防止手指伸入\n（三）备有专用刮面板，不得用手推、刮面粉', '901', '《家用及类似用途电器的安全》（IEC 60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('187', '应急照明设施并确保完好、有效。', '902', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('188', '按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，按规定报废，确保完好有效。', '903', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('189', '变配电室变压器、高压开关柜、低压开关柜操作面地面应铺设绝缘胶垫。', '904', '参考《变配电室安全管理规范》（DB11/527-2008）第5.3条');
INSERT INTO `t_item_tbl` VALUES ('190', '变配电室出入口应设置高度不低于400mm 的挡板。', '905', '参考《变配电室安全管理规范》（DB11/527-2008）5.6条');
INSERT INTO `t_item_tbl` VALUES ('191', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '906', '参考《变配电室安全管理规范》（DB11/527-2008）第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('192', '通往室外的门应向外开。设备间与附属房间之间的门应向附属房间方向开。高压间与低压间之间的门，应向低压间方向开。配电装置室的中间门应采用双向开启门。', '907', '参考《变配电室安全管理规范》（DB11/527-2008）第5.8.3条');
INSERT INTO `t_item_tbl` VALUES ('193', '通往室外的窗应装有纱窗。', '907', '参考《变配电室安全管理规范》（DB11/527-2008）5.9.3');
INSERT INTO `t_item_tbl` VALUES ('194', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '908', '参考《变配电室安全管理规范》第5.18.3条');
INSERT INTO `t_item_tbl` VALUES ('195', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '908', '参考《变配电室安全管理规范》（DB11/527-2008）第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('196', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '908', '参考《变配电室安全管理规范》（DB11/527-2008）第5.18.3.2条');
INSERT INTO `t_item_tbl` VALUES ('197', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：\n（一) 绝缘拉杆应悬挂或架在支架上，不应与墙接触；\n（二) 绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；\n(三) 绝缘垫和绝缘台应经常保持清洁、无损伤；\n（四) 高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；\n（五) 安全用具不允许当作其它工具使用；\n（六) 安全用具不合格的不得存放在工作现场。', '908', '参考《变配电室安全管理规范》（DB11/527-2008）第5.18.5条');
INSERT INTO `t_item_tbl` VALUES ('198', '禁止类：禁止合闸、禁止攀登、高压危险；警告类：止步、高压危险；准许类：从此上下、在此工作', '909', '参考《变配电室安全管理规范》（DB11/527-2008）第5.16.1条');
INSERT INTO `t_item_tbl` VALUES ('199', '变配电室内环境整洁，场地平整，设备间不应存放与运行无关的闲散器材和私人物品，禁止无关人员进入场地；', '910', '参考《变配电室安全管理规范》（DB11/527-2008）第6.8.1条');
INSERT INTO `t_item_tbl` VALUES ('200', '主控制室、高压配电室不应带入食物及储放粮食，值班室不应设置、使用寝具、灶具，并应有防止小动物的安全措施；', '910', '参考《变配电室安全管理规范》（DB11/527-2008）第6.8.4条');
INSERT INTO `t_item_tbl` VALUES ('201', '非变配电室从业人员因工作需要进入变配电室时，应经值班人员许可，并办理登记手续。当需要进入设备区时，应有值班人员监护', '911', '参考《变配电室安全管理规范》（DB11/527-2008）第7.3条');
INSERT INTO `t_item_tbl` VALUES ('202', '柜、屏、台、箱、盘的金属框架及基础型钢必须接地（PE）或接零（PEN）可靠；装有电器的可开启门与框架的接地端子间应用裸线编织铜线连接，作好标识。', '912', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.1.2条');
INSERT INTO `t_item_tbl` VALUES ('203', '箱盘内开关灵活可靠。带有漏电保护的回路，漏电保护装置动作电流和动作时间按设计要求。', '913', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.2.7.2条');
INSERT INTO `t_item_tbl` VALUES ('204', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '913', '《剩余电流动作保护装置安装和运行》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('205', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '913', '《剩余电流动作保护装置安装和运行》第7.2条');
INSERT INTO `t_item_tbl` VALUES ('206', '配电箱、柜1米范围内不应有物品遮挡\n用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。', '914', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('207', '直敷布线可用于正常环境的屋内场所，应采用护套绝缘导线。当导线垂直敷设至地面低于1．8m时，应穿管保护。', '915', '《低压配电设计规范》第5.2.1条');
INSERT INTO `t_item_tbl` VALUES ('208', '（一）设备的金属外壳应采取防漏电保护接地\n（二）PE线若明设时，应选用不小于4平方毫米的铜芯线，不得使用铝芯线\n（三）PE线若随穿线管接入设备本体时，应选用不小于2.5平方毫米的铜芯线或不小于4平方毫米的铝芯线\n（四）PE线不得搭接或串接，接线规范、接触可靠\n（五）明设的应沿管道或设备外壳敷设，暗设的在接线处外部应有接地标志\n（六）PE线接线间不得涂漆或加绝缘垫', '916', 'GB19517-2004《国家电气设备安全技术规范》第2.2.4条');
INSERT INTO `t_item_tbl` VALUES ('209', '插座回路均应设置剩余电流动作保护装置。宜装设漏电保护器，禁止使用无保护线插头插座。', '917', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('210', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。不应从带插座灯头上引接电源供给电器。', '918', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('211', '临时线路使用必须经过审批，一般使用期限为过15天，最长不超过一个月', '919', '《用电安全导则》第4.2条');
INSERT INTO `t_item_tbl` VALUES ('212', '临时用电的电气设备，必须安装剩余电流保护装置。', '920', '《剩余电流动作保护装置安装和运行》第4．5 条');
INSERT INTO `t_item_tbl` VALUES ('213', '线路敷设应符合电气装置设置及安装规范，室内不低于2.5m ，室外不低于4.5m，道路上方不低于6m。', '921', '《电气安全管理规程》第20条（1）');
INSERT INTO `t_item_tbl` VALUES ('214', '设备末端额定漏电动作电流应不大于15mA，漏电动作时间应不大于0.1s。 ', '922', '《潮湿作业场所用电》（GB3787-93）');
INSERT INTO `t_item_tbl` VALUES ('215', '（一）在潮湿作业场所或金属构架上等导电性能良好的作业场所，应用二类或三类工具；\n（二）在狭窄作业场所应有人在外监护；\n（三）工具在发出或收回时，保管人员必须进行一次日常检查，在使用前，使用者必须进行日常检查；\n（四）长期搁置不用的工具，在使用前必须测量绝缘电阻                                          （五）绝缘电阻应不小于规定的数值；\n（六）工具如有绝缘损坏，电源线护套破裂、保护线脱落、插头插座裂开或有损于安全的机械损伤等故障时，应立即进行修理，在未修复前，不得继续使用；\n（七）在维修时，工具内的绝缘衬垫、套管不得任意拆除或漏装。', '923', '《潮湿作业场所用电》（GB3787-93）');
INSERT INTO `t_item_tbl` VALUES ('216', '（一）手提式照明使用36伏（二）危险环境携带式电动工具使用36伏（三）金属容器内使用24伏(四)室内灯具离地面低于2.4m，手持照明灯具，一般潮湿作业场所（地下室、潮湿室内、潮湿楼梯、隧道、人防工程以及有高温、导电灰尘等）的照明，电源电压应不大于36V。 \n（五）在潮湿和易触及带电体场所的照明电源电压，应不大于24V。 \n（六） 在特别潮湿的场所，锅炉或金属容器内，导电良好的地面使用手持照明灯具等，照明电源电压不得大于12V。', '924', '《安全电压标准》');
INSERT INTO `t_item_tbl` VALUES ('217', '（一）危险化学品应根据分类，隔离、隔开和分离存放；\n（二）爆炸物品、自身能形成爆炸的危险品、腐蚀性物品应单独存放\n（三）相互接触或混合后能引起燃烧或爆炸的物品不能同库存放\n（四）灭火方式不同的物质不得同库存放\n（五）遇火、遇热、遇潮能引起燃烧、爆炸或发生化学反应，产生有毒气体的化学危险品不得露天或在潮湿、积水的建筑中存放；\n（六）配置相应的灭火器材。', '925', '《危险化学品安全管理条例》');
INSERT INTO `t_item_tbl` VALUES ('218', '开关、灯具、线路、用电设施均应符合防爆要求', '926', '《爆炸危险场所安全规定》第17条');
INSERT INTO `t_item_tbl` VALUES ('219', '作业现场不得储存、堆放其它易燃易爆物品，易燃易爆废料及时从现场清理', '927', '《喷漆室安全技术规定》第5.2条（GB14444-2006）');
INSERT INTO `t_item_tbl` VALUES ('220', '物品堆放整齐、稳固，高度不超过2米，易挥发可燃物品桶盖随时拧紧盖严', '927', '《工业用化学产品采样安全通则》（GB/3723-1999）');
INSERT INTO `t_item_tbl` VALUES ('221', '易燃易爆用品现场存量最好为1个班的用量', '927', '《工业用化学产品采样安全通则》（GB/3723-1999）');
INSERT INTO `t_item_tbl` VALUES ('222', '通道保持畅通，出、入口处不得堆放易燃易爆物品', '927', '《工业用化学产品采样安全通则》（GB/3723-1999）');
INSERT INTO `t_item_tbl` VALUES ('223', '危险化学品储存及有限空间应保持良好的通风', '927', '《工业用化学产品采样安全通则》（GB/3723-1999）');
INSERT INTO `t_item_tbl` VALUES ('224', '作业区域应采取自然通风或机械通风，保持通风良好', '927', '《喷漆室安全技术规定》第5.3条（GB14444-2006）');
INSERT INTO `t_item_tbl` VALUES ('225', '不得使易燃易爆和有毒有害气体在现场积存', '927', '《涂装作业安全规程 涂漆工艺安全及其通风净化》第15.5条（GB6514-1995）');
INSERT INTO `t_item_tbl` VALUES ('226', '封闭式易燃易爆物品使用场所，必须安装自动报警或抑爆系统；喷涂车间、调漆间规范设置通风装置和防爆电气设备设施。', '927', '《涂装作业安全规程涂层烘干室安全技术规定》第4.4.1.1条（GB14443-2007）');
INSERT INTO `t_item_tbl` VALUES ('227', '易燃易爆作业场所区域内所有金属部件、构件都应可靠接地', '927', '《涂装作业安全规程涂层烘干室安全技术规定》第4.4.1.1条（GB14443-2007）');
INSERT INTO `t_item_tbl` VALUES ('228', '易燃易爆作业场所不得从事动火作业，严禁人员吸烟', '927', '《喷漆室安全技术规定》第6.1.5条（GB14444-2006）');
INSERT INTO `t_item_tbl` VALUES ('229', '专人管理，并有防潮防雨淋设施', '927', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('230', '设置警示安全标志标识', '928', '《安全标志》（GB2894-1996）');
INSERT INTO `t_item_tbl` VALUES ('231', '易燃易爆的车间、库房等作业场所应设置防静电装置', '929', '《危险化学品安全管理条例条》第16条');
INSERT INTO `t_item_tbl` VALUES ('232', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '930', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('233', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '931', '《中华人民共和国职业病防治法》第20条');
INSERT INTO `t_item_tbl` VALUES ('234', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '932', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('235', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '933', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('236', '容易造成职业危害的工序应隔离设置。', '934', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('237', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '935', '《中华人民共和国职业病防治法》第25条');
INSERT INTO `t_item_tbl` VALUES ('238', '应在可产生职业危害的工作场所和设备上，按GBZ158《工作场所职业病危害警示标识》和GBZ/T203《高度物品作业岗位职业病危害告知规范》的要求设置职业危害警示标识。', '936', '《中华人民共和国职业病防治法》第22条');
INSERT INTO `t_item_tbl` VALUES ('239', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '937', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('240', '（一）室外消防栓应合理配置，低压消防栓布置间距不超过120米，应沿道路两侧和路口设置，消防栓距路面边不超过2米，距建筑物外墙不小于5米\n（二）有明显的漆色标志，其1米范围内无障碍物\n（三）手提式灭火器的配置数量符合规定\n（四）所有消防器材应完好，在有效期限内使用', '938', '《机械制造企业安全质量标准化考评细则》');
INSERT INTO `t_item_tbl` VALUES ('241', '（一）搬运、安装、其他一些作业和高压线保持安全距离；                                （二）厂房的装修、维修、做防水等和高压线保持安全距离；\n（三）高压线下严禁码放材料和有建筑物。', '939', '《中华人民共和国电力行业标准》');
INSERT INTO `t_item_tbl` VALUES ('242', '（一）车间通道根据生产要求，宽度标准如下：\n通道只行人通道，宽度不小于1米；\n通过电瓶车、叉车的通道，宽度不小于1.8米；\n通行汽车的车间通道，宽度应不小于3.5米；\n如有铁路线，车间通道，宽度应不小于5米\n（二）通道线应明显清晰，宽度不小于10厘米', '940', '《机械制造企业安全质量标准化考评细则》');
INSERT INTO `t_item_tbl` VALUES ('243', '（一）通道路面应平整，无台阶、无坑、沟\n（二）无积油、积水，无绊脚物', '941', '《机械制造企业安全质量标准化考评细则》');
INSERT INTO `t_item_tbl` VALUES ('244', '（一）任何堆放在通道标记线内和压住安全通道标记线的物件，都判定为占道\n（二）车间占道率应低于5%\n（三）不得有物件将通道横向全部堵死', '942', '《机械制造企业安全质量标准化考评细则》');
INSERT INTO `t_item_tbl` VALUES ('245', '（一）车行道上方的悬挂物高度不小于4米，人行道上方悬挂物高度不小于2.5米\n（二）悬挂应牢固可靠', '943', '《机械制造企业安全质量标准化考评细则》');
INSERT INTO `t_item_tbl` VALUES ('246', '（一）仓库通道的宽度应满足以下要求：\n车行道宽度不小于3.5米\n人行道宽度不小于1米\n（二）路面平坦，无积油积水，无绊脚物\n（三）占道率应小于5%', '944', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('247', '（一）照明灯具完好率达100%\n（二）库房内不得使用移动式照明灯具', '945', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('248', '（一）库房内不得存在临时线\n（二）库房内敷设的电气线路必须，穿金属管或非燃硬塑料管\n（三）每个库房应当在库房外单独安装开关箱\n（四）甲、乙类物品和丙类液体库房的电气装置，必须安装防爆型电气装置，储存丙类固体物品的仓库，不得使用碘钨灯或超过60瓦以上的白炽灯等高温照明灯具', '946', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('249', '（一）防火制度、消防设施标志、防火标志齐全\n（二）按标准配备消防器材的数量和种类\n（三）防火通道畅通，无封死和堵塞现象', '947', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('250', '以下仓库应安装火灾报警装置：\n1、甲、乙、丙类物品储备库；\n2、专业性仓库\n3、大型物资仓库\n4、耐火等级为三、四级的仓库', '948', '');
INSERT INTO `t_item_tbl` VALUES ('251', '（一）物品包装应牢固，不得有严重破损、残缺、变形、物品变质、分解等现象\n（二）甲、乙类物品的包装应严密，不得有跑、冒]滴、漏现象', '949', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('252', '杜绝违章指挥、违章操作、违反劳动纪律的行为', '950', '国家安监总局15号令第44条');
INSERT INTO `t_item_tbl` VALUES ('253', '民用建筑的安全出口应分散布置。每个防火分区、一个防火分区的每个楼层，安全出口数量不应少于2个。其相邻 2 个安全出口最近边缘之间的水平距离不应小于5.0m。 公共建筑内的每个防火分区、一个防火分区内的每个楼层，其安全出口的数量应经计算确定，且不应少于2个。', '879', '《建筑设计防火规范》第5.3.1  条；第5.3.2 条；');
INSERT INTO `t_item_tbl` VALUES ('254', '保障疏散通道、安全出口畅通，并设置符合国家规定的消防安全疏散标志。', '880', '《中华人民共和国消防法》第14条');
INSERT INTO `t_item_tbl` VALUES ('255', '保证疏散通道、安全出口的畅通。不得占用疏散通道或者在疏散通道、安全出口上设置影响疏散的障碍物，防火卷帘下不得堆放杂物；不得在营业、工作期间封闭安全出口，不得遮挡安全疏散指示标志。', '880', '《商场企业安全生产标准化评定标准》6.2');
INSERT INTO `t_item_tbl` VALUES ('256', '在穿过建筑物或进入建筑物内院的消防车道两侧，不应设置影响消防车通行或人员安全疏散的设施。', '881', '《建筑设计防火规范》第6.0.4条');
INSERT INTO `t_item_tbl` VALUES ('257', '按照国家有关规定配置消防设施和器材、设置消防安全标志，并定期组织检验、维修，确保消防设施和器材完好、有效；', '882', '《机关、团体、企业、事业单位消防安全管理规定》第31条');
INSERT INTO `t_item_tbl` VALUES ('258', '不得在营业场所和库房内设置宿舍或饭堂。', '883', '商场企业安全生产标准化评定标准6.2');
INSERT INTO `t_item_tbl` VALUES ('259', '配电箱金属可开启门与框架应用裸线编织铜线连接', '884', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》4.1.2');
INSERT INTO `t_item_tbl` VALUES ('260', '配电箱内开关应标明被控设备编号、名称或操作位置。', '884', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》4.2.3.4');
INSERT INTO `t_item_tbl` VALUES ('261', '配电箱内插座回路应安装漏电保护。', '884', '《剩余电流动作保护装置安装和运行》4．5');
INSERT INTO `t_item_tbl` VALUES ('262', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。', '884', '《用电安全导则》4.8');
INSERT INTO `t_item_tbl` VALUES ('263', '杜绝配电箱、柜被遮挡', '884', '《用电安全导则》4.8');
INSERT INTO `t_item_tbl` VALUES ('264', '应采用双层绝缘的护套绝缘导线。严禁使用双绞线。', '885', '《低压配电设计规范》5.2.1（1）');
INSERT INTO `t_item_tbl` VALUES ('265', '导线敷设应距地1.8米以下应穿管保护。', '885', '《低压配电设计规范》5.2.1（3）');
INSERT INTO `t_item_tbl` VALUES ('266', '插头与插座（含移动的插座）保护接地极应有保护接地。', '885', '《剩余电流动作保护装置安装和运行》4.12　');
INSERT INTO `t_item_tbl` VALUES ('267', '电气设备必须有接地保护。', '885', 'GB19517-2004《国家电气设备安全技术规范》2.2.4');
INSERT INTO `t_item_tbl` VALUES ('268', '临时用电线路必须安装漏电保护。', '885', '《剩余电流动作保护装置安装和运行》4．5');
INSERT INTO `t_item_tbl` VALUES ('269', '地下空间出入口应按设计规范使用，用于人员聚集场所的每个防火分区应具有2个以上的安全出入口，且人民防空工程应具备与居民出入相分离的室外主要出入口。人防工程地面管理用房应与其地下空间配套使用。', '886', '《商场企业安全生产标准化评定标准》6.2');
INSERT INTO `t_item_tbl` VALUES ('270', '地下空间内的机械排烟设施、自动喷水灭火系统、火灾自动报警系统的设置，应按照国家标准《人民防空工程设计防火规范》的有关规定执行。', '887', '《商场企业安全生产标准化评定标准》6.2');
INSERT INTO `t_item_tbl` VALUES ('271', '安全出入口、疏散通道、楼梯口及公共活动场所等部位应按规定设置符合标准的疏散指示标志和应急照明设施，并保证其应急照明时间连续供电不少于30分钟。\n疏散指示标志应由疏散方向标志灯、荧光疏散指示标志和安全出口标志灯组成。疏散方向标志灯、荧光疏散指示标志应设置在疏散通道、楼梯间及其拐角处的墙面上，并不高于室内地坪1米，其间距不应大于15米；安全出入口标志灯应设置在安全出入口上部的顶棚下或墙面上。', '888', '《商场企业安全生产标准化评定标准》6.2');
INSERT INTO `t_item_tbl` VALUES ('272', '使用可燃气体作燃料时，应采用管道供气，且使用可燃气体的房间或部位应有自然通风窗井。', '889', '《商场企业安全生产标准化评定标准》6.2');
INSERT INTO `t_item_tbl` VALUES ('273', '杜绝存放液化石油气瓶、易燃易爆物品', '890', '《人民防空工程平时开发利用管理办法》第14条');
INSERT INTO `t_item_tbl` VALUES ('274', '不得在地下空间内从事危险化学品、烟花爆竹等危险物品的生产经营。不得在地下空间内储存易燃易爆物品。 ', '890', '《人民防空工程平时开发利用管理办法》第14条');
INSERT INTO `t_item_tbl` VALUES ('275', '利用地下空间开办商场（商店、市场）的，其营业场所不得设置在地下三层以及地下三层以下。禁止利用地下空间开办商品批发市场。 ', '890', '《人民防空工程平时开发利用管理办法》第14条');
INSERT INTO `t_item_tbl` VALUES ('276', '不得租赁给不具备安全生产条件的承租户', '890', '《安全生产法》第41条');
INSERT INTO `t_item_tbl` VALUES ('277', '按照国家有关消防安全技术规定安装、使用电器产品，设计、敷设用电线路；禁止超负荷用电。 ', '1016', '《人民防空工程平时开发利用管理办法》第21条');
INSERT INTO `t_item_tbl` VALUES ('278', ' 利用地下空间从事商业、文化娱乐业、旅店业以及其他生产经营活动或者作为居住场所的，地下空间的产权人、管理单位应当保证地下空间符合下列条件：（一）符合防火、卫生等管理规定，并经公安消防机构、卫生主管部门检查合格。（二）房屋建筑安全，不存在危险构件。 （三）具有上下水、卫生间、用电设施。 （四）通风良好，设置机械通风系统或者空气调节装置，并保证有效使用。地下空间平时使用必需的新风量，以及相应的新风系统、回风系统等设置符合设计规范要求。  （五）具有防汛、防雨水倒灌设施。 （六）按规定设置和配备机械防烟排烟系统、自动喷淋系统、应急照明系统、火灾自动报警系统以及其他消防设施和器材。', '891', '《人民防空工程平时开发利用管理办法》第5、6条');
INSERT INTO `t_item_tbl` VALUES ('279', '应当持有《户外广告登记证》，设置户外广告、牌匾是否进行经常性检查和维护，确保安全、牢固。', '892', '《江苏省广告条例》第26条');
INSERT INTO `t_item_tbl` VALUES ('280', '杜绝违章指挥、违章操作、违反劳动纪律的行为', '893', '国家安监总局15号令第44条');
INSERT INTO `t_item_tbl` VALUES ('281', '安全警示标志应当明显、保持完好、便于从业人员和社会公众识别。', '840', '');
INSERT INTO `t_item_tbl` VALUES ('282', '安全设备的设计、制造、安装、使用、检测、维修、改造和报废，应当符合国家标准或者行业标准。\n　　生产经营单位必须对安全设备进行经常性维护、保养，并定期检测，保证正常运转。维护、保养、检测应当作好记录，并由有关人员签字。', '841', '');
INSERT INTO `t_item_tbl` VALUES ('283', '生产经营场所和设备、设施符合有关安全生产法律、法规的规定和国家标准或者行业标准的要求。', '842', '');
INSERT INTO `t_item_tbl` VALUES ('284', '营业区域内的安全出口和疏散通道及其转角处应当设置发光疏散指示标志。指示标志应当能够在断电且无自然光照明时，指引疏散位置和疏散方向。指示标志应当设置在安全出口的顶部和疏散通道及其转角处距地面高度1米以下的墙面上；设置在疏散通道上的指示标志的间距不得大于10米。', '843', '');
INSERT INTO `t_item_tbl` VALUES ('285', '营业区域内的安全出口、疏散通道和重点部位应当设置应急照明灯。应急照明灯的连续照明时间不得少于20分钟，其地面最低照度不得低于0.5勒克斯。', '1017', '');
INSERT INTO `t_item_tbl` VALUES ('286', '营业区域内的安全出口数目、安全疏散距离、疏散门和疏散通道的宽度应当符合国家标准或者行业标准。', '844', '');
INSERT INTO `t_item_tbl` VALUES ('287', '营业区域内应当设置主要疏散通道和辅助疏散通道。主要疏散通道应当直接通向安全出口，其宽度不得小于2.4米；辅助疏散通道的宽度不得小于1.5米。疏散通道内不得设置摊位或者堆放货物。', '844', '');
INSERT INTO `t_item_tbl` VALUES ('288', '疏散门应当向疏散方向开启，不得采用卷帘门、转门、吊门、侧拉门。门内和门外1．4米范围内不得设置踏步。', '844', '');
INSERT INTO `t_item_tbl` VALUES ('289', '消防车通道应畅通。', '844', '');
INSERT INTO `t_item_tbl` VALUES ('290', '游艺娱乐场所与商场等单位设在同一建筑物内的，游艺娱乐场所经营单位应当保证通往建筑物外的疏散通道畅通，并在商场等单位营业结束后安排工作人员指引人群疏散。', '844', '');
INSERT INTO `t_item_tbl` VALUES ('291', '保证疏散通道、安全出口的畅通。不得占用疏散通道或者在疏散通道、安全出口上设置影响疏散的障碍物，防火卷帘下不得堆放杂物；不得在营业、工作期间封闭安全出口，不得遮挡安全疏散指示标志。', '844', '');
INSERT INTO `t_item_tbl` VALUES ('292', '设有集中收银区的超市等商业零售经营单位应当在收银区设置无购物出口，其宽度不得小于1.5米，并设置明显标志。', '845', '');
INSERT INTO `t_item_tbl` VALUES ('293', '收银区的宽度在20米以下的，应当至少设置1个无购物出口；宽度超过20米的，每增加20米，至少增加1个无购物出口，增加的宽度不足20米的，按照增加20米计算。', '845', '');
INSERT INTO `t_item_tbl` VALUES ('294', '营业区域内的购物筐和购物车应当及时清理。', '845', '');
INSERT INTO `t_item_tbl` VALUES ('295', '消防控制室必须实行每日24小时专人值班制度，每班不应少于2人。', '846', '');
INSERT INTO `t_item_tbl` VALUES ('296', '消防控制值班室内不得堆放杂物，应保证其环境满足设备正常运行的要求；应具备消防设施平面布置图、完整的消防设施设计、施工和验收资料、灭火和迎急疏散预案等。消防控制室值班记录应完整，字迹清晰，保管完好。', '846', '');
INSERT INTO `t_item_tbl` VALUES ('297', '消防设施与器材不得遮挡影响使用或者被挪作他用；常闭式防火门不得处于开启状态，防火卷帘下不得堆放物品影响使用。', '847', '');
INSERT INTO `t_item_tbl` VALUES ('298', '投入使用后的防雷装置实行定期检测制度。防雷装置检测应当每年1次，对爆炸危险环境场所的防雷装置应当每半年检测1次。', '848', '');
INSERT INTO `t_item_tbl` VALUES ('299', '操作面地面应铺设绝缘胶垫铺设。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('300', '出入口应设置高度不低于400㎜的挡板。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('301', '应正确设置应急照明灯具。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('302', '通往室外的门应向外开；配电装置室的中间门应采用双向开启门。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('303', '通往室外的窗应装有纱窗。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('304', '通风口应加金属网。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('305', '各种安全用具定期试验、检验。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('306', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('307', '变配电室应设有安全标志。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('308', '10kV及以上的变配电室内的安装、检修工作应有工作票、操作票。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('309', '变配电室内环境整洁，设备间不应存放与运行无关的闲散器材和私人物品，禁止无关人员进入场地。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('310', '值班室不应设置、使用寝具、灶具。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('311', '变配电室从业人员应持有效特种作业操作证上岗。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('312', '变配电室应有入变配电室登记簿。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('313', '变配电室从业人员应正确穿戴绝缘鞋。', '849', '');
INSERT INTO `t_item_tbl` VALUES ('314', '配电箱金属可开启门与框架应用裸线编织铜线连接', '850', '');
INSERT INTO `t_item_tbl` VALUES ('315', '配电箱内开关应标明被控设备编号、名称或操作位置。', '850', '');
INSERT INTO `t_item_tbl` VALUES ('316', '配电箱内插座回路应安装漏电保护。', '850', '');
INSERT INTO `t_item_tbl` VALUES ('317', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。', '850', '');
INSERT INTO `t_item_tbl` VALUES ('318', '杜绝配电箱、柜被遮挡。', '850', '');
INSERT INTO `t_item_tbl` VALUES ('319', '应采用双层绝缘的护套绝缘导线。严禁使用双绞线', '851', '');
INSERT INTO `t_item_tbl` VALUES ('320', '导线敷设应距地1.8米以下应穿管保护。', '851', '');
INSERT INTO `t_item_tbl` VALUES ('321', '插头与插座（含移动的插座）保护接地极应有保护接地。', '851', '');
INSERT INTO `t_item_tbl` VALUES ('322', '电气设备必须有接地保护。', '851', '');
INSERT INTO `t_item_tbl` VALUES ('323', '临时用电线路必须安装漏电保护。', '851', '');
INSERT INTO `t_item_tbl` VALUES ('324', '电梯内设置的报警装置应可靠，联系应畅通', '851', '');
INSERT INTO `t_item_tbl` VALUES ('325', '灶具与可燃或难燃的墙壁、地板、家具之间应采取有效的防火隔热措施（如灭火毯）', '852', '');
INSERT INTO `t_item_tbl` VALUES ('326', '灶具与钢瓶之间的净距不应小于0.5m。', '853', '');
INSERT INTO `t_item_tbl` VALUES ('327', '应采用耐油橡胶胶管。', '854', '');
INSERT INTO `t_item_tbl` VALUES ('328', '燃气灶具、钢瓶、阀门等设施的维修、检修应由生产厂家或取得安装维修资格的专业人员负责。', '854', '');
INSERT INTO `t_item_tbl` VALUES ('329', '钢瓶与灶具之间连接所用软管的长度应控制在1.2～2.0m之间。', '854', '');
INSERT INTO `t_item_tbl` VALUES ('330', '软管连接处应采用压紧螺帽（锁母）或管卡固定。', '854', '');
INSERT INTO `t_item_tbl` VALUES ('331', '软管不得穿墙、窗和门。', '854', '');
INSERT INTO `t_item_tbl` VALUES ('332', '燃具与管道的连接软管应使用燃气专用软管，并应定期更换。', '854', '');
INSERT INTO `t_item_tbl` VALUES ('333', '用瓶组方式供气的，应采用硬管连接。', '854', '');
INSERT INTO `t_item_tbl` VALUES ('334', '在燃气的供应与使用过程中，禁止下列行为：\n（一）倒灌瓶装液化石油气；\n（二）摔、砸、滚动、倒置气瓶；\n（三）加热气瓶、倾倒瓶内残液或者拆修瓶阀等附件；\n（四）擅自拆除、改装、迁移、安装室内管道燃气设施；\n（五）在安装燃气计量表、阀门、燃气蒸发器等燃气设施的房间内堆放易燃易爆物品、居住和办公, 在燃气设施的专用房间内使用明火；\n（六）使用明火检查燃气泄漏；\n（七）将燃气管道作为负重支架或者电器设备的接地导线；\n（八）其他危害公共安全和公共利益的供用气行为。', '855', '');
INSERT INTO `t_item_tbl` VALUES ('335', '液化石油气钢瓶应定期检测。', '855', '');
INSERT INTO `t_item_tbl` VALUES ('336', '地下空间的使用人，应当遵守下列规定：不得存放液化石油气钢瓶，不得使用液化石油气和闪点小于60°C的液体做燃料。', '856', '');
INSERT INTO `t_item_tbl` VALUES ('337', '钢瓶存储要求：地下空间内严禁使用液化石油气。', '856', '');
INSERT INTO `t_item_tbl` VALUES ('338', '餐饮经营单位操作间的集烟罩和烟道入口处1米范围内，应当每日进行清洗。中餐操作间的排油烟管道应当每60日至少清理1次，清理应当做好记录。', '857', '');
INSERT INTO `t_item_tbl` VALUES ('339', '杜绝存放液化石油气瓶、易燃易爆物品', '858', '');
INSERT INTO `t_item_tbl` VALUES ('340', '不得在地下空间内从事危险化学品、烟花爆竹等危险物品的生产经营。不得在地下空间内储存易燃易爆物品。', '858', '');
INSERT INTO `t_item_tbl` VALUES ('341', '不得租赁给不具备安全生产条件的承租户', '858', '');
INSERT INTO `t_item_tbl` VALUES ('342', '按照国家有关消防安全技术规定安装、使用电器产品，设计、敷设用电线路；禁止超负荷用电。 ', '859', '');
INSERT INTO `t_item_tbl` VALUES ('343', ' 利用地下空间从事商业、文化娱乐业、旅店业以及其他生产经营活动或者作为居住场所的，地下空间的产权人、管理单位应当保证地下空间符合下列条件：（一）符合防火、卫生等管理规定，并经公安消防机构、卫生主管部门检查合格。（二）房屋建筑安全，不存在危险构件。 （三）具有上下水、卫生间、用电设施。 （四）通风良好，设置机械通风系统或者空气调节装置，并保证有效使用。地下空间平时使用必需的新风量，以及相应的新风系统、回风系统等设置符合设计规范要求。  （五）具有防汛、防雨水倒灌设施。 （六）按规定设置和配备机械防烟排烟系统、自动喷淋系统、应急照明系统、火灾自动报警系统以及其他消防设施和器材。', '860', '');
INSERT INTO `t_item_tbl` VALUES ('344', '营业区域内实际容纳的消费者人数不得超过最大容纳人数。最大容纳人数按照营业区域的公共面积计算，超市人均不得小于0.8平方米，其他商业零售经营单位人均不得小于0.6平方米。当接近最大容纳人数或者人员相对聚集时，商业零售经营单位应当采取有效的控制和疏散措施，确保安全。', '861', '');
INSERT INTO `t_item_tbl` VALUES ('345', '营业区域的公共面积占营业区域总面积的比例，超市不得小于35%，其他商业零售经营单位不得小于40%。', '862', '');
INSERT INTO `t_item_tbl` VALUES ('346', '不得在营业场所内设置宿舍或饭堂。', '863', '');
INSERT INTO `t_item_tbl` VALUES ('347', '商业零售经营单位的库房不得设置移动式照明灯具，不得使用碘钨灯、超过60瓦的白炽灯等高温照明灯具。灯具与货物的间距不得小于0.5米。日光灯的镇流器应当采取隔热、散热等防火措施。', '864', '');
INSERT INTO `t_item_tbl` VALUES ('348', ' 库房内敷设的配电线路，需穿金属管或用非燃硬塑料管保护。电气设备应当符合防爆要求。', '865', '');
INSERT INTO `t_item_tbl` VALUES ('349', '仓库应当设置醒目的防火标志', '866', '');
INSERT INTO `t_item_tbl` VALUES ('350', '库存物品应当分类、分垛储存，每垛占地面积不宜大于一百平方米，垛与垛间距不小于一米，垛与墙间距不小于零点五米，垛与梁、柱间距不小于零点三米，主要通道的宽度不小于二米。危险物品应当单独存放，专人管理。', '867', '');
INSERT INTO `t_item_tbl` VALUES ('351', '不得在库房内设置宿舍或饭堂。', '868', '');
INSERT INTO `t_item_tbl` VALUES ('352', '应当持有《户外广告登记证》，设置户外广告、牌匾是否进行经常性检查和维护，确保安全、牢固。', '869', '');
INSERT INTO `t_item_tbl` VALUES ('353', '地下空间出入口应按设计规范使用，用于人员聚集场所的每个防火分区应具有2个以上的安全出入口，且人民防空工程应具备与居民出入相分离的室外主要出入口。人防工程地面管理用房应与其地下空间配套使用。', '870', '');
INSERT INTO `t_item_tbl` VALUES ('354', '地下空间内的机械排烟设施、自动喷水灭火系统、火灾自动报警系统的设置，应按照国家标准《人民防空工程设计防火规范》的有关规定执行。', '871', '');
INSERT INTO `t_item_tbl` VALUES ('355', '安全出入口、疏散通道、楼梯口及公共活动场所等部位应按规定设置符合标准的疏散指示标志和应急照明设施，并保证其应急照明时间连续供电不少于30分钟。', '871', '');
INSERT INTO `t_item_tbl` VALUES ('356', '疏散指示标志应由疏散方向标志灯、荧光疏散指示标志和安全出口标志灯组成。疏散方向标志灯、荧光疏散指示标志应设置在疏散通道、楼梯间及其拐角处的墙面上，并不高于室内地坪1米，其间距不应大于15米；安全出入口标志灯应设置在安全出入口上部的顶棚下或墙面上。', '872', '');
INSERT INTO `t_item_tbl` VALUES ('357', '使用可燃气体作燃料时，应采用管道供气，且使用可燃气体的房间或部位应有自然通风窗井。', '873', '');
INSERT INTO `t_item_tbl` VALUES ('359', '杜绝存放液化石油气瓶、易燃易爆物品', '875', '');
INSERT INTO `t_item_tbl` VALUES ('360', '不得在地下空间内从事危险化学品、烟花爆竹等危险物品的生产经营。不得在地下空间内储存易燃易爆物品。 ', '875', '');
INSERT INTO `t_item_tbl` VALUES ('361', '不得租赁给不具备安全生产条件的承租户', '875', '');
INSERT INTO `t_item_tbl` VALUES ('362', '按照国家有关消防安全技术规定安装、使用电器产品，设计、敷设用电线路；禁止超负荷用电。 ', '876', '');
INSERT INTO `t_item_tbl` VALUES ('363', ' 利用地下空间从事商业、文化娱乐业、旅店业以及其他生产经营活动或者作为居住场所的，地下空间的产权人、管理单位应当保证地下空间符合下列条件：（一）符合防火、卫生等管理规定，并经公安消防机构、卫生主管部门检查合格。（二）房屋建筑安全，不存在危险构件。 （三）具有上下水、卫生间、用电设施。 （四）通风良好，设置机械通风系统或者空气调节装置，并保证有效使用。地下空间平时使用必需的新风量，以及相应的新风系统、回风系统等设置符合设计规范要求。  （五）具有防汛、防雨水倒灌设施。 （六）按规定设置和配备机械防烟排烟系统、自动喷淋系统、应急照明系统、火灾自动报警系统以及其他消防设施和器材。', '877', '');
INSERT INTO `t_item_tbl` VALUES ('364', '杜绝违章指挥、违章操作、违反劳动纪律的行为', '878', '');
INSERT INTO `t_item_tbl` VALUES ('365', '传动部位防护要求：\r\n（一）如有下列情况之一的，应安装防护罩、盖或栏：\r\n1、以操作人员站立平面为基准，高度在2米以下的外露传动部位；2、旋转的键、销、楔等突出大于3毫米的部位；3、产生切屑、磨屑、冷却液等飞溅，可能触及人体或造成设备与环境污染的部位；4、产生射线或弧光的部位；5、伸入通道的超长工件；6、超出设备后端300毫米以上的工件；7、容易伤人的设备往复运动部位；8、悬挂输送装置跨越通道的下部；9、高于地面0.7米的操作平台。\r\n（二）防护罩、盖、栏应满足以下要求：1、有足够强度和刚度，无明显锈蚀和变形；2、安装牢固。\r\n（三）食品制造企业涉及烘制、油炸等设施设备，应采取防过热自动报警切断装置和隔热防护措施。', '757', '《企业安全生产标准化基本规范》（AQ/T9006-2010）');
INSERT INTO `t_item_tbl` VALUES ('366', '各种限位装置状况：（一）各类行程限位装置应安全可靠，运动机构的行程限位应在规定的范围内；\n（二）凡限制运动件范围的，应装设限位开关，且固定牢靠有效。', '758', '《企业安全生产标准化基本规范》（AQ/T9006-2010）');
INSERT INTO `t_item_tbl` VALUES ('367', '（一）剪切机的机架及其他零、部件外露的表面，不准有锯齿状及锐利的棱角或突起等危险部分；（二）剪切机的重要部件上所使用的螺栓、螺母、销钉等紧固件，必须采取严格的防松措施。', '759', 'GB6077-85《剪切机械安全规程》');
INSERT INTO `t_item_tbl` VALUES ('368', '（一）对于外露的传动齿轮、皮带轮、飞轮、联轴节等传动零、部件，均应装设防护罩或挡板；（二）当飞轮未进入正常运转时，不允许进行剪切工作；（三）飞轮应用红色箭头表示转动的方向。', '760', 'GB6077-85《剪切机械安全规程》');
INSERT INTO `t_item_tbl` VALUES ('369', '（一）剪切机的刀片在安装或更换时，必须正确、牢固、可靠、安全、省力；（二）上、下刀片间隙调整安装应具有调整准确、锁紧可靠的结构', '761', 'GB6077-85《剪切机械安全规程》');
INSERT INTO `t_item_tbl` VALUES ('370', '（一）剪切机上应有润滑、操纵和安全等各种标牌和标志。对于无法加保护装置的危险部位，应有必要的危险标志（二）为了防止剪切机的剪切料或碎片的迸濺飞出，必须在其危险区加设防护挡板。', '762', 'GB6077-85《剪切机械安全规程》');
INSERT INTO `t_item_tbl` VALUES ('371', '（一）包装机械的安全要求除应符合本标准规定外，还应符合各有关专业标准的规定。\n（二）包装机械应在明显部位固定产品标牌，并写明机器或设备正常工作所必需的主要技术参数，如：额定电流和电压、额定压力和热温度等。\n（三）包装机械上可造成人身危害的危险部位必须采取相应的安全措施。作相对运动的部件，如夹紧、剪切等运动部件之间必须保证安全距离，以防操作人员被夹伤或剪伤。可造成危险的传运装置应尽可能置于机体内。外露的影响人身安全的旋转、移动或往复运动的零部件必须对其采取防护措施。运动部件上外露的定位件或突出部分必须平滑或施加防护。\n（四）机械运转中有可能松脱的零部件必须有可靠的防松措施。\n（五）包装机械需进行排废料、排烟时。应装有相应的装置，以避免对操作人员造成危害。\n（六）包装机械上仅要求电机单向旋转时必须在电机上或在适当部位作出转向标记。\n（七）人员进入工作区有较大危险时，应在工作区设置安全防护装置。当防护装置将包装机械的整个工作区域隔离时，则工作区内的危险部件可不另设护装置。工作区防护装置的设置应保证安全距离。\n(八）需离地进行操作或设整的包装机械，应设有符合有关标准要求的平台及通向平台的梯子及防护栏杆等相应装置。\n（九）包装机械工作机构应具有联锁保护以便在故障发生时使机器或设备停止工作。\n（十）包装机械上一般应装有安全开关，在任何紧急情况下，按下此开关即能停机，以避免事故的发生。\n（十一）当机器或设备进入运动状态之前，需提醒一切人员及是离开危险区域的情况下，包装机械上应装设警装置。\n（十二）包装机械上应有清晰醒目的操纵、润滑、安全或警告等各种标志。安全色及安全标志应符合GB2893和GB2894的规定，标志中的图形符号应符合有关标准或规定。\n（十三）包装机械的安全操作机构应位于操作人员易操作和控制的位置。\n（十四）处于易燃爆环境下工作的包装机械，其机械与电器装置应有可靠的防护措施并符合有关标准或规定。', '763', '《机械电气安全》（GB5226）');
INSERT INTO `t_item_tbl` VALUES ('372', '（一）包装机械上安全防护装置应保证在操作人员遵守操作规程的条件下应确保人身的安全。\n（二）对操作人员具有危险的外露运动部件，应设有防护罩。（三）供观察危险部位运动情况的防护罩应由安全透明材料或其它网状材料制作。\n（四）打开防护装置有可能造成危险时，该装置应与包装机械传动机构联锁。\n（五）对于采用光电控制的隔离防护装置时，当人员靠近危险工作区时，应做到及时停机。\n（六）包装机械在工作期间，有可能因被包装物品（如充填可燃体）产生燃烧、爆炸或因其装置产生辐射，危险时，其防护装置应采用能防止引起燃烧爆炸和辐射，以保证操作人员安全的结构和材料。\n（七）安全防护装置的固定均应可靠，不会自行打开，应有足够的强度、刚度、稳定性和耐腐蚀性。', '764', '《机械电气安全》（GB5226）');
INSERT INTO `t_item_tbl` VALUES ('373', '（一）包装机械仅允许受过技术培训的人员进行操作与维护。\n（二）包装机械应具有能够正确指导人员进行操作的使用说明书。\n（三）包装机械就定期进行检查，以保证机器或设备的功能和所有零部件以及安全防护措施处在正常工作状态下。\n（四）包装机械应尽可能在不需拆除安全防护装置的情况下即能进行调整、润滑和日常保养。\n（五）包装机械上难以实现手工润滑或工作期间需在危险区内润滑的部件，应能实现自动润滑。\n（六）在包装机械工作期间，一般不得对运动部件进行清洗或清理。若必须在工作状态下进行时，则必须采取消除事故的防范措施。如使用合适的辅助工具。\n（七）需在运动状态下对包装机械进行调整、维护和排除故障时，则仅允许通过手动或借助于点动按钮开关使包装机械进行运动。\n（八）如果在调整和维护时，机器或设备的按钮开关位于操作人员所在操作位置之外，或者几名人员同时工作，则应由专人在可进行工作监视以及能使机器或设备迅速停机的工作位置进行操作。只要工作在进行，则该人员不允许离开其位置。\n（九）在包装机械工作时，在有可能造成操作人员眼和脸部受伤的情况下，应对眼和脸部采取防护措施。', '765', '《机械电气安全》（GB5226）');
INSERT INTO `t_item_tbl` VALUES ('374', '保险机构：(1)破坏式剪切销。这种带剪切销的保险机构是在连接两个轴的部位有两个圆盘，两圆盘靠孔中用销子连接，销子强度小于传动系统中最弱的零件，当设备超负荷传动时，销子首先切断，使被动轴停止转动。\n(2)自动保险机构。在机床传动中，摩擦离合器、齿爪式离合器、安全阀、脱落蜗杆等已成为机床本身的传动部件。一般情况下，这些机构随机床正常工作，当由于某种原因机床出现过载时，这些保险机构就会由于摩擦力不足而打滑，使动力中断传输，被动轴停止转动。当负荷恢复正常时，能自动复位。', '766', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('375', '制动机构：在大批量生产中多使用气动或液压夹具，由于工作紧张，往往发生夹具未夹紧工件即开车加工，以致工件飞出而造成事故。', '766', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('376', '自动停车机构：自动停车机构的主要作用是在机床受到阻碍或在预定位置碰到挡块时，有关部件能自动停止运动。', '766', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('377', '互锁机构是一种更完善的安全装置，能直接防止操作失误而引起的伤害。', '766', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('378', '机械的安全要求和措施：（一）可能触及到的外露部分应尽量平整、光滑，不应有可能导致刺伤或划伤人员的尖棱、尖角、凸出部分和开口。（二）溜板纵向运动应设有可靠的限位装置。\n（三）机床飞轮应采取切实可靠的防护措施，确保在运动中不会因其松脱、甩出及缠绕而出现危险。\n（四）外露带和带轮应设置防护罩。应在遮蔽危险部位的防护罩内表面、挂轮箱内表面涂上安全色，以提醒操作、调整和维护人员注意危险的存在。\n（五）因位置原因，在运动中可能造成缠绕危险的花键轴、丝杠应采取切实可靠的安全防护措施。\n（六）控制系统不应有导致危险情况的出现。\n（七）电力偶然中断，不应给人员造成危险；电力重新恢复后，机床不应自行起动。\n（八）按钮、指示灯、开关等控制器件的位置应确保操作时不会引起误操作和附加的危险。\n（九）控制器件应清晰可辨，易于区别，必要时应设置表示其功能或用途的标志。标志一般应符合GB／T3167的规定。\n（十）在每个“起动”控制器件附近应设置一个“停止”控制器件。在每个操作位置应设置一个紧急停止控制器件，紧急停止控制器件的动作不应影响保护操作者或机床装置的功能。\n（十二）冷却系统应能保证操作者的人身安全。\n（十三）机床应能有效地回收冷却液，应设置防止冷却液飞溅的防护挡板。（十四）应设置防止切屑飞溅的防护挡板。\n（十五）冷却液喷嘴应能方便、安全、可靠地固定在所需位置上。', '766', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('379', '离合器要求：（一）刚性离合器的转键、键柄和直键无裂纹；\n（二）操纵器的杆件、销钉或弹簧无裂纹、折断；\n（三）离合器安装紧固，动作可靠，没有连冲；\n（四）滑块从上死点至上死点25毫米处行程范围内需要制动时，离合器应对滑块制动。', '767', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('380', '制动器要求：（一）制动器与离合器必须相互协调与连锁，开机时动器先松闸，离合器稍滞后再结合；\n（二）连锁应准确、紧固，工作稳定、协调；\n（三）行程限位装置、控制装置正确，动作灵敏有效。', '767', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('381', '紧停装置要求：（一）大型冲压机械前后都应安装急停按钮，位置在人手可迅速触及的地方；（二）剪板机剪板长度大于2500毫米时，两侧应安装紧停装置；（三）紧停装置应有红色标记。', '767', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('382', '距作业平台2米以上旋转部位应装设防护罩或防护栏。', '767', '《机械设备防护罩安全要求（GB8196-87）');
INSERT INTO `t_item_tbl` VALUES ('383', '脚踏开关要求：（一）外露在工作台面的脚踏开关，只要有物体撞压可能的，应在开关上部和两侧装设防护罩；（二）剪板机脚踏杠在整个长度上应装设防护罩；\n（三）脚踏开关的踏面应用防滑的花纹钢板制作；\n（四）防护罩有足够的强度，无棱角毛边，便于开关检修。', '767', 'GB8196-87《机械设备防护罩安全要求》、JB8781-1998《剪板机安全技术要求》5.2.6、');
INSERT INTO `t_item_tbl` VALUES ('384', '冲、剪、压、钻机械的金属结构件、电机、变压器、穿线金属管等都应有接地保护', '767', '《用电安全导则》');
INSERT INTO `t_item_tbl` VALUES ('385', '安全防护附件要求：（一）必须采取各种安全措施，确保在滑块下落时，操作者任何部位不可能进入作业危险区；\n（二）使用双按钮之间的内侧距离必须大于25厘米。', '767', '《冷冲压安全规程》（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('386', '位置和朝向要求：（一）安装地点应保证人员和设备安全；（二）朝向不得对着通道、门口和操作岗位。\n防护罩应符合国家安全标准。\n挡屑板应有足够强度。\n砂轮无裂纹、无破损。\n法兰盘使用软垫连接。\n托架安装牢固可靠。\n运行平稳，砂轮磨损量符合要求。\n接地可靠，控制电器符合规定。', '768', '《麿削机械安全规程》（GB4678-84）');
INSERT INTO `t_item_tbl` VALUES ('387', '电源线、电源插头应采用橡皮护套绝缘软线。与电源联接，应采用开关、插头座。严禁用导线直接插入插座，或挂在电源线上使用。', '769', '《电气安全工作规程》第88条');
INSERT INTO `t_item_tbl` VALUES ('388', '工具必须由专职人员每年至少检查一次，并记录。', '769', '《手持式电动工具的管理、使用、检查和维修安全技术规程》第6.3条');
INSERT INTO `t_item_tbl` VALUES ('389', '在使用移动式的I类设备时，应先确认其金属外壳或构架已可靠接地，使用带保护接地极的插座，同时宜装设漏电保护器，禁止使用无保护线插头插座。', '769', '《用电安全导则》第4.16条');
INSERT INTO `t_item_tbl` VALUES ('390', '露天使用的用电设备、配电装置应采取防雨、防雪、防雾和防尘的措施。', '769', '《用电安全导则》第4.25条');
INSERT INTO `t_item_tbl` VALUES ('391', '电源线、焊接电缆与焊机连接处的裸露接线板，应采取安全防护罩或防护板隔离，以防止人员或金属物体接触。', '770', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('392', '焊机外壳防漏电保护要求：（一）焊机外壳必须接地或接零保护，接地或接零装置连接良好，并定期检查；\n（二）严禁使用易燃易爆气体管道作为接地装置；\n（三）在有接地或接零装置的焊件上进行弧焊操作，或焊接与地面密切连接的焊件时，应注意避免焊机和工件的双重接地。', '770', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('393', '绝缘电阻要求:（一）焊接变压器一、二次绕组、绕组与外壳之间的绝缘电阻值不小于1兆欧；\n（二）每半年应对焊机绝缘电阻遥测一次，且记录完整。', '770', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('394', '焊机一次线使用要求：（一）焊机一次侧电源线长度不超过3米；\n（二）一次线不得在地面拖拽使用，不得在地面跨越通道使用；\n（三）电焊机应接在专用开关上使用，不得与其它设备共用一个开关；（四）在高处或金属容器内工作时应设专人监护。', '770', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('395', '焊机二次线使用要求：（一）焊机二次线必须连接紧固，无松动；（二）二次线接头不超过3个；\n（三）二次线截面选用正确，避免长期过载使用造成绝缘老化；\n（四）严禁使用厂房金属结构、管道、轨道等作为焊接二次回路使用。', '770', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('396', '焊钳夹紧力好，绝缘良好，手柄隔热层完整，焊钳与导线连接可靠。', '770', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('397', '焊机使用场所要求：（一）焊机应安放在通风、干燥、无碰撞、无剧烈震动、无高温、无易燃品存在的地方；\n（二）在室外或特殊环境下使用，应采取防护措施保证其正常使用。雨、雪、雾天，无防护措施不准作业；（三）焊机使用场所应清洁，无严重粉尘；\n（四）操作人员应穿绝缘鞋、防护服、面罩（护镜）、电焊手套。', '770', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('398', '旋转部位防护要求：（一）炊事机械的传动部位的皮带轮、齿轮、链轮与链条、联轴器等，应加防护罩或防护盖，不便安装的应加设防护栏；（二）防护罩、盖和栏完好可靠，强度符合要求。', '771', '《家用及类似用途电器的安全》（IEC60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('399', '电气部分要求：（一）炊事机械的金属外壳、电动机外壳的PE线均应可靠；（二）电源线最好穿管敷设，若采用橡胶电缆时，应敷设在无浸泡、无高温和无压砸的沿墙壁面；（三）线路不得有接头、破损、老化或过载；（四）随机配备的电气箱柜应符合要求。', '771', '《家用及类似用途电器的安全》（IEC60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('400', '控制开关要求：（一）炊事机械电源控制开关应单机单设，不得几台设备共用一个开关或用距离较远的闸刀控制；（二）有尘、水可能进入的按钮开关应有防护装置，且不防碍操作。', '771', '《家用及类似用途电器的安全》（IEC60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('401', '搅拌、合面机要求：（一）应加盖密封，容器盖应采用规定的材料制作；（二）必须安装机盖联锁安全装置，联锁开关应固定在机器本体上，启盖应即刻断电。', '771', '《家用及类似用途电器的安全》（IEC60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('402', '绞肉、绞菜机要求：（一）加料口护手装置应保证操作人员手指不能触及刀口或螺旋部位，加料口较大的其高度应适当增加，否则应设小口径加料托盘；（二）护手装置、加高的加料口和加料托盘应用耐腐蚀的材料制作，具有足够的强度，与加料口连接固定，应能翻转清洗；（三）应配备送料的辅助工具。', '771', '《家用及类似用途电器的安全》（IEC60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('403', '压面机要求：（一）轧辊应便于装拆，调整灵活、定位可靠；（二）加料处应有防护装置，防止手指伸入；（三）备有专用刮面板，不得用手推、刮面粉。', '771', '《家用及类似用途电器的安全》（IEC60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('404', '果蔬压榨机、分离机过滤机(果蔬类饮料企业):\n1.各固定螺栓齐全无松动、变形。\n2.传动部位安全防护装置齐全、可靠。\n3.设备各部分密封良好，无泄漏、渗漏现象。\n4.刮板链条和传动链条松紧一致，无卡碰机壳现象。\n5.进出液管路阀门配置正确合理，工作压力符合要求。\n6.急停开关，保护开关、按钮灵敏可靠。', '772', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('405', 'PET瓶吹瓶机：\n1.检查各气动元件是否漏气，动作是否灵敏。2．检查高压气源，低压气源、电源、水源是否正常。3．检查三联体是否异常、漏气、是否堵塞。4．检查各急停开关，安全门开关、保护装置检测开关是否正常。5．全自动吹瓶机电磁阀遇有异常时要及时清洗。6．生产前检查各运动部件是否牢固，螺丝是否松动脱落，特别是冲击力强的地方及传动部分。', '773', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('406', '温瓶机：\n 1.外漏的旋转部位应安装防护装置，并确保完好有效。 2.紧固件、连接件的锁紧装置应完整、可靠。3.电气设备的绝缘、屏护、防护间距应符合相关规定。急停装置、连锁装置、操作按钮应标识清晰，灵敏可靠，并有故障报警装置。4.设备附属仪表灵敏准确。5.加热器无泄漏，各蒸汽管道应符合有关规定。\n6.观察窗玻璃无破损，密封条无泄漏。玻璃强度和刚度应与温度相适应。\n7.PE线应连接可靠，线径截面积及安装方法符合相关规定，保护回路齐全可靠。', '774', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('407', '洗瓶机（限于玻璃瓶、瓷瓶）：\n1.机械系统、电气自控系统、温度自控系统、液碱自控系统等各部位正常，润滑良好，防护装置完整齐全。\n2.查看槽水箱视窗开关应与停车、停止喷淋联锁。', '775', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('408', '灌装、压盖、包装机、溶糖及配料设备：\n1.固体饮料包装设备应满足粉尘防爆安全要求，采用防尘结构的粉尘防爆电气设备（电动机、灯具等），电缆线路不应有中间接头，且应采用阻燃电缆，电缆接线盒应为防尘型或尘密型。\n2.对易导致糖粉等沉积、扬尘的区域，应采取防爆措施。', '776', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('409', '易拉罐液位检测机：\n1.在设备上设置明显的辐射警示标识。\n2.在检测机主射线方向侧采用铅板屏蔽。\n3.定期请有资质的单位对所在的作业场所进行检测，检测结果符合相关规定。\n4.放射源应有使用登记证。', '777', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('410', '输送机械：\n1.安全装置：\n（1）皮带输送机在两边应设置防跑偏挡轮，并运转灵活，销轴无窜动；转弯处应设置防倾覆装置。\n（2）驱动装置中应设置过载保护装置，且运行可靠。\n（3）链式输送机上坡、下坡处应设置止退器或捕捉器，并运行可靠，转弯处应设置防倾覆装置。\n（4）垂直升降机应设置上升、下降限位装置及止挡器，并设有防护栏，其门应设置联锁装置。\n2.通道、梯台和防护网（栏）：\n（1）输送机械下方的通道净空高度应大于2米。\n（2）输送机械上坡、下坡段或下面有人员通过的部位，应在输送机械的下面设置坚固的防护网（板）；输送机械穿越楼层而出现孔口时应设护栏，在人员能接近的重锤张紧装置下方应设立防护栅（栏）。\n（3）人员需经常跨越输送机械的部位应设置人行过道（桥）。\n（4）工业梯台应符合GB50205及GB4053的相关规定；防护护网（栏）的安全距离应符合GB23821的相关规定。', '778', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('411', '卸罐机、码垛机、装箱机、贴标机：\n 1.外漏的旋转部位应安装防护装置，并确保完好有效。\n 2.紧固件、连接件的锁紧装置应完整、可靠。\n3.电气设备的绝缘、屏护、防护间距应符合相关规定。急停装置、连锁装置、操作按钮应标识清晰，灵敏可靠，并有故障报警装置。\n4.设备附属仪表灵敏准确。\n5.PE（地）线应连接可靠，线径截面积及安装方法符合相关规定，保护回路齐全可靠。', '779', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('412', '喷码机：\n1.开机前检查电气线路，气动管路是否完好。\n2.进行高度调整，观察墨点有无缺失。\n3.附属设备运转正常，附属仪表齐全灵敏。\n4.设备内外整洁无污垢，安全防护装置齐全可靠。\n5.电气系统装置齐全，接地良好。', '780', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('413', '膜缩包机：\n1.传动系统运转正常，各传动部位工作协调一致。\n2.安全防护装置齐全可靠。\n3.热风箱工作运行无堵塞现象，热风温度控制正常。\n4.急停开关，保护开关、按钮灵敏可靠。电气系统装置齐全，接地良好。', '781', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('414', '制冷站（制冷设备及站房）：\n1.液氨储罐有喷淋。\n2.液氨储罐应按规定设置围堰。\n3.事故排风机防爆且有连锁装置。\n4.现场设置洗眼器，配备呼吸器、防护服、防毒面具、应急药箱等。\n5.安装氨气浓度报警器,气体浓度报警装置应有备用电源。\n6.氨制冷机房应设置事故排风机，在控制室排风机控制柜上和制冷机房门外墙上应安装人工启停控制按钮。\n7.事故排风机必须选用防爆型,事故排风机应按二级负荷供电，当制冷系统因故障被切除供电电源停止运行时，应保证排风机的可靠供电。\n8.在氨制冷机房门口外侧便于操作的位置，应设置切断制冷系统电源的紧急控制开关，并应设置警示标识。\n9.每套制冷压缩机组启动控制柜（箱）及机组控制台应设紧急停机按钮。\n10.制冷系统应装设紧急泄氨器，在紧急情况下，可将系统中的氨液溶于水中，排至贮罐、水池。\n11.现场有防液氨泄露中毒应急预案。\n12.现场设风向标。\n13.管道标明介质、流向、安全色。\n14.氨压缩机房的自动控制室或操作人员值班室应与机器间隔开，并设固定密封观察窗。\n15．氨制冷机房、应设置应急照明，照明灯具应选用防爆型。\n16.控制室门向疏散方向开启。', '782', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('415', '冷库：\n1.库房公路站台应符合下列规定：\n（1）站台宽度不宜小于5.0米。\n（2）站台边缘停车侧面应装设缓冲橡胶条块，并应涂有明显的黄、黑相间防撞警示色带。\n（3）站台上应设罩棚，靠站台边缘一侧如有结构柱时，柱边距站台边缘净距不宜小于0.6米，罩棚净高应与运输车辆的高度相适应，且应设有排水设施。\n（4）根据需要可设封闭站台，封闭站台应与冷库穿堂合并布置。\n2.库房的楼梯间应设在靠穿堂处，并应采用不燃材料建造。首层楼梯出口应直通室外或距直通室外的出口不大于15米。\n3.建筑面积在1000米2以上的冷藏间应至少设两个冷藏门（含隔墙上的门）。冷藏门内侧应设有应急内开门锁装置，并应有醒目的标识，防止人员被困。\n4.在库房内严禁设置与库房生产、管理无直接关系的其他用房。\n5.库房的隔热材料应为不散发有害或异味等对食品有污染，且难燃或不燃烧\\不易变质的物质。\n6.冷库宜采用钢筋混凝土结构或钢结构。小型冷库如采用砖混结构，应采取措施防止因冻融循环而损害结构。\n7.冷间内照明灯具宜选用外壳防护等级为IP54级并带有保护罩的防潮型灯具。布置在低温潮湿的穿堂内的配电箱应采用防潮密封型。当不集中设置照明配电箱，各冷间照明控制开关分散布置在冷间外穿堂上时，应选用带指示灯的防潮型开关或气密式开关。\n8.冷库宜采用AC220V/380VTN-S或TN-C-S配电系统。冷间照明支路宜采用AC220V单相供电，灯具的金属外壳应接专用保护线。当灯具安装高度等于或小于2.2米时应采用AC24V安全电压供电。各照明支路应设置剩余电流保护装置。\n9.冷间内动力、照明、控制线路应根据不同的冷间温度要求，选用适用的耐低温的铜芯电力电缆，并宜明敷。\n10.采用松散保温材料（如稻壳）的冷库阁楼层内不应安装电气设备及敷设电气线路。\n11.冷库货运出入口应设置限高、限速标志。各库房之间若采用机动车运输，应根据需要在各库房货运通道口设置防撞桩，防撞桩应采用固定式，并涂有明显的黄、黑相间防撞警示色带。\n12.冷藏间内宜在门口附近设置呼唤按钮，呼唤信号应传送到制冷机房控制室或有人值班的房间，并应在冷藏间外设有呼唤信号显示。设有呼唤信号按钮的冷藏间，应在冷藏间内门的上方设长明灯。设有专用疏散门的冷藏间，应在冷藏间内疏散门的上方设置长明灯。', '783', '《饮料生产企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('416', '（一）对于从事自动化生产线的维修、操作的人员必须进行安全技术培训。\n（二）正确使用安全装置，不得擅自改变安全装置的结构，降低或消除设备设施的本质安全。\n（三）在有可能进入自动化生产线危险区域的地方，应设置安全装置（光电、光删、安全地毯等）并与自动化生产线动作实现联锁。', '784', '《机械电气安全》（GB5226）');
INSERT INTO `t_item_tbl` VALUES ('417', '（一）工作中按规定正确穿戴好劳动保护用品；（二）严格遵守本岗位工艺纪律及操作程序；（三）所使用的工具，要符合其使用规定，并放在指定位置；（四）工作中注意安全警告标志提示，严禁穿行、跨越生产线；（五）当所用设备及安全装置发生故障时，要找专业人员处理，不得擅自处理；（六）进入危险区域内作业，必须使用安全装置（拔下安全销等），并设专人监护，保证安全；（七）发生人身及设备事故，要及时果断地按下急停按钮，并保护好现场。', '785', '《机械电气安全》（GB5226）');
INSERT INTO `t_item_tbl` VALUES ('418', '应急照明设施并确保完好、有效。', '786', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('419', '按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，确保完好有效。', '787', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('420', '变配电室变压器、高压开关柜、低压开关柜操作面地面应铺设绝缘胶垫。', '788', '参考《变配电室安全管理规范》第5.3条');
INSERT INTO `t_item_tbl` VALUES ('421', '变配电室出入口应设置高度不低于400mm的挡板。', '789', '参考《变配电室安全管理规范》5.6条');
INSERT INTO `t_item_tbl` VALUES ('422', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '1613', '参考《变配电室安全管理规范》第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('423', '通往室外的门应向外开。设备间与附属房间之间的门应向附属房间方向开。高压间与低压间之间的门，应向低压间方向开。配电装置室的中间门应采用双向开启门。', '790', '参考《变配电室安全管理规范》第5.8.3条');
INSERT INTO `t_item_tbl` VALUES ('424', '通往室外的窗应装有纱窗。', '790', '参考《变配电室安全管理规范》5.9.3');
INSERT INTO `t_item_tbl` VALUES ('425', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '791', '《变配电室安全管理规范》第5.18.3条');
INSERT INTO `t_item_tbl` VALUES ('426', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '791', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('427', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '791', '参考《变配电室安全管理规范》第5.18.3.2条');
INSERT INTO `t_item_tbl` VALUES ('428', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：\n（一)绝缘拉杆应悬挂或架在支架上，不应与墙接触；\n（二)绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；\n(三)绝缘垫和绝缘台应经常保持清洁、无损伤；\n（四)高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；\n（五)安全用具不允许当作其它工具使用；\n（六)安全用具不合格的不得存放在工作现场。', '791', '参考《变配电室安全管理规范》第5.18.5条');
INSERT INTO `t_item_tbl` VALUES ('429', '禁止类：禁止合闸、禁止攀登、高压危险；警告类：止步、高压危险；准许类：从此上下、在此工作。', '792', '参考《变配电室安全管理规范》第5.16.1条');
INSERT INTO `t_item_tbl` VALUES ('430', '变配电室内环境整洁，场地平整，设备间不应存放与运行无关的闲散器材和私人物品，禁止无关人员进入场地。', '793', '参考《变配电室安全管理规范》第6.8.1条');
INSERT INTO `t_item_tbl` VALUES ('431', '主控制室、高压配电室不应带入食物及储放粮食，值班室不应设置、使用寝具、灶具，并应有防止小动物的安全措施。', '793', '参考《变配电室安全管理规范》第6.8.4条');
INSERT INTO `t_item_tbl` VALUES ('432', '非变配电室从业人员因工作需要进入变配电室时，应经值班人员许可，并办理登记手续。当需要进入设备区时，应有值班人员监护。', '794', '参考《变配电室安全管理规范》第7.3条');
INSERT INTO `t_item_tbl` VALUES ('433', '柜、屏、台、箱、盘的金属框架及基础型钢必须接地（PE）或接零（PEN）可靠；装有电器的可开启门与框架的接地端子间应用裸线编织铜线连接，作好标识。', '795', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.1.2条');
INSERT INTO `t_item_tbl` VALUES ('434', '箱盘内开关灵活可靠。带有漏电保护的回路，漏电保护装置动作电流和动作时间按设计要求。', '796', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.2.7.2条');
INSERT INTO `t_item_tbl` VALUES ('435', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '796', '《剩余电流动作保护装置安装和运行》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('436', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '796', '《剩余电流动作保护装置安装和运行》第7.2条');
INSERT INTO `t_item_tbl` VALUES ('437', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。', '797', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('438', '直敷布线可用于正常环境的屋内场所，应采用护套绝缘导线。当导线垂直敷设至地面低于1.8m时，应穿管保护。', '798', '《低压配电设计规范》第5.2.1条');
INSERT INTO `t_item_tbl` VALUES ('439', '（一）设备的金属外壳应采取防漏电保护接地；\n（二）PE线若明设时，应选用不小于4平方毫米的铜芯线，不得使用铝芯线；\n（三）PE线若随穿线管接入设备本体时，应选用不小于2.5平方毫米的铜芯线或不小于4平方毫米的铝芯线；\n（四）PE线不得搭接或串接，接线规范、接触可靠；\n（五）明设的应沿管道或设备外壳敷设，暗设的在接线处外部应有接地标志；\n（六）PE线接线间不得涂漆或加绝缘垫。', '799', '《国家电气设备安全技术规范》第2.2.4条（GB19517-2004）');
INSERT INTO `t_item_tbl` VALUES ('440', '插座回路均应设置剩余电流动作保护装置。宜装设漏电保护器，禁止使用无保护线插头插座。', '800', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('441', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。不应从带插座灯头上引接电源供给电器。', '801', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('442', '临时线路使用必须经过审批，一般使用期限为过15天，最长不超过一个月。', '802', '《用电安全导则》第4.2条');
INSERT INTO `t_item_tbl` VALUES ('443', '临时用电的电气设备，必须安装剩余电流保护装置。', '803', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('444', '线路敷设应符合电气装置设置及安装规范，室内不低于2.5m，室外不低于4.5m，道路上方不低于6m。', '804', '《电气安全管理规程》第20条（1）');
INSERT INTO `t_item_tbl` VALUES ('445', '设备末端额定漏电动作电流应不大于15mA，漏电动作时间应不大于0.1s。', '805', '《潮湿作业场所用电》（GB3787-93）');
INSERT INTO `t_item_tbl` VALUES ('446', '（一）在潮湿作业场所或金属构架上等导电性能良好的作业场所，应用二类或三类工具；（二）在狭窄作业场所应有人在外监护；（三）工具在发出或收回时，保管人员必须进行一次日常检查，在使用前，使用者必须进行日常检查；四）长期搁置不用的工具，在使用前必须测量绝缘电阻；（五）绝缘电阻应不小于规定的数值；（六）工具如有绝缘损坏，电源线护套破裂、保护线脱落、插头插座裂开或有损于安全的机械损伤等故障时，应立即进行修理，在未修复前，不得继续使用；（七）在维修时，工具内的绝缘衬垫、套管不得任意拆除或漏装。', '806', '《潮湿作业场所用电》（GB3787-93）');
INSERT INTO `t_item_tbl` VALUES ('447', '我国规定42伏、36伏、24伏、12伏、6伏为安全电压的额定值。', '807', '《安全电压标准》');
INSERT INTO `t_item_tbl` VALUES ('448', '（一）手提式照明使用36伏（二）危险环境携带式电动工具使用36伏（三）金属容器内使用24伏(四)室内灯具离地面低于2.4m，手持照明灯具，一般潮湿作业场所（地下室、潮湿室内、潮湿楼梯、隧道、人防工程以及有高温、导电灰尘等）的照明，电源电压应不大于36V；（五）在潮湿和易触及带电体场所的照明电源电压，应不大于24V；（六）在特别潮湿的场所，锅炉或金属容器内，导电良好的地面使用手持照明灯具等，照明电源电压不得大于12V。', '808', '《安全电压标准》');
INSERT INTO `t_item_tbl` VALUES ('449', '（一）危险化学品应根据分类，分区、分库存放；\n（二）爆炸物品、自身能形成爆炸的危险品、腐蚀性物品应单独存放；\n（三）相互接触或混合后能引起燃烧或爆炸的物品不能同库存放；\n（四）灭火方式不同的物质不得同库存放；\n（五）遇火、遇热、遇潮能引起燃烧、爆炸或发生化学反应，产生有毒气体的化学危险品不得露天或在潮湿、积水的建筑中存放。', '809', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('450', '开关、灯具、线路、用电设施均应符合防爆要求。', '810', '《爆炸危险场所安全规定》第17条');
INSERT INTO `t_item_tbl` VALUES ('451', '作业现场不得储存、堆放其它易燃易爆物品，易燃易爆废料及时从现场清理。', '811', '《喷漆室安全技术规定》第5.2条（GB14444-2006）');
INSERT INTO `t_item_tbl` VALUES ('452', '物品堆放整齐、稳固，高度不超过2米，易挥发可燃物品桶盖随时拧紧盖严。', '811', '《工业用化学产品采样安全通则》（GB/3723-1999）');
INSERT INTO `t_item_tbl` VALUES ('453', '易燃易爆用品现场存量最好为1个班的用量。', '811', '《工业用化学产品采样安全通则》（GB/3723-1999）');
INSERT INTO `t_item_tbl` VALUES ('454', '通道保持畅通，出、入口处不得堆放易燃易爆物品。', '811', '《工业用化学产品采样安全通则》（GB/3723-1999）');
INSERT INTO `t_item_tbl` VALUES ('455', '危险化学品储存及有限空间应保持良好的通风。', '811', '《工业用化学产品采样安全通则》（GB/3723-1999）');
INSERT INTO `t_item_tbl` VALUES ('456', '作业区域应采取自然通风或机械通风，保持通风良好。', '811', '《喷漆室安全技术规定》第5.3条（GB14444-2006））');
INSERT INTO `t_item_tbl` VALUES ('457', '不得使易燃易爆和有毒有害气体在现场积存。', '811', '《涂装作业安全规程涂漆工艺安全及其通风净化》第15.5条\n（GB6514-1995）');
INSERT INTO `t_item_tbl` VALUES ('458', '封闭式易燃易爆物品使用场所，必须安装自动报警或抑爆系统\r\n白酒储存、勾兑场所应规范设置乙醇浓度检测报警装置。', '811', '《涂装作业安全规程涂漆工艺安全及其通风净化》第15.5条\n（GB6514-1995）');
INSERT INTO `t_item_tbl` VALUES ('459', '易燃易爆作业场所区域内所有金属部件、构件都应可靠接地。', '811', '《涂装作业安全规程涂屋烘干室安全技术规定》第4.1.3.2条\n（GB14443-2007）');
INSERT INTO `t_item_tbl` VALUES ('460', '易燃易爆作业场所不得从事动火作业，严禁人员吸烟。', '811', '《喷漆室安全技术规定》第6.1.5条\n（GB14444-2006）');
INSERT INTO `t_item_tbl` VALUES ('461', '专人管理，并有防潮防雨淋设施。', '811', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('462', '设置安全标志。', '812', '《安全标志》\n（GB2894-1996）');
INSERT INTO `t_item_tbl` VALUES ('463', '车间、库房等作业场所应设置防静电装置。', '813', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('464', '（一）危险化学使用实行领取制度；（二）在进行工作之前，所有人员要根据工作对象的性质配戴好防护用具（如手套、防毒面具、口罩、水鞋等）才能进行调配；（三）调配车间要有通风、换气装置，防止危化品深度积聚。', '814', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('465', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '815', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('466', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '816', '《中华人民共和国职业病防治法》第20条');
INSERT INTO `t_item_tbl` VALUES ('467', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '817', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('468', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '818', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('469', '容易造成职业危害的工序应隔离设置。', '819', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('470', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '820', '《中华人民共和国职业病防治法》第25条');
INSERT INTO `t_item_tbl` VALUES ('471', '应在可产生职业危害的工作场所和设备上，按GBZ158《工作场所职业病危害警示标识》和GBZ/T203《高度物品作业岗位职业病危害告知规范》的要求设置职业危害警示标识。', '821', '《中华人民共和国职业病防治法》第22条');
INSERT INTO `t_item_tbl` VALUES ('472', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '822', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('473', '（一）厂区内实行定置管理：厂区有定置图，对各类厂房建筑、物料堆放点、交通道路等应注明；\n（二）厂区内按照定置管理的要求实现定置摆放，厂区内无杂物、无图物不符等状况；\n（三）工业垃圾和生活垃圾分开定点存放，有防吹散、防污染措施；\n（四）危险固体废料应有专门存放地点，存放点有防渗漏措施，且符合国家规定妥善处理；\n（五）厂区大门开关灵活、方便，迅速，无卡死现象；', '823', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('474', '（一）厂区双向主干道宽度不小于5米，单向主干道宽度不小于3米，且为环形；\n（二）路面排水良好，路面平整，无台阶、无坑沟，盖板齐全；\n（三）厂区主干道在平原地区应小于6度，山区应小于8度；\n（四）厂区大门、危险路段的车速应限制在每小时5公里；人员稠密地段、下坡路、转弯路、交叉路口、装卸作业区限速每小时15公里，并设置限速标志；\n（五）交叉路口若有视线盲区，应设立反光镜，反光镜无破损，角度和高度应便于观察道路盲区；\n（六）厂区道路应有明显的人、车分流线，人行道宽度不小于1米，分流线清晰，宽度大于10厘米。', '824', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('475', '（一）利用主干道堆放物品或作为停车位置，应有划线标志，通行部分宽度必须不小于5米；\n（二）不得在转弯处或道路两侧堆放物品或停置车辆；\n（三）道路施工应有警示牌或护栏，夜间要有红灯警示；\n（四）厂区主干道占道率应小于道路长度的5%；\n（五）不得将厂区主干道横向全部堵死。', '825', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('476', '（一）照明灯布置合理，无照明盲区；\n（二）照明灯具完好率达100%。', '826', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('477', '（一）室外消防栓应合理配置，低压消防栓布置间距不超过120米，应沿道路两侧和路口设置，消防栓距路面边不超过2米，距建筑物外墙不小于5米；\n（二）有明显的漆色标志，其1米范围内无障碍物；\n（三）手提式灭火器的配置数量符合规定；\n（四）所有消防器材应完好，在有效期限内使用。', '827', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('478', '（一）搬运、安装、其他一些作业和高压线保持安全距离；（二）厂房的装修、维修、做防水等和高压线保持安全距离；（三）高压线下严禁码放材料和有建筑物。', '828', '《中华人民共和国电力行业标准》');
INSERT INTO `t_item_tbl` VALUES ('479', '（一）车间通道根据生产要求，宽度标准如下：\n通道只行人通道，宽度不小于1米；\n通过电瓶车、叉车的通道，宽度不小于1.8米；\n通行汽车的车间通道，宽度应不小于3米；\n如有铁路线，车间通道，宽度应不小于5米\n（二）通道线应明显清晰，宽度不小于10厘米', '829', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('480', '（一）通道路面应平整，无台阶、无坑、沟；\n（二）无积油、积水，无绊脚物。', '830', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('481', '（一）任何堆放在通道标记线内和压住安全通道标记线的物件，都判定为占道；\n（二）车间占道率应低于5%；\n（三）不得有物件将通道横向全部堵死。', '831', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('482', '（一）车行道上方的悬挂物高度不小于4米，人行道上方悬挂物高度不小于2.5米；\n（二）悬挂应牢固可靠。', '832', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('483', '（一）仓库通道的宽度应满足以下要求：\n车行道宽度不小于3.5米；\n人行道宽度不小于1米；\n（二）路面平坦，无积油积水，无绊脚物；\n（三）占道率应小于5%。', '833', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('484', '（一）照明灯具完好率达100%；\n（二）库房内不得使用移动式照明灯具。', '834', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('485', '（一）库房内不得存在临时线；\n（二）库房内敷设的电气线路必须，穿金属管或非燃硬塑料管；\n（三）每个库房应当在库房外单独安装开关箱；\n（四）甲、乙类物品和丙类液体库房的电气装置，必须安装防爆型电气装置，储存丙类固体物品的仓库，不得使用碘钨灯或超过60瓦以上的白炽灯等高温照明灯具。', '835', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('486', '（一）防火制度、消防设施标志、防火标志齐全；\n（二）按标准配备消防器材的数量和种类；\n（三）防火通道畅通，无封死和堵塞现象。', '836', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('487', '以下仓库应安装火灾报警装置：\n1、甲、乙、丙类物品储备库；\n2、专业性仓库；\n3、大型物资仓库；\n4、耐火等级为三、四级的仓库；', '837', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('488', '（一）物品包装应牢固，不得有严重破损、残缺、变形、物品变质、分解等现象；\n（二）甲、乙类物品的包装应严密，不得有跑、冒]滴、漏现象；', '838', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('489', '杜绝违章指挥、违章操作、违反劳动纪律的行为', '839', '国家安监总局15号令第44条');
INSERT INTO `t_item_tbl` VALUES ('490', '应急照明设施并确保完好、有效。', '728', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('491', '生产经营场所应当设有符合紧急疏散要求、标志明显、保持畅通的出口。禁止封闭、堵塞生产经营场所的出口。', '729', '《中华人民共和国安全生产法》第34条');
INSERT INTO `t_item_tbl` VALUES ('492', '室外集贸市场不得堵塞消防车通道和影响公共消防设施的使用；与甲、乙类火灾危险性的厂房、仓库和易燃、可燃材料堆场要保持50米以上的安全距离。', '729', '《集贸市场消防管理办法》第14条');
INSERT INTO `t_item_tbl` VALUES ('493', '集贸市场要按商品的种类和火灾危险性，划分若干区域，区域之间保持相应的安全疏散通道。', '729', '《集贸市场消防管理办法》第16条');
INSERT INTO `t_item_tbl` VALUES ('494', '按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，确保完好有效。', '730', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('495', '公共消防设施、器材，应当布置在明显和便于取用的地点，明确专人管理。任何人不得将公共消火栓圈入摊位内。', '730', '《中华人民共和国安全生产法》第27条');
INSERT INTO `t_item_tbl` VALUES ('496', '集贸市场内严禁燃放烟花爆竹和焚烧物品。在划定的严禁烟火的部位或区域，应当设置醒目的禁烟火标志。', '731', '《集贸市场消防管理办法》第17条');
INSERT INTO `t_item_tbl` VALUES ('497', '集贸市场应当配备基本的消防通讯和报警装置，一旦发生火灾能做到及时报警。', '732', '《集贸市场消防管理办法》第28条');
INSERT INTO `t_item_tbl` VALUES ('498', '投入使用后的防雷装置实行定期检测制度。防雷装置检测应当每年一次，对爆炸危险环境场所的防雷装置应当每半年检测一次。', '733', '《防雷减灾管理办法》第19条');
INSERT INTO `t_item_tbl` VALUES ('499', '操作面地面应铺设绝缘胶垫铺设', '734', '参考《变配电室安全管理规范》5.3');
INSERT INTO `t_item_tbl` VALUES ('500', '出入口应设置高度不低于400㎜的挡板。', '734', '参考《变配电室安全管理规范》5.6');
INSERT INTO `t_item_tbl` VALUES ('501', '应正确设置应急照明灯具', '734', '参考《变配电室安全管理规范》5.14.2');
INSERT INTO `t_item_tbl` VALUES ('502', '通往室外的门应向外开；配电装置室的中间门应采用双向开启门。', '734', '参考《变配电室安全管理规范》5.8.3');
INSERT INTO `t_item_tbl` VALUES ('503', '通往室外的窗应装有纱窗。', '734', '参考《变配电室安全管理规范》5.9.3');
INSERT INTO `t_item_tbl` VALUES ('504', '通风口应加金属网', '734', '参考《变配电室安全管理规范》5.9.1');
INSERT INTO `t_item_tbl` VALUES ('505', '各种安全用具定期试验、检验', '734', '参考《变配电室安全管理规范》5.18.3');
INSERT INTO `t_item_tbl` VALUES ('506', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。', '734', '参考《变配电室安全管理规范》5.18.5');
INSERT INTO `t_item_tbl` VALUES ('507', '变配电室应设有安全标志', '734', '参考《变配电室安全管理规范》5.16.1');
INSERT INTO `t_item_tbl` VALUES ('508', '10kV及以上的变配电室内的安装、检修工作应有工作票、操作票', '734', '参考《变配电室安全管理规范》6.3.1、6.3.7');
INSERT INTO `t_item_tbl` VALUES ('509', '变配电室内环境整洁，设备间不应存放与运行无关的闲散器材和私人物品，禁止无关人员进入场地。', '734', '参考《变配电室安全管理规范》6.8.1');
INSERT INTO `t_item_tbl` VALUES ('510', '值班室不应设置、使用寝具、灶具。', '734', '参考《变配电室安全管理规范》6.8.4');
INSERT INTO `t_item_tbl` VALUES ('511', '变配电室应有入变配电室登记簿', '734', '参考《变配电室安全管理规范》7.3');
INSERT INTO `t_item_tbl` VALUES ('512', '变配电室从业人员应正确穿戴绝缘鞋。', '734', '参考《变配电室安全管理规范》7.4');
INSERT INTO `t_item_tbl` VALUES ('513', '柜、屏、台、箱、盘的金属框架及基础型钢必须接地（PE）或接零（PEN）可靠；装有电器的可开启门与框架的接地端子间应用裸线编织铜线连接，作好标识。', '735', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.1.2条');
INSERT INTO `t_item_tbl` VALUES ('514', '箱盘内开关灵活可靠。带有漏电保护的回路，漏电保护装置动作电流和动作时间按设计要求。', '736', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.2.7.2条');
INSERT INTO `t_item_tbl` VALUES ('515', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '736', '《剩余电流动作保护装置安装和运行》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('516', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '736', '《剩余电流动作保护装置安装和运行》第7.2条');
INSERT INTO `t_item_tbl` VALUES ('518', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。', '737', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('519', '直敷布线可用于正常环境的屋内场所，应采用护套绝缘导线。当导线垂直敷设至地面低于1．8m时，应穿管保护。', '738', '《低压配电设计规范》第5.2.1条');
INSERT INTO `t_item_tbl` VALUES ('520', '插座回路均应设置剩余电流动作保护装置。宜装设漏电保护器，禁止使用无保护线插头插座。', '739', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('521', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。不应从带插座灯头上引接电源供给电器。', '740', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('522', '应采用橡皮护套绝缘软线。与电源联接，应采用开关、插头座。严禁用导线直接插入插座，或挂在电源线上使用。', '741', '《电气安全工作规程》第88条');
INSERT INTO `t_item_tbl` VALUES ('523', '工具必须由专职人员每年至少检查一次', '742', '《手持式电动工具的管理、使用、检查和维修安全技术规程》第6.3条');
INSERT INTO `t_item_tbl` VALUES ('524', '在使用移动式的I类设备时，应先确认其金属外壳或构架已可靠接地，使用带保护接地极的插座，同时宜装设漏电保护器，禁止使用无保护线插头插座。', '743', '《用电安全导则》第4.16条');
INSERT INTO `t_item_tbl` VALUES ('525', '露天使用的用电设备、配电装置应采取防雨、防雪、防雾和防尘的措施。', '744', '《用电安全导则》第4.25条');
INSERT INTO `t_item_tbl` VALUES ('526', '电气设备必须有可靠的接地保护', '745', 'GB19517-2004《国家电气设备安全技术规范》第2.2.4条');
INSERT INTO `t_item_tbl` VALUES ('527', '临时线路使用必须经过审批，一般使用期限为过15天，最长不超过一个月', '746', '《用电安全导则》第4.2条');
INSERT INTO `t_item_tbl` VALUES ('528', '临时用电的电气设备，必须安装剩余电流保护装置。', '747', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('529', '线路敷设应符合电气装置设置及安装规范，室内不低于2.5m，室外不低于4.5m，道路上方不低于6m。', '748', '《电气安全管理规程》第20条（1）');
INSERT INTO `t_item_tbl` VALUES ('530', '集贸市场内经营者使用的电气线路和用电设备，必须统一由主办\n单位委托具有资格的施工单位和持有合格证的电工负责安装、检查和维修。严禁个人拉设临时线路。', '749', '《集贸市场消防管理办法》第19条');
INSERT INTO `t_item_tbl` VALUES ('531', '行灯等手持式电动工具、器具应根据使用现场，分别采取可靠的安全保护措施，如漏电保护电器或使用３６伏以下的安全电压。安全变压器应采用双圈的。', '750', '《电气安全工作规程》第89条');
INSERT INTO `t_item_tbl` VALUES ('532', '手提式和局部照明灯具应选用安全电压或双重绝缘结构。在使用螺口灯头时，灯头螺纹端应接至电源的工作中性线。', '751', '《用电安全导则》第4.18条');
INSERT INTO `t_item_tbl` VALUES ('533', '室外集贸市场不应设置碘钨灯等高温照明灯具。', '752', '《集贸市场消防管理办法》第24条');
INSERT INTO `t_item_tbl` VALUES ('534', '危险化学品必须储存在专用仓库、专用场地或者专用储存室（以下统称专用仓库）内，储存方式、方法与储存数量必须符合国家标准，并由专人管理。（危险化学品包括：油漆漆料、汽油等）', '753', '《危险化学品安全管理条例》第22条');
INSERT INTO `t_item_tbl` VALUES ('535', '室外集贸市场在高压线下两侧5米以内，不得摆摊设点。', '754', '《集贸市场消防管理办法》第15条');
INSERT INTO `t_item_tbl` VALUES ('536', '应当持有《户外广告登记证》，设置户外广告、牌匾是否进行经常性检查和维护，确保安全、牢固。', '755', '《户外广告登记管理规定》 第5条、第32条 ');
INSERT INTO `t_item_tbl` VALUES ('537', '杜绝违章指挥、违章操作、违反劳动纪律的行为', '756', '国家安监总局15号令第44条');
INSERT INTO `t_item_tbl` VALUES ('579', '(1)重大危险源涉及的温度、压力、液位等重要参数有远传和连续记录，可燃气体和有毒有害气体设置泄漏检测报警装置； \r\n(2)重大危险源中的毒性气体、剧毒液体和易燃气体等重点设施设置紧急切断装置；\r\n(3)毒性气体的设施，设置泄漏物紧急处置装置，配备独立的安全仪表系统（SIS）；\r\n(4)重大危险源中储存剧毒物质的场所或者设施，设置视频监控系统。\r\n（5)构成一级、二级重大危险源的危险化学品罐区实现紧急切断功能；', '681', '《危险化学品重大危险源监督管理暂行规定》（安监总局令第40号）第三章');
INSERT INTO `t_item_tbl` VALUES ('580', '保证下列设备设施运行安全可靠、完整：\n（1）压力容器和压力管道，包括管件和阀门；\n（2）泄压和排空系统；\n（3）紧急停车系统；\n（4）监控、报警系统；\n（5）联锁系统；\n（6）各类动设备，包括备用设备等。', '682', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('581', '建立关键装置、重点部位档案，建立企业、管理部门、基层单位及班组监控机制，明确各级组织、各专业的职责，定期进行监督检查，并形成记录。', '682', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('582', '应制定关键装置、重点部位应急预案，至少每半年进行一次演练，确保关键装置、重点部位的操作、检修、仪表、电气等人员能够识别和及时处理各种事件及事故。', '682', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('583', '保证下列设备设施运行安全可靠、完整：\n（1）压力容器和压力管道，包括管件和阀门；\n（2）泄压和排空系统；\n（3）紧急停车系统；\n（4）监控、报警系统；\n（5）联锁系统；\n（6）各类动设备，包括备用设备等。', '683', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('584', '建立关键装置、重点部位档案，建立企业、管理部门、基层单位及班组监控机制，明确各级组织、各专业的职责，定期进行监督检查，并形成记录。', '683', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('585', '应制定关键装置、重点部位应急预案，至少每半年进行一次演练，确保关键装置、重点部位的操作、检修、仪表、电气等人员能够识别和及时处理各种事件及事故。', '683', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('586', '危险化学品应储存在专用仓库、专用场地或者专用储存室（以下统称专用仓库）内，并按照相关技术标准规定的储存方法、储存数量和安全距离，实行隔离、隔开、分离储存，禁止将危险化学品与禁忌物品混合储存；危险化学品专用仓库应当符合相关技术标准对安全、消防的要求，设置明显标志，并由专人管理；危险化学品出入库应当进行核查登记，并定期检查。', '684', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('587', '剧毒化学品及储存数量构成重大危险源的其他危险化学品必须在专用仓库单独存放，实行双人收发、双人保管制度；', '684', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('588', '（1）库房应有良好的通风设备，并根据物品的性质和不同储存方法，设置相应的防爆、泄压、防火、调温、导除静电和防止液体流散等安全措施；\r\n(2)危化品应该分类、分堆储存，堆垛不得过高、过密，堆垛之间，应留出一定的间距、通道和通风口；\r\n(3)库房应有消防通讯、报警设备，并准确有效。储存剧毒和放射性物品的仓库应配备防毒面具及隔离、消除、吸收毒物的设施；\r\n(4)库房应有足够的消防水源和配备相应的灭火器材及防护医疗设备。', '685', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('589', '按《储罐区防火堤设计规范》（GB50351），在可燃液体罐区设置防火堤，在酸碱罐区设置围堤并进行防腐处理；', '686', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('590', '(1) 储存甲B、乙A类液体采用内浮顶罐, 甲B类储罐设置减少日晒升温的措施；\n(2) 罐体、顶板壁厚定期检测, 罐体及附件无渗漏点；\n(3) 储罐成组布置，同一罐组内布置火灾危险性级别相同或相近的储罐；\n(4) 甲B类、乙液体的固定顶罐设置阻火器和呼吸阀，采用氮封储罐设置事故泄压设备；\n(5) 罐区设置事故存液池符合要求；\n(6) 储罐的进料管从罐体下部接入，储罐的进出管采用柔性连接；\n(7) 液化烃罐组内的储罐不多于2排，球罐之间的防火间距符合规范, 液化烃罐组的防火堤符合要求，同时按照规范设置隔堤；\n(8) 储罐平台、罐顶有踏步、边缘有护栏，且牢固; 消防设备齐全、有效。', '686', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('591', '(1) 管线无渗漏，无严重损伤、锈蚀，其局部损伤深度不超1.5mm；\n(2) 地上管线应架空式沿地面敷设，如局部采用封闭式管沟，要有防范措施；\n(3) 埋地管线要有明显的走向标志，并按规定时间开挖检查。', '687', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('592', ' 装卸料应采用密闭管道作业，装卸车应采用液下装卸车鹤管', '687', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('593', '根据《安全标志及其使用导则》（GB 2894-2008）、以及《工业管道的基本识别色、识别符号和安全标识》（GB7231-2003）的相关规定，在管外（保护层外或保温层外）涂上不同的颜色，如水管为艳绿色、水蒸汽管为大红色，并标明物料名称及走向。', '687', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('594', '按《石油化工静电接地设计规范》SH3097-2000，在输送易燃物料的设备、管道上安装防静电设施；', '688', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('595', '按《建筑物防雷设计规范》（GB50057），在厂区（包括储罐区、仓库区及分装、充装等生产作业区）安装防雷设施', '688', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('596', ' 易燃、易爆液体（气体）装卸车台设置能检测静电接地状态的防静电接地仪器，泵房门外、储罐上罐扶梯入口处设置人体静电释放仪。', '688', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('597', '防雷装置检测应当每年一次，对爆炸危险环境场所的防雷装置应当每半年检测一次。所有防静电设备、测试仪表及防护用品，要定期检查、维修，并建立设备档案。', '688', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('598', '企业应制订年度综合检维修计划，落实“五定”，即定检修方案、定检修人员、定安全措施、定检修质量、定检修进度原则。', '689', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('599', '企业在进行检维修作业时，应执行下列程序：\n(1)检维修前：\n①危险、有害因素识别；②编制检维修方案；③办理工艺、设备设施交付检维修手续；④对检维修人员进行安全培训教育；⑤检维修前对安全控制措施进行确认；⑥为检维修作业人员配备适当的劳动保护用品；⑺办理各种作业许可证。\n(2)对检维修现场进行安全检查。\n(3)检维修后办理检维修交付生产手续。', '689', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('600', '在检维修、施工、吊装等作业现场设置相应的警戒区域和警示标志；在检修现场的坑、井、洼、沟、陡坡等场所设置围栏和警示灯。', '689', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('601', '在易燃易爆场所进行维护、检修时，应使用防爆型照明设备、防爆工具。', '689', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('602', '各类作业人员上岗时应按规定穿戴个人防护用品', '689', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('603', '进行清罐、清线、置换作业时，应按照《危险化学品储罐区作业安全通则》（AQ3018-2008）规定要求进行作业。涉及动火等危险作业，应按规定办理相关的作业票证。', '689', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('604', '在装置、仓库、罐区、装卸区、危险化学品输送管道等危险场所的醒目位置设置符合GB2894规定的安全标志。', '690', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('605', '易燃、易爆、有毒介质储存或充装区安装明显可见的风向标或风向袋', '690', '《移动式压力容器充装许可规则》（TSG R4002-2011）A5.2');
INSERT INTO `t_item_tbl` VALUES ('606', '在重大危险源所在场所应当设置明显的安全警示标志，写明紧急情况下的应急处置办法。', '690', '《危险化学品重大危险源监督管理暂行规定》（安监总局令第40号）第十八条');
INSERT INTO `t_item_tbl` VALUES ('607', '厂房、库房建筑及安全间距符合《建筑设计防火规范》（GB50016）/《石油化工企业设计防火规范》（GB50160）的有关要求；石油库的内部布局应符合《石油库设计规范》（GB50074）', '691', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('608', '储罐区与厂区内其他建构/筑物的防火间距，以及甲、乙、丙类储罐间防火间距符合GB50016/GB50160的有关要求', '691', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('609', '桶装、瓶装甲类液体不得露天堆放。桶装棚设置应符合规范要求。', '691', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('610', '仓库区（罐区、堆场）、充装（分装）作业区与生产区、生活区有适当的间隔；甲乙丙类液体储罐区、液化石油气储罐区，可燃、助燃气体储罐区，应与装卸区、办公区、辅助生产区分开设置。', '691', '《建筑设计防火规范》（GB50016）4.1');
INSERT INTO `t_item_tbl` VALUES ('611', '仓库区（罐区、堆场）、充装（分装）作业区等与厂区周边的建筑/构物、厂外道路、铁路、架空电力线等安全间距应当符合GB50016/GB50160的有关要求。', '692', '《建筑设计防火规范》（GB50016）、《石油化工企业设计防火规范》（GB50160）');
INSERT INTO `t_item_tbl` VALUES ('612', '储存数量构成重大危险源的，与下列场所、设施、区域的距离应当符合国家有关规定：\n　　(一)居住区以及商业中心、公园等人员密集场所；\n　　(二)学校、医院、影剧院、体育场(馆)等公共设施；\n　　(三)饮用水源、水厂以及水源保护区；\n　　(四)车站、码头(依法经许可从事危险化学品装卸作业的除外)、机场以及通信干线、通信枢纽、铁路线路、道路交通干线、水路交通干线、地铁风亭以及地铁站出入口；\n　　(五)基本农田保护区、基本草原、畜禽遗传资源保护区、畜禽规模化养殖场(养殖小区)、渔业水域以及种子、种畜禽、水产苗种生产基地；\n　　(六)河流、湖泊、风景名胜区、自然保护区；\n　　(七)军事禁区、军事管理区；\n　　(八)法律、行政法规规定的其他场所、设施、区域。', '692', '《危险化学品安全管理条例》第十九条');
INSERT INTO `t_item_tbl` VALUES ('613', '应按规定进行装卸作业，查验危险品运输车辆、驾驶员、押运人员的资质证书，并提供或索取化学品安全技术说明书和化学品安全标签。', '693', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('614', '装卸作业时，有关人员必须正确使用劳动防护用品。\n进入作业区域的车辆，应办理车辆进入许可手续，按规定的路线和速度行驶，按规定位置停放。', '693', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('615', '遵章守纪、服从管理，杜绝违章指挥、违章操作、违反劳动纪律的行为。', '694', '《中华人民共和国安全生产法》第三章');
INSERT INTO `t_item_tbl` VALUES ('616', '从业人员在作业过程中，必须按照安全生产规章制度和劳动防护用品使用规则，正确佩戴和使用劳动防护用品；未按规定佩戴和使用劳动防护用品的，不得上岗作业。', '695', '《劳动防护用品监督管理规定》（安监总局令第1号）第十四条、十六条、十九条');
INSERT INTO `t_item_tbl` VALUES ('617', '应急照明设施并确保完好、有效。', '696', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('618', '按GB50016、《建筑灭火器配置设计规范》（GB50140）配置消防设施与器材；', '697', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('619', '对建筑消防设施每年至少进行一次全面检测，消防泵应定期检查、运行，确保完好有效，检测记录应当完整准确，存档备查；', '697', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('620', '厂区应设有符合标准的消防通道，并保证其畅通。', '698', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('621', '厂房、仓库等设置明显标志，保持畅通。', '699', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('622', '电气作业人员必须持证上岗，并按规定穿戴好劳动防护用品，正确使用符合安全要求的电气工具。', '700', '《用电安全导则》（GB/T13869)10');
INSERT INTO `t_item_tbl` VALUES ('623', '电气检修必须执行电气检修工作票制度，并明确工作票签发人、 工作负责人（监护人）、工作许可人、操作人员责任。工作票必须经签发人签发，许可人许可，并办理许可手续后方可作业。', '700', '《用电安全导则》（GB/T13869)10');
INSERT INTO `t_item_tbl` VALUES ('624', '不应随意拉设临时线路。', '700', '《用电安全导则》（GB/T13869)10');
INSERT INTO `t_item_tbl` VALUES ('625', '供配电系统应能满足企业的负荷性质、用电容量等要求', '701', '《供配电系统设计规范》（GB50052）1.0.3');
INSERT INTO `t_item_tbl` VALUES ('626', '变配电房与危化品储罐区、仓库、充装/分装厂房等间距符合GB50016的规定，门应向外开。', '701', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('627', '变配电间应具备的标志牌齐全，悬挂准确无误，有防雨、雪和防鼠、鸟等进入设施，通风良好；', '701', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('628', '在爆炸危险区域场所、供配电设施设计、选型、安装、维护符合应符合《爆炸和火灾危险环境电力装置设计规范》（GB50058）的有关规定。', '701', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('629', '埋地电缆路径无挖掘痕迹，无重物和腐蚀性堆物，设电缆桩，标志明显，电缆头无渗漏的现象；', '701', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('630', '总配电间、发电房配备应急灯；', '701', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('631', '化学危险品库房必须采用隔离、封闭、防爆或其它相应的安全电气照明设备；', '701', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('632', '临时用电的电气设备，必须安装剩余电流保护装置。', '701', '《剩余电流动作保护装置安装和运行》（GB13955)第4．5 条');
INSERT INTO `t_item_tbl` VALUES ('633', '行灯等手持式电动工具、器具应根据使用现场，分别采取可靠的安全保护措施，如漏电保护电器或使用36伏以下的安全电压。安全变压器应采用双圈的。', '701', '《电气安全工作规程》第八十九条');
INSERT INTO `t_item_tbl` VALUES ('634', '落地式配电箱的底部宜抬高，高出地面的高度室内不应低于50mm，室外不应低于200mm，其底座周围应采取封闭措施，并应能防止鼠、蛇等小动物进入箱内。', '701', '《低压配电设计规范》（GB50054-2011）4.2.1、4.3.7');
INSERT INTO `t_item_tbl` VALUES ('635', '配电室的门、窗关闭应密合。', '701', '《低压配电设计规范》（GB50054-2011）4.2.1、4.3.7');
INSERT INTO `t_item_tbl` VALUES ('636', '1、生产布局合理，有害作业与无害作业分开；2、工作场所与生活场所分开，工作场所不得住人；3、有与职业病防治工作相适应的有效防护设施；4、职业病危害因素的强度或者浓度符合国家职业卫生标准；5、有配套的更衣间、洗浴间、孕妇休息间等卫生设施；6、设备、工具、用具等设施符合保护劳动者生理、心理健康的要求；7、法律、法规、规章和国家职业卫生标准的其他规定。', '702', '《工作场所职业卫生监督管理规定》（安监总局令第47号）第十二条');
INSERT INTO `t_item_tbl` VALUES ('637', '应当在醒目位置设置公告栏，公布有关职业病防治的规章制度、操作规程、职业病危害事故应急救援措施和工作场所职业病危害因素检测结果。对产生严重职业病危害的作业岗位，应当在其醒目位置，设置警示标识和中文警示说明。', '703', '《中华人民共和国职业病防治法》第二十五条');
INSERT INTO `t_item_tbl` VALUES ('638', '（1）制氮系统应符合相关规范要求；\n（2）应定期检测氮气的纯度。', '704', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('639', ' 冷冻冷却水系统应具有足够的容量，符合系统的冷却要求。', '705', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('640', '其他不属于上述隐患分类的安全生产现场管理类的项目，应当符合有关规定。', '706', '');
INSERT INTO `t_item_tbl` VALUES ('641', '(1)重大危险源涉及的温度、压力、液位等重要参数有远传和连续记录，可燃气体和有毒有害气体设置泄漏检测报警装置； \n(2)重大危险源中的毒性气体、剧毒液体和易燃气体等重点设施设置紧急切断装置；\n(3)毒性气体的设施，设置泄漏物紧急处置装置，配备独立的安全仪表系统（SIS）；\n(4)重大危险源中储存剧毒物质的场所或者设施，设置视频监控系统。涉及毒性气体、液化气体、剧毒液体的一级、二级重大危险源的危险化学品罐区配备独立的安全仪表系统。', '655', '《危险化学品重大危险源监督管理暂行规定》（安监总局令第40号）第三章');
INSERT INTO `t_item_tbl` VALUES ('642', '不得采用国家明令淘汰、禁止使用和危及安全生产的工艺、设备；新开发的危险化学品生产工艺必须在小试、中试、工业化试验的基础上逐步放大到工业化生产；国内首次使用的化工工艺，必须经过省级人民政府有关部门组织的安全可靠性论证；不得使用淘汰落后安全技术工艺、设备目录列出的工艺、设备。', '656', '《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第九条\n');
INSERT INTO `t_item_tbl` VALUES ('643', '保证下列设备设施运行安全可靠、完整：\n（1）压力容器和压力管道，包括管件和阀门；\n（2）泄压和排空系统；\n（3）紧急停车系统；\n（4）监控、报警系统；\n（5）联锁系统；\n（6）各类动设备，包括备用设备等。', '656', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('644', '生产装置泄压系统或排空系统排放的危险化学品应引至安全地点并得到妥善处理。', '656', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('645', '建立关键装置、重点部位档案，建立企业、管理部门、基层单位及班组监控机制，明确各级组织、各专业的职责，定期进行监督检查，并形成记录。', '657', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('646', '应制定关键装置、重点部位应急预案，至少每半年进行一次演练，确保关键装置、重点部位的操作、检修、仪表、电气等人员能够识别和及时处理各种事件及事故。', '657', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('647', '按GB50493，在易燃、易爆、有毒区域设置固定式可燃气体和（或）毒有害气体泄漏的检测报警设施，报警信号发送至控制室或操作室；', '658', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('648', '在工艺装置上可能引起火灾、爆炸的部位设置超温、超压等检测仪表、声和（或）光报警和安全联锁装置等设施；涉及可燃和有毒有害气体泄漏的场所未按国家标准设置检测报警装置，爆炸危险场所按国家标准安装使用防爆电气设备。控制室或机柜间面向具有火灾、爆炸危险性装置一侧要满足国家标准关于防火防爆的要求。', '658', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('649', '涉及重点监管危险化学品的装置和设施，根据实际情况，进行自动控制改造，实现工艺过程的自动控制和压力、温度等重要参数的自动报警。涉及重点监管危险化工工艺的装置实现自动化控制，系统实现紧急停车功能，装备的自动化控制系统、紧急停车系统投入使用。', '658', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('650', '危险化学品应储存在专用仓库、专用场地或者专用储存室（以下统称专用仓库）内，并按照相关技术标准规定的储存方法、储存数量和安全距离，实行隔离、隔开、分离储存，禁止将危险化学品与禁忌物品混合储存；危险化学品专用仓库应当符合相关技术标准对安全、消防的要求，设置明显标志，并由专人管理；危险化学品出入库应当进行核查登记，并定期检查。', '659', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('651', '剧毒化学品及储存数量构成重大危险源的其他危险化学品必须在专用仓库单独存放，实行双人收发、双人保管制度；', '659', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('652', '（1）库房应有良好的通风设备，并根据物品的性质和不同储存方法，设置相应的防爆、泄压、防火、调温、导除静电和防止液体流散等安全措施；\n(2)危化品应该分类、分堆储存，堆垛不得过高、过密，堆垛之间，应留出一定的间距、通道和通风口；\n(3)库房应有消防通讯、报警设备，并准确有效。储存剧毒和放射性物品的仓库应配备防毒面具及隔离、消除、吸收毒物的设施；\n(4)库房应有足够的消防水源和配备相应的灭火器材及防护医疗设备。', '660', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('653', '按《储罐区防火堤设计规范》（GB50351），在可燃液体罐区设置防火堤，在酸碱罐区设置围堤并进行防腐处理；', '661', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('654', '(1) 储存甲B、乙A类液体采用内浮顶罐, 甲B类储罐设置减少日晒升温的措施；\r\n(2) 罐体、顶板壁厚定期检测, 罐体及附件无渗漏点；\r\n(3) 储罐成组布置，同一罐组内布置火灾危险性级别相同或相近的储罐；\r\n(4) 甲B类、乙液体的固定顶罐设置阻火器和呼吸阀，采用氮封储罐设置事故泄压设备；\r\n(5) 罐区设置事故存液池符合要求；\r\n(6) 储罐的进料管从罐体下部接入，储罐的进出管采用柔性连接；\r\n(7) 液化烃罐组内的储罐不多于2排，球罐之间的防火间距符合规范, 液化烃罐组的防火堤符合要求，同时按照规范设置隔堤；\r\n(8) 储罐平台、罐顶有踏步、边缘有护栏，且牢固; 消防设备齐全、有效。\r\n(9)全压力式液化烃储罐按国家标准设置注水措施。', '661', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('655', '(1) 管线无渗漏，无严重损伤、锈蚀，其局部损伤深度不超1.5mm；\n(2) 地上管线应架空式沿地面敷设，如局部采用封闭式管沟，要有防范措施；\n(3) 埋地管线要有明显的走向标志，并按规定时间开挖检查。', '662', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('656', ' (1)装卸料应采用密闭管道作业，装卸车应采用液下装卸车鹤管\r\n(2)液化烃、液氨、液氯等易燃易爆、有毒有害液化气体的充装使用万向管道充装系统。\r\n(3)光气、氯气等剧毒气体及硫化氢气体管道不得穿越除厂区(包括化工园区、工业园区）外的公共区域。', '662', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('657', '根据《安全标志及其使用导则》（GB 2894-2008）、以及《工业管道的基本识别色、识别符号和安全标识》（GB7231-2003）的相关规定，在管外（保护层外或保温层外）涂上不同的颜色，如水管为艳绿色、水蒸汽管为大红色，并标明物料名称及走向。', '662', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('658', '按《石油化工静电接地设计规范》SH3097-2000，在输送易燃物料的设备、管道上安装防静电设施；', '663', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('659', '按《建筑物防雷设计规范》（GB50057），在厂区（包括储罐区、仓库区及分装、充装等生产作业区）安装防雷设施', '663', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('660', ' 易燃、易爆液体（气体）装卸车台设置能检测静电接地状态的防静电接地仪器，泵房门外、储罐上罐扶梯入口处设置人体静电释放仪。', '663', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('661', '防雷装置检测应当每年一次，对爆炸危险环境场所的防雷装置应当每半年检测一次。所有防静电设备、测试仪表及防护用品，要定期检查、维修，并建立设备档案。', '663', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('662', '企业应制订年度综合检维修计划，落实“五定”，即定检修方案、定检修人员、定安全措施、定检修质量、定检修进度原则。', '664', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('663', '企业在进行检维修作业时，应执行下列程序：\n(1)检维修前：\n①危险、有害因素识别；②编制检维修方案；③办理工艺、设备设施交付检维修手续；④对检维修人员进行安全培训教育；⑤检维修前对安全控制措施进行确认；⑥为检维修作业人员配备适当的劳动保护用品；⑺办理各种作业许可证。\n(2)对检维修现场进行安全检查。\n(3)检维修后办理检维修交付生产手续。', '664', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('664', '在检维修、施工、吊装等作业现场设置相应的警戒区域和警示标志；在检修现场的坑、井、洼、沟、陡坡等场所设置围栏和警示灯。', '664', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('665', '在易燃易爆场所进行维护、检修时，应使用防爆型照明设备、防爆工具。', '664', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('666', '各类作业人员上岗时应按规定穿戴个人防护用品', '664', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('667', '进行清罐、清线、置换作业时，应按照《危险化学品储罐区作业安全通则》（AQ3018-2008）规定要求进行作业。涉及动火等危险作业，应按规定办理相关的作业票证。', '664', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('668', '在装置、仓库、罐区、装卸区、危险化学品输送管道等危险场所的醒目位置设置符合GB2894规定的安全标志。', '665', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('669', '易燃、易爆、有毒介质储存或充装区安装明显可见的风向标或风向袋', '665', '《移动式压力容器充装许可规则》（TSG R4002-2011）A5.2');
INSERT INTO `t_item_tbl` VALUES ('670', '在重大危险源所在场所应当设置明显的安全警示标志，写明紧急情况下的应急处置办法。', '665', '《危险化学品重大危险源监督管理暂行规定》（安监总局令第40号）第十八条');
INSERT INTO `t_item_tbl` VALUES ('671', '厂房、库房建筑符合《建筑设计防火规范》（GB50016）/《石油化工企业设计防火规范》（GB50160）的有关要求；', '666', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('672', '甲、乙、丙类储罐间防火间距符合GB50016/GB50160的有关要求', '666', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('673', '生产区与非生产区分开设置，并符合国家标准或者行业标准规定的距离；', '666', '《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第九条');
INSERT INTO `t_item_tbl` VALUES ('674', '危险化学品生产装置和储存设施之间及其与建（构）筑物之间的距离符合GB50016/GB50160等有关标准规范的规定。', '666', '《危险化学品生产企业安全生产许可证实施办法》（安监总局令第41号）第九条');
INSERT INTO `t_item_tbl` VALUES ('675', '厂房、仓库（罐区）等与厂区周边的建筑/构物、厂外道路、铁路、架空电力线等安全间距应当符合GB50016/GB50160的有关要求。', '667', '《建筑设计防火规范》（GB50016）、《石油化工企业设计防火规范》（GB50160）');
INSERT INTO `t_item_tbl` VALUES ('676', '危险化学品生产装置及储存数量构成重大危险源的储存设施，与下列场所、设施、区域的距离应当符合国家有关规定：\r\n　　(一)居住区以及商业中心、公园等人员密集场所；\r\n　　(二)学校、医院、影剧院、体育场(馆)等公共设施；\r\n　　(三)饮用水源、水厂以及水源保护区；\r\n　　(四)车站、码头(依法经许可从事危险化学品装卸作业的除外)、机场以及通信干线、通信枢纽、铁路线路、道路交通干线、水路交通干线、地铁风亭以及地铁站出入口；\r\n　　(五)基本农田保护区、基本草原、畜禽遗传资源保护区、畜禽规模化养殖场(养殖小区)、渔业水域以及种子、种畜禽、水产苗种生产基地；\r\n　　(六)河流、湖泊、风景名胜区、自然保护区；\r\n　　(七)军事禁区、军事管理区；\r\n　　(八)法律、行政法规规定的其他场所、设施、区域。\r\n    (九)地区架空电力线路穿越生产区且符合国家标准要求。\r\n   （十）涉及“两重点一重大”的生产装置、储存设施外部安全防护距离符合国家标准要求。', '667', '《危险化学品安全管理条例》第十九条');
INSERT INTO `t_item_tbl` VALUES ('677', '应按规定进行装卸作业，查验危险品运输车辆、驾驶员、押运人员的资质证书，并提供或索取化学品安全技术说明书和化学品安全标签。', '668', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('678', '装卸作业时，有关人员必须正确使用劳动防护用品。\n进入作业区域的车辆，应办理车辆进入许可手续，按规定的路线和速度行驶，按规定位置停放。', '668', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('679', '遵章守纪、服从管理，杜绝违章指挥、违章操作、违反劳动纪律的行为。', '669', '《中华人民共和国安全生产法》第三章');
INSERT INTO `t_item_tbl` VALUES ('680', '从业人员在作业过程中，必须按照安全生产规章制度和劳动防护用品使用规则，正确佩戴和使用劳动防护用品；未按规定佩戴和使用劳动防护用品的，不得上岗作业。', '670', '《劳动防护用品监督管理规定》（安监总局令第1号）第十四条、十六条、十九条');
INSERT INTO `t_item_tbl` VALUES ('681', '应急照明设施并确保完好、有效。', '671', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('682', '按GB50016、《建筑灭火器配置设计规范》（GB50140）配置消防设施与器材；', '672', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('683', '对建筑消防设施每年至少进行一次全面检测，消防泵应定期检查、运行，确保完好有效，检测记录应当完整准确，存档备查；', '672', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('684', '厂区应设有符合标准的消防通道，并保证其畅通。', '673', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('685', '厂房、仓库等设置明显标志，保持畅通。', '674', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('686', '电气作业人员必须持证上岗，并按规定穿戴好劳动防护用品，正确使用符合安全要求的电气工具。', '675', '《用电安全导则》（GB/T13869)10');
INSERT INTO `t_item_tbl` VALUES ('687', '电气检修必须执行电气检修工作票制度，并明确工作票签发人、 工作负责人（监护人）、工作许可人、操作人员责任。工作票必须经签发人签发，许可人许可，并办理许可手续后方可作业。', '675', '《用电安全导则》（GB/T13869)10');
INSERT INTO `t_item_tbl` VALUES ('688', '不应随意拉设临时线路。', '675', '《用电安全导则》（GB/T13869)10');
INSERT INTO `t_item_tbl` VALUES ('689', '供配电系统应能满足企业的负荷性质、用电容量等要求', '676', '《供配电系统设计规范》（GB50052）1.0.3');
INSERT INTO `t_item_tbl` VALUES ('690', '变配电房与危化品储罐区、仓库、充装/分装厂房等间距符合GB50016的规定，门应向外开。', '676', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('691', '变配电间应具备的标志牌齐全，悬挂准确无误，有防雨、雪和防鼠、鸟等进入设施，通风良好；', '676', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('692', '在爆炸危险区域场所、供配电设施设计、选型、安装、维护符合应符合《爆炸和火灾危险环境电力装置设计规范》（GB50058）的有关规定。', '676', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('693', '埋地电缆路径无挖掘痕迹，无重物和腐蚀性堆物，设电缆桩，标志明显，电缆头无渗漏的现象；', '676', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('694', '总配电间、发电房配备应急灯；', '676', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('695', '化学危险品库房必须采用隔离、封闭、防爆或其它相应的安全电气照明设备；', '676', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('696', '临时用电的电气设备，必须安装剩余电流保护装置。', '676', '《剩余电流动作保护装置安装和运行》（GB13955)第4．5 条');
INSERT INTO `t_item_tbl` VALUES ('697', '行灯等手持式电动工具、器具应根据使用现场，分别采取可靠的安全保护措施，如漏电保护电器或使用36伏以下的安全电压。安全变压器应采用双圈的。', '676', '《电气安全工作规程》第八十九条');
INSERT INTO `t_item_tbl` VALUES ('698', '落地式配电箱的底部宜抬高，高出地面的高度室内不应低于50mm，室外不应低于200mm，其底座周围应采取封闭措施，并应能防止鼠、蛇等小动物进入箱内。', '676', '《低压配电设计规范》（GB50054-2011）4.2.1、4.3.7');
INSERT INTO `t_item_tbl` VALUES ('699', '配电室的门、窗关闭应密合。', '676', '《低压配电设计规范》（GB50054-2011）4.2.1、4.3.7');
INSERT INTO `t_item_tbl` VALUES ('700', '1、生产布局合理，有害作业与无害作业分开；2、工作场所与生活场所分开，工作场所不得住人；3、有与职业病防治工作相适应的有效防护设施；4、职业病危害因素的强度或者浓度符合国家职业卫生标准；5、有配套的更衣间、洗浴间、孕妇休息间等卫生设施；6、设备、工具、用具等设施符合保护劳动者生理、心理健康的要求；7、法律、法规、规章和国家职业卫生标准的其他规定。', '677', '《工作场所职业卫生监督管理规定》（安监总局令第47号）第十二条');
INSERT INTO `t_item_tbl` VALUES ('701', '应当在醒目位置设置公告栏，公布有关职业病防治的规章制度、操作规程、职业病危害事故应急救援措施和工作场所职业病危害因素检测结果。对产生严重职业病危害的作业岗位，应当在其醒目位置，设置警示标识和中文警示说明。', '678', '《中华人民共和国职业病防治法》第二十五条');
INSERT INTO `t_item_tbl` VALUES ('702', '（1）制氮系统应符合相关规范要求；\n（2）应定期检测氮气的纯度。', '679', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('703', ' 冷冻冷却水系统应具有足够的容量，符合系统的冷却要求。', '680', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('704', '生产经营单位应当在有较大危险因素的生产经营场所和有关设施、设备上，设置明显的安全警示标志。（管理制度、定期检查记录）', '596', '《中华人民共和国安全生产法》第28条');
INSERT INTO `t_item_tbl` VALUES ('705', '冶金企业应当在煤气储罐区等（管道、烘烤器、煤气除尘、车间内排水器）可能发生煤气泄漏、聚集的场所，设置固定式煤气检测报警仪，建立预警系统，悬挂醒目的安全警示牌，并加强通风换气。\n进入煤气区域作业的人员（两人以上），应当携带煤气检测报警仪器；在作业前，应当检查作业场所的煤气含量，并采取可靠的安全防护措施，经检查确认煤气含量符合规定后，方可进入作业。（煤气区域检修、动火作业要进行审批）', '596', '《冶金企业安全生产监督管理规定》第23条');
INSERT INTO `t_item_tbl` VALUES ('706', '1、冶金企业应当根据本单位的安全生产实际状况，科学、合理确定煤气柜容积，按照《工业企业煤气安全规程》(GB6222)的规定，合理选择柜址位置，设置安全保护装置，制定煤气柜事故应急预案。\r\n2、煤气柜建设在居民稠密区，远离大型建筑、仓库、通信和交通枢纽等重要设施；附属设备设施按防火防爆要求配置防爆型设备；柜顶设置防雷装置。\r\n3、煤气区域的值班室、操作室等人员较集中的地方，设置固定式一氧化碳监测报警装置。', '597', '《冶金企业安全生产监督管理规定》第27条');
INSERT INTO `t_item_tbl` VALUES ('707', '炎热地区的冶炼、轧钢等工厂的医疗单位，在暑季应设置中暑抢救室（现场配置相应（防暑、煤气等）急救器材）', '598', '《冶金企业高温作业职业卫生管理规程》5.2');
INSERT INTO `t_item_tbl` VALUES ('708', '防暑降温设备应按规定有专人检查与维修，切实执行运行管理制度。', '599', '《冶金企业高温作业职业卫生管理规程》5.3');
INSERT INTO `t_item_tbl` VALUES ('709', '1、冶金企业应当定期对安全设备设施和安全保护装置进行检查、校验。对超过使用年限和不符合国家产业政策的设备，及时予以报废。对现有设备设施进行更新或者改造的，不得降低其安全技术性能。（安全设备设施和安全保护装置报废应由使用单位进行审批）\r\n2、盛装铁水、钢水与液渣的罐（包、盆）等容器耳轴按国家标准规定要求定期进行探伤检测。\r\n3、炉、窑、槽、罐类设备本体及附属设施应定期检查，出现严重焊缝开裂、腐蚀、破损、衬砖损坏、壳体发红及明显弯曲变形等未报修或报废，不应继续使用。', '600', '《冶金企业安全生产监督管理规定》第28条');
INSERT INTO `t_item_tbl` VALUES ('710', '1、氧气系统应当采取可靠的安全措施（要有跨接线、阀门用不锈钢），防止氧气燃爆事故以及氮气、氩气、珠光砂窒息事故。\r\n2、.氧枪等水冷元件应配置出水温度与进出水流量差检测、报警装置及温度监测，且与炉体倾动、氧气开闭等联锁。', '601', '《冶金企业安全生产监督管理规定》第24条');
INSERT INTO `t_item_tbl` VALUES ('711', '1、冶金企业的会议室、活动室、休息室、更衣室等人员密集场所应当设置在安全地点，不得设置在高温液态金属的吊运影响范围内。\r\n2、会议室、活动室、休息室、更衣室等场所不应设置在铁水、钢水与液渣吊运影响的范围内。', '602', '《冶金企业安全生产监督管理规定》第21条');
INSERT INTO `t_item_tbl` VALUES ('712', '1．选择厂址时，应慎重考虑地震、热带风暴等自然灾害，高温、严寒等异常气候，不利的工程地质(如存在滑坡、泥石流、流砂、淤泥、喀斯特溶洞、断层、采空区、塌陷区)和水文地质条件以及矿石中对人体有害的成分等，优选对安全、卫生危害最小的厂址。2．在较厚的III级自重湿陷性黄土、新近堆积黄土、III级膨胀土等地区选择厂址时，应有可靠的安全措施，并报请上级主管部门批准。3．厂址宜选在地下水位较低、场地雨水和工业废水易于排出的地区，应避免选在受洪水或内涝危害的地区。在山区选厂址时，应避免山洪、滑坡及泥石流爆发对厂区的威胁。在高寒积雪地区选厂址时，应防止溶雪、雪崩的影响。在海边选厂址时，应注意海潮、热带风暴等的影响。4．厂址应避免选在已有或拟建的通讯电台、电视传播、雷达导航以及重要军事设施的影响区域和火灾爆炸危险源、厂、矿有毒、有害物的影响范围内。在影响飞机起落、航行的地方选择厂址，应遵守国家有关保护机场净空的规定。5．厂区工业场地地面的设计标高，不设堤防时应高于计算水位0．5米；设堤防时应高于历年最高内涝水位或常年洪水位(大汛平均高潮位)。6．生活居住区、饮用水源、渣场、排土场、尾矿库、炸药库等，应与厂址同时选择，统一规划。饮用水源严禁设在污染区和地方病常发地区。尾矿库应设在远离人口稠密的地方。铁渣、钢渣场的设置应考虑爆炸安全距离。7．厂区应位于居住区常年最小频率风向的上风侧。厂区与居住区之间的卫生防护距离必须遵守国家现行有关法令、标准的规定。', '603', '《冶金企业安全卫生设计规定》第11条');
INSERT INTO `t_item_tbl` VALUES ('713', '1．厂区布置，应按生产工艺流程的主要车间与辅助车间的性质及安全、卫生，防火等的要求，合理确定厂区的功能分区，并根据生产工序间的相关性，确定各区的位置。各区之间必须保持物流顺畅、运距短捷，避免或减少折返迂回。2．基础荷载较大的建构筑物，宜布置在土质均匀、地基承载力较大、地下水位较低的地段。在不良地质地段布置建构筑物和运输线路时，应采取必要的加固措施。沿山坡布置的建构筑物，应防止滑坡可能引起的危害。3．应根据生产要求及地形、气象等条件，合理布置产生尘、毒、震动、噪声、放射性的车间以及生产和贮存易燃易爆等化学危险品的车间和仓库。要求环境洁净的生产车间、辅助设施和矿山的进凤井，宜布置在污染源常年最小频率风向的下风侧；火灾危险性较大、产生大量烟尘及有害气体的设施，应布置在生产厂区的常年最小频率风向的上风侧；产生较大噪声的车间宜布置在低凹地带或厂区边缘人口密度较小的地带；高温车间宜布置在通凤良好的地带。4．厂(矿)区运输线路(铁路和道路)的布置应尽可能避免或减少运输线路平面交叉，使主要人流与较大货流分开。5．厂区建筑群体的平面与空间布置，应统筹考虑建构筑物之间的防火间距及消防车道，不得妨碍车辆驾驶人员的视线，宜满足自然通凤与自然采光要求。主厂房夏季最大频率风向的迎风面尽可能不布置辅助建构筑物，以确保自然通风。高温热加工车间和生活设施建筑应尽量避免西晒。6．改扩建工程，在合理利用原有建构筑物等设施的同时，应合理布局，避免厂区生产车间的过分拥挤以及有毒、易燃、易爆车间的潜在危害。', '604', '《冶金企业安全卫生设计规定》第12条');
INSERT INTO `t_item_tbl` VALUES ('714', '1．车间布置应按生产工艺流程及防火、安全、卫生等的要求使生产工序衔接紧密，物料运距短捷，设备操作和维修方便。\r\n2．车间的门(矿井的坑口)与通道的位置、数量及尺寸等，应与工艺设备、人行道、运输方式、运输线路相适应。有火灾、爆炸危险的生产车间，至少应有两个安全出口。\r\n3．机动车辆通道应尽量与人行道分开。铁路、道路与门、设备、建构筑物、人行道及其他设备、设施之间的安全距离应遵守YJB52《钢铁企业总图运输设计规范》和GB4387《工业企业厂内铁路、道路运输安全规程》的规定。运输通道和人行道应在车间平面图上标明。\r\n4．车间内人行道与机动车道或移动机械的通道的交叉处，应设信号报警装置。\r\n5．设备与设备之间及设备与建构筑物之间的距离，必须满足操作、检修要求，宜不小于1米。\r\n6、重包运行路线要畅通、与周边建筑最小间距1.5米，路线上不得设置休息室、操作室等人员密集场所', '605', '《冶金企业安全卫生设计规定》第13条');
INSERT INTO `t_item_tbl` VALUES ('720', '1．操作室和休息室应尽量避免正面对着渣口、铁口等高温设施和氧气、煤气阀门，操作室应便于操作人员观察、联络和直接控制需操纵的全部设备。距铁路较近的操作室、休息室和楼梯出口不得正对铁路。（转炉操作室观察窗要设置防爆门）\n2．操作方式宜优先设计坐式，并应具有保证操作人员活动的足够空间。在寒冷、高温、强辐射、有害气体、噪声以及其他可能危及人体健康的操作室，应采取相应的防护措施。\n3．直接受高温辐射的操作室，应设空调和隔热防护玻璃窗。横跨热生产线的操作室的底部应采取铺隔热材料或通水冷却等隔热措施。当热辐射强度大于10．4焦耳/平方厘米.分时，应在操作室的窗外0．2～0．5米处加铝丝网或水幕。', '606', '《冶金企业安全卫生设计规定》第14条');
INSERT INTO `t_item_tbl` VALUES ('721', '①大中型厂(矿)区的主干道两侧应设人行道。\n②生产车间厂房内应设安全走道，其宽度不得小于1米，两侧用宽8厘米的黄色油漆标明。\n③带式输送机应根据需要设人行过桥，一般应每隔30～70米设一座人行过桥。\n④轧钢生产线的辊道，在人员需要横跨的地方，应设能隔热的人行过桥，必要时人行过桥应设防护网或做成封闭式过桥。\n⑤人行过桥、临空走道及平台的净高不得影响生产作业及检修，宜不低于2．2米。', '607', '《冶金企业安全卫生设计规定》第16条第一项');
INSERT INTO `t_item_tbl` VALUES ('722', '①高处作业场所和需经常登高检修的设备、设施，宜安设钢梯。②钢梯的设计，必须符合GB4053．1～4053．2《固定式钢直梯和钢斜梯安全技术条件》的规定（要有围笼）。③钢直梯的攀登高度一般不应超过9米，超过9米时，应设梯间平台并分段交错设梯；钢斜梯的梯高一般不大于5米，大于5米时必须设梯间平台并分段交错设梯。斜梯上方的净空高度不应小于1．9米。', '608', '《冶金企业安全卫生设计规定》第16条第二项');
INSERT INTO `t_item_tbl` VALUES ('723', '①平台的位置和尺寸，应便于人员通行、操作和检修。\n②固定式工业钢平台的设计，必须符合GB4053．4《固定式工业钢平台》的规定。\n③平台应有防滑措施，室外平台还应有排雨水措施。应尽量减少室外梯子、平台。', '609', '《冶金企业安全卫生设计规定》第16条第三项');
INSERT INTO `t_item_tbl` VALUES ('724', '①平台、架空走道、人行通道和有坠落危险的梯子、坑池边、升降口、安装孔等场所，必须设防护栏杆、围栏或盖板。梯子、坑池口、升降孔、安装孔等应避开人行通道。②固定式工业防护栏杆，应遵守GB4053．3《固定式工业防护栏杆安全技术条件》的规定。③有可能对人体产生机械伤害(无防护罩的旋转体、连续可移动的机械设备等)、灼烫、腐蚀、触电等的危险场所，必须设防护屏。④防护屏的设计必须符合GB8197《防护屏安全要求》的规定。⑤以操作人员的操作位置所在平面为基准，凡高度在2米以内的所有传动带、转轴、传动链、联轴节、带轮、齿轮、飞轮、电锯等危险零部件和危险部分，都必须设置防护罩。⑥防护罩的设计必须符合GB8196《机械设备防护罩安全要求》的规定。⑦平台、走道和梯子的上方有可触及的电缆线、酸碱液、高温管道以及高温材料时，应采取相应的防触电、防漏、隔热等安全设施。⑧火焰清理机、砂轮机等有火花飞出的设备和矫直机等可能有物件移位、飞出的设备，必须设防护罩或挡板。⑨设备水平移动所形成的开口处，应设置随设备移动的罩体，或设置易于拆卸的盖板，或不妨碍作业的栏杆。⑩往复运动的机械有危及人体安全的部分应设置防护罩或防护栏杆。', '610', '《冶金企业安全卫生设计规定》第16条第四项');
INSERT INTO `t_item_tbl` VALUES ('725', '1各种炉窑和散热设备，尽理布置在夏季主导风向的下风侧或厂房（车间）外。\n2钢锭、铸坯、铸件及轧材等炽热的半成品及成品，应尽快运至专设的存放场地或运出厂房（车间），存放场地应布置在厂房（车间）主导风向的下风侧。\n3当热源较多而采用天窗排气时，应将热源集中在排气天窗下侧，并对热源采取隔热措施。', '611', '《冶金企业高温作业职业卫生管理规程》第4.6条');
INSERT INTO `t_item_tbl` VALUES ('726', '1在较长时间内直接受到热辐射影响的作业场所或工作室，当辐射强度在2．1J/（平方厘米•min）以上时，应采取水幕、隔热水箱、隔热屏等隔热措施。2对职工经常停留的高温地面或靠近人体的高温壁板，当表面平均温度高于40℃时，也应采取隔热措施。3屋顶高度在5m以下，通风情况较差者，可采用开设天窗或层顶搭设凉棚、使用隔热层或喷水等降温措施。4厂区绿化面积应达国家绿化标准的规定。', '612', '《冶金企业高温作业职业卫生管理规程》第4.7条');
INSERT INTO `t_item_tbl` VALUES ('727', '自然通风\n1以自然通风为主的厂房，其方位应根据主要进风风面和建筑形式，按夏季主导风向，尽量布置在热源的上风侧；厂房的长轴与夏季主导风向尽量垂直。\n2历年最热月份平均气温高于或等于28℃地区的辅助建筑，可采用通风屋顶。\n3高温车间应采取有组织的自然通风，合理安排进、排风口，进风口下缘距地面应不高于1.2m，天窗应装有挡风板。\n4当生产工艺无特殊要求时，炎热地区的厂房宜采用敞开型或半敞开型。\n机械通风\n1炼焦、烧结、炼铁、炼钢、轧钢及耐火炉窑等高温作业场所或操作室的所温，当不能达到卫生标准或热辐射强度大于2．1J/（平方厘米•min）时，应设置局部送风或空调设施。\n2局部送风的风向应避免经过热源吹向人体，送到作业场所的风速，应以吹至人体有爽快感为宜。风速可分别为：0、1级高温作业2~4m/s；II、III级高温作业3~5m/s；N级高温作业5~7m/s。\n3气温高于35℃，热辐射强度大于8.4J/（平方厘米•min）的冶炼、浇铸与轧钢等高温作业场所，可采用喷雾风扇降温，其风速应控制在3~5m/s，雾滴直径应不大于100，以小于60为宜。', '613', '《冶金企业高温作业职业卫生管理规程》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('728', '对钳式吊车司机室、均热炉揭盖机操作室、轧钢操作室、拦焦车及熄焦车操作室等（连铸P3工作业区）特殊高温作业操作室，应采取有效的降温措施；在采用冷风机组或空气调节机组时，室温一般为24~28℃。', '614', '《冶金企业高温作业职业卫生管理规程》第4.11条');
INSERT INTO `t_item_tbl` VALUES ('729', '在高温作为业场所附近，应设置工间休息室，室温可在28~30℃，在炼焦炉顶等特殊高温作业场所附近的工间休息室，如采用冷风机组或空调机组时，室温以25℃左右为宜。', '615', '《冶金企业高温作业职业卫生管理规程》第4.12条');
INSERT INTO `t_item_tbl` VALUES ('730', '炎热地区的钢铁冶炼、轧钢等工厂应设有高温作业工人休息室，室内可用风扇，室温以不超过30℃为宜；如采用冷风机组或空调机组时，室温可采用26~29℃。', '615', '《冶金企业高温作业职业卫生管理规程》第4.13条');
INSERT INTO `t_item_tbl` VALUES ('731', '1企业应按规定供给高温作业和夏季露天作业人员茶水、含盐汽水等清凉饮料及防暑药品。2盐汽水含盐量一般采用0.1~0.2%。盐汽水水温以8~12℃为宜。3盐汽水等饮料的质量必须符合国家标准。4饮水量以少量、多次，并根据个人需要自由饮用为宜，不应暴饮。（休息室内配备防暑药箱）', '616', '《冶金企业高温作业职业卫生管理规程》第4.14条');
INSERT INTO `t_item_tbl` VALUES ('732', '1对白工作的露天作业，应尽量安排早出工，晚收工，延长午休时间。\n2缩短一次性持续接触高温时间，工作中应多次轮换作业，执行国家有关标准的规定。\n3在暑期，不应占用职工的业余时间，并应按上班班次调整单身同班次职工住宿。', '617', '《冶金企业高温作业职业卫生管理规程》第4.15条');
INSERT INTO `t_item_tbl` VALUES ('733', '应为露天作业的职工设置防阳光暴晒的休息场所。', '618', '《冶金企业高温作业职业卫生管理规程》第4.16条');
INSERT INTO `t_item_tbl` VALUES ('734', '1气象条件测定的选点、使用仪器、测试技术等均应执行国家标准的规定。\n2测定项目为气温、气湿、风速和热辐射强度。\n3测定期限应根据劳动环境热源的稳定情况与高温作业分级级别来确定：I、II级高温作业，每年暑期测定1次；III、IN级高温作业每年暑期测定2次。\n4测定时间：选择最热月份测定1~2次，每次连续测定2日，在夏时制14~16时测定，取2日的平均值和最高值。\n5防暑降温设施在改进前、后，应进行测定，并作出技术效果评价报送有关部门', '619', '《冶金企业高温作业职业卫生管理规程》第5.1条');
INSERT INTO `t_item_tbl` VALUES ('735', '冶金企业对涉及煤气、氧气、氢气等危险化学品生产、输送、使用、储存的设施以及油库、电缆隧道（沟）等重点防火部位，应当按照有关规定采取有效、可靠的防火防爆措施。', '620', '《机关、团体、企业、事业单位消防安全管理规定》第26条');
INSERT INTO `t_item_tbl` VALUES ('736', '消防安全重点单位应当进行每日防火巡查，并确定巡查的人员、内容、部位和频次。其他单位可以根据需要组织防火巡查。巡查的内容应当包括：(一)用火、用电有无违章情况；(二)安全出口、疏散通道是否畅通，安全疏散指示标志、应急照明是否完好；(三)消防设施、器材和消防安全标志是否在位、完整；(四)常闭式防火门是否处于关闭状态，防火卷帘下是否堆放物品影响使用；(五)消防安全重点部位的人员在岗情况；(六)其他消防安全情况。公众聚集场所在营业期间的防火巡查应当至少每二小时一次；营业结束时应当对营业现场进行检查，消除遗留火种。医院、养老院、寄宿制的学校、托儿所、幼儿园应当加强夜间防火巡查，其他消防安全重点单位可以结合实际组织夜间防火巡查。防火巡查人员应当及时纠正违章行为，妥善处置火灾危险，无法当场处置的，应当立即报告。发现初起火灾应当立即报警并及时扑救。防火巡查应当填写巡查记录，巡查人员及其主管人员应当在巡查记录上签名。', '621', '《机关、团体、企业、事业单位消防安全管理规定》第26条');
INSERT INTO `t_item_tbl` VALUES ('737', '机关、团体、事业单位应当至少每季度进行一次防火检查，其他单位应当至少每月进行一次防火检查。', '622', '《机关、团体、企业、事业单位消防安全管理规定》第26条第（四）项');
INSERT INTO `t_item_tbl` VALUES ('738', '任何单位、个人不得损坏、挪用或者擅自拆除、停用消防设施、器材，不得埋压、圈占、遮挡消火栓或者占用防火间距，不得占用、堵塞、封闭疏散通道、安全出口、消防车通道。人员密集场所的门窗不得设置影响逃生和灭火救援的障碍物。', '622', '《中华人民共和国消防法》第28条');
INSERT INTO `t_item_tbl` VALUES ('739', '集中火灾报警系统、控制中心报警系统中的消防控制室或消防控制中心应当实行每日24小时专人值班制度，每班不应少于2人。', '623', '《GA767-2008消防控制室通用技术要求》第4.2.1.1条');
INSERT INTO `t_item_tbl` VALUES ('740', '进行电焊、气焊等具有火灾危险作业的人员和自动消防系统的操作人员，必须持证上岗，并遵守消防安全操作规程。', '623', '《中华人民共和国消防法》第21条第二款');
INSERT INTO `t_item_tbl` VALUES ('741', '消防安全管理人、消防控制室值班员和消防设施操作维护人员应经过消防职业培训，持证上岗。', '623', 'GA654-2006《人员密集场所消防安全管理》5.1.2');
INSERT INTO `t_item_tbl` VALUES ('742', '消防控制室应有值班情况、消防安全检查情况及巡查情况的记录。', '624', '《GA767-2008消防控制室通用技术要求》第4.1.5条');
INSERT INTO `t_item_tbl` VALUES ('743', '消防控制室值班人员应真实填写消防控制室值班记录，记录包括：控制器日运行情况记录、控制器日检查情况记录', '624', '《建筑消防设施的维护管理（GA587-2005）》第5.3条 ');
INSERT INTO `t_item_tbl` VALUES ('744', '消防控制室值班人员应当每日检查火灾报警控制器的自检、消音、复位功能以及主备电源切换功能。', '624', '《建筑消防设施的维护管理（GA587-2005）》第5.2条');
INSERT INTO `t_item_tbl` VALUES ('745', '消防控制室应有值班情况、消防安全检查情况及巡查情况的记录。', '624', '《GA767-2008消防控制室通用技术要求》第4.1.5条');
INSERT INTO `t_item_tbl` VALUES ('746', '建筑消防设施巡视检查应明确各类建筑消防设施巡查部位，设有电子巡更系统的单位，应将建筑消防设施巡查部位纳入其中。巡视检查每日应至少组织一次，', '624', '《建筑消防设施的维护管理（GA587-2005）》第6.1.1条');
INSERT INTO `t_item_tbl` VALUES ('747', '消防控制室应有建（构）筑物竣工后的总平面布局图、建筑消防设施平面布置图、建筑消防设施系统图及安全出口布置图、重点部位位置图等。', '625', '《GA767-2008消防控制室通用技术要求》第4.1.1条');
INSERT INTO `t_item_tbl` VALUES ('748', '消防控制室应有消防安全管理规章制度、应急灭火预案、应急疏散预案等。', '625', '《GA767-2008消防控制室通用技术要求》第4.1.2条');
INSERT INTO `t_item_tbl` VALUES ('749', '消防控制室应有消防安全组织结构图，包括消防安全责任人、管理人、专职、义务消防人员等内容。', '625', '《GA767-2008消防控制室通用技术要求》第4.1.3条');
INSERT INTO `t_item_tbl` VALUES ('750', '防控制室应有消防设施一览表，包括消防设施的类型、数量、状态等内容。', '625', '《GA767-2008消防控制室通用技术要求》第4.1.6条');
INSERT INTO `t_item_tbl` VALUES ('751', '消防控制室应有消防系统控制逻辑关系说明、设备使用说明书、系统操作规程、系统和设备维护保养制度等。', '625', '《GA767-2008消防控制室通用技术要求》第4.1.7条');
INSERT INTO `t_item_tbl` VALUES ('752', '消防控制室应定期保存和归档设备运行状况、接报警记录、火灾处理情况、设备检修检测报告等资料。', '625', '《GA767-2008消防控制室通用技术要求》第4.1.8条');
INSERT INTO `t_item_tbl` VALUES ('753', '消防控制室应能与各消防电话分机通话，并具有插入通话功能。\n消防控制室应能接收来自消防电话插孔的呼叫，并能通话。\n消防控制室应有消防电话通话录音功能。\n消防控制室应能显示消防电话的故障状态。', '626', '《GA767-2008消防控制室通用技术要求》第5.3.11条');
INSERT INTO `t_item_tbl` VALUES ('754', '消防控制室应能分别通过手动和按照预设控制逻辑自动控制选择广播分区、启动或停止应急广播，并在扬声器进行应急广播时自动对广播内容进行录音。\n消防控制室应能显示应急广播的故障状态。', '627', '《GA767-2008消防控制室通用技术要求》第5.3.12.2、5.3.12.3条');
INSERT INTO `t_item_tbl` VALUES ('755', '消防电源\n消防控制室应能显示消防用电设备的供电电源和备用电源的工作状态和欠压报警信息。', '628', '《GA767-2008消防控制室通用技术要求》第5.3.14条');
INSERT INTO `t_item_tbl` VALUES ('756', '除住宅外的民用建筑、厂房和丙类仓库的下列部位，应设置消防应急照明灯具：\n消防控制室、消防水泵房、自备发电机房、配电室、防烟与排烟机房以及发生火灾时仍需正常工作的其它房间；', '629', '《建筑设计防火规范》（JB50016-2006）第11.3.1条（2）');
INSERT INTO `t_item_tbl` VALUES ('757', '变配电室出入口应设置高度不低于400mm的挡板。', '630', '参考《变配电室安全管理规范》第5.6条');
INSERT INTO `t_item_tbl` VALUES ('758', '长度大于7m的配电室应有2个出入口，并宜布置在配电室的两端（室内面积小于6m²而无人值班的可以设一个）。当变配电室为多层建筑时，应有一个出口通向室外楼梯平台，平台应有固定的护栏。', '631', '参考《变配电室安全管理规范》第5.7条');
INSERT INTO `t_item_tbl` VALUES ('759', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '632', '参考《变配电室安全管理规范》第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('760', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '633', '参考《变配电室安全管理规范》第5.18.3条');
INSERT INTO `t_item_tbl` VALUES ('761', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '633', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('762', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '633', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('763', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：\na)绝缘拉杆应悬挂或架在支架上，不应与墙接触；\nb)绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；\nc)绝缘垫和绝缘台应经常保持清洁、无损伤；\nd)高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；\ne)安全用具不允许当作其它工具使用；\nf)安全用具不合格的不得存放在工作现场。', '633', '参考《变配电室安全管理规范》第5.18.5条');
INSERT INTO `t_item_tbl` VALUES ('764', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '634', '参考《变配电室安全管理规范》第5.18.3条');
INSERT INTO `t_item_tbl` VALUES ('765', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '634', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('766', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '634', '参考《变配电室安全管理规范》第5.18.3.2条');
INSERT INTO `t_item_tbl` VALUES ('767', '配电室长度超过7m时（室内面积小于6m²而无人值班的可以设一个）应设两个出口，并宜布置在配电室的两端。当配电室为楼上楼下两部分布置时，楼上部分的出口应至少有一个通向该层走廊或室外的安全出口。', '635', '《低压配电设计规范GB50054-95》第3.3.2条');
INSERT INTO `t_item_tbl` VALUES ('768', '配电室的门均应向外开启，但通向高压配电室的门应为双向开启门。', '636', '《低压配电设计规范》第3.3.2条');
INSERT INTO `t_item_tbl` VALUES ('769', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '637', '参考《变配电室安全管理规范》第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('770', '变配电室出入口应设置高度不低于400mm的挡板。', '638', '参考《变配电室安全管理规范》第5.6条');
INSERT INTO `t_item_tbl` VALUES ('771', '电气设备外露可导电部分，必须与接地装置有可靠的电气连接。成排的配电装置的两端均应与接地线相连。低压电气设备非带电的金属外壳接地电阻不大于4欧姆。接地线截面积不得小于2.5m²，配电柜、台、箱门必须要有可靠跨接。', '639', '《10kV及以下变电所设计规范》第3.1.4条');
INSERT INTO `t_item_tbl` VALUES ('772', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '640', '《剩余电流动作保护装置安装和运行》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('773', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '640', '《剩余电流动作保护装置安装和运行》第7.2条');
INSERT INTO `t_item_tbl` VALUES ('774', '直敷布线可用于正常环境的屋内场所，并应符合下列要求：\r\n一、直敷布线应采用护套绝缘导线。', '641', '《低压配电设计规范》第5.2.1条（1）');
INSERT INTO `t_item_tbl` VALUES ('776', '直敷布线可用于正常环境的屋内场所，并应符合下列要求：\r\n三、当导线垂直敷设至地面低于1．8m时，应穿管保护。', '641', '《低压配电设计规范》第5.2.1条（3）');
INSERT INTO `t_item_tbl` VALUES ('778', '插座回路均应设置剩余电流动作保护装置。', '642', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('779', '电气设备必须有接地保护。', '643', 'GB19517-2004《国家电气设备安全技术规范》第2.2.4条');
INSERT INTO `t_item_tbl` VALUES ('780', '临时用电的电气设备，必须安装剩余电流保护装置。', '644', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('781', '危险场所必须按《中华人民共和国爆炸危险所电气安全规程（试行）》划定危险场所区域等级图，并按危险区域等级和爆炸性混合物的级别、组别配置相应符合国家标准规定的防爆等级的电气设备。防爆电气设备的配置应符合整体防爆要求；防爆电气设备的施工、安装、维护和检修也必须符合规程要求。', '645', '《爆炸危险场所安全规定》第17条');
INSERT INTO `t_item_tbl` VALUES ('782', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '646', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('783', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '647', '《中华人民共和国职业病防治法》第20条');
INSERT INTO `t_item_tbl` VALUES ('784', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '648', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('785', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '649', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('786', '容易造成职业危害的工序应隔离设置。', '650', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('787', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '651', '《中华人民共和国职业病防治法》第25条');
INSERT INTO `t_item_tbl` VALUES ('788', '应在可产生职业危害的工作场所和设备上，按GBZ158《工作场所职业病危害警示标识》和GBZ/T203《高度物品作业岗位职业病危害告知规范》的要求设置职业危害警示标识。', '652', '《中华人民共和国职业病防治法》第22条');
INSERT INTO `t_item_tbl` VALUES ('789', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '653', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('790', '冶金企业应当根据本单位的安全生产实际状况，科学、合理确定煤气柜容积，按照《工业企业煤气安全规程》(GB6222)的规定，合理选择柜址位置，设置安全保护装置，制定煤气柜事故应急预案。', '654', '《冶金企业安全生产监督管理规定》第27条');
INSERT INTO `t_item_tbl` VALUES ('791', '应急照明设施并确保完好、有效。', '542', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('792', '生产经营场所和员工宿舍应当设有符合紧急疏散要求、标志明显、保持畅通的出口。禁止封闭、堵塞生产经营场所或者员工宿舍的出口。', '543', '《中华人民共和国安全生产法》第34条');
INSERT INTO `t_item_tbl` VALUES ('793', '按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，确保完好有效。', '544', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('794', '投入使用后的防雷装置实行定期检测制度。防雷装置检测应当每年一次，对爆炸危险环境场所的防雷装置应当每半年检测一次。', '545', '《防雷减灾管理办法》第19条');
INSERT INTO `t_item_tbl` VALUES ('795', '变配电室变压器、高压开关柜、低压开关柜操作面地面应铺设绝缘胶垫。', '546', '参考《变配电室安全管理规范》第5.3条');
INSERT INTO `t_item_tbl` VALUES ('796', '变配电室出入口应设置高度不低于400mm 的挡板。', '547', '参考《变配电室安全管理规范》5.6条');
INSERT INTO `t_item_tbl` VALUES ('797', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '548', '参考《变配电室安全管理规范》第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('798', '通往室外的门应向外开。设备间与附属房间之间的门应向附属房间方向开。高压间与低压间之间的门，应向低压间方向开。配电装置室的中间门应采用双向开启门。', '549', '参考《变配电室安全管理规范》第5.8.3条');
INSERT INTO `t_item_tbl` VALUES ('799', '通往室外的窗应装有纱窗。', '549', '参考《变配电室安全管理规范》5.9.3');
INSERT INTO `t_item_tbl` VALUES ('800', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '550', '参考《变配电室安全管理规范》第5.18.3条');
INSERT INTO `t_item_tbl` VALUES ('801', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '550', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('802', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '550', '参考《变配电室安全管理规范》第5.18.3.2条');
INSERT INTO `t_item_tbl` VALUES ('803', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：\na) 绝缘拉杆应悬挂或架在支架上，不应与墙接触；\nb) 绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；\nc) 绝缘垫和绝缘台应经常保持清洁、无损伤；\nd) 高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；\ne) 安全用具不允许当作其它工具使用；\nf) 安全用具不合格的不得存放在工作现场。', '550', '');
INSERT INTO `t_item_tbl` VALUES ('804', '禁止类：禁止合闸、禁止攀登、高压危险；警告类：止步、高压危险；准许类：从此上下、在此工作', '551', '');
INSERT INTO `t_item_tbl` VALUES ('805', '变配电室内环境整洁，场地平整，设备间不应存放与运行无关的闲散器材和私人物品，禁止无关人员进入场地；', '552', '');
INSERT INTO `t_item_tbl` VALUES ('806', '主控制室、高压配电室不应带入食物及储放粮食，值班室不应设置、使用寝具、灶具，并应有防止小动物的安全措施；', '552', '');
INSERT INTO `t_item_tbl` VALUES ('807', '非变配电室从业人员因工作需要进入变配电室时，应经值班人员许可，并办理登记手续。当需要进入设备区时，应有值班人员监护', '553', '');
INSERT INTO `t_item_tbl` VALUES ('808', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '554', '');
INSERT INTO `t_item_tbl` VALUES ('809', '变配电室出入口应设置高度不低于400mm 的挡板。', '555', '');
INSERT INTO `t_item_tbl` VALUES ('810', '变配电室变压器、高压开关柜、低压开关柜操作面地面应铺设绝缘胶垫。', '556', '');
INSERT INTO `t_item_tbl` VALUES ('811', '通往室外的门应向外开；配电装置室的中间门应采用双向开启门。', '557', '');
INSERT INTO `t_item_tbl` VALUES ('812', '通往室外的窗应装有纱窗。', '557', '');
INSERT INTO `t_item_tbl` VALUES ('813', '柜、屏、台、箱、盘的金属框架及基础型钢必须接地（PE）或接零（PEN）可靠；装有电器的可开启门与框架的接地端子间应用裸线编织铜线连接，作好标识。', '558', '');
INSERT INTO `t_item_tbl` VALUES ('814', '箱盘内开关灵活可靠。带有漏电保护的回路，漏电保护装置动作电流和动作时间按设计要求。', '559', '');
INSERT INTO `t_item_tbl` VALUES ('815', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '559', '');
INSERT INTO `t_item_tbl` VALUES ('816', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '559', '');
INSERT INTO `t_item_tbl` VALUES ('817', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。', '560', '');
INSERT INTO `t_item_tbl` VALUES ('818', '直敷布线可用于正常环境的屋内场所，应采用护套绝缘导线。当导线垂直敷设至地面低于1．8m时，应穿管保护。', '561', '');
INSERT INTO `t_item_tbl` VALUES ('819', '插座回路均应设置剩余电流动作保护装置。宜装设漏电保护器，禁止使用无保护线插头插座。', '562', '');
INSERT INTO `t_item_tbl` VALUES ('820', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。不应从带插座灯头上引接电源供给电器。', '563', '');
INSERT INTO `t_item_tbl` VALUES ('821', '应采用橡皮护套绝缘软线。与电源联接，应采用开关、插头座。严禁用导线直接插入插座，或挂在电源线上使用。', '564', '');
INSERT INTO `t_item_tbl` VALUES ('822', '工具必须由专职人员每年至少检查一次', '565', '');
INSERT INTO `t_item_tbl` VALUES ('823', '在使用移动式的I类设备时，应先确认其金属外壳或构架已可靠接地，使用带保护接地极的插座，同时宜装设漏电保护器，禁止使用无保护线插头插座。', '566', '');
INSERT INTO `t_item_tbl` VALUES ('824', '露天使用的用电设备、配电装置应采取防雨、防雪、防雾和防尘的措施。', '567', '');
INSERT INTO `t_item_tbl` VALUES ('825', '电气设备必须有可靠的接地保护', '568', '');
INSERT INTO `t_item_tbl` VALUES ('826', '临时线路使用必须经过审批，一般使用期限为过15天，最长不超过一个月', '569', '');
INSERT INTO `t_item_tbl` VALUES ('827', '临时用电的电气设备，必须安装剩余电流保护装置。', '570', '');
INSERT INTO `t_item_tbl` VALUES ('828', '线路敷设应符合电气装置设置及安装规范，室内不低于2.5m ，室外不低于4.5m，道路上方不低于6m。', '571', '');
INSERT INTO `t_item_tbl` VALUES ('829', '行灯等手持式电动工具、器具应根据使用现场，分别采取可靠的安全保护措施，如漏电保护电器或使用３６伏以下的安全电压。安全变压器应采用双圈的。', '572', '');
INSERT INTO `t_item_tbl` VALUES ('830', '手提式和局部照明灯具应选用安全电压或双重绝缘结构。在使用螺口灯头时，灯头螺纹端应接至电源的工作中性线。', '573', '');
INSERT INTO `t_item_tbl` VALUES ('831', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '574', '');
INSERT INTO `t_item_tbl` VALUES ('832', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '575', '');
INSERT INTO `t_item_tbl` VALUES ('833', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '576', '');
INSERT INTO `t_item_tbl` VALUES ('834', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '577', '');
INSERT INTO `t_item_tbl` VALUES ('835', '容易造成职业危害的工序应隔离设置。', '578', '');
INSERT INTO `t_item_tbl` VALUES ('836', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '579', '');
INSERT INTO `t_item_tbl` VALUES ('837', '应在可产生职业危害的工作场所和设备上，按GBZ158《工作场所职业病危害警示标识》和GBZ/T203《高度物品作业岗位职业病危害告知规范》的要求设置职业危害警示标识。', '580', '');
INSERT INTO `t_item_tbl` VALUES ('838', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '581', '');
INSERT INTO `t_item_tbl` VALUES ('839', '生产、储存、使用危险化学品的，应当根据危险化学品的种类、特性，在车间、库房等作业场所设置相应的监测、通风、防晒、调温、防火、灭火、防爆、泄压、防毒、消毒、中和、防潮、防雷、防静电、防腐、防渗漏、防护围堤或者隔离操作等安全设施、设备，并按照国家标准和国家有关规定进行维护、保养，保证符合安全运行要求。', '582', '');
INSERT INTO `t_item_tbl` VALUES ('840', '开关、灯具、线路、用电设施均应符合防爆要求', '583', '');
INSERT INTO `t_item_tbl` VALUES ('841', '专人管理，并有防潮防雨淋设施', '584', '');
INSERT INTO `t_item_tbl` VALUES ('842', '根据危险品性能分区、分类、分库贮存。 各类危险品不得与禁忌物料混合贮存', '585', '');
INSERT INTO `t_item_tbl` VALUES ('843', '使用危险化学品从事生产的单位，其生产条件必须符合国家标准和国家有关规定，并依照国家有关法律、法规的规定取得相应的许可，必须建立、健全危险化学品使用的安全管理规章制度，保证危险化学品的安全使用和管理。 ', '586', '');
INSERT INTO `t_item_tbl` VALUES ('844', '设置安全标志', '587', '');
INSERT INTO `t_item_tbl` VALUES ('845', '车间、库房等作业场所应设置防静电装置', '588', '');
INSERT INTO `t_item_tbl` VALUES ('846', '   生产、经营、储存、使用危险物品的车间、商店、仓库不得与员工宿舍在同一座建筑物内，并应当与员工宿舍保持安全距离。', '589', '');
INSERT INTO `t_item_tbl` VALUES ('847', '  生产经营场所和员工宿舍应当设有符合紧急疏散要求、标志明显、保持畅通的出口。禁止封闭、堵塞生产经营场所或者员工宿舍的出口。', '589', '');
INSERT INTO `t_item_tbl` VALUES ('848', '储存丙类固体物品的库房，不准使用碘钨灯和超过六十瓦以上的白炽灯等高温照明灯具。当使用日光灯等低温照明灯具和其他防燃型照明灯具时，应当对镇流器采取隔热、散热等防火保护措施，确保安全。', '590', '');
INSERT INTO `t_item_tbl` VALUES ('849', ' 库房内敷设的配电线路，需穿金属管或用非燃硬塑料管保护。', '591', '');
INSERT INTO `t_item_tbl` VALUES ('850', '仓库应当设置醒目的防火标志', '592', '');
INSERT INTO `t_item_tbl` VALUES ('851', '库存物品应当分类、分垛储存，每垛占地面积不宜大于一百平方米，垛与垛间距不小于一米，垛与墙间距不小于零点五米，垛与梁、柱间距不小于零点三米，主要通道的宽度不小于二米。', '593', '');
INSERT INTO `t_item_tbl` VALUES ('852', '杜绝违章指挥、违章操作、违反劳动纪律的行为', '594', '');
INSERT INTO `t_item_tbl` VALUES ('853', '印刷、折页、胶订、裁切作业应佩戴耳塞或耳罩', '595', '');
INSERT INTO `t_item_tbl` VALUES ('854', '调墨、印刷、清洗油墨作业应佩戴防毒面具，不得使用自行装填活性炭的滤毒盒', '595', '');
INSERT INTO `t_item_tbl` VALUES ('855', '胶订作业应佩戴防烟尘口罩', '595', '');
INSERT INTO `t_item_tbl` VALUES ('856', '显影、调墨、清洗墨辊作业应佩戴防化手套。', '595', '');
INSERT INTO `t_item_tbl` VALUES ('857', '生产经营单位应当在有较大危险因素的生产经营场所和有关设施、设备上，设置明显的安全警示标志。', '477', '《中华人民共和国安全生产法》第28条');
INSERT INTO `t_item_tbl` VALUES ('858', '企业应当按照相关规定，加强设备设施定置管理，划设安全通道、物料区域等标志、标识，设备和管道正确采用安全色和安全标志，在有危险因素的生产作业场所和有关设备、设施上设置明显的安全警示标志或标识。', '477', '《安全色》、《安全标志及其使用导则》');
INSERT INTO `t_item_tbl` VALUES ('859', '冶金企业应当在煤气储罐区等可能发生煤气泄漏、聚集的场所，设置固定式煤气检测报警仪，建立预警系统，悬挂醒目的安全警示牌，并加强通风换气。', '477', '参考《冶金企业安全生产监督管理规定》第23条');
INSERT INTO `t_item_tbl` VALUES ('860', '爆炸和火灾危险环境应当设灭火装置和自动报警装置。', '478', '《冶金等工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('861', '1、在煤气危险区域，应当安装固定式一氧化碳监测报警装置；在煤气区域作业的人员应当配备便携式一氧化碳检测报警仪。\r\n2、使用煤气（天然气）的烧嘴等燃烧装置，应设置防突然熄火或点火失败的快速切断阀，以切断煤气（天然气）。', '479', '《冶金等工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('862', '二氧化硫、氯气、砷化氢、氟化氢等有毒有害气体易泄漏的场所，应当保持通风良好，并安装固定式检测报警装置。', '479', '《冶金等工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('863', '1、生产设备、设施应当按国家有关规定要求配备有温度、压力、流量、液位及粉尘浓度、可燃、有毒气体浓度等工艺指标的超限报警装置，并配有必要的安全防护设施。\r\n2、冶炼炉窑的水冷元件应配置温度、进出水流量差检测及报警装置；应设置防止冷却水大量进入炉内的安全设施（如：快速切断阀等）。', '480', '《冶金等工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('864', '企业应当根据本单位的安全生产实际状况，科学、合理确定煤气柜容积，按照《工业企业煤气安全规程》(GB6222)的规定，合理选择柜址位置，设置安全保护装置，制定煤气柜事故应急预案。', '481', '参考《冶金企业安全生产监督管理规定》第27条');
INSERT INTO `t_item_tbl` VALUES ('865', '炎热地区的冶炼、轧钢等工厂的医疗单位，在暑季应设置中暑抢救室', '482', '《冶金等工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('866', '防暑降温设备应按规定有专人检查与维修，切实执行运行管理制度。', '483', '《冶金等工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('867', '生产装置停产或检维修后，复产前应当组织安全检查，进行严格的安全条件确认。', '484', '冶金等工贸企业安全生产标准化基本规范');
INSERT INTO `t_item_tbl` VALUES ('868', '安全设备设施和安全保护装置应当定期进行检查、监测、校验，并作出记录。对超过使用年限或不满足安全使用要求的，及时予以报废。', '485', '冶金等工贸企业安全生产标准化基本规范');
INSERT INTO `t_item_tbl` VALUES ('869', '1、企业应当定期对安全设备设施和安全保护装置进行检查、校验。对超过使用年限和不符合国家产业政策的设备，及时予以报废。对现有设备设施进行更新或者改造的，不得降低其安全技术性能。\r\n2、吊运铜水等熔融有色金属及渣的起重机要符合冶金起重机的相关要求；横梁、耳轴销和吊钩、钢丝绳及其端头固定零件，进行定期检查，发现问题及时处理。\r\n3、盛装铜水等熔融有色金属及渣的罐（包、盆）等容器耳轴定期进行检测。\r\n4、炉、窑、槽、罐类设备本体及附属设施应定期检查，出现严重焊缝开裂、腐蚀、破损、衬砖损坏、壳体发红及明显弯曲变形等未报修或报废，不应继续使用。', '485', '参考《冶金企业安全生产监督管理规定》第28条');
INSERT INTO `t_item_tbl` VALUES ('870', '氧气系统应当采取可靠的安全措施，防止氧气燃爆事故以及氮气、氩气、珠光砂窒息事故。', '486', '参考《冶金企业安全生产监督管理规定》第24条');
INSERT INTO `t_item_tbl` VALUES ('871', '（一）厂区内实行定置管理：厂区有定置图，对各类厂房建筑、物料堆放点、交通道路等应注明\n（二）厂区内按照定置管理的要求实现定置摆放，厂区内无杂物、无图物不符等状况\n（三）工业垃圾和生活垃圾分开定点存放，有防吹散、防污染措施\n（四）危险固体废料应有专门存放地点，存放点有防渗漏措施，且符合国家规定妥善处理\n（五）厂区大门开关灵活、方便，迅速，无卡死现象', '487', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('872', '（一）厂区双向主干道宽度不小于5米，单向主干道宽度不小于3米，且为环形\n（二）路面排水良好，路面平整，无台阶、无坑沟，盖板齐全\n（三）厂区主干道在平原地区应小于6度，山区应小于8度\n（四）厂区大门、危险路段的车速应限制在每小时5公里；人员稠密地段、下坡路、转弯路、交叉路口、装卸作业区限速每小时15公里，并设置限速标志\n（五）交叉路口若有视线盲区，应设立反光镜，反光镜无破损，角度和高度应便于观察道路盲区\n（六）厂区道路应有明显的人、车分流线，人行道宽度不小于1米，分流线清晰，宽度大于10厘米', '488', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('873', '（一）利用主干道堆放物品或作为停车位置，应有划线标志，通行部分宽度必须不小于5米\n（二）不得在转弯处或道路两侧堆放物品或停置车辆\n（三）道路施工应有警示牌或护栏，夜间要有红灯警示\n（四）厂区主干道占道率应小于道路长度的5%\n（五）不得将厂区主干道横向全部堵死', '489', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('874', '（一）照明灯布置合理，无照明盲区\n（二）照明灯具完好率达100%', '490', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('875', '（一）室外消防栓应合理配置，低压消防栓布置间距不超过120米，应沿道路两侧和路口设置，消防栓距路面边不超过2米，距建筑物外墙不小于5米\n（二）有明显的漆色标志，其1米范围内无障碍物\n（三）手提式灭火器的配置数量符合规定\n（四）所有消防器材应完好，在有效期限内使用', '491', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('876', '（一）搬运、安装、其他一些作业和高压线保持安全距离；（二）厂房的装修、维修、做防水等和高压线保持安全距离；（三）高压线下严禁码放材料和有建筑物。', '492', '《中华人民共和国电力行业标准》');
INSERT INTO `t_item_tbl` VALUES ('877', '（一）车间通道根据生产要求，宽度标准如下：\n通道只行人通道，宽度不小于1米；\n通过电瓶车、叉车的通道，宽度不小于1.8米；\n通行汽车的车间通道，宽度应不小于3米；\n如有铁路线，车间通道，宽度应不小于5米\n（二）通道线应明显清晰，宽度不小于10厘米', '493', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('878', '（一）通道路面应平整，无台阶、无坑、沟\n（二）无积油、积水，无绊脚物', '494', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('879', '（一）任何堆放在通道标记线内和压住安全通道标记线的物件，都判定为占道\n（二）车间占道率应低于5%\n（三）不得有物件将通道横向全部堵死', '495', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('880', '（一）车行道上方的悬挂物高度不小于4米，人行道上方悬挂物高度不小于2.5米\n（二）悬挂应牢固可靠', '496', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('881', '（一）仓库通道的宽度应满足以下要求：\n车行道宽度不小于3.5米\n人行道宽度不小于1米\n（二）路面平坦，无积油积水，无绊脚物\n（三）占道率应小于5%', '497', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('882', '（一）照明灯具完好率达100%\n（二）库房内不得使用移动式照明灯具', '498', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('883', '（一）库房内不得存在临时线\n（二）库房内敷设的电气线路必须，穿金属管或非燃硬塑料管\n（三）每个库房应当在库房外单独安装开关箱\n（四）甲、乙类物品和丙类液体库房的电气装置，必须安装防爆型电气装置，储存丙类固体物品的仓库，不得使用碘钨灯或超过60瓦以上的白炽灯等高温照明灯具', '499', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('884', '（一）防火制度、消防设施标志、防火标志齐全\n（二）按标准配备消防器材的数量和种类\n（三）防火通道畅通，无封死和堵塞现象', '500', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('885', '以下仓库应安装火灾报警装置：\n1、甲、乙、丙类物品储备库；\n2、专业性仓库\n3、大型物资仓库\n4、耐火等级为三、四级的仓库', '501', '');
INSERT INTO `t_item_tbl` VALUES ('886', '（一）物品包装应牢固，不得有严重破损、残缺、变形、物品变质、分解等现象\n（二）甲、乙类物品的包装应严密，不得有跑、冒]滴、漏现象', '501', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('887', '电器产品、燃气用具的安装、使用及其线路、管路的设计、敷设、维护保养、检测，必须符合消防技术标准和管理规定。', '502', '《中华人民共和国消防法》第27条第二款');
INSERT INTO `t_item_tbl` VALUES ('888', '消防安全管理情况应当包括以下内容：有关燃气、电气设备检测（包括防雷、防静电）等记录资料；', '502', '《机关、团体、企业、事业单位消防安全管理规定》第43条项');
INSERT INTO `t_item_tbl` VALUES ('889', '消防安全管理情况应当包括以下内容：消防奖惩情况记录。', '503', '同上第43条');
INSERT INTO `t_item_tbl` VALUES ('890', '单位应当将消防安全工作纳入内部检查、考核、评比内容。对在消防安全工作中成绩突出的部门（班组）和个人，单位应当给予表彰奖励。对未依法履行消防安全职责或者违反单位消防安全制度的行为，应当依照有关规定对责任人员给予行政纪律处分或者其他处理。', '503', '《机关、团体、企业、事业单位消防安全管理规定》第45条');
INSERT INTO `t_item_tbl` VALUES ('891', '住宅区以及其他由两个以上单位管理或者使用的建筑物的消防车通道应当设置标志，加强日常管理，保持畅通。', '504', '《江苏省消防条例》第18条第四款');
INSERT INTO `t_item_tbl` VALUES ('892', '人员密集场所应当在建筑消防设施、消防器材、疏散设施的醒目位置设置消防安全标识，告知维护、使用消防设施、器材以及紧急情况下逃生自救的方法、要求。', '504', '《江苏省消防条例》第22条第一款');
INSERT INTO `t_item_tbl` VALUES ('893', '机关、团体、事业单位应当至少每季度进行一次防火检查，其他单位应当至少每月进行一次防火检查。检查的内容应当包括：消防安全标志的设置情况和完好、有效情况；', '504', '《机关、团体、企业、事业单位消防安全管理规定》第26条');
INSERT INTO `t_item_tbl` VALUES ('894', '消防设施与器材的设置情况，主要包括：1.消防站设置情况，如消防站、消防车、消防人员、移动式消防设备、通讯等；\n2.消防水系统与泡沫系统，如消防水源、消防泵、泡沫液储罐、消防给水管道、消防管网的分区阀门、消火栓、泡沫栓，消防水炮、泡沫炮、固定式消防水喷淋等；\n3.油罐区、液化烃罐区、危险化学品罐区、装置区等设置的固定式和半固定式灭火系统；\n4.甲、乙类装置、罐区、控制室、配电室等重要场所的火灾报警系统；\n5.生产区、工艺装置区、建构筑物的灭火器材配置；6.其他消防器材。', '505', '《危险化学品企业事故隐患排查治理实施导则》4.9.2');
INSERT INTO `t_item_tbl` VALUES ('895', '消防设施与器材的设置情况，主要包括：1.消防站设置情况，如消防站、消防车、消防人员、移动式消防设备、通讯等；\n2.消防水系统与泡沫系统，如消防水源、消防泵、泡沫液储罐、消防给水管道、消防管网的分区阀门、消火栓、泡沫栓，消防水炮、泡沫炮、固定式消防水喷淋等；\n3.油罐区、液化烃罐区、危险化学品罐区、装置区等设置的固定式和半固定式灭火系统；\n4.甲、乙类装置、罐区、控制室、配电室等重要场所的火灾报警系统；\n5.生产区、工艺装置区、建构筑物的灭火器材配置；6.其他消防器材。', '505', '《危险化学品企业事故隐患排查治理实施导则》4.9.2');
INSERT INTO `t_item_tbl` VALUES ('896', '高层建筑、地下工程和人员密集场所应当按照有关规定配备自救器材和辅助逃生设施。生产、经营、储存易燃易爆危险品的场所，应当根据需要配备专用灭火器材、储备专用灭火剂并保持完好有效。', '506', '《江苏省消防条例》第22条第三款');
INSERT INTO `t_item_tbl` VALUES ('897', '机关、团体、企业、事业等单位应当按照消防技术标准，定期组织对消防设施、器材进行检验、检测、维修和保养，对建筑消防设施每年至少进行一次全面检测，确保消防设施、器材完好有效。单位自身不具备检验、检测、维修、保养条件的，应当委托具有相应资质的消防技术服务机构对消防设施、器材进行检验、检测、维修和保养。', '506', '《江苏省消防条例》第17条');
INSERT INTO `t_item_tbl` VALUES ('898', '机关、团体、事业单位应当至少每季度进行一次防火检查，其他单位应当至少每月进行一次防火检查。检查的内容应当包括：（四）灭火器材配置及有效情况；', '506', '《机关、团体、企业、事业单位消防安全管理规定》第26条第（四）项');
INSERT INTO `t_item_tbl` VALUES ('899', '任何单位、个人不得损坏、挪用或者擅自拆除、停用消防设施、器材，不得埋压、圈占、遮挡消火栓或者占用防火间距，不得占用、堵塞、封闭疏散通道、安全出口、消防车通道。人员密集场所的门窗不得设置影响逃生和灭火救援的障碍物。', '507', '《中华人民共和国消防法》第28条');
INSERT INTO `t_item_tbl` VALUES ('900', '任何单位和个人不得圈占或者妨碍他人使用疏散通道、安全出口、建筑消防设施和消防车通道。', '507', '《江苏省消防条例》第18条第三款');
INSERT INTO `t_item_tbl` VALUES ('901', '的集中火灾报警系统、控制中心报警系统中的消防控制室或消防控制中心应当实行每日24小时专人值班制度，每班不应少于2人。', '508', '《消防控制室通用技术要求》第4.2.1.1条');
INSERT INTO `t_item_tbl` VALUES ('902', '进行电焊、气焊等具有火灾危险作业的人员和自动消防系统的操作人员，必须持证上岗，并遵守消防安全操作规程。', '508', '《中华人民共和国消防法》第21条第二款');
INSERT INTO `t_item_tbl` VALUES ('903', '消防安全管理人、消防控制室值班员和消防设施操作维护人员应经过消防职业培训，持证上岗。', '508', '《人员密集场所消防安全管理》5.1.2');
INSERT INTO `t_item_tbl` VALUES ('904', '消防控制室应有值班情况的记录。', '509', '《消防控制室通用技术要求》第4.1.5条');
INSERT INTO `t_item_tbl` VALUES ('905', '消防控制室值班人员应真实填写消防控制室值班记录，记录包括：控制器日运行情况记录、控制器日检查情况记录。', '509', '《建筑消防设施的维护管理》第5.3条');
INSERT INTO `t_item_tbl` VALUES ('906', '消防控制室值班人员应当每日检查火灾报警控制器的自检、消音、复位功能以及主备电源切换功能。', '510', '《建筑消防设施的维护管理》第5.2条');
INSERT INTO `t_item_tbl` VALUES ('907', '消防控制室应有消防安全检查情况及巡查情况的记录。', '510', '《消防控制室通用技术要求》第4.1.5条');
INSERT INTO `t_item_tbl` VALUES ('908', '建筑消防设施巡视检查应明确各类建筑消防设施巡查部位，设有电子巡更系统的单位，应将建筑消防设施巡查部位纳入其中。巡视检查每日应至少组织一次，', '510', '《建筑消防设施的维护管理》第6.1.1条');
INSERT INTO `t_item_tbl` VALUES ('909', '消防控制室应能与各消防电话分机通话，并具有插入通话功能。消防控制室应能接收来自消防电话插孔的呼叫，并能通话。消防控制室应有消防电话通话录音功能。消防控制室应能显示消防电话的故障状态。', '511', '《消防控制室通用技术要求》第4.1.4条');
INSERT INTO `t_item_tbl` VALUES ('910', '消防控制室应能分别通过手动和按照预设控制逻辑自动控制选择广播分区、启动或停止应急广播，并在扬声器进行应急广播时自动对广播内容进行录音。消防控制室应能显示应急广播的故障状态。', '512', '《消防控制室通用技术要求》第4.1.1条');
INSERT INTO `t_item_tbl` VALUES ('911', '消防电源消防控制室应能显示消防用电设备的供电电源和备用电源的工作状态和欠压报警信息。', '513', '《消防控制室通用技术要求》第4.1.2条');
INSERT INTO `t_item_tbl` VALUES ('912', '除住宅外的民用建筑、厂房和丙类仓库的下列部位，应设置消防应急照明灯具：消防控制室、消防水泵房、自备发电机房、配电室、防烟与排烟机房以及发生火灾时仍需正常工作的其它房间；', '514', '《消防控制室通用技术要求》第4.1.3条');
INSERT INTO `t_item_tbl` VALUES ('913', '变配电室出入口应设置高度不低于400mm的挡板。', '515', '《消防控制室通用技术要求》第4.1.6条');
INSERT INTO `t_item_tbl` VALUES ('914', '长度大于7m的配电室应有2个出入口，并宜布置在配电室的两端。当变配电室为多层建筑时，应有一个出口通向室外楼梯平台，平台应有固定的护栏。', '515', '《消防控制室通用技术要求》第4.1.7条');
INSERT INTO `t_item_tbl` VALUES ('915', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '516', '《消防控制室通用技术要求》第4.1.8条');
INSERT INTO `t_item_tbl` VALUES ('916', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '517', '《消防控制室通用技术要求》第5.3.11条');
INSERT INTO `t_item_tbl` VALUES ('917', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '517', '《消防控制室通用技术要求》第5.3.12.2、5.3.12.3条');
INSERT INTO `t_item_tbl` VALUES ('918', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '517', '《消防控制室通用技术要求》第5.3.14条');
INSERT INTO `t_item_tbl` VALUES ('919', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：\n1..绝缘拉杆应悬挂或架在支架上，不应与墙接触；\n2.绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；\n3.绝缘垫和绝缘台应经常保持清洁、无损伤；\n4.高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；\n5.安全用具不允许当作其它工具使用；\n6.安全用具不合格的不得存放在工作现场。', '517', '《建筑设计防火规范》第11.3.1条（2）');
INSERT INTO `t_item_tbl` VALUES ('920', '配电室长度超过7m时应设两个出口，并宜布置在配电室的两端。当配电室为楼上楼下两部分布置时，楼上部分的出口应至少有一个通向该层走廊或室外的安全出口。', '518', '《低压配电设计规范》第3.3.2条');
INSERT INTO `t_item_tbl` VALUES ('921', '变配电室出入口应设置高度不低于400mm的挡板。', '518', '参考《变配电室安全管理规范》第5.6条');
INSERT INTO `t_item_tbl` VALUES ('922', '配电室的门均应向外开启，但通向高压配电室的门应为双向开启门。', '519', '《低压配电设计规范》第3.3.2条');
INSERT INTO `t_item_tbl` VALUES ('923', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '520', '参考《变配电室安全管理规范》第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('924', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '521', '参考《变配电室安全管理规范》第5.18.3条');
INSERT INTO `t_item_tbl` VALUES ('925', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '521', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('926', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '521', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('927', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：\n1..绝缘拉杆应悬挂或架在支架上，不应与墙接触；\n2.绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；\n3.绝缘垫和绝缘台应经常保持清洁、无损伤；\n4.高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；\n5.安全用具不允许当作其它工具使用；\n6.安全用具不合格的不得存放在工作现场。', '521', '参考《变配电室安全管理规范》第5.18.5条');
INSERT INTO `t_item_tbl` VALUES ('928', '电气设备外露可导电部分，必须与接地装置有可靠的电气连接。成排的配电装置的两端均应与接地线相连。', '522', '《10kV及以下变电所设计规范》第3.1.4条');
INSERT INTO `t_item_tbl` VALUES ('929', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '523', '《剩余电流动作保护装置安装和运行》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('930', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '523', '《剩余电流动作保护装置安装和运行》第7.2条');
INSERT INTO `t_item_tbl` VALUES ('931', '直敷布线可用于正常环境的屋内场所，并应符合下列要求：一、直敷布线应采用护套绝缘导线。三、当导线垂直敷设至地面低于1．8m时，应穿管保护。', '524', '《低压配电设计规范》第5.2.1条（1）、（3）条');
INSERT INTO `t_item_tbl` VALUES ('932', '插座回路均应设置剩余电流动作保护装置。', '525', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('933', '电气设备必须有接地保护。', '526', '《国家电气设备安全技术规范》第2.2.4条');
INSERT INTO `t_item_tbl` VALUES ('934', '临时用电的电气设备，必须安装剩余电流保护装置。', '527', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('935', '危险场所必须按《中华人民共和国爆炸危险所电气安全规程（试行）》划定危险场所区域等级图，并按危险区域等级和爆炸性混合物的级别、组别配置相应符合国家标准规定的防爆等级的电气设备。防爆电气设备的配置应符合整体防爆要求；防爆电气设备的施工、安装、维护和检修也必须符合规程要求。', '528', '《爆炸危险场所安全规定》第17条');
INSERT INTO `t_item_tbl` VALUES ('936', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '529', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('937', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '530', '《中华人民共和国职业病防治法》第20条');
INSERT INTO `t_item_tbl` VALUES ('938', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '531', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('939', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '532', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('940', '容易造成职业危害的工序应隔离设置。', '533', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('941', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '534', '《中华人民共和国职业病防治法》第25条');
INSERT INTO `t_item_tbl` VALUES ('942', '应在可产生职业危害的工作场所和设备上，按GBZ158《工作场所职业病危害警示标识》和GBZ/T203《高度物品作业岗位职业病危害告知规范》的要求设置职业危害警示标识。', '535', '《中华人民共和国职业病防治法》第22条');
INSERT INTO `t_item_tbl` VALUES ('943', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '536', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('944', '危险化学品必须储存在专用仓库、专用场地或者专用储存室（以下统称专用仓库）内，储存方式、方法与储存数量必须符合国家标准，并由专人管理。', '537', '《危险化学品安全管理条例》第22条第一款');
INSERT INTO `t_item_tbl` VALUES ('945', '1.危险化学使用实行领取制度;2.在进行工作之前，所有人员要根据工作对象的性质配戴好防护用具（如手套、防毒面具、口罩、水鞋等）才能进行调配;3.调配车间要有通风、换气装置，防止危化品深度积聚', '537', '《危险化学品安全管理条例条》第16条');
INSERT INTO `t_item_tbl` VALUES ('948', '1.物品堆放整齐、稳固，高度不超过2米，易挥发可燃物品桶盖随时拧紧盖严。2.易燃易爆用品现场存量最好为1个班的用量。3.通道保持畅通，出、入口处不得堆放易燃易爆物品。4.危险化学品储存及有限空间应保持良好的通风。5.作业区域应采取自然通风或机械通风，保持通风良好。6.不得使易燃易爆和有毒有害气体在现场积存。', '538', '《工业用化学产品采样安全通则》');
INSERT INTO `t_item_tbl` VALUES ('950', '装有电气设备的烘干室，其金属外壳应有保护接地，接地电阻值小于10欧。金属外壳的各部件之间，应保持良好的电气连接。', '538', '《涂装作业安全规程涂层烘干室安全技术规定》第4.1.3.3条');
INSERT INTO `t_item_tbl` VALUES ('951', '喷漆区和爆炸危险区域内不应设置有引起明火、火花的设备和外表超过喷漆涂料自燃点温度的设备。', '538', '《喷漆室安全技术规定》第6.1.5条');
INSERT INTO `t_item_tbl` VALUES ('952', '生产、储存危险化学品的单位，应当在其作业场所设置通信、报警装置，并保证处于适用状态。', '538', '《危险化学品安全管理条例》第21条');
INSERT INTO `t_item_tbl` VALUES ('953', '烘干室应设置温度自动控制及超温报警装置。', '538', '《涂装作业安全规程涂层烘干室安全技术规定》第4.4.1.1条（GB14443-2007）');
INSERT INTO `t_item_tbl` VALUES ('954', '生产、储存、使用危险化学品的，应当根据危险化学品的种类、特性，在车间、库房等作业场所设置相应的监测、通风、防晒、调温、防火、灭火、防爆、泄压、防毒、消毒、中和、防潮、防雷、防静电、防腐、防渗漏、防护围堤或者隔离操作等安全设施、设备，并按照国家标准和国家有关规定进行维护、保养，保证符合安全运行要求。', '539', '《危险化学品安全管理条例条》第16条');
INSERT INTO `t_item_tbl` VALUES ('955', '火法冶炼的，应当采取下列安全措施：\n1.应当制定科学的工艺技术措施防止熔融金属喷溅伤人。\n2.高温熔融金属冶炼、保温、运输、吊运、铸造等区域应当采取高温熔融金属遇水爆炸的措施。\n3.吊运高温熔融金属必须采用冶金铸造起重机。司机室等高温作业应当采取隔热、降温措施。\n4.高温熔融金属地面运输车辆应当采用专用运输车辆，并设置安全警示报警装置。\n5.高温熔融金属吊运及运输车辆应当与建构筑物之间保留足够的安全距离，不得与其他物体碰撞或跨过其他生产设施。\n6.装运熔融金属的专用车辆不得在煤气、氧气、氢气管道下方和有易燃易爆物质的区域停留。', '540', '参考《冶金等工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('956', '采用湿法冶炼的，应当采取下列安全措施：\n1.应当制定科学的工艺技术措施防止湿法冶炼剧烈反应导致发生喷溅及爆炸事故。\n2.反应槽、罐、池、釜和储液罐、槽等应当具有足够的强度，并采取。3.进入槽、罐、池、釜等内作业时，应当按照有限空间作业的要求，办理相关许可手续，进行充分的通风、清洗，防止发生人员中毒窒息、火灾爆炸事故。\n4.适用酸、碱的，应当采取防酸碱腐蚀，水冲洗的措施。\n5.高温设备及管道应采取隔热措施，采取防止高温高压蒸汽泄露、灼烫、爆炸的措施。', '541', '参考《冶金等工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('957', '（一）应根据设备使用说明书，以及技术、工艺和安全操作方面的要求，自定安全生产操作规程\n（二）安全操作规程包括技术资料、限位装置、安全防护装置、接地装置、旋转部位防护、电气安全要求、环境要求等内容\n', '172', '《江苏省安全生产条例》第8条');
INSERT INTO `t_item_tbl` VALUES ('958', '（一）如有下列情况之一的，应安装防护罩、盖或栏：\n1、以操作人员站立平面为基准，高度在2米以下的外露传动部位；2、旋转的键、销、楔等突出大于3毫米的部位；3、产生切屑、磨屑、冷却液等飞溅，可能触及人体或造成设备与环境污染的部位；4、产生射线或弧光的部位；5、伸入通道的超长工件；6、超出设备后端300毫米以上的工件；7、容易伤人的设备往复运动部位；8、悬挂输送装置跨越通道的下部；9、高于地面0.7米的操作平台\n（二）防护罩、盖、栏应满足以下要求：\n1、有足够强度和刚度，无明显锈蚀和变形2、安装牢固\n', '173', '《工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('959', '（一）各类行程限位装置应安全可靠，运动机构的行程限位应在规定的范围内\n（二）凡限制运动件范围的，应装设限位开关，且固定牢靠有效\n', '174', '《纺织工业企业安全管理规范》(AQ3003-2006)');
INSERT INTO `t_item_tbl` VALUES ('960', '专用设备（一）：棉纺\n1.开清棉设备：\n（1）抓棉机吸斗观察窗必须配备机械和电气联锁，联锁装置必须安全可靠，只有当机械全部停妥后，观察窗才能打开，抓棉机打手的抓棉口处应有护栏，抓棉设备必须配备上、下定位装置，平台式抓棉机必须配备运行碰撞自停装置和防止误入的隔离措施。\n（2）混开棉机滚筒部位必须配备机械和电气联锁，滚筒顶盖的机械联锁的锁杆长度不小于设备宽度的三分之二，打手部位应同时配备机械和电气联锁，观察窗应使用不易破碎的材料。\n（3）清棉机打手传动轴应配置轴套，危险点应有联锁装置。\n（4）开棉机打手部位应配备机械和电气联锁，机械联锁销杆的长度必须大于观察窗30mm，观察窗与打手距离不小于800mm。\n（5）成卷机紧压罗拉手轮处应加装防护板，手轮弹簧必须处于松驰状态，各传动部位必须加装防护栏或防护罩。\n（6）成卷机综合打手处必须配备机械或电气等联锁装置，机械联锁与观察窗的上下间隙不大于20mm，压辊棉层输出部位必须安装生头器，或配置生头板。\n（7）清棉机应按规定安装操作和检修平台。\n（8）在抓棉机吸斗观察窗、混开棉机滚筒部、开棉机打手等存在打击伤害部位如没有配备机械和电气联锁，应加锁，开锁钥匙由当班值班长保存，当转动机械完全停稳后，才能开锁处理故障。\n2.梳棉机：\n（1）锡林抄针门间隙不大于10mm，并有安全警示标志，联锁装置灵活有效。\n（2）刺辊后车肚应有安全措施，有安全警示标志。各传动部位应安装安全防护罩。\n（3）剥棉部位应安装安全防护罩，上绒辊应安装绒辊防绕断电限位装置。\n（4）锡林道夫三角区应有安全挡板。\n3.精梳机：传动部位安全防护罩必须安装断电限位装置。分离皮辊安全防护罩应齐全，抬高超过200mm时，联锁装置应灵敏启动。车头、车后、车尾自停开关、工艺自停装置有效。\n4．粗纱机：锭翼转动部位外侧应安装光电装置，车头传动齿轮安全门应有安全断电限位装置；\n5.细纱机：车头传动齿轮安全门应有安全断电限位装置。游动电机及导轨应完整、牢固可靠。计长表、导纱横动装置、车头、车尾应安装安全防护罩。车头、车尾箱门的门钩、插门应配有自锁装置。\n6.气流纺机：转杯、分梳辊、压轮、轴承不允许过热、振动、异响。工艺排风、排杂装置辅助吸嘴无破损、漏风、堵塞。龙带不允许有异响。\n7.以上设备及场所均应符合防火要求。\n', '175', '《纺织企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('961', '专用设备（二）：织造\n1.整经机：经轴两端应加装安全防护罩，并应设置自停保险装置。主电机摩擦盘皮带、落轴电机传动部位、制动锯齿轮等处安全防护罩应牢固。游动风扇电机及导轨应完整、牢固。\n2.浆纱机：\n（1）传动部位、齿轮、链轮必须设置安全防护罩，铁炮轴、拖行辊露出机外部位应安装轴套，车头轧辊部位应设置安全自停装置。\n（2）压力表、安全阀的工作压力应根据生产工艺要求控制在额定范围之内，压力表最高工作压力应标有红线，按周期经专业部门检测、检验合格后方可使用。\n（3）蒸汽管道、箱体、排气装置应当采取隔热防烫措施。\n（4）浆纱和浆纱烘箱设备内及潮湿处的电气装置、工作照明等必须采用安全电压及防水、防潮灯具。\n3.有梭织机：\n（1）传动部位应设置安全防护罩。\n（2）梭子运行过程中应设置防飞梭装置和防护挡板。\n（3）探针及换梭作用良好、飞梭装置完好。\n（4）三大关车（断经、换梭、轧梭）自停装置必须灵敏有效。\n（5）36牙、72牙齿轮安全防护罩、送经侧轴伞齿轮、送经蜗杆安全防护罩均应完整、牢固可靠。\n4.无梭织机：绞边器安全防护罩须完好，各种气管、气阀、油管、油阀等不允许漏气、漏油、堵塞。断经、断纬必须停车，检修开关电器连锁可靠有效。\n5.针织大园机：大圆机油箱处于完好正常工作状态，油路不渗油、不漏气，保证机台周围地面干净；大圆机运转状态正常，无异常震动、噪声、发热等现象。\n', '176', '《纺织企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('962', '专用设备（三）：化纤工序设备\n1.热媒生产设备：\n（1）热媒系统中所有导热管道必须用压缩空气进行气密性试验,不得有泄漏，合格后方可正式投入使用。\n（2）确认电机转向正确,风机叶轮能自由转动,润滑良好，风机启动前消音器上的手动阀门应处于关闭状态,风机启动后应骤然打开。\n（3）热媒泵出口的压力表要定期检测，温度表处于完好状态。\n（4）热媒系统中每个阀门操作均必须灵活可靠。\n2.酯化生产设备：\n（1）酯化、聚合等专用设备中，各反应釜或者酯交换塔必须做到管道完整无泄漏。\n（2）安全阀和压力表应齐全可靠、定期检测、合格使用。\n（3）反应釜的反应装置、贮罐降温设施及温度报警装置灵敏有效。\n（4）联苯加热器液位标志明显清晰，温度和压力上下限位联锁报警装置、防爆片等可靠到位；现场应当有明显的安全警示标志。\n3.PTA投料设备：\n（1）电动葫芦必须定期检测。\n（2）限位开关、钢丝绳、吊钩等设施必须安全可靠。\n（3）风机等各电气设备的金属外壳必须可靠接地。\n4.二硫化碳计量设备：\n（1）二硫化碳计量室、贮库的照明、电气开关等装置应当符合防爆要求，并应装设单独的避雷装置。\n（2）必须装置可靠、良好的送风、排风装置。\n（3）二硫化碳的设备、管道、阀门、考克、液面计等应当严密无泄漏。\n（4）各法兰处必须装设接地片，接地良好。\n5.五合机设备：\n（1）黄化机、五合机等专用设备的管道必须密闭无泄漏，装设符合设计要求的防爆装置。\n（2）二硫化碳管道、排毒风管、开关、法兰片等处应有接地装置。\n（3）操作平台应当铺设木制地板或者橡胶地毯。\n（4）黄化、五合工序使用的工具必须使用不产生火花的材料制成，严禁使用金属制成的工具。\n6.后溶解设备：\n（1）玻璃液位管必须有安全防护装置。\n（2）进出料口考克、盐水进口阀门应灵活、可靠。\n（3）电动机、开关箱等电气设施应有防潮措施。\n7.纺丝设备：\n（1）短丝纺丝机等专用设备中，联苯箱体以及直(弯)管应当完整无泄漏。\n（2）安全阀和压力表应当齐全可靠、定期检测、合格使用。\n（3）联苯加热器的装置应当具备液面镜、超温超压联锁、安全阀、压力表等，并且齐全可靠。\n（4）熔体过滤器的前后电接点压力表、熔体压力联锁装置必须灵敏有效。\n8.欠伸设备：\n（1）紧张热定形设备的蒸汽加热系统、温度表、压力表、安全阀应当齐全有效、定期检测。\n（2）牵伸机轧点处必须设置安全挡板。\n（3）各种电气安全联锁、信号装置、报警装置等应当齐全可靠。\n（4）安装于高处的阀门必须灵活可靠。\n（5）钩刀、剪刀要放在规定位置，以防被丝束带入设备造成意外事故。\n9.涤纶短丝打包设备：\n（1）液压打包机的油箱及液压管路必须密闭，不得有泄漏。\n（2）机器各润滑处应按要求定期加注润滑油。\n（3）打包机上的压力表应定期检测，显示明显、清晰。\n（4）定期对打包机上的泵、阀、压力表进行调整。\n', '177', '《纺织企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('963', '专用设备（四）：染整工序设备\n1.烧毛机：\n（1）烧毛间厂房结构及材料符合建筑设计防火规范（GB50016-2006）及印染工厂设计规范（GB50426-2007）要求。\n（2）自动联锁点火装置要定期检查、及时维修更换。\n（3）汽油、液化气或煤气的储油房、风泵、油泵等有单独的符合规范的作业间。\n（4）排气隔热装置应完整，牢固可靠。\n（5）烧毛间装有可燃气体浓度报警装置，并灵敏可靠。\n（6）汽化器的各类阀门必须无缺损，输油泵、供油管路要确保完好畅通、无泄漏。\n（7）热板烧毛设备的炉灶、炉门无破裂、漏火现象。\n（8）烧毛间有良好的自然或强制通风、降温措施。\n（9）防爆膜完好、可靠，符合防爆要求。\n2.漂白设备：\n（1）采用氯漂的生成车间内应装有有毒有害气体报警仪，同时符合氯气使用规程。\n（2）漂白车间内应定期检查设备、设施、门窗等的腐蚀情况。\n（3）定期监测漂槽的浓度和温度。\n（4）贮存漂白液的容器和池、槽均应加盖。\n（5）车间和配液室应设置防腐蚀的通风排气设备。\n3.铜辊印花机：\n（1）印花机花筒轧点进口处装有插口式安全挡板或光电自动停车装置。\n（2）机架上装有紧急停车的保险开关。\n（3）刮浆刀用毕后放在专用刀架上，并加上刀口保护套。\n（4）液压系统、气压系统符合要求无泄漏。\n（5）定期检查花筒轴梗，发现裂纹及时更换。\n4.平网印花机：\n（1）自动导布机构的顶头与筛框柱头接近剪切口处应装有机玻璃安全挡板。\n（2）台板筛框架旁纬向搁置踏脚板。\n（3）橡皮衬布受压小导辊应装有安全防护圈与防护托网。\n（4）烘燥部分有撑挡的大烘筒，两侧应安装防护装置。\n（5）车间内通风良好。\n（6）印花机应安装紧急停车装置。\n5.圆网印花机：\n（1）进布轧点（近打样处）应安装安全挡板或防护罩，花筒轴头应装有防护罩。\n（2）机两旁设有专用的防滑排水铁栅平台，平台下有畅通的排水沟。\n（3）清洗机架时不得用水冲。\n（4）烘燥部分有撑挡的大烘筒，两侧应安装防护装置。\n（5）车间内通风良好。\n6.预缩机、轧光机及磨、起、刷毛机：\n（1）预缩机加热辊进出口处及橡胶毯上下装有安全防护网，并有灵敏可靠的电气安全联锁装置。\n（2）预缩机两边装有安全防护网。\n（3）采用气体燃烧的轧光机应安装防爆设施。\n（4）预缩机、轧光机及磨、起、刷毛机的电气、线路定期进行检查防止老化。\n（5）磨、起、刷毛机应安装吸尘装置，牢固可靠。\n（6）磨、起、刷毛室及其设备定期清理，去除积尘。\n', '178', '《纺织企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('964', '专用设备（五）：成衣工序部分设备\n1.卧式、移动裁断等专用设备必须定期进行安全检测，合格有效，电气装置符合设计要求，绝缘可靠，安全防护装置完整、牢固。\n2.电熨斗等定型工具、设备应当符合移动电具安全设计要求，电线、插头、温控等完好无损，绝缘可靠；严格使用管理，定期进行安全检测。\n3.缝制机、拷边机、锁边机、钉筘机、锁洞机等缝纫专用生产设备涉及到旋转、冲压、用刀等部位应当做到防护装置齐全、完整、安全、有效。\n', '179', '《服装企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('965', '纺织机械制造企业自查标准可参照《机械企业安全生产标准化评定标准》的相关要求执行。', '180', '《机械企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('966', '设备裸露的转动或快速移动部分，应设有结构可靠的安全防护罩、防护栏杆或防护档板。', '180', '《纺织企业安全生产标准化评定标准》');
INSERT INTO `t_item_tbl` VALUES ('967', '通用机械', '180', '纺织机械传动部位防护要求');
INSERT INTO `t_item_tbl` VALUES ('968', '（一）在发生危险，不能及时通过控制开关停止设备运行，或在操作控制台不能观察全部控制状态时，应装设紧急停车装置；\n（二）需要安装紧急停车装置的设备，应在每个操作位置和需要的地方，都要有紧急停车装置，并便于操作，灵敏可靠；\n（三）紧急停车装置应保证瞬间动作时，能终止设备一切运动，其开关形状应区别于一般控制开关，颜色为红色；\n', '181', '《缝纫机的特殊要求》(GB4706.74-2004)');
INSERT INTO `t_item_tbl` VALUES ('969', '噪声指标应低于85分贝', '182', '《中华人民共和国职业病防治法》第26条，');
INSERT INTO `t_item_tbl` VALUES ('970', '（一）布局合理，布线整齐，符合电气线路安装规程；\n（二）防短路、过载、失压、欠压、自锁、互锁、过流及信号等保护装置齐全，完好，有效；\n（三）各机构电气开关、手柄定位可靠，各指示灯、仪表、操作按钮等指示清楚，使用完好；\n（四）各线路无老化、破损，蛇型管无压砸破坏脱落；\n', '183', '《缝纫机的特殊要求》(GB4706.74-2004)');
INSERT INTO `t_item_tbl` VALUES ('971', '纺织机械应设计并制造成能够降低火的风险，并且一旦产生火可以阻止并熄灭之。（一）防止火的措施有：\n1）自动探测和去除金属杂质；\n2）使用防火花材料；\n3）建立维修保养制度，减少轴承过热；\n4）当机器停止时，把加工材料从热表面，燃烧器及散热器护罩上剥离下来；\n5）使用装置以检测纤维材料卷绕；\n6）防止灰尘积累。\n（二)一旦产生火，阻止并熄灭火的方法有：\n1）提供火焰和烟雾探测器，并和自动灭火器一起作为机器的主要随机件；\n2）在操作手册中应说明需要安装固定自动灭火器（如灭火系统），或在建筑物内放置手动灭火器。\n', '184', '《纺织机械安全要求5.4.3》（GB/T17780.1）');
INSERT INTO `t_item_tbl` VALUES ('972', '(一）操作旋转机械严禁戴手套，在操作旋转机械时一定要做到工作服的“三紧”，即：袖口紧、下摆紧、领口紧，不准带围巾；（二）工作中戴好护目镜。', '185', '《劳动防护用品配备标准》');
INSERT INTO `t_item_tbl` VALUES ('973', '(1)破坏式剪切销。这种带剪切销的保险机构是在连接两个轴的部位有两个圆盘，两圆盘靠孔中用销子连接，销子强度小于传动系统中最弱的零件，当设备超负荷传动时，销子首先切断，使被动轴停止转动。\n(2)自动保险机构。在机床传动中，摩擦离合器、齿爪式离合器、安全阀、脱落蜗杆等已成为机床本身的传动部件。一般情况下，这些机构随机床正常工作，当由于某种原因机床出现过载时，这些保险机构就会由于摩擦力不足而打滑，使动力中断传输，被动轴停止转动。当负荷恢复正常时，能自动复位。\n', '186', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('974', '在大批量生产中多使用气动或液压夹具，由于工作紧张，往往发生夹具未夹紧工件即开车加工，以致工件飞出而造成事故。', '187', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('975', '自动停车机构的主要作用是在机床受到阻碍或在预定位置碰到挡块时，有关部件能自动停止运动。', '188', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('976', '互锁机构是一种更完善的安全装置，能直接防止操作失误而引起的伤害。', '189', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('977', '（一）可能触及到的外露部分应尽量平整、光滑，不应有可能导致刺伤或划伤人员的尖棱、尖角、凸出部分和开口。\n（二）溜板纵向运动应设有可靠的限位装置。\n（三）机床飞轮应采取切实可靠的防护措施，确保在运动中不会因其松脱、甩出及缠绕而出现危险。\n（四）外露带和带轮应设置防护罩。应在遮蔽危险部位的防护罩内表面、挂轮箱内表面涂上安全色，以提醒操作、调整和维护人员注意危险的存在。\n（五）因位置原因，在运动中可能造成缠绕危险的花键轴、丝杠应采取切实可靠的安全防护措施。\n（六）控制系统不应有导致危险情况的出现。\n（七）电力偶然中断，不应给人员造成危险；电力重新恢复后，机床不应自行起动。\n（八）按钮、指示灯、开关等控制器件的位置应确保操作时不会引起误操作和附加的危险。\n（九）控制器件应清晰可辨，易于区别，必要时应设置表示其功能或用途的标志。\n（十）在每个“起动”控制器件附近应设置一个“停止”控制器件。在每个操作位置应设置一个紧急停止控制器件，紧急停止控制器件的动作不应影响保护操作者或机床装置的功能。\n（十二）冷却系统应能保证操作者的人身安全。\n（十三）机床应能有效地回收冷却液，应设置防止冷却液飞溅的防护挡板。（十四）应设置防止切屑飞溅的防护挡板。\n（十五）冷却液喷嘴应能方便、安全、可靠地固定在所需位置上。\n', '190', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('978', '离合器要求：（一）刚性离合器的转键、键柄和直键无裂纹\n（二）操纵器的杆件、销钉或弹簧无裂纹、折断\n（三）离合器安装紧固，动作可靠，没有连冲\n（四）滑块从上死点至上死点25毫米处行程范围内需要制动时，离合器应对滑块制动\n', '191', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('979', '制动器要求：（一）制动器与离合器必须相互协调与连锁，开机时动器先松闸，离合器稍滞后再结合\n（二）连锁应准确、紧固，工作稳定、协调\n（三）行程限位装置、控制装置正确，动作灵敏有效\n', '191', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('980', '紧停装置：（一）大型冲压机械前后都应安装急停按钮，位置在人手可迅速触及的地方\n（二）剪板机剪板长度大于2500毫米时，两侧应安装紧停装置\n（三）紧停装置应有红色标记\n', '191', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('981', '传动部位：距作业平台2米以上旋转部位应装设防护罩或防护栏', '191', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('982', '脚踏开关：（一）外露在工作台面的脚踏开关，只要有物体撞压可能的，应在开关上部和两侧装设防护罩\n（二）剪板机脚踏杠在整个长度上应装设防护罩\n（三）脚踏开关的踏面应用防滑的花纹钢板制作\n（四）防护罩有足够的强度，无棱角毛边，便于开关检修\n', '191', '《机械设备防护罩安全要求》（GB8196-87）、《剪板机安全技术要求》5.2.6（JB8781-1998）');
INSERT INTO `t_item_tbl` VALUES ('983', '机床接地保护：冲、剪、压、钻机械的金属结构件、电机、变压器、穿线金属管等都应有接地保护', '191', '《用电安全导则》');
INSERT INTO `t_item_tbl` VALUES ('984', '安全防护附件：（一）必须采取各种安全措施，确保在滑块下落时，操作者任何部位不可能进入作业危险区\n（二）使用双按钮之间的内侧距离必须大于25厘米\n', '191', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('985', '位置和朝向：（一）安装地点应保证人员和设备安全；（二）朝向不得对着通道、门口和操作岗位。\n防护罩应符合国家安全标准\n挡屑板应有足够强度\n砂轮无裂纹、无破损\n法兰盘使用软垫连接\n托架安装牢固可靠\n运行状况：运行平稳，砂轮磨损量符合要求\n接地可靠，控制电器符合规定\n接地可靠，控制电器符合规定\n', '192', '（GB4678-84）《麿削机械安全规程》');
INSERT INTO `t_item_tbl` VALUES ('986', '电源线、焊接电缆与焊机连接处的裸露接线板，应采取安全防护罩或防护板隔离，以防止人员或金属物体接触', '193', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('987', '焊机外壳防漏电保护：（一）焊机外壳必须接地或接零保护，接地或接零装置连接良好，并定期检查\n（二）严禁使用易燃易爆气体管道作为接地装置\n（三）在有接地或接零装置的焊件上进行弧焊操作，或焊接与地面密切连接的焊件时，应注意避免焊机和工件的双重接地\n', '193', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('988', '绝缘电阻：（一）焊接变压器一、二次绕组、绕组与外壳之间的绝缘电阻值不小于1兆欧\n（二）每半年应对焊机绝缘电阻遥测一次，且记录完整\n', '193', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('989', '（一）焊机一次侧电源线长度不超过3米\n（二）一次线不得在地面拖拽使用，不得在地面跨越通道使用\n（三）电焊机应接在专用开关上使用，不得与其它设备共用一个开关\n（四）在高处或金属容器内工作时应设专人监护\n', '193', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('993', '（一）焊机二次线必须连接紧固，无松动\n（二）二次线接头不超过3个\n（三）二次线截面选用正确，避免长期过载使用造成绝缘老化\n（四）严禁使用厂房金属结构、管道、轨道等作为焊接二次回路使用\n', '193', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('994', '焊钳夹紧力好，绝缘良好，手柄隔热层完整，焊钳与导线连接可靠', '193', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('995', '焊机使用场所：（一）焊机应安放在通风、干燥、无碰撞、无剧烈震动、无高温、无易燃品存在的地方\n（二）在室外或特殊环境下使用，应采取防护措施保证其正常使用。雨、雪、雾天，无防护措施不准作业\n（三）焊机使用场所应清洁，无严重粉尘\n（四）操作人员应穿绝缘鞋、防护服、面罩（护镜）、电焊手套\n', '193', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('996', '旋转部位防护要求：（一）炊事机械的传动部位的皮带轮、齿轮、链轮与链条、联轴器等，应加防护罩或防护盖，不便安装的应加设防护栏\n（二）防护罩、盖和栏完好可靠，强度符合要求\n', '194', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('997', '电气部分要求：（一）炊事机械的金属外壳、电动机外壳的PE线均应可靠；（二）电源线最好穿管敷设，若采用橡胶电缆时，应敷设在无浸泡、无高温和无压砸的沿墙壁面；（三）线路不得有接头、破损、老化或过载；（四）随机配备的电气箱柜应符合要求', '194', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('998', '控制开关：（一）炊事机械电源控制开关应单机单设，不得几台设备共用一个开关或用距离较远的闸刀控制；（二）有尘、水可能进入的按钮开关应有防护装置，且不防碍操作', '194', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('999', '搅拌、合面机：（一）应加盖密封，容器盖应采用规定的材料制作\n（二）必须安装机盖联锁安全装置，联锁开关应固定在机器本体上，启盖应即刻断电\n', '194', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('1000', '绞肉、绞菜机：（一）加料口护手装置应保证操作人员手指不能触及刀口或螺旋部位，加料口较大的其高度应适当增加，否则应设小口径加料托盘（二）护手装置、加高的加料口和加料托盘应用耐腐蚀的材料制作，具有足够的强度，与加料口连接固定，应能翻转清洗\n（三）应配备送料的辅助工具\n', '194', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('1001', '压面机：（一）轧辊应便于装拆，调整灵活、定位可靠（二）加料处应有防护装置，防止手指伸入（三）备有专用刮面板，不得用手推、刮面粉', '194', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('1002', '应急照明设施并确保完好、有效。', '195', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('1003', '（一）、消防疏散通道的要求：任何时候都应保障疏散通道、安全出口畅通，并设置符合国家规定的消防安全疏散指示标志和应急照明设施，保持防火门、防火卷帘、消防安全疏散指示标志、应急照明、机械排烟送风、火灾事故广播等设施处于正常状态。\n（二）严禁占用消防疏散通道，不能在安全出口或者疏散通道上安装栅栏等影响疏散的障碍物，遮挡或者将消防安全疏散指示标志遮挡、覆盖。\n（三）、正确识别消防标志\n1.禁止标志2.警告标志3.疏散标志4.提示标志\n', '196', '《消防疏散通道要求》');
INSERT INTO `t_item_tbl` VALUES ('1004', '按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，确保完好有效。', '197', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('1005', '变配电室变压器、高压开关柜、低压开关柜操作面地面应铺设绝缘胶垫。', '198', '参考《变配电室安全管理规范》第5.3条');
INSERT INTO `t_item_tbl` VALUES ('1006', '变配电室出入口应设置高度不低于400mm的挡板。', '199', '参考《变配电室安全管理规范》5.6条');
INSERT INTO `t_item_tbl` VALUES ('1007', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '200', '参考《变配电室安全管理规范》第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('1008', '通往室外的门应向外开。设备间与附属房间之间的门应向附属房间方向开。高压间与低压间之间的门，应向低压间方向开。配电装置室的中间门应采用双向开启门。', '201', '参考《变配电室安全管理规范》第5.8.3条');
INSERT INTO `t_item_tbl` VALUES ('1009', '通往室外的窗应装有纱窗。', '201', '参考《变配电室安全管理规范》5.9.3');
INSERT INTO `t_item_tbl` VALUES ('1010', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '202', '参考《变配电室安全管理规范》第5.18.3条');
INSERT INTO `t_item_tbl` VALUES ('1011', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '202', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('1012', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '202', '参考《变配电室安全管理规范》第5.18.3.2条');
INSERT INTO `t_item_tbl` VALUES ('1013', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：（一)绝缘拉杆应悬挂或架在支架上，不应与墙接触；（二)绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；(三)绝缘垫和绝缘台应经常保持清洁、无损伤；（四)高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；（五)安全用具不允许当作其它工具使用；（六)安全用具不合格的不得存放在工作现场。', '202', '参考《变配电室安全管理规范》第5.18.5条');
INSERT INTO `t_item_tbl` VALUES ('1014', '禁止类：禁止合闸、禁止攀登、高压危险；警告类：止步、高压危险；准许类：从此上下、在此工作', '203', '参考《变配电室安全管理规范》第5.16.1条');
INSERT INTO `t_item_tbl` VALUES ('1015', '变配电室内环境整洁，场地平整，设备间不应存放与运行无关的闲散器材和私人物品，禁止无关人员进入场地；', '204', '参考《变配电室安全管理规范》第6.8.1条');
INSERT INTO `t_item_tbl` VALUES ('1016', '主控制室、高压配电室不应带入食物及储放粮食，值班室不应设置、使用寝具、灶具，并应有防止小动物的安全措施；', '204', '参考《变配电室安全管理规范》第6.8.4条');
INSERT INTO `t_item_tbl` VALUES ('1017', '非变配电室从业人员因工作需要进入变配电室时，应经值班人员许可，并办理登记手续。当需要进入设备区时，应有值班人员监护', '205', '参考《变配电室安全管理规范》第7.3条');
INSERT INTO `t_item_tbl` VALUES ('1018', '柜、屏、台、箱、盘的金属框架及基础型钢必须接地（PE）或接零（PEN）可靠；装有电器的可开启门与框架的接地端子间应用裸线编织铜线连接，作好标识。', '206', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.1.2条');
INSERT INTO `t_item_tbl` VALUES ('1019', '箱盘内开关灵活可靠。带有漏电保护的回路，漏电保护装置动作电流和动作时间按设计要求。', '207', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.2.7.2条');
INSERT INTO `t_item_tbl` VALUES ('1020', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '207', '《剩余电流动作保护装置安装和运行》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('1021', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '207', '《剩余电流动作保护装置安装和运行》第7.2条');
INSERT INTO `t_item_tbl` VALUES ('1022', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。', '208', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('1023', '直敷布线可用于正常环境的屋内场所，应采用护套绝缘导线。当导线垂直敷设至地面低于1．8m时，应穿管保护。', '209', '《低压配电设计规范》第5.2.1条');
INSERT INTO `t_item_tbl` VALUES ('1024', '（一）设备的金属外壳应采取防漏电保护接地\n（二）PE线若明设时，应选用不小于4平方毫米的铜芯线，不得使用铝芯线\n（三）PE线若随穿线管接入设备本体时，应选用不小于2.5平方毫米的铜芯线或不小于4平方毫米的铝芯线\n（四）PE线不得搭接或串接，接线规范、接触可靠\n（五）明设的应沿管道或设备外壳敷设，暗设的在接线处外部应有接地标志\n（六）PE线接线间不得涂漆或加绝缘垫\n', '210', 'GB19517-2004《国家电气设备安全技术规范》第2.2.4条');
INSERT INTO `t_item_tbl` VALUES ('1025', '应采用橡皮护套绝缘软线。与电源联接，应采用开关、插头座。严禁用导线直接插入插座，或挂在电源线上使用。', '211', '《电气安全工作规程》第88条');
INSERT INTO `t_item_tbl` VALUES ('1026', '工具必须由专职人员每年至少检查一次，并记录', '212', '《手持式电动工具的管理、使用、检查和维修安全技术规程》第6.3条');
INSERT INTO `t_item_tbl` VALUES ('1027', '在使用移动式的I类设备时，应先确认其金属外壳或构架已可靠接地，使用带保护接地极的插座，同时宜装设漏电保护器，禁止使用无保护线插头插座。', '213', '《用电安全导则》第4.16条');
INSERT INTO `t_item_tbl` VALUES ('1028', '露天使用的用电设备、配电装置应采取防雨、防雪、防雾和防尘的措施。', '214', '《用电安全导则》第4.25条');
INSERT INTO `t_item_tbl` VALUES ('1029', '插座回路均应设置剩余电流动作保护装置。宜装设漏电保护器，禁止使用无保护线插头插座。', '215', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('1030', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。不应从带插座灯头上引接电源供给电器。', '216', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('1031', '临时线路使用必须经过审批，一般使用期限为过15天，最长不超过一个月', '217', '《用电安全导则》第4.2条');
INSERT INTO `t_item_tbl` VALUES ('1032', '临时用电的电气设备，必须安装剩余电流保护装置。', '218', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('1033', '线路敷设应符合电气装置设置及安装规范，室内不低于2.5m，室外不低于4.5m，道路上方不低于6m。', '219', '《电气安全管理规程》第20条（1）');
INSERT INTO `t_item_tbl` VALUES ('1034', '我国规定42伏、36伏、24伏、12伏、6伏为安全电压的额定值', '220', '《安全电压标准》');
INSERT INTO `t_item_tbl` VALUES ('1035', '（一）手提式照明使用36伏（二）危险环境携带式电动工具使用36伏（三）金属容器内使用24伏(四)室内灯具离地面低于2.4m，手持照明灯具，一般潮湿作业场所（地下室、潮湿室内、潮湿楼梯、隧道、人防工程以及有高温、导电灰尘等）的照明，电源电压应不大于36V。\n（五）在潮湿和易触及带电体场所的照明电源电压，应不大于24V。\n（六）在特别潮湿的场所，锅炉或金属容器内，导电良好的地面使用手持照明灯具等，照明电源电压不得大于12V。\n', '221', '《安全电压标准》');
INSERT INTO `t_item_tbl` VALUES ('1036', '（一）危险化学品应根据分类，分区、分库存放\r\n（二）爆炸物品、自身能形成爆炸的危险品、腐蚀性物品应单独存放\r\n（三）相互接触或混合后能引起燃烧或爆炸的物品不能同库存放\r\n（四）灭火方式不同的物质不得同库存放\r\n（五）遇火、遇热、遇潮能引起燃烧、爆炸或发生化学反应，产生有毒气体的化学危险品不得露天或在潮湿、积水的建筑中存放\r\n（六）保险粉、双氧水、亚氯酸钠、雕白粉（吊白块）等危险品与禁忌物料混合贮存的；保险粉露天堆放，或储存场所未采取防水、防潮等措施。\r\n', '222', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('1037', '开关、灯具、线路、用电设施均应符合防爆要求', '223', '《爆炸危险场所安全规定》第17条');
INSERT INTO `t_item_tbl` VALUES ('1038', '作业现场不得储存、堆放其它易燃易爆物品，易燃易爆废料及时从现场清理', '224', 'GB14444-2006《喷漆室安全技术规定》');
INSERT INTO `t_item_tbl` VALUES ('1039', '物品堆放整齐、稳固，高度不超过2米，易挥发可燃物品桶盖随时拧紧盖严', '224', 'GB/3723-1999工业用化学产品采样安全通则');
INSERT INTO `t_item_tbl` VALUES ('1040', '易燃易爆用品现场存量最好为1个班的用量', '224', 'GB/3723-1999工业用化学产品采样安全通则');
INSERT INTO `t_item_tbl` VALUES ('1041', '通道保持畅通，出、入口处不得堆放易燃易爆物品', '224', 'GB/3723-1999工业用化学产品采样安全通则');
INSERT INTO `t_item_tbl` VALUES ('1042', '危险化学品储存及有限空间应保持良好的通风', '224', 'GB/3723-1999工业用化学产品采样安全通则');
INSERT INTO `t_item_tbl` VALUES ('1043', '作业区域应采取自然通风或机械通风，保持通风良好', '224', 'GB14444-2006《喷漆室安全技术规定》第5.3条');
INSERT INTO `t_item_tbl` VALUES ('1044', '不得使易燃易爆和有毒有害气体在现场积存', '224', 'GB6514-1995《涂装作业安全规程涂漆工艺安全及其通风净化》第15.5条');
INSERT INTO `t_item_tbl` VALUES ('1046', '封闭式易燃易爆物品使用场所，必须安装自动报警或抑爆系统', '224', 'GB14443-2007《涂装作业安全规程涂层烘干室安全技术规定》');
INSERT INTO `t_item_tbl` VALUES ('1047', '易燃易爆作业场所区域内所有金属部件、构件都应可靠接地', '224', 'GB14443-2007');
INSERT INTO `t_item_tbl` VALUES ('1048', '易燃易爆作业场所不得从事动火作业，严禁人员吸烟', '224', 'GB14444-2006《喷漆室安全技术规定》');
INSERT INTO `t_item_tbl` VALUES ('1049', '专人管理，并有防潮防雨淋设施', '224', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('1050', '设置安全标志', '225', '《安全标志》（GB2894-1996）');
INSERT INTO `t_item_tbl` VALUES ('1051', '车间、库房等作业场所应设置防静电装置', '226', '《危险化学品安全管理条例条》第16条');
INSERT INTO `t_item_tbl` VALUES ('1052', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '227', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1053', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '228', '《中华人民共和国职业病防治法》第20条');
INSERT INTO `t_item_tbl` VALUES ('1054', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '229', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1055', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '230', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1056', '容易造成职业危害的工序应隔离设置。', '231', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1057', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '1023', '《中华人民共和国职业病防治法》第25条');
INSERT INTO `t_item_tbl` VALUES ('1058', '应在可产生职业危害的工作场所和设备上，按GBZ158《工作场所职业病危害警示标识》和GBZ/T203《高度物品作业岗位职业病危害告知规范》的要求设置职业危害警示标识。', '232', '《中华人民共和国职业病防治法》第22条');
INSERT INTO `t_item_tbl` VALUES ('1059', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '233', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1060', '厂区内实行定置管理：厂区有定置图，对各类厂房建筑、物料堆放点、交通道路等应注明', '234', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1061', '（一）厂区双向主干道宽度不小于5米，单向主干道宽度不小于3米，且为环形\n（二）路面排水良好，路面平整，无台阶、无坑沟，盖板齐全\n（三）厂区主干道在平原地区应小于6度，山区应小于8度\n（四）厂区大门、危险路段的车速应限制在每小时5公里；人员稠密地段、下坡路、转弯路、交叉路口、装卸作业区限速每小时15公里，并设置限速标志\n（五）交叉路口若有视线盲区，应设立反光镜，反光镜无破损，角度和高度应便于观察道路盲区\n（六）厂区道路应有明显的人、车分流线，人行道宽度不小于1米，分流线清晰，宽度大于10厘米\n', '235', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1062', '（一）利用主干道堆放物品或作为停车位置，应有划线标志，通行部分宽度必须不小于5米\n（二）不得在转弯处或道路两侧堆放物品或停置车辆\n（三）道路施工应有警示牌或护栏，夜间要有红灯警示\n（四）厂区主干道占道率应小于道路长度的5%\n（五）不得将厂区主干道横向全部堵死\n', '236', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1063', '（一）照明灯布置合理，无照明盲区\n（二）照明灯具完好率达100%\n', '237', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1064', '（一）室外消防栓应合理配置，低压消防栓布置间距不超过120米，应沿道路两侧和路口设置，消防栓距路面边不超过2米，距建筑物外墙不小于5米\n（二）有明显的漆色标志，其1米范围内无障碍物\n（三）手提式灭火器的配置数量符合规定\n（四）所有消防器材应完好，在有效期限内使用\n', '238', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1065', '（一）搬运、安装、其他一些作业和高压线保持安全距离；（二）厂房的装修、维修、做防水等和高压线保持安全距离；（三）高压线下严禁码放材料和有建筑物。', '239', '《中华人民共和国电力行业标准》');
INSERT INTO `t_item_tbl` VALUES ('1066', '（一）车间通道根据生产要求，宽度标准如下：\n通道只行人通道，宽度不小于1米；\n通过电瓶车、叉车的通道，宽度不小于1.8米；\n通行汽车的车间通道，宽度应不小于3米；\n如有铁路线，车间通道，宽度应不小于5米\n（二）通道线应明显清晰，宽度不小于10厘米\n', '240', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1067', '（一）通道路面应平整，无台阶、无坑、沟\n（二）无积油、积水，无绊脚物\n', '241', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1068', '（一）任何堆放在通道标记线内和压住安全通道标记线的物件，都判定为占道\n（二）车间占道率应低于5%\n（三）不得有物件将通道横向全部堵死\n', '242', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1069', '（一）车行道上方的悬挂物高度不小于4米，人行道上方悬挂物高度不小于2.5米\n（二）悬挂应牢固可靠\n', '243', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('1070', '（一）仓库通道的宽度应满足以下要求：\n车行道宽度不小于3.5米\n人行道宽度不小于1米\n（二）路面平坦，无积油积水，无绊脚物\n（三）占道率应小于5%\n', '244', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1071', '（一）照明灯具完好率达100%\n（二）库房内不得使用移动式照明灯具\n', '245', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1072', '（一）库房内不得存在临时线\n（二）库房内敷设的电气线路必须，穿金属管或非燃硬塑料管\n（三）每个库房应当在库房外单独安装开关箱\n（四）甲、乙类物品和丙类液体库房的电气装置，必须安装防爆型电气装置，储存丙类固体物品的仓库，不得使用碘钨灯或超过60瓦以上的白炽灯等高温照明灯具\n', '246', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1073', '（一）防火制度、消防设施标志、防火标志齐全\n（二）按标准配备消防器材的数量和种类\n（三）防火通道畅通，无封死和堵塞现象\n\n', '247', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1074', '以下仓库应安装火灾报警装置：\n1、甲、乙、丙类物品储备库；\n2、专业性仓库\n3、大型物资仓库\n4、耐火等级为三、四级的仓库\n', '248', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1075', '1、无防火罩的汽车、拖拉机不得进入库房；\n2、库内用电插座应在库外用四芯胶皮软线接入库内，电线不得有接头；\n3、仓库办公室、休息室不得设在库房内；\n4、运输入库货物的车船必须用篷布严密封盖；\n5、原料、成品和下脚必须分库存放，未成包回花不得入库。\n', '249', '《关于纺织原料成品仓库防火安全管理的暂行规定》');
INSERT INTO `t_item_tbl` VALUES ('1076', '（一）物品包装应牢固，不得有严重破损、残缺、变形、物品变质、分解等现象\n（二）甲、乙类物品的包装应严密，不得有跑、冒]滴、漏现象\n', '250', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1077', '防止和减少违章指挥、违规操作、违反劳动纪律的行为', '251', '国务院安委会《关于进一步加强安全培训工作的决定》安委（2012）10号');
INSERT INTO `t_item_tbl` VALUES ('1078', '露天开采应当由上而下分台阶开采,台阶式开采,高度应当符合国家有关规定,严禁掏采。采石场上部需要剥离的,剥离工作面应当超前于开采工作面4米以上。', '252', '《矿山安全法实施条例》第17条');
INSERT INTO `t_item_tbl` VALUES ('1079', '不能采用台阶式开采的小型露天采场,应当自上而下分层、按顺序开采,分层高度根据岩性确定,浅眼爆破时分层高度不超过6米,中深孔爆破时分层高度不超过20米。分层凿岩平台宽度不得小于4米;最终边坡角根据岩体的稳定性确定,最大不得超过60度。', '252', '《露天矿山实施指南》10.1.4.2');
INSERT INTO `t_item_tbl` VALUES ('1080', '矿用起重、运输、提升、排水等机械设备应当具有国家资质的检测机构出具的检验报告,且该报告须在检验有效期内,并制定预防事故的安全技术保障措施。', '253', '《矿山安全法》第15条');
INSERT INTO `t_item_tbl` VALUES ('1081', '非煤矿山企业应当在矿石粉碎机、矿石转载平台、矿石溜井等地点设置防护设施,防止从业人员误入。', '253', '《矿山安全法》第15条');
INSERT INTO `t_item_tbl` VALUES ('1082', '矿山使用的设备、器材、防护用品和安全检测仪器,应当符合国家安全标准或者行业安全标准。', '253', '《矿山安全法》第15条');
INSERT INTO `t_item_tbl` VALUES ('1083', '在临近最终边坡爆破时,应采用控制爆破减振。临近最终边坡的采掘作业,应按设计确定的宽度预留安全、运输、清扫平台。', '254', '《金属非金属矿山安全规程》5.2.5.4');
INSERT INTO `t_item_tbl` VALUES ('1084', '每个台阶采掘结束,均应及时清理坡面上的浮石和平台上的疏松岩石,并明确部门或人员进行验收。', '255', '《金属非金属矿山安全规程》5.2.5.4');
INSERT INTO `t_item_tbl` VALUES ('1085', '班前应对边坡进行安全稳定性检查,雨后要及时检查。', '255', '');
INSERT INTO `t_item_tbl` VALUES ('1086', '非煤矿山企业应当根据国家有关规定,按照不同作业场所的要求,设置警示标志和矿山安全标志。', '256', '《露天矿山实施指南》10.3');
INSERT INTO `t_item_tbl` VALUES ('1087', '矿山企业应当对机电设备及其防护装置、安全检测仪器定期检查、维修,并建立技术档案。', '257', '《露天矿山实施指南》10.1.4.2');
INSERT INTO `t_item_tbl` VALUES ('1088', '露天采场的总出入沟口、平硐口、排水井口和工业场地等处必须采取防洪措施,深凹露天采场有专用防洪设施。', '258', '《金属非金属矿山安全标准化规范》（AQ2007-2006）5.6.2');
INSERT INTO `t_item_tbl` VALUES ('1089', '爆破作业必须由专职爆破员进行,并设置爆破警戒范围。4.2.1.3', '259', 'GBl6423—2006《金属非金属矿山安全规程》5.9.1.2');
INSERT INTO `t_item_tbl` VALUES ('1090', '禁止在雷雨天、夜间和雾天进行爆破作业。4.7.3', '260', '《爆破安全规程》《民用爆炸物品安全管理条例》');
INSERT INTO `t_item_tbl` VALUES ('1091', '爆破器材管理、爆破安全距离和爆破作业必须符合《爆破安全规程》(GB6722)规定。', '261', '');
INSERT INTO `t_item_tbl` VALUES ('1092', '爆炸物品的使用登记制度要符合《民用爆炸物品管理条例》规定。', '262', '');
INSERT INTO `t_item_tbl` VALUES ('1093', '爆炸物品、有毒化学药品和放射源必须有单独存放的仓库和安全保存的措施,并按有关规定配备消防设施和通讯报警装置。', '263', '');
INSERT INTO `t_item_tbl` VALUES ('1094', '应急照明设施并确保完好、有效。', '264', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('1095', '按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，确保完好有效。', '265', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('1096', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '266', '参考《变配电室安全管理规范》第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('1097', '变配电室出入口应设置高度不低于400mm的挡板。', '267', '参考《变配电室安全管理规范》5.6条');
INSERT INTO `t_item_tbl` VALUES ('1098', '变配电室变压器、高压开关柜、低压开关柜操作面地面应铺设绝缘胶垫。', '268', '参考《变配电室安全管理规范》第5.3条');
INSERT INTO `t_item_tbl` VALUES ('1099', '通往室外的门应向外开；配电装置室的中间门应采用双向开启门。', '269', '参考《变配电室安全管理规范》5.8.3');
INSERT INTO `t_item_tbl` VALUES ('1100', '通往室外的窗应装有纱窗。', '269', '参考《变配电室安全管理规范》5.9.3');
INSERT INTO `t_item_tbl` VALUES ('1101', '生产经营单位必须为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', '270', '《安全生产法》第37条');
INSERT INTO `t_item_tbl` VALUES ('1102', '柜、屏、台、箱、盘的金属框架及基础型钢必须接地（PE）或接零（PEN）可靠；装有电器的可开启门与框架的接地端子间应用裸线编织铜线连接，作好标识。', '271', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.1.2条');
INSERT INTO `t_item_tbl` VALUES ('1103', '箱盘内开关灵活可靠。带有漏电保护的回路，漏电保护装置动作电流和动作时间按设计要求。', '272', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.2.7.2条');
INSERT INTO `t_item_tbl` VALUES ('1104', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '272', '《剩余电流动作保护装置安装和运行》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('1105', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '272', '《剩余电流动作保护装置安装和运行》第7.2条');
INSERT INTO `t_item_tbl` VALUES ('1106', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。', '273', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('1107', '直敷布线可用于正常环境的屋内场所，应采用护套绝缘导线。当导线垂直敷设至地面低于1．8m时，应穿管保护。', '274', '《低压配电设计规范》第5.2.1条');
INSERT INTO `t_item_tbl` VALUES ('1108', '插座回路均应设置剩余电流动作保护装置。宜装设漏电保护器，禁止使用无保护线插头插座。', '275', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('1109', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。不应从带插座灯头上引接电源供给电器。', '276', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('1110', '应采用橡皮护套绝缘软线。与电源联接，应采用开关、插头座。严禁用导线直接插入插座，或挂在电源线上使用。', '277', '《电气安全工作规程》第88条');
INSERT INTO `t_item_tbl` VALUES ('1111', '工具必须由专职人员每年至少检查一次', '278', '《手持式电动工具的管理、使用、检查和维修安全技术规程》第6.3条');
INSERT INTO `t_item_tbl` VALUES ('1112', '在使用移动式的I类设备时，应先确认其金属外壳或构架已可靠接地，使用带保护接地极的插座，同时宜装设漏电保护器，禁止使用无保护线插头插座。', '279', '《用电安全导则》第4.16条');
INSERT INTO `t_item_tbl` VALUES ('1113', '露天使用的用电设备、配电装置应采取防雨、防雪、防雾和防尘的措施。', '280', '《用电安全导则》第4.25条');
INSERT INTO `t_item_tbl` VALUES ('1114', '电气设备必须有可靠的接地保护', '281', 'GB19517-2004《国家电气设备安全技术规范》第2.2.4条');
INSERT INTO `t_item_tbl` VALUES ('1115', '临时线路使用必须经过审批，一般使用期限不超过15天，最长不超过一个月', '282', '《用电安全导则》第4.2条');
INSERT INTO `t_item_tbl` VALUES ('1116', '临时用电的电气设备，必须安装剩余电流保护装置。', '283', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('1117', '线路敷设应符合电气装置设置及安装规范，室内不低于2.5m，室外不低于4.5m，道路上方不低于6m。', '284', '《电气安全管理规程》第20条（1）');
INSERT INTO `t_item_tbl` VALUES ('1118', '行灯等手持式电动工具、器具应根据使用现场，分别采取可靠的安全保护措施，如漏电保护电器或使用３６伏以下的安全电压。安全变压器应采用双圈的。', '285', '《电气安全工作规程》第89条');
INSERT INTO `t_item_tbl` VALUES ('1119', '手提式和局部照明灯具应选用安全电压或双重绝缘结构。在使用螺口灯头时，灯头螺纹端应接至电源的工作中性线。', '286', '《用电安全导则》第4.18条');
INSERT INTO `t_item_tbl` VALUES ('1120', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '287', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1121', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '288', '《中华人民共和国职业病防治法》第20条');
INSERT INTO `t_item_tbl` VALUES ('1122', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '289', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1123', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '290', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1124', '容易造成职业危害的工序应隔离设置。', '291', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1125', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '292', '《中华人民共和国职业病防治法》第25条');
INSERT INTO `t_item_tbl` VALUES ('1126', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '293', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1127', '作业人员在铲装、运输作业时,应当严格遵守装载、运输安全规程的规定。同一工作面有两台铲装机械作业时,最小间距应当大于铲装机械最大回转半径的3倍（不小于50米）。卸矿地点车挡设施牢固可靠,车挡高度不小于该卸矿点各种车辆最大轮胎直径的五分之二。', '294', '《金属非金属矿山安全规程》5.2.3.7');
INSERT INTO `t_item_tbl` VALUES ('1128', '杜绝违章指挥、违章操作、违反劳动纪律的行为', '295', '国家安监总局15号令第44条');
INSERT INTO `t_item_tbl` VALUES ('1129', '重大危险源涉及的温度、压力、液位等重要参数有远传和连续记录，可燃气体和有毒有害气体设置泄漏检测报警装置；', '296', '《危险化学品重大危险源监督管理暂行规定》（安监总局令第40号）第三章');
INSERT INTO `t_item_tbl` VALUES ('1130', '不得采用国家明令淘汰、禁止使用和危及安全生产的工艺、设备；新开发的使用危险化学品从事化工生产的工艺（以下简称化工工艺），在小试、中试、工业化试验的基础上逐步放大到工业化生产；国内首次使用的化工工艺，经过省级人民政府有关部门组织的安全可靠性论证；', '297', '《危险化学品安全使用许可证实施办法》（安监总局令第57号）第七条，国家安监总局 工信部《关于危险化学品企业贯彻落实<国务院关于进一步加强企业安全生产工作的通知>的实施意见》（安监管总三[2010]186号）');
INSERT INTO `t_item_tbl` VALUES ('1131', '保证下列设备设施运行安全可靠、完整：\n（1）压力容器和压力管道，包括管件和阀门；\n（2）泄压和排空系统；\n（3）紧急停车系统；\n（4）监控、报警系统；\n（5）联锁系统；\n（6）各类动设备，包括备用设备等。', '297', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）\n');
INSERT INTO `t_item_tbl` VALUES ('1132', '生产装置泄压系统或排空系统排放的危险化学品应引至安全地点并得到妥善处理。', '297', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞203号）\n');
INSERT INTO `t_item_tbl` VALUES ('1133', '建立关键装置、重点部位档案，建立企业、管理部门、基层单位及班组监控机制，明确各级组织、各专业的职责，定期进行监督检查，并形成记录。', '298', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞204号）\n');
INSERT INTO `t_item_tbl` VALUES ('1134', '应制定关键装置、重点部位应急预案，至少每半年进行一次演练，确保关键装置、重点部位的操作、检修、仪表、电气等人员能够识别和及时处理各种事件及事故。', '298', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞205号）\n');
INSERT INTO `t_item_tbl` VALUES ('1135', '按GB50493，在易燃、易爆、有毒区域设置固定式可燃气体和（或）毒有害气体泄漏的检测报警设施，报警信号发送至控制室或操作室；', '299', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞206号）\n');
INSERT INTO `t_item_tbl` VALUES ('1136', '在工艺装置上可能引起火灾、爆炸的部位设置超温、超压等检测仪表、声和（或）光报警和安全联锁装置等设施；', '299', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞207号）\n');
INSERT INTO `t_item_tbl` VALUES ('1137', '涉及重点监管危险化学品的装置和设施，根据实际情况，进行自动控制改造，实现工艺过程的自动控制和压力、温度等重要参数的自动报警。', '299', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞208号）\n');
INSERT INTO `t_item_tbl` VALUES ('1138', '危险化学品应储存在专用仓库、专用场地或者专用储存室（以下统称专用仓库）内，并按照相关技术标准规定的储存方法、储存数量和安全距离，实行隔离、隔开、分离储存，禁止将危险化学品与禁忌物品混合储存；危险化学品专用仓库应当符合相关技术标准对安全、消防的要求，设置明显标志，并由专人管理；危险化学品出入库应当进行核查登记，并定期检查。', '300', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞209号）\n');
INSERT INTO `t_item_tbl` VALUES ('1139', '剧毒化学品及储存数量构成重大危险源的其他危险化学品必须在专用仓库单独存放，实行双人收发、双人保管制度；', '300', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞210号）\n');
INSERT INTO `t_item_tbl` VALUES ('1140', '（1）库房应有良好的通风设备，并根据物品的性质和不同储存方法，设置相应的防爆、泄压、防火、调温、导除静电和防止液体流散等安全措施；\n(2)危化品应该分类、分堆储存，堆垛不得过高、过密，堆垛之间，应留出一定的间距、通道和通风口；\n(3)库房应有消防通讯、报警设备，并准确有效。储存剧毒和放射性物品的仓库应配备防毒面具及隔离、消除、吸收毒物的设施；\n(4)库房应有足够的消防水源和配备相应的灭火器材及防护医疗设备。', '301', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞211号）\n');
INSERT INTO `t_item_tbl` VALUES ('1141', '按《储罐区防火堤设计规范》（GB50351），在可燃液体罐区设置防火堤，在酸碱罐区设置围堤并进行防腐处理；', '302', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞212号）\n');
INSERT INTO `t_item_tbl` VALUES ('1142', '(1) 储存甲B、乙A类液体采用内浮顶罐, 甲B类储罐设置减少日晒升温的措施；\n(2) 罐体、顶板壁厚定期检测, 罐体及附件无渗漏点；\n(3) 储罐成组布置，同一罐组内布置火灾危险性级别相同或相近的储罐；\n(4) 甲B类、乙液体的固定顶罐设置阻火器和呼吸阀，采用氮封储罐设置事故泄压设备；\n(5) 罐区设置事故存液池符合要求；\n(6) 储罐的进料管从罐体下部接入，储罐的进出管采用柔性连接；\n(7) 液化烃罐组内的储罐不多于2排，球罐之间的防火间距符合规范, 液化烃罐组的防火堤符合要求，同时按照规范设置隔堤；\n(8) 储罐平台、罐顶有踏步、边缘有护栏，且牢固; 消防设备齐全、有效。', '302', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞213号）\n');
INSERT INTO `t_item_tbl` VALUES ('1143', '(1) 管线无渗漏，无严重损伤、锈蚀，其局部损伤深度不超1.5mm；\n(2) 地上管线应架空式沿地面敷设，如局部采用封闭式管沟，要有防范措施；\n(3) 埋地管线要有明显的走向标志，并按规定时间开挖检查。', '303', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞214号）\n');
INSERT INTO `t_item_tbl` VALUES ('1144', ' 装卸料应采用密闭管道作业，装卸车应采用液下装卸车鹤管', '303', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞215号）\n');
INSERT INTO `t_item_tbl` VALUES ('1145', '根据《安全标志及其使用导则》（GB 2894-2008）、以及《工业管道的基本识别色、识别符号和安全标识》（GB7231-2003）的相关规定，在管外（保护层外或保温层外）涂上不同的颜色，如水管为艳绿色、水蒸汽管为大红色，并标明物料名称及走向。', '303', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞216号）\n');
INSERT INTO `t_item_tbl` VALUES ('1146', '按《石油化工静电接地设计规范》SH3097-2000，在输送易燃物料的设备、管道上安装防静电设施；', '304', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞217号）\n');
INSERT INTO `t_item_tbl` VALUES ('1147', '按《建筑物防雷设计规范》（GB50057），在厂区（包括储罐区、仓库区及分装、充装等生产作业区）安装防雷设施', '304', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞218号）\n');
INSERT INTO `t_item_tbl` VALUES ('1148', ' 易燃、易爆液体（气体）装卸车台设置能检测静电接地状态的防静电接地仪器，泵房门外、储罐上罐扶梯入口处设置人体静电释放仪。', '304', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞219号）\n');
INSERT INTO `t_item_tbl` VALUES ('1149', '防雷装置检测应当每年一次，对爆炸危险环境场所的防雷装置应当每半年检测一次。所有防静电设备、测试仪表及防护用品，要定期检查、维修，并建立设备档案。', '304', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞220号）\n');
INSERT INTO `t_item_tbl` VALUES ('1150', '企业应制订年度综合检维修计划，落实“五定”，即定检修方案、定检修人员、定安全措施、定检修质量、定检修进度原则。', '305', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞221号）\n');
INSERT INTO `t_item_tbl` VALUES ('1151', '企业在进行检维修作业时，应执行下列程序：\n(1)检维修前：\n①危险、有害因素识别；②编制检维修方案；③办理工艺、设备设施交付检维修手续；④对检维修人员进行安全培训教育；⑤检维修前对安全控制措施进行确认；⑥为检维修作业人员配备适当的劳动保护用品；⑺办理各种作业许可证。\n(2)对检维修现场进行安全检查。\n(3)检维修后办理检维修交付生产手续。', '305', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞222号）\n');
INSERT INTO `t_item_tbl` VALUES ('1152', '在检维修、施工、吊装等作业现场设置相应的警戒区域和警示标志；在检修现场的坑、井、洼、沟、陡坡等场所设置围栏和警示灯。', '305', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞223号）\n');
INSERT INTO `t_item_tbl` VALUES ('1153', '在易燃易爆场所进行维护、检修时，应使用防爆型照明设备、防爆工具。', '305', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞224号）\n');
INSERT INTO `t_item_tbl` VALUES ('1154', '各类作业人员上岗时应按规定穿戴个人防护用品', '305', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞225号）\n');
INSERT INTO `t_item_tbl` VALUES ('1155', '进行清罐、清线、置换作业时，应按照《危险化学品储罐区作业安全通则》（AQ3018-2008）规定要求进行作业。涉及动火等危险作业，应按规定办理相关的作业票证。', '305', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞226号）\n');
INSERT INTO `t_item_tbl` VALUES ('1156', '在装置、仓库、罐区、装卸区、危险化学品输送管道等危险场所的醒目位置设置符合GB2894规定的安全标志。', '306', '');
INSERT INTO `t_item_tbl` VALUES ('1157', '易燃、易爆、有毒介质储存或充装区安装明显可见的风向标或风向袋', '306', '《移动式压力容器充装许可规则》（TSG R4002-2011）A5.2');
INSERT INTO `t_item_tbl` VALUES ('1158', '在重大危险源所在场所应当设置明显的安全警示标志，写明紧急情况下的应急处置办法。', '306', '《危险化学品重大危险源监督管理暂行规定》（安监总局令第40号）第十八条');
INSERT INTO `t_item_tbl` VALUES ('1159', '厂房、库房建筑符合《建筑设计防火规范》（GB50016）/《石油化工企业设计防火规范》（GB50160）的有关要求；', '307', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('1160', '甲、乙、丙类储罐间防火间距符合GB50016/GB50160的有关要求', '307', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞203号）');
INSERT INTO `t_item_tbl` VALUES ('1161', '新建企业的生产区与非生产区分开设置，并符合国家标准或者行业标准规定的距离；', '307', '《危险化学品安全使用许可证实施办法》（安监总局令第57号）第七条');
INSERT INTO `t_item_tbl` VALUES ('1162', '新建企业的生产装置和储存设施之间及其建（构）筑物之间的距离符合GB50016/GB50160等有关标准规范的规定。', '307', '《危险化学品安全使用许可证实施办法》（安监总局令第58号）第七条');
INSERT INTO `t_item_tbl` VALUES ('1163', '厂房、仓库（罐区）等与厂区周边的建筑/构物、厂外道路、铁路、架空电力线等安全间距应当符合GB50016/GB50160的有关要求。', '308', '');
INSERT INTO `t_item_tbl` VALUES ('1164', '储存数量构成重大危险源的储存设施，与下列场所、设施、区域的距离应当符合国家有关规定：\n　　(一)居住区以及商业中心、公园等人员密集场所；\n　　(二)学校、医院、影剧院、体育场(馆)等公共设施；\n　　(三)饮用水源、水厂以及水源保护区；\n　　(四)车站、码头(依法经许可从事危险化学品装卸作业的除外)、机场以及通信干线、通信枢纽、铁路线路、道路交通干线、水路交通干线、地铁风亭以及地铁站出入口；\n　　(五)基本农田保护区、基本草原、畜禽遗传资源保护区、畜禽规模化养殖场(养殖小区)、渔业水域以及种子、种畜禽、水产苗种生产基地；\n　　(六)河流、湖泊、风景名胜区、自然保护区；\n　　(七)军事禁区、军事管理区；\n　　(八)法律、行政法规规定的其他场所、设施、区域。', '308', '《危险化学品安全管理条例》第十九条');
INSERT INTO `t_item_tbl` VALUES ('1165', '应按规定进行装卸作业，查验危险品运输车辆、驾驶员、押运人员的资质证书，并提供或索取化学品安全技术说明书和化学品安全标签。', '309', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('1166', '装卸作业时，有关人员必须正确使用劳动防护用品。\n进入作业区域的车辆，应办理车辆进入许可手续，按规定的路线和速度行驶，按规定位置停放。', '309', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞203号）');
INSERT INTO `t_item_tbl` VALUES ('1167', '遵章守纪、服从管理，杜绝违章指挥、违章操作、违反劳动纪律的行为。', '310', '《中华人民共和国安全生产法》第三章');
INSERT INTO `t_item_tbl` VALUES ('1168', '特种作业人员按照国家有关规定，经专门的安全作业培训，取得特种作业操作资格证书', '311', '《特种作业人员安全技术培训考核管理规定》（安监总局令第30号）第五条');
INSERT INTO `t_item_tbl` VALUES ('1169', '按照《劳动防护用品选用规则》（GB11651）和国家颁发的劳动防护用品配备标准以及有关规定，为从业人员配备劳动防护用品，并督促、教育从业人员正确佩戴和使用劳动防护用品 。', '312', '《劳动防护用品监督管理规定》（安监总局令第1号）第十四条、十六条、十九条');
INSERT INTO `t_item_tbl` VALUES ('1170', '从业人员在作业过程中，必须按照安全生产规章制度和劳动防护用品使用规则，正确佩戴和使用劳动防护用品；未按规定佩戴和使用劳动防护用品的，不得上岗作业。', '312', '《劳动防护用品监督管理规定》（安监总局令第2号）第十四条、十六条、十九条');
INSERT INTO `t_item_tbl` VALUES ('1171', '应急照明设施并确保完好、有效。', '313', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('1172', '按GB50016、《建筑灭火器配置设计规范》（GB50140）配置消防设施与器材；', '314', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('1173', '对建筑消防设施每年至少进行一次全面检测，消防泵应定期检查、运行，确保完好有效，检测记录应当完整准确，存档备查；', '314', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('1174', '厂区应设有符合标准的消防通道，并保证其畅通。', '315', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('1175', '厂房、仓库等设置明显标志，保持畅通。', '316', '《中华人民共和国消防法》第十六条');
INSERT INTO `t_item_tbl` VALUES ('1176', '电气作业人员必须持证上岗，并按规定穿戴好劳动防护用品，正确使用符合安全要求的电气工具。', '317', '《用电安全导则》（GB/T13869)10');
INSERT INTO `t_item_tbl` VALUES ('1177', '电气检修必须执行电气检修工作票制度，并明确工作票签发人、 工作负责人（监护人）、工作许可人、操作人员责任。工作票必须经签发人签发，许可人许可，并办理许可手续后方可作业。', '317', '《用电安全导则》（GB/T13869)11');
INSERT INTO `t_item_tbl` VALUES ('1178', '不应随意拉设临时线路。', '317', '《用电安全导则》（GB/T13869)12');
INSERT INTO `t_item_tbl` VALUES ('1179', '供配电系统应能满足企业的负荷性质、用电容量等要求', '318', '《供配电系统设计规范》（GB50052）1.0.3');
INSERT INTO `t_item_tbl` VALUES ('1180', '变配电房与危化品储罐区、仓库、充装/分装厂房等间距符合GB50016的规定，门应向外开。', '318', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）\n');
INSERT INTO `t_item_tbl` VALUES ('1181', '变配电间应具备的标志牌齐全，悬挂准确无误，有防雨、雪和防鼠、鸟等进入设施，通风良好；', '318', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞203号）\n');
INSERT INTO `t_item_tbl` VALUES ('1182', '在爆炸危险区域场所、供配电设施设计、选型、安装、维护符合应符合《爆炸和火灾危险环境电力装置设计规范》（GB50058）的有关规定。', '318', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞204号）\n');
INSERT INTO `t_item_tbl` VALUES ('1183', '埋地电缆路径无挖掘痕迹，无重物和腐蚀性堆物，设电缆桩，标志明显，电缆头无渗漏的现象；', '318', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞205号）\n');
INSERT INTO `t_item_tbl` VALUES ('1184', '总配电间、发电房配备应急灯；', '318', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞206号）\n');
INSERT INTO `t_item_tbl` VALUES ('1185', '化学危险品库房必须采用隔离、封闭、防爆或其它相应的安全电气照明设备；', '318', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞207号）\n');
INSERT INTO `t_item_tbl` VALUES ('1186', '临时用电的电气设备，必须安装剩余电流保护装置。', '318', '《剩余电流动作保护装置安装和运行》（GB13955)第4．5 条');
INSERT INTO `t_item_tbl` VALUES ('1187', '行灯等手持式电动工具、器具应根据使用现场，分别采取可靠的安全保护措施，如漏电保护电器或使用36伏以下的安全电压。安全变压器应采用双圈的。', '318', '《电气安全工作规程》第八十九条');
INSERT INTO `t_item_tbl` VALUES ('1188', '落地式配电箱的底部宜抬高，高出地面的高度室内不应低于50mm，室外不应低于200mm，其底座周围应采取封闭措施，并应能防止鼠、蛇等小动物进入箱内。', '318', '《低压配电设计规范》（GB50054-2011）4.2.1、4.3.7');
INSERT INTO `t_item_tbl` VALUES ('1189', '配电室的门、窗关闭应密合。', '318', '《低压配电设计规范》（GB50054-2011）4.2.1、4.3.8');
INSERT INTO `t_item_tbl` VALUES ('1191', '1、生产布局合理，有害作业与无害作业分开；2、工作场所与生活场所分开，工作场所不得住人；3、有与职业病防治工作相适应的有效防护设施；4、职业病危害因素的强度或者浓度符合国家职业卫生标准；5、有配套的更衣间、洗浴间、孕妇休息间等卫生设施；6、设备、工具、用具等设施符合保护劳动者生理、心理健康的要求；7、法律、法规、规章和国家职业卫生标准的其他规定。', '319', '《工作场所职业卫生监督管理规定》（安监总局令第47号）第十二条');
INSERT INTO `t_item_tbl` VALUES ('1192', '应当在醒目位置设置公告栏，公布有关职业病防治的规章制度、操作规程、职业病危害事故应急救援措施和工作场所职业病危害因素检测结果。对产生严重职业病危害的作业岗位，应当在其醒目位置，设置警示标识和中文警示说明。', '320', '《中华人民共和国职业病防治法》第二十五条');
INSERT INTO `t_item_tbl` VALUES ('1193', '（1）制氮系统应符合相关规范要求；\n（2）应定期检测氮气的纯度。', '321', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('1194', ' 冷冻冷却水系统应具有足够的容量，符合系统的冷却要求。', '322', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞203号）');
INSERT INTO `t_item_tbl` VALUES ('1195', '传动部位防护要求：（一）如有下列情况之一的，应安装防护罩、盖或栏：\n1、以操作人员站立平面为基准，高度在2米以下的外露传动部位；\n2、旋转的键、销、楔等突出大于3毫米的部位；\n3、产生切屑、磨屑、冷却液等飞溅，可能触及人体或造成设备与环境污染的部位\n4、产生射线或弧光的部位\n5、伸入通道的超长工件\n6、超出设备后端300毫米以上的工件\n7、容易伤人的设备往复运动部位\n8、悬挂输送装置跨越通道的下部\n9、高于地面0.7米的操作平台\n（二）防护罩、盖、栏应满足以下要求：\n1、有足够强度和刚度，无明显锈蚀和变形；2、安装牢固。\n', '323', '');
INSERT INTO `t_item_tbl` VALUES ('1196', '各种限位装置状况：（一）各类行程限位装置应安全可靠，运动机构的行程限位应在规定的范围内；\n（二）凡限制运动件范围的，应装设限位开关，且固定牢靠有效。\n', '1024', '');
INSERT INTO `t_item_tbl` VALUES ('1197', '(一）操作旋转机械最忌戴手套，如车床、钻床等要扣紧袖口，不要戴围巾等，在操作旋转机械时一定要做到工作服的“三紧”，即：袖口紧、下摆紧、裤脚紧；不要戴手套、围巾； \n（二）工作中戴好护目镜。\n', '324', '');
INSERT INTO `t_item_tbl` VALUES ('1198', ' 保险机构要求：(1)破坏式剪切销。这种带剪切销的保险机构是在连接两个轴的部位有两个圆盘，两圆盘靠孔中用销子连接，销子强度小于传动系统中最弱的零件，当设备超负荷传动时，销子首先切断，使被动轴停止转动。\n (2)自动保险机构。在机床传动中，摩擦离合器、齿爪式离合器、安全阀、脱落蜗杆等已成为机床本身的传动部件。一般情况下，这些机构随机床正常工作，当由于某种原因机床出现过载时，这些保险机构就会由于摩擦力不足而打滑，使动力中断传输，被动轴停止转动。当负荷恢复正常时，能自动复位。\n', '324', '');
INSERT INTO `t_item_tbl` VALUES ('1199', '制动机构要求：在大批量生产中多使用气动或液压夹具，由于工作紧张，往往发生夹具未夹紧工件即开车加工，以致工件飞出而造成事故。', '324', '');
INSERT INTO `t_item_tbl` VALUES ('1200', '自动停车机构要求：自动停车机构的主要作用是在机床受到阻碍或在预定位置碰到挡块时，有关部件能自动停止运动。', '324', '');
INSERT INTO `t_item_tbl` VALUES ('1201', '互锁机构要求：互锁机构是一种更完善的安全装置，能直接防止操作失误而引起的伤害。', '324', '');
INSERT INTO `t_item_tbl` VALUES ('1202', '机械的安全要求和措施要求：（一）可能触及到的外露部分应尽量平整、光滑，不应有可能导致刺伤或划伤人员的尖棱、尖角、凸出部分和开口。（二） 溜板纵向运动应设有可靠的限位装置。\n（三）机床飞轮应采取切实可靠的防护措施，确保在运动中不会因其松脱、甩出及缠绕而出现危险。\n（四）外露带和带轮应设置防护罩。应在遮蔽危险部位的防护罩内表面、挂轮箱内表面涂上安全色，以提醒操作、调整和维护人员注意危险的存在。\n（五）因位置原因，在运动中可能造成缠绕危险的花键轴、丝杠应采取切实可靠的安全防护措施。\n（六）控制系统不应有导致危险情况的出现。\n（七） 电力偶然中断，不应给人员造成危险；电力重新恢复后，机床不应自行起动。\n（八） 按钮、指示灯、开关等控制器件的位置应确保操作时不会引起误操作和附加的危险。\n（九）控制器件应清晰可辨，易于区别，必要时应设置表示其功能或用途的标志。\n（十）在每个“起动”控制器件附近应设置一个“停止”控制器件。在每个操作位置应设置一个紧急停止控制器件，紧急停止控制器件的动作不应影响保护操作者或机床装置的功能。\n（十二）冷却系统应能保证操作者的人身安全。 \n（十三）机床应能有效地回收冷却液，应设置防止冷却液飞溅的防护挡板。                                                                                    \n（十四）应设置防止切屑飞溅的防护挡板。\n（十五）冷却液喷嘴应能方便、安全、可靠地固定在所需位置上。\n', '324', '');
INSERT INTO `t_item_tbl` VALUES ('1203', '离合器要求：（一）刚性离合器的转键、键柄和直键无裂纹；\n（二）操纵器的杆件、销钉或弹簧无裂纹、折断；\n（三）离合器安装紧固，动作可靠，没有连冲；\n（四）滑块从上死点至上死点25毫米处行程范围内需要制动时，离合器应对滑块制动。\n', '325', '');
INSERT INTO `t_item_tbl` VALUES ('1204', '（一）制动器与离合器必须相互协调与连锁，开机时动器先松闸，离合器稍滞后再结合；\n（二）连锁应准确、紧固，工作稳定、协调；\n（三）行程限位装置、控制装置正确，动作灵敏有效。\n', '325', '');
INSERT INTO `t_item_tbl` VALUES ('1205', '紧停装置要求：（一）大型冲压机械前后都应安装急停按钮，位置在人手可迅速触及的地方；\n（二）剪板机剪板长度大于2500毫米时，两侧应安装紧停装置；\n（三）紧停装置应有红色标记。\n', '325', '');
INSERT INTO `t_item_tbl` VALUES ('1206', '传动部位要求：距作业平台2米以上旋转部位应装设防护罩或防护栏', '325', '');
INSERT INTO `t_item_tbl` VALUES ('1207', '脚踏开关要求：（一）外露在工作台面的脚踏开关，只要有物体撞压可能的，应在开关上部和两侧装设防护罩；\n（二）剪板机脚踏杠在整个长度上应装设防护罩；\n（三）脚踏开关的踏面应用防滑的花纹钢板制作；\n（四）防护罩有足够的强度，无棱角毛边，便于开关检修。\n', '325', '');
INSERT INTO `t_item_tbl` VALUES ('1208', '机床接地保护要求：冲、剪、压、钻机械的金属结构件、电机、变压器、穿线金属管等都应有接地保护。', '325', '');
INSERT INTO `t_item_tbl` VALUES ('1209', '安全防护附件要求：（一）必须采取各种安全措施，确保在滑块下落时，操作者任何部位不可能进入作业危险区；\n（二）使用双按钮之间的内侧距离必须大于25厘米。\n', '325', '');
INSERT INTO `t_item_tbl` VALUES ('1210', '位置和朝向要求：（一）安装地点应保证人员和设备安全。（二）朝向不得对着通道、门口和操作岗位。\n防护罩应符合国家安全标准。\n挡屑板应有足够强度。\n砂轮无裂纹、无破损。\n法兰盘使用软垫连接。\n托架安装牢固可靠。\n运行平稳，砂轮磨损量符合要求。\n接地可靠，控制电器符合规定。\n', '326', '');
INSERT INTO `t_item_tbl` VALUES ('1211', '电源线、电源插头应采用橡皮护套绝缘软线。与电源联接，应采用开关、插头座。严禁用导线直接插入插座，或挂在电源线上使用。', '327', '');
INSERT INTO `t_item_tbl` VALUES ('1212', '工具必须由专职人员每年至少检查一次，并记录。', '327', '');
INSERT INTO `t_item_tbl` VALUES ('1213', '在使用移动式的I类设备时，应先确认其金属外壳或构架已可靠接地，使用带保护接地极的插座，同时宜装设漏电保护器，禁止使用无保护线插头插座。', '327', '');
INSERT INTO `t_item_tbl` VALUES ('1214', '露天使用的用电设备、配电装置应采取防雨、防雪、防雾和防尘的措施。', '327', '');
INSERT INTO `t_item_tbl` VALUES ('1215', '电源线、焊接电缆与焊机连接处的裸露接线板，应采取安全防护罩或防护板隔离，以防止人员或金属物体接触', '329', '');
INSERT INTO `t_item_tbl` VALUES ('1216', '焊机外壳防漏电保护要求：（一）焊机外壳必须接地或接零保护，接地或接零装置连接良好，并定期检查；\n（二）严禁使用易燃易爆气体管道作为接地装置；\n（三）在有接地或接零装置的焊件上进行弧焊操作，或焊接与地面密切连接的焊件时，应注意避免焊机和工件的双重接地。\n', '329', '');
INSERT INTO `t_item_tbl` VALUES ('1217', '绝缘电阻要求：（一）焊接变压器一、二次绕组、绕组与外壳之间的绝缘电阻值不小于1兆欧；\n（二）每半年应对焊机绝缘电阻遥测一次，且记录完整。\n', '329', '');
INSERT INTO `t_item_tbl` VALUES ('1218', '焊机一次线使用要求：（一）焊机一次侧电源线长度不超过3米；\n（二）一次线不得在地面拖拽使用，不得在地面跨越通道使用；\n（三）电焊机应接在专用开关上使用，不得与其它设备共用一个开关；\n（四）在高处或金属容器内工作时应设专人监护。\n', '329', '');
INSERT INTO `t_item_tbl` VALUES ('1219', '焊机二次线使用要求：（一）焊机二次线必须连接紧固，无松动；\n（二）二次线接头不超过3个；\n（三）二次线截面选用正确，避免长期过载使用造成绝缘老化；\n（四）严禁使用厂房金属结构、管道、轨道等作为焊接二次回路使用。\n', '329', '');
INSERT INTO `t_item_tbl` VALUES ('1220', '焊钳夹紧力好，绝缘良好，手柄隔热层完整，焊钳与导线连接可靠。', '329', '');
INSERT INTO `t_item_tbl` VALUES ('1221', '焊机使用场所要求：（一）焊机应安放在通风、干燥、无碰撞、无剧烈震动、无高温、无易燃品存在的地方；\n（二）在室外或特殊环境下使用，应采取防护措施保证其正常使用。雨、雪、雾天，无防护措施不准作业；\n（三）焊机使用场所应清洁，无严重粉尘；\n（四）操作人员应穿绝缘鞋、防护服、面罩（护镜）、电焊手套。\n', '329', '');
INSERT INTO `t_item_tbl` VALUES ('1222', '旋转部位要求：防护要求（一）炊事机械的传动部位的皮带轮、齿轮、链轮与链条、联轴器等，应加防护罩或防护盖，不便安装的应加设防护栏；\n（二）防护罩、盖和栏完好可靠，强度符合要求。\n', '330', '');
INSERT INTO `t_item_tbl` VALUES ('1223', '电气部分要求：（一）炊事机械的金属外壳、电动机外壳的PE线均应可靠；\n（二）电源线最好穿管敷设，若采用橡胶电缆时，应敷设在无浸泡、无高温和无压砸的沿墙壁面；\n（三）线路不得有接头、破损、老化或过载；\n（四）随机配备的电气箱柜应符合要求。\n', '330', '');
INSERT INTO `t_item_tbl` VALUES ('1224', '控制开关要求：（一）炊事机械电源控制开关应单机单设，不得几台设备共用一个开关或用距离较远的闸刀控制；\n（二）有尘、水可能进入的按钮开关应有防护装置，且不防碍操作。\n', '330', '');
INSERT INTO `t_item_tbl` VALUES ('1225', '搅拌、合面机要求：（一）应加盖密封，容器盖应采用规定的材料制作；\n（二）必须安装机盖联锁安全装置，联锁开关应固定在机器本体上，启盖应即刻断电。\n', '330', '');
INSERT INTO `t_item_tbl` VALUES ('1226', '绞肉、绞菜机要求：（一）加料口护手装置应保证操作人员手指不能触及刀口或螺旋部位，加料口较大的其高度应适当增加，否则应设小口径加料托盘；\n（二）护手装置、加高的加料口和加料托盘应用耐腐蚀的材料制作，具有足够的强度，与加料口连接固定，应能翻转清洗；\n（三）应配备送料的辅助工具。\n', '330', '');
INSERT INTO `t_item_tbl` VALUES ('1227', '压面机要求：（一）轧辊应便于装拆，调整灵活、定位可靠；\n（二）加料处应有防护装置，防止手指伸入；\n（三）备有专用刮面板，不得用手推、刮面粉。\n', '330', '');
INSERT INTO `t_item_tbl` VALUES ('1228', '应急照明设施并确保完好、有效。', '331', '');
INSERT INTO `t_item_tbl` VALUES ('1229', '按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，确保完好有效。', '332', '');
INSERT INTO `t_item_tbl` VALUES ('1230', '变配电室变压器、高压开关柜、低压开关柜操作面地面应铺设绝缘胶垫。', '333', '');
INSERT INTO `t_item_tbl` VALUES ('1231', '变配电室出入口应设置高度不低于400mm 的挡板。', '334', '');
INSERT INTO `t_item_tbl` VALUES ('1232', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '335', '');
INSERT INTO `t_item_tbl` VALUES ('1233', '通往室外的门应向外开。设备间与附属房间之间的门应向附属房间方向开。高压间与低压间之间的门，应向低压间方向开。配电装置室的中间门应采用双向开启门。通往室外的窗应装有纱窗。', '336', '');
INSERT INTO `t_item_tbl` VALUES ('1234', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '337', '');
INSERT INTO `t_item_tbl` VALUES ('1235', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '337', '');
INSERT INTO `t_item_tbl` VALUES ('1236', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '337', '');
INSERT INTO `t_item_tbl` VALUES ('1237', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：\n（一) 绝缘拉杆应悬挂或架在支架上，不应与墙接触；\n（二) 绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；\n(三) 绝缘垫和绝缘台应经常保持清洁、无损伤；\n（四) 高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；\n（五) 安全用具不允许当作其它工具使用；\n（六) 安全用具不合格的不得存放在工作现场。\n', '337', '');
INSERT INTO `t_item_tbl` VALUES ('1238', '禁止类：禁止合闸、禁止攀登、高压危险；警告类：止步、高压危险；准许类：从此上下、在此工作。', '338', '');
INSERT INTO `t_item_tbl` VALUES ('1239', '变配电室内环境整洁，场地平整，设备间不应存放与运行无关的闲散器材和私人物品，禁止无关人员进入场地。', '339', '');
INSERT INTO `t_item_tbl` VALUES ('1240', '主控制室、高压配电室不应带入食物及储放粮食，值班室不应设置、使用寝具、灶具，并应有防止小动物的安全措施。', '339', '');
INSERT INTO `t_item_tbl` VALUES ('1241', '非变配电室从业人员因工作需要进入变配电室时，应经值班人员许可，并办理登记手续。当需要进入设备区时，应有值班人员监护。', '340', '');
INSERT INTO `t_item_tbl` VALUES ('1242', '柜、屏、台、箱、盘的金属框架及基础型钢必须接地（PE）或接零（PEN）可靠；装有电器的可开启门与框架的接地端子间应用裸线编织铜线连接，作好标识。', '341', '');
INSERT INTO `t_item_tbl` VALUES ('1243', '箱盘内开关灵活可靠。带有漏电保护的回路，漏电保护装置动作电流和动作时间按设计要求。', '342', '');
INSERT INTO `t_item_tbl` VALUES ('1244', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '342', '');
INSERT INTO `t_item_tbl` VALUES ('1245', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '342', '');
INSERT INTO `t_item_tbl` VALUES ('1246', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。', '343', '');
INSERT INTO `t_item_tbl` VALUES ('1247', '直敷布线可用于正常环境的屋内场所，应采用护套绝缘导线。当导线垂直敷设至地面低于1．8m时，应穿管保护。', '344', '');
INSERT INTO `t_item_tbl` VALUES ('1248', '（一）设备的金属外壳应采取防漏电保护接地\n（二）PE线若明设时，应选用不小于4平方毫米的铜芯线，不得使用铝芯线\n（三）PE线若随穿线管接入设备本体时，应选用不小于2.5平方毫米的铜芯线或不小于4平方毫米的铝芯线\n（四）PE线不得搭接或串接，接线规范、接触可靠\n（五）明设的应沿管道或设备外壳敷设，暗设的在接线处外部应有接地标志\n（六）PE线接线间不得涂漆或加绝缘垫\n', '345', '');
INSERT INTO `t_item_tbl` VALUES ('1249', '插座回路均应设置剩余电流动作保护装置。宜装设漏电保护器，禁止使用无保护线插头插座。', '346', '');
INSERT INTO `t_item_tbl` VALUES ('1250', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。不应从带插座灯头上引接电源供给电器。', '347', '');
INSERT INTO `t_item_tbl` VALUES ('1251', '临时线路使用必须经过审批，一般使用期限为过15天，最长不超过一个月。', '348', '');
INSERT INTO `t_item_tbl` VALUES ('1252', '临时用电的电气设备，必须安装剩余电流保护装置。', '349', '');
INSERT INTO `t_item_tbl` VALUES ('1253', '线路敷设应符合电气装置设置及安装规范，室内不低于2.5m ，室外不低于4.5m，道路上方不低于6m。', '350', '');
INSERT INTO `t_item_tbl` VALUES ('1254', '（一）手提式照明使用36伏（二）危险环境携带式电动工具使用36伏（三）金属容器内使用24伏(四)室内灯具离地面低于2.4m，手持照明灯具，一般潮湿作业场所（地下室、潮湿室内、潮湿楼梯、隧道、人防工程以及有高温、导电灰尘等）的照明，电源电压应不大于36V。 \n（五）在潮湿和易触及带电体场所的照明电源电压，应不大于24V。 \n（六） 在特别潮湿的场所，锅炉或金属容器内，导电良好的地面使用手持照明灯具等，照明电源电压不得大于12V。 \n', '351', '');
INSERT INTO `t_item_tbl` VALUES ('1255', '（一）危险化学品应根据分类，分区、分库存放；\n（二）爆炸物品、自身能形成爆炸的危险品、腐蚀性物品应单独存放；\n（三）相互接触或混合后能引起燃烧或爆炸的物品不能同库存放；\n（四）灭火方式不同的物质不得同库存放；\n（五）遇火、遇热、遇潮能引起燃烧、爆炸或发生化学反应，产生有毒气体的化学危险品不得露天或在潮湿、积水的建筑中存放。\n', '352', '');
INSERT INTO `t_item_tbl` VALUES ('1256', '开关、灯具、线路、用电设施均应符合防爆要求。', '353', '');
INSERT INTO `t_item_tbl` VALUES ('1257', '作业现场不得储存、堆放其它易燃易爆物品，易燃易爆废料及时从现场清理。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1258', '物品堆放整齐、稳固，高度不超过2米，易挥发可燃物品桶盖随时拧紧盖严。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1259', '易燃易爆用品现场存量最好为1个班的用量。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1260', '通道保持畅通，出、入口处不得堆放易燃易爆物品。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1261', '危险化学品储存及有限空间应保持良好的通风。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1262', '作业区域应采取自然通风或机械通风，保持通风良好。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1263', '1、封闭式易燃易爆物品使用场所，必须安装自动报警或抑爆系统；2、涂装调漆间和喷漆室未规范设置可燃气体报警装置和防爆电气设备设施；3、天然气（煤气）加热炉燃烧器操作部位未设置可燃气体泄漏报警装置，或燃烧系统未设置防突然熄火或点火失败的安全装置；4、使用易燃易爆稀释剂（如天拿水）清洗设备设施，未采取有效措施及时清除集聚在地沟、地坑等有限空间内的可燃气体。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1264', '封闭式易燃易爆物品使用场所，必须安装自动报警或抑爆系统。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1265', '易燃易爆作业场所区域内所有金属部件、构件都应可靠接地。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1266', '易燃易爆作业场所不得从事动火作业，严禁人员吸烟。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1267', '专人管理，并有防潮防雨淋设施。', '354', '');
INSERT INTO `t_item_tbl` VALUES ('1268', '设置安全标志。', '355', '');
INSERT INTO `t_item_tbl` VALUES ('1269', '车间、库房等作业场所应设置防静电装置。', '356', '');
INSERT INTO `t_item_tbl` VALUES ('1270', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '357', '');
INSERT INTO `t_item_tbl` VALUES ('1271', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '358', '');
INSERT INTO `t_item_tbl` VALUES ('1272', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '359', '');
INSERT INTO `t_item_tbl` VALUES ('1273', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '360', '');
INSERT INTO `t_item_tbl` VALUES ('1274', '容易造成职业危害的工序应隔离设置。', '361', '');
INSERT INTO `t_item_tbl` VALUES ('1275', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '362', '');
INSERT INTO `t_item_tbl` VALUES ('1276', '应在可产生职业危害的工作场所和设备上，按GBZ158《工作场所职业病危害警示标识》和GBZ/T203《高度物品作业岗位职业病危害告知规范》的要求设置职业危害警示标识。', '363', '');
INSERT INTO `t_item_tbl` VALUES ('1277', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '364', '');
INSERT INTO `t_item_tbl` VALUES ('1278', '（一）厂区内实行定置管理：厂区有定置图，对各类厂房建筑、物料堆放点、交通道路等应注明。\r\n（二）厂区内按照定置管理的要求实现定置摆放，厂区内无杂物、无图物不符等状况。\r\n（三）工业垃圾和生活垃圾分开定点存放，有防吹散、防污染措施。\r\n（四）危险固体废料应有专门存放地点，存放点有防渗漏措施，且符合国家规定妥善处理。\r\n（五）厂区大门开关灵活、方便，迅速，无卡死现象。\r\n（六）会议室、活动室、休息室、更衣室等场所设置在熔炼炉、熔融金属吊运和浇注影响范围内。', '365', '');
INSERT INTO `t_item_tbl` VALUES ('1279', '（一）厂区双向主干道宽度不小于5米，单向主干道宽度不小于3米，且为环形。\n（二）路面排水良好，路面平整，无台阶、无坑沟，盖板齐全。\n（三）厂区主干道在平原地区应小于6度，山区应小于8度\n（四）厂区大门、危险路段的车速应限制在每小时5公里；人员稠密地段、下坡路、转弯路、交叉路口、装卸作业区限速每小时15公里，并设置限速标志。\n（五）交叉路口若有视线盲区，应设立反光镜，反光镜无破损，角度和高度应便于观察道路盲区。\n（六）厂区道路应有明显的人、车分流线，人行道宽度不小于1米，分流线清晰，宽度大于10厘米。\n', '366', '');
INSERT INTO `t_item_tbl` VALUES ('1280', '（一）利用主干道堆放物品或作为停车位置，应有划线标志，通行部分宽度必须不小于5米。\n（二）不得在转弯处或道路两侧堆放物品或停置车辆。\n（三）道路施工应有警示牌或护栏，夜间要有红灯警示。\n（四）厂区主干道占道率应小于道路长度的5%。\n（五）不得将厂区主干道横向全部堵死。\n', '367', '');
INSERT INTO `t_item_tbl` VALUES ('1281', '（一）照明灯布置合理，无照明盲区。\n（二）照明灯具完好率达100%。\n', '368', '');
INSERT INTO `t_item_tbl` VALUES ('1282', '（一）室外消防栓应合理配置，低压消防栓布置间距不超过120米，应沿道路两侧和路口设置，消防栓距路面边不超过2米，距建筑物外墙不小于5米。\n（二）有明显的漆色标志，其1米范围内无障碍物。\n（三）手提式灭火器的配置数量符合规定。\n（四）所有消防器材应完好，在有效期限内使用。\n', '369', '');
INSERT INTO `t_item_tbl` VALUES ('1283', '（一）搬运、安装、其他一些作业和高压线保持安全距离；                                （二）厂房的装修、维修、做防水等和高压线保持安全距离；                                            （三）高压线下严禁码放材料和有建筑物。                                                         ', '370', '');
INSERT INTO `t_item_tbl` VALUES ('1284', '（一）车间通道根据生产要求，宽度标准如下：\n通道只行人通道，宽度不小于1米；\n通过电瓶车、叉车的通道，宽度不小于1.8米；\n通行汽车的车间通道，宽度应不小于3米；\n如有铁路线，车间通道，宽度应不小于5米。\n（二）通道线应明显清晰，宽度不小于10厘米。\n', '371', '');
INSERT INTO `t_item_tbl` VALUES ('1285', '（一）通道路面应平整，无台阶、无坑、沟。\n（二）无积油、积水，无绊脚物。\n', '372', '');
INSERT INTO `t_item_tbl` VALUES ('1286', '（一）任何堆放在通道标记线内和压住安全通道标记线的物件，都判定为占道。\n（二）车间占道率应低于5%。\n（三）不得有物件将通道横向全部堵死。\n', '373', '');
INSERT INTO `t_item_tbl` VALUES ('1287', '（一）车行道上方的悬挂物高度不小于4米，人行道上方悬挂物高度不小于2.5米。\n（二）悬挂应牢固可靠。\n', '374', '');
INSERT INTO `t_item_tbl` VALUES ('1288', '（一）仓库通道的宽度应满足以下要求：\n车行道宽度不小于3.5米。\n人行道宽度不小于1米。\n（二）路面平坦，无积油积水，无绊脚物。\n（三）占道率应小于5%。\n', '375', '');
INSERT INTO `t_item_tbl` VALUES ('1289', '（一）照明灯具完好率达100%。\n（二）库房内不得使用移动式照明灯具。\n', '376', '');
INSERT INTO `t_item_tbl` VALUES ('1290', '（一）库房内不得存在临时线。\n（二）库房内敷设的电气线路必须，穿金属管或非燃硬塑料管。\n（三）每个库房应当在库房外单独安装开关箱。\n（四）甲、乙类物品和丙类液体库房的电气装置，必须安装防爆型电气装置，储存丙类固体物品的仓库，不得使用碘钨灯或超过60瓦以上的白炽灯等高温照明灯具。\n', '377', '');
INSERT INTO `t_item_tbl` VALUES ('1291', '（一）防火制度、消防设施标志、防火标志齐全。\n（二）按标准配备消防器材的数量和种类。\n（三）防火通道畅通，无封死和堵塞现象。\n', '378', '');
INSERT INTO `t_item_tbl` VALUES ('1292', '以下仓库应安装火灾报警装置：\n1、甲、乙、丙类物品储备库；\n2、专业性仓库；\n3、大型物资仓库；\n4、耐火等级为三、四级的仓库。\n', '379', '');
INSERT INTO `t_item_tbl` VALUES ('1293', '（一）物品包装应牢固，不得有严重破损、残缺、变形、物品变质、分解等现象。\n（二）甲、乙类物品的包装应严密，不得有跑、冒]滴、漏现象。\n', '380', '');
INSERT INTO `t_item_tbl` VALUES ('1294', '杜绝违章指挥、违章操作、违反劳动纪律的行为。', '381', '');
INSERT INTO `t_item_tbl` VALUES ('1295', '油罐应采取卸油时的防满溢措施。油料达到油罐容量的90%时，应能触动高液位报警装置；油料达到油罐容量的95%时，应能自动停止油料继续进罐。', '382', '《汽车加油加气站设计与施工规范》(GB50156-2012)6.1、6.2、6.3');
INSERT INTO `t_item_tbl` VALUES ('1296', '设有油气回收系统的，油罐应设带有高液位报警的液位监测系统。', '382', '《汽车加油加气站设计与施工规范》(GB50156-2012)6.1、6.2、6.4');
INSERT INTO `t_item_tbl` VALUES ('1297', '加油软管上宜设安全拉断阀', '383', '《汽车加油加气站设计与施工规范》(GB50156-2012)6.1、6.2、6.5');
INSERT INTO `t_item_tbl` VALUES ('1298', '位于加油岛端部的加油机附近应设防撞柱（栏），其高度不应小于0.5m.', '383', '《汽车加油加气站设计与施工规范》(GB50156-2012)6.1、6.2、6.6');
INSERT INTO `t_item_tbl` VALUES ('1299', '每个油罐应各自设置卸油管道和卸油接口，并有明显的标识。', '384', '《汽车加油加气站设计与施工规范》(GB50156-2012)6.1、6.2、6.7');
INSERT INTO `t_item_tbl` VALUES ('1300', '汽油罐与柴油罐的通气管应分开设置，管口应高出地面 4m 及以上；沿建筑物的墙(柱)向上敷设的通气管管口，应高出建筑物的顶面 1.5m 及以上。通气管管口应装设阻火器。 ', '384', '《汽车加油加气站设计与施工规范》(GB50156-2012)6.1、6.2、6.8');
INSERT INTO `t_item_tbl` VALUES ('1301', '当加油站采取油气回收系统时，汽油罐通气管管口除装设阻火器外，尚应装设呼吸阀。', '384', '《汽车加油加气站设计与施工规范》(GB50156-2012)6.1、6.2、6.9');
INSERT INTO `t_item_tbl` VALUES ('1302', ' 380／220V供配电系统宜采用TN-S系统，供电系统的电缆金属外皮或电缆金属保护管两端均应接地，在供配电系统的电源端应安装与设备耐压水平相适应的过电压(电涌)保护器。 ', '385', '《汽车加油加气站设计与施工规范》(GB50156-2012)11.2.9、11.2.11');
INSERT INTO `t_item_tbl` VALUES ('1303', '汽油罐车卸车场地，应设罐车卸车时用的防静电接地装置，并应设置能检测跨接线及监视接地装置状态的静电接地仪。', '385', '');
INSERT INTO `t_item_tbl` VALUES ('1304', '防雷装置检测应当每年一次，对爆炸危险环境场所的防雷装置应当每半年检测一次。所有防静电设备、测试仪表及防护用品，要定期检查、维修，并建立设备档案。', '385', '《加油站作业安全规范》（AQ3010-2007）8.5');
INSERT INTO `t_item_tbl` VALUES ('1305', '加油枪胶管上的金属屏蔽线和机体之间的静电连接应可靠。', '385', '《加油站作业安全规范》（AQ3010-2007）8.6');
INSERT INTO `t_item_tbl` VALUES ('1306', '维护、检修应使用防爆型照明设备、防爆工具。', '386', '《加油站作业安全规范》（AQ3010-2007）8.1、8.2、8.3、8.4');
INSERT INTO `t_item_tbl` VALUES ('1307', '各类作业人员上岗时应穿防静电工作服，防静电工作鞋、袜；严禁穿带铁钉的鞋。', '386', '《加油站作业安全规范》（AQ3010-2007）8.1、8.2、8.3、8.5');
INSERT INTO `t_item_tbl` VALUES ('1308', '涉及动火等危险作业，应按规定办理相关的作业票证。', '386', '《加油站作业安全规范》（AQ3010-2007）8.1、8.2、8.3、8.6');
INSERT INTO `t_item_tbl` VALUES ('1309', '作业场所应按CB16179、GB15630规定设置“禁止烟火”、“禁止使用手机”等禁止标志，“当心火灾”、 “当心车辆”等警告标志，“入口”、“出口”等指令标志，', '387', '《加油站作业安全规范》（AQ3010-2007）10.3');
INSERT INTO `t_item_tbl` VALUES ('1310', '车辆入口和出口应分开设置', '388', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.1、5.0.2、5.0.3、5.0.5、5.0.13');
INSERT INTO `t_item_tbl` VALUES ('1311', '加油作业内的停车位和道路路面不应采用沥青路面', '388', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.1、5.0.2、5.0.3、5.0.5、5.0.14');
INSERT INTO `t_item_tbl` VALUES ('1312', '加油作业区与辅助服务区之间应由界线标识。', '388', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.1、5.0.2、5.0.3、5.0.5、5.0.15');
INSERT INTO `t_item_tbl` VALUES ('1313', '加油作业区内，不得有明火地点或散发火花地点。', '388', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.1、5.0.2、5.0.3、5.0.5、5.0.16');
INSERT INTO `t_item_tbl` VALUES ('1314', '加油站内设施（包括油罐、通气管管控、加油机、站房、站区围墙等）之间的防火距离，不应小于GB50156中表 5.0.13-1、5.0.13-2 的规定。', '388', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.1、5.0.2、5.0.3、5.0.5、5.0.17');
INSERT INTO `t_item_tbl` VALUES ('1315', '.加油站的汽油设备、柴油设备（包括油罐、加油机和通气管管口）与站外建、构筑物（包括架空电力线、通讯线、道路等）的安全间距，分别不应小于《汽车加油加气站设计与施工规范》GB50156-2012表4.0.4、表4.0.5的规定。', '389', '《汽车加油加气站设计与施工规范》(GB50156-2012)4.0.4、4.0.5');
INSERT INTO `t_item_tbl` VALUES ('1316', '基本要求\n(1）加油员在使用加油机前，应检查加油机运转是否正常及有无渗漏油品现象，并要保持加油机的整洁。\n(2）汽油加油流量不应大于60L/min。\n(3）加油岛上不得放置收录音机、电扇等一般电器设备及其他杂物。\n(4）有车辆进站时，加油人员应站在加油岛上以防被撞，作业人员避免穿过两车中间。\n(5）客车进站加油时不得载有乘客。\n(6）禁止使用绝缘性容器加注汽油、煤油等。', '390', '《加油站作业安全规范》（AQ3010-2007）6.1、6.2');
INSERT INTO `t_item_tbl` VALUES ('1317', '加油\n(1）车辆驶入站时，加油员应主动引导车辆进入加油位置，待其停稳、发动机熄火后，方可打开油箱盖加油。\n(2）非自助加油机上的加油作业应由加油员操作，不得由顾客自行处置。\n(3）加油时应避免油料溅出，尤其机车加油时应特别注意不可溅出油料溅及高温引擎及排气管。\n(4）加油时若有油料溢出，应立即擦拭，含有油污布料应妥善收存有盖容器中。\n(5）加完油后，应立即将加油枪拉出，以防被拖走。\n(6）加油前及加油后应保持橡皮管放置于加油机上，防止被车辆压坏。\n(7）当加油、结算等程序完成后，应及时引导车辆离开加油岛。\n(8）站内有人吸烟或使用移动电话时，应立即停止加油。\n(9）摩托车加油后，应用人力将摩托车推离加油岛4.5m后，方可启动。\n(10）加油站上空有高强闪电或雷击频繁时，应停止加油作业，采取防护措施。', '390', '');
INSERT INTO `t_item_tbl` VALUES ('1318', '基本要求\n(1）应具备密闭卸油的条件。\n(2）防静电接地设施完好。\n(3）油罐车车辆正常，防火防静电设施完备。\n(4）卸油作业所需消防器材配备齐全。\n(5）雷雨期间不得进行卸油作业。', '391', '《加油站作业安全规范》（AQ3010-2007）5.1、5.2');
INSERT INTO `t_item_tbl` VALUES ('1319', '卸油\n(1）油罐车进、出加油站或倒车时，应由加油站人员引导、指挥。\n(2）油罐车应停放于卸油专用区，熄火并拉上手剎车、于车轮处放置轮挡；并使车头向外，以利紧急事故发生时，可迅速驶离。\n(3）卸油过程中，卸油人员和油罐车驾驶员不应离开作业现场，打雷时应停止卸油作业。\n(4）向地下罐注油时，与该罐连接的给油设备应停止使用。卸油前应检查油罐的存油量，以防灌油时溢油。卸油作业中，严禁用量油尺计量油罐。\n(5）卸油作业中，必须有专人在现场监视，并禁止车辆及非工作人员进入卸油区。\n(6）检查确认油罐计量孔密闭良好。\n(7）油罐车进站后，卸油人员应立即检查油罐车的安全设施是否齐全有效，油罐车的排气管应安装防火罩。检查合格后，引导油罐车进入卸油现场，应先接妥静电接地线夹头接线并确实接触。\n(8）油罐车熄火并静置15min后，卸油员按工艺流程连接卸油管及油气回收管及接头，将接头结合紧密，保持卸油管自然弯曲；经计量后准备接卸；按规定在卸油位置上风处摆放干粉灭火器。\n(9）卸油前，核对罐车与油罐中油品的品名、牌号是否一致，各项准备工作检查无误后，能自流卸油的不泵送卸油。\n(10）油罐车驾驶员缓慢开启卸油阀卸油。卸油员集中精力监视、观察卸油管线、相关闸阀、过滤器等设备的运行情况，随时准备处理可能发生的问题。\n(11）卸油时严格控制油的流速，在油面淹没进油管口200mm前，初始流速不应大于1m/s，正常卸油时流速控制在4.5m/s以内，以防产生静电。\n(12）卸油完毕，油罐车驾驶员应关闭卸油阀；卸油员应先拆卸油管与油罐车连接端头，并将卸油管抬高使管内油料流入油罐内并防止溅出。盖严罐口处的卸油帽，收回静电导线。收存卸油管、油气回收管时不可拋摔，以防接头变形。\n(13）卸油完毕罐车静置5min后，卸油员引导油罐车启车、离站，清理卸油现场，将消防器材放回原位。\n(14）待罐内油面静止平稳后，通知加油员开机加油。\n(15）卸油时若发生油料溅溢时， 应立即停止卸油并立即处理。\n(16）卸油时如发生交通事故、火灾事故、爆炸事故、破坏事故和伤亡事故等重大事故，应立即停止卸油作业，同时应将油罐车驶离加油站。\n(17）在卸油过程中，严禁擦洗罐车物品、按喇叭、修车等，对器具要轻拿轻放，夜间照明须使用防爆灯具。\n(18）卸油口未使用时应加锁。', '391', '');
INSERT INTO `t_item_tbl` VALUES ('1320', '加油站及加油加气合建站的消防泵房、罩棚、营业室等处，均应设事故照明。', '392', '《汽车加油加气站设计与施工规范》(GB50156-2012)11.1.3');
INSERT INTO `t_item_tbl` VALUES ('1321', '灭火器材配置应符合下列规定：\n　　1、每2台加油机应设置不少于2只4kg手提式干粉灭火器，或一1只4kg手提式干粉灭火器和1只6L泡沫灭火器；加油机不足2台按2台计算；\n　　2、地下储罐应设置35kg推车式干粉灭火器1个。当两种介质储罐之间的距离超过15m时，应分别设置；\n　　3、泵、压缩机操作间（棚）应按建筑面积每50㎡设8kg手提式干粉灭火器1只，总数不应少于2只；\n　　4、一、二级加油站应备灭火毯5块、沙子2立方米，三级加油站应备灭火毯2块、沙子2立方米，加油加气合建站，按同级别的加油站配置灭火毯和沙子；\n　　5、其他建筑物灭火器材配置，应符合现行国家标准《建筑灭火器材配置设计规范》GB50140的有关规定。', '393', '《汽车加油加气站设计与施工规范》(GB50156-2012)10.1');
INSERT INTO `t_item_tbl` VALUES ('1322', '标志明显，保持畅通。', '394', '《汽车加油加气站设计与施工规范》(GB50156-2012)10.2');
INSERT INTO `t_item_tbl` VALUES ('1323', '电气作业人员必须持证上岗，并按规定穿戴好劳动防护用品并正确使用符合安全要求的电气工具。', '395', '《加油站作业安全规范》（AQ3010-2007）8.6');
INSERT INTO `t_item_tbl` VALUES ('1324', '电气检修必须执行电气检修工作票制度，并明确工作票签发人、 工作负责人（监护人）、工作许可人、操作人员责任。工作票必须经签发人签发，许可人许可，并办理许可手续后方可作业。', '395', '《加油站作业安全规范》（AQ3010-2007）8.7');
INSERT INTO `t_item_tbl` VALUES ('1325', '不应随意拉设临时线路。', '395', '《加油站作业安全规范》（AQ3010-2007）8.8');
INSERT INTO `t_item_tbl` VALUES ('1326', '变配电间或室外变压器应设置在爆炸危险区域外，且与爆炸危险区域边界线的距离不应小于3m。', '396', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.8、11.1');
INSERT INTO `t_item_tbl` VALUES ('1327', '宜采用电压为380/220V的外接电源，电力线路宜采用电缆并直埋敷设。电缆穿越行车道部分，应用钢管保护。', '396', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.8、11.2');
INSERT INTO `t_item_tbl` VALUES ('1328', '爆炸危险区域内的电气设备选型、安装、电力线路敷设等，应符合《爆炸和火灾危险环境电力装置设计规范》（GB50058）的有关规定。', '396', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.8、11.3');
INSERT INTO `t_item_tbl` VALUES ('1329', '爆炸危险区域外的照明灯具，可选用非防爆型；罩棚下处于非爆炸危险区域的灯具，应选用防护等级不低于IP44级的照明灯具。', '396', '《汽车加油加气站设计与施工规范》(GB50156-2012)5.0.8、11.4');
INSERT INTO `t_item_tbl` VALUES ('1330', '落地式配电箱的底部宜抬高，高出地面的高度室内不应低于50mm，室外不应低于200mm，其底座周围应采取封闭措施，并应能防止鼠、蛇等小动物进入箱内。', '396', '《低压配电设计规范》（GB50054-2011）4.2.1、4.3.7');
INSERT INTO `t_item_tbl` VALUES ('1331', '配电室的门、窗关闭应密合。', '396', '《低压配电设计规范》（GB50054-2011）4.2.1、4.3.8');
INSERT INTO `t_item_tbl` VALUES ('1348', '1、生产布局合理，有害作业与无害作业分开；2、工作场所与生活场所分开，工作场所不得住人；3、有与职业病防治工作相适应的有效防护设施；4、职业病危害因素的强度或者浓度符合国家职业卫生标准；5、有配套的更衣间、洗浴间、孕妇休息间等卫生设施；6、设备、工具、用具等设施符合保护劳动者生理、心理健康的要求；7、法律、法规、规章和国家职业卫生标准的其他规定。', '397', '《工作场所职业卫生监督管理规定》（安监总局令第47号）第十二条');
INSERT INTO `t_item_tbl` VALUES ('1349', '应当在醒目位置设置公告栏，公布有关职业病防治的规章制度、操作规程、职业病危害事故应急救援措施和工作场所职业病危害因素检测结果。对产生严重职业病危害的作业岗位，应当在其醒目位置，设置警示标识和中文警示说明。', '398', '《中华人民共和国职业病防治法》第二十五条');
INSERT INTO `t_item_tbl` VALUES ('1350', '加油加气站内可设置小型内燃发电组、内燃机，内燃机的排烟管口应安装阻火器，排烟管口至爆炸危险区域边界的水平距离应符合以下规定：1、排烟口高出地面4.5m以下时，不应小于5m；2、排烟口高出地面4.5m及以上时，不应小于3m。　　', '399', '《汽车加油加气站设计与施工规范》（GB50156-2012）第11.1.4条');
INSERT INTO `t_item_tbl` VALUES ('1351', '发电、供电过程中必须有专人监护', '399', '《加油站作业安全规范》（AQ3010-2007）8.6.11');
INSERT INTO `t_item_tbl` VALUES ('1352', '（一）如有下列情况之一的，应安装防护罩、盖或栏：\n1、以操作人员站立平面为基准，高度在2米以下的外露传动部位；\n2、旋转的键、销、楔等突出大于3毫米的部位；\n3、产生切屑、磨屑、冷却液等飞溅，可能触及人体或造成设备与环境污染的部位\n4、产生射线或弧光的部位\n5、伸入通道的超长工件\n6、超出设备后端300毫米以上的工件\n7、容易伤人的设备往复运动部位\n8、悬挂输送装置跨越通道的下部\n9、高于地面0.7米的操作平台\n（二）防护罩、盖、栏应满足以下要求：\n1、有足够强度和刚度，无明显锈蚀和变形\n2、安装牢固\n', '400', '《工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1353', '（一）各类行程限位装置应安全可靠，运动机构的行程限位应在规定的范围内\r\n（二）凡限制运动件范围的，应装设限位开关，且固定牢靠有效\r\n（三）进入筒型储库、磨机、破碎机、篦冷机、各种焙烧窑等有限空间作业时，采取有效的防止电气设备意外启动、热气涌入等隔离防护措施。\r\n（四）玻璃窑炉、玻璃锡槽，水冷、风冷保护系统存在漏水、漏气，设置监测报警装置。', '401', '《工贸企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1354', '（一)带式传送设备：夹伤最易产生在传送带及传动轮的结合部位，传动轮是最主要的危险部位，因此，要求对其封闭，或者设有安全装置。在皮带转向、加料及设有导向轮的地点，也有导致夹伤的风险，因此，也应采取类似的安全措施。在传输带上，也可以使用全封闭或用绊网来切断原料供应这类形式的安全装置。对于长的传送带，在适当的位置，应设置安全通道；\n（二)滚轴传输：滚轴可以是有动力的，也可以是无动力的。对于动力驱动形式，在动力驱动轴处要有安全装置。在传送带上方需要通过时，应提供专门的通道设施；\n（三)齿轮传输：任何时候都要求有安全装置，只有在驱动器锁定时，才能进行保养和维修。（四）危险部位加安全警示标识\n', '402', '《机械设备的安全管理》');
INSERT INTO `t_item_tbl` VALUES ('1355', '严禁站在砂石料上部从事下料作业，防止发生坠落被砂石料掩埋事故', '403', '《机械设备的安全管理》');
INSERT INTO `t_item_tbl` VALUES ('1356', '大型喷漆室除应配置排风系统外，还应配置送风系统，冬季送风温度不应低于12℃。', '404', '《涂装作业安全规程喷漆室安全技术规定》（GB14444－2006）');
INSERT INTO `t_item_tbl` VALUES ('1357', '在连续喷漆作业中的大型喷漆室、流平室、调漆室应设自动灭火系统。', '404', '《涂装作业安全规程喷漆室安全技术规定》（GB14444－2006）');
INSERT INTO `t_item_tbl` VALUES ('1358', '喷漆室所在建筑物应配置灭火器材', '404', '《涂装作业安全规程喷漆室安全技术规定》（GB14444－2006）');
INSERT INTO `t_item_tbl` VALUES ('1359', '大型喷漆室宜设置多点可燃气体检测报警仪，其报警浓度下限值应调整在所监测的可燃气体浓度(体积)爆炸极限下限的25％。', '404', '《涂装作业安全规程喷漆室安全技术规定》（GB14444－2006）');
INSERT INTO `t_item_tbl` VALUES ('1360', '喷漆室不应设置电气设备，照明应使用防爆灯，临时灯应使用安全电压的防爆灯', '404', '《涂装作业安全规程喷漆室安全技术规定》（GB14444－2006）');
INSERT INTO `t_item_tbl` VALUES ('1361', '作业前：（一）作业前，应先启动搅拌机空载运转。应确认搅拌筒或叶片旋转方向与筒体上箭头所示方向一致。对反转出料的搅拌机，应使搅拌筒正、反转运转数分钟，并应无冲击抖动现象和异常噪声。\r\n（二）作业前，应进行料斗提升试验，应观察并确认离合器、制动器灵活可靠。\r\n（三）应检查并校正供水系统的指示水量与实际水量的一致性；当误差超过2％时，应检查管路的漏水点，或应校正节流阀。\r\n（四）应检查骨料规格并应与搅拌机性能相符，超出许可范围的不得使用。', '405', '《混凝土搅拌机》（JG/T62-1999）');
INSERT INTO `t_item_tbl` VALUES ('1365', '作业中：（一）搅拌机启动后，应使搅拌筒达到正常转速后进行上料。上料时应及时加水。每次加入的拌合料不得超过搅拌机的额定容量并应减少物料粘罐现象，加料的次序应为石子-水泥-砂子或砂子-水泥-石子。\n（二）进料时，严禁将头或手伸入料斗与机架之间。运转中，严禁用手或工具伸入搅拌筒内扒料、出料。\n（三）搅拌机作业中，当料斗升起时，严禁任何人在料斗下停留或通过；当需要在料斗下检修或清理料坑时，应将料斗提升后用铁链或插入销锁住。\n（四）向搅拌筒内加料应在运转中进行，添加新料应先将搅拌筒内原有的混凝土全部卸出后方可进行。\n', '405', '《混凝土搅拌机》（JG/T62-1999）');
INSERT INTO `t_item_tbl` VALUES ('1366', '作业后：（一）作业后，应对搅拌机进行全面清理，做好润滑保养，切断电源锁好箱门；当操作人员需进入筒内时，应固定好料斗，切断电源或卸下熔断器，锁好开关箱，挂上\"禁止合闸\"标牌，并应有专人在外监护。（二）作业后，应将料斗降落到坑底，当需升起时，应用链条或插销扣牢。（三）冬季作业后，应将水泵、放水开关、量水器中的积水排尽。（四）搅拌机在场内移动或远距离运输时，应将进料斗提升到上止点，用保险铁链或插销锁住。', '405', '《混凝土搅拌机》（JG/T62-1999）');
INSERT INTO `t_item_tbl` VALUES ('1367', '（一）操作平台应搭设牢固，施工人员在上面操作时必须系好安全带。\n（二）使用振捣棒的作业人员，应穿胶鞋，戴绝缘手套，使用带有漏电保护的开关箱。\n（三）夜间作业，应有足够的照明设备，并防止眩光。\n（四）清管时，管端应设置挡板或安全罩，严禁在管端站立人员，以防喷射伤人。\n（五）拆除泵管接头时,应先进行多次反抽，卸除管道内混凝土压力,以防混凝土喷出伤人。\n', '406', '《振捣棒操作标准》（GB3787-93）');
INSERT INTO `t_item_tbl` VALUES ('1368', '(一）操作旋转机械最忌戴手套，如车床、钻床等要扣紧袖口，不要戴围巾等，在操作旋转机械时一定要做到工作服的“三紧”，即：袖口紧、下摆紧、裤脚紧；不要戴手套、围巾等（二）工作中戴好护目镜', '407', '《劳动防护用品配备标准》');
INSERT INTO `t_item_tbl` VALUES ('1369', '(1)破坏式剪切销。这种带剪切销的保险机构是在连接两个轴的部位有两个圆盘，两圆盘靠孔中用销子连接，销子强度小于传动系统中最弱的零件，当设备超负荷传动时，销子首先切断，使被动轴停止转动。\n(2)自动保险机构。在机床传动中，摩擦离合器、齿爪式离合器、安全阀、脱落蜗杆等已成为机床本身的传动部件。一般情况下，这些机构随机床正常工作，当由于某种原因机床出现过载时，这些保险机构就会由于摩擦力不足而打滑，使动力中断传输，被动轴停止转动。当负荷恢复正常时，能自动复位。\n', '408', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('1370', '在大批量生产中多使用气动或液压夹具，由于工作紧张，往往发生夹具未夹紧工件即开车加工，以致工件飞出而造成事故。', '409', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('1371', '自动停车机构的主要作用是在机床受到阻碍或在预定位置碰到挡块时，有关部件能自动停止运动。', '410', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('1372', '互锁机构是一种更完善的安全装置，能直接防止操作失误而引起的伤害。', '411', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('1373', '（一）可能触及到的外露部分应尽量平整、光滑，不应有可能导致刺伤或划伤人员的尖棱、尖角、凸出部分和开口。（二）溜板纵向运动应设有可靠的限位装置。\n（三）机床飞轮应采取切实可靠的防护措施，确保在运动中不会因其松脱、甩出及缠绕而出现危险。\n（四）外露带和带轮应设置防护罩。应在遮蔽危险部位的防护罩内表面、挂轮箱内表面涂上安全色，以提醒操作、调整和维护人员注意危险的存在。\n（五）因位置原因，在运动中可能造成缠绕危险的花键轴、丝杠应采取切实可靠的安全防护措施。\n（六）控制系统不应有导致危险情况的出现。\n（七）电力偶然中断，不应给人员造成危险；电力重新恢复后，机床不应自行起动。\n（八）按钮、指示灯、开关等控制器件的位置应确保操作时不会引起误操作和附加的危险。\n（九）控制器件应清晰可辨，易于区别，必要时应设置表示其功能或用途的标志。\n（十）在每个“起动”控制器件附近应设置一个“停止”控制器件。在每个操作位置应设置一个紧急停止控制器件，紧急停止控制器件的动作不应影响保护操作者或机床装置的功能。\n（十二）冷却系统应能保证操作者的人身安全。\n（十三）机床应能有效地回收冷却液，应设置防止冷却液飞溅的防护挡板。（十四）应设置防止切屑飞溅的防护挡板。\n（十五）冷却液喷嘴应能方便、安全、可靠地固定在所需位置上。\n', '412', '《机械安全安全标准的起草与表述规则》（GB／T16755—1997）');
INSERT INTO `t_item_tbl` VALUES ('1374', '离合器要求：（一）刚性离合器的转键、键柄和直键无裂纹\n（二）操纵器的杆件、销钉或弹簧无裂纹、折断\n（三）离合器安装紧固，动作可靠，没有连冲\n（四）滑块从上死点至上死点25毫米处行程范围内需要制动时，离合器应对滑块制动\n', '413', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('1375', '制动器要求：（一）制动器与离合器必须相互协调与连锁，开机时制动器先松闸，离合器稍滞后再结合\n（二）连锁应准确、紧固，工作稳定、协调\n（三）行程限位装置、控制装置正确，动作灵敏有效\n', '413', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('1376', '紧停装置：（一）大型冲压机械前后都应安装急停按钮，位置在人手可迅速触及的地方\n（二）剪板机剪板长度大于2500毫米时，两侧应安装紧停装置\n（三）紧停装置应有红色标记\n', '413', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('1377', '传动部位：距作业平台2米以上旋转部位应装设防护罩或防护栏', '413', '《冷冲压安全规程》5.2.3.14（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('1378', '脚踏开关：（一）外露在工作台面的脚踏开关，只要有物体撞压可能的，应在开关上部和两侧装设防护罩\n（二）剪板机脚踏杠在整个长度上应装设防护罩\n（三）脚踏开关的踏面应用防滑的花纹钢板制作\n（四）防护罩有足够的强度，无棱角毛边，便于开关检修\n', '413', '《机械设备防护罩安全要求》（GB8196-87）');
INSERT INTO `t_item_tbl` VALUES ('1379', '冲、剪、压、钻机械的金属结构件、电机、变压器、穿线金属管等都应有接地保护', '413', '《用电安全导则》');
INSERT INTO `t_item_tbl` VALUES ('1380', '安全防护附件：（一）必须采取各种安全措施，确保在滑块下落时，操作者任何部位不可能进入作业危险区\n（二）使用双按钮之间的内侧距离必须大于25厘米\n', '413', '《冷冲压安全规程》（GB13887—2008）');
INSERT INTO `t_item_tbl` VALUES ('1381', '位置和朝向：（一）安装地点应保证人员和设备安全；（二）朝向不得对着通道、门口和操作岗位。\n防护罩应符合国家安全标准\n挡屑板应有足够强度\n砂轮无裂纹、无破损\n法兰盘使用软垫连接\n托架安装牢固可靠\n运行状况：运行平稳，砂轮磨损量符合要求\n接地可靠，控制电器符合规定\n', '414', '《麿削机械安全规程》（GB4678-84）');
INSERT INTO `t_item_tbl` VALUES ('1382', '裸露接线板防触电保护：电源线、焊接电缆与焊机连接处的裸露接线板，应采取安全防护罩或防护板隔离，以防止人员或金属物体接触', '415', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('1383', '焊机外壳防漏电保护：（一）焊机外壳必须接地或接零保护，接地或接零装置连接良好，并定期检查\n（二）严禁使用易燃易爆气体管道作为接地装置\n（三）在有接地或接零装置的焊件上进行弧焊操作，或焊接与地面密切连接的焊件时，应注意避免焊机和工件的双重接地\n', '415', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('1384', '绝缘电阻：（一）焊接变压器一、二次绕组、绕组与外壳之间的绝缘电阻值不小于1兆欧\n（二）每半年应对焊机绝缘电阻遥测一次，且记录完整\n', '415', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('1385', '（一）焊机一次侧电源线长度不超过3米\n（二）一次线不得在地面拖拽使用，不得在地面跨越通道使用\n（三）电焊机应接在专用开关上使用，不得与其它设备共用一个开关\n（四）在高处或金属容器内工作时应设专人监护\n', '415', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('1386', '（一）焊机二次线必须连接紧固，无松动\n（二）二次线接头不超过3个\n（三）二次线截面选用正确，避免长期过载使用造成绝缘老化\n（四）严禁使用厂房金属结构、管道、轨道等作为焊接二次回路使用\n', '415', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('1387', '焊钳夹紧力好，绝缘良好，手柄隔热层完整，焊钳与导线连接可靠', '415', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('1388', '焊机使用场所：（一）焊机应安放在通风、干燥、无碰撞、无剧烈震动、无高温、无易燃品存在的地方\n（二）在室外或特殊环境下使用，应采取防护措施保证其正常使用。雨、雪、雾天，无防护措施不准作业\n（三）焊机使用场所应清洁，无严重粉尘\n（四）操作人员应穿绝缘鞋、防护服、面罩（护镜）、电焊手套\n', '415', '《电弧焊机通用技术条件》（GB8118-87）');
INSERT INTO `t_item_tbl` VALUES ('1389', '旋转部位防护要求：（一）炊事机械的传动部位的皮带轮、齿轮、链轮与链条、联轴器等，应加防护罩或防护盖，不便安装的应加设防护栏\n（二）防护罩、盖和栏完好可靠，强度符合要求\n', '416', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('1390', '电气部分要求：（一）炊事机械的金属外壳、电动机外壳的PE线均应可靠。\n（二）电源线最好穿管敷设，若采用橡胶电缆时，应敷设在无浸泡、无高温和无压砸的沿墙壁面。\n（三）线路不得有接头、破损、老化或过载。\n（四）随机配备的电气箱柜应符合要求。\n', '416', '《家用及类似用途电器的安全》（IEC60335-2-64-2002）');
INSERT INTO `t_item_tbl` VALUES ('1392', '控制开关：（一）炊事机械电源控制开关应单机单设，不得几台设备共用一个开关或用距离较远的闸刀控制\n（二）有尘、水可能进入的按钮开关应有防护装置，且不妨碍操作\n', '416', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('1393', '搅拌、合面机：（一）应加盖密封，容器盖应采用规定的材料制作\n（二）必须安装机盖联锁安全装置，联锁开关应固定在机器本体上，启盖应即刻断电\n', '416', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('1394', '绞肉、绞菜机：（一）加料口护手装置应保证操作人员手指不能触及刀口或螺旋部位，加料口较大的其高度应适当增加，否则应设小口径加料托盘\n（二）护手装置、加高的加料口和加料托盘应用耐腐蚀的材料制作，具有足够的强度，与加料口连接固定，应能翻转清洗\n（三）应配备送料的辅助工具\n', '416', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('1395', '压面机：（一）轧辊应便于装拆，调整灵活、定位可靠\n（二）加料处应有防护装置，防止手指伸入\n（三）备有专用刮面板，不得用手推、刮面粉\n', '416', '《家用及类似用途电器的安全》');
INSERT INTO `t_item_tbl` VALUES ('1396', '破碎设备（包括颚式破碎机、锤式破碎机、立轴式破碎机、辊式破碎机等）：\n1、破碎设备周围应留有足够的操作和维修空间，操作位置应有良好的通道及可视性，设备检修人孔门坚固可靠，传动皮带完好。\n2、设备应有总停开关及相应的急停和安全装置，并定期进行检查。\n3、设备的调整、维护、修理和清洁工作必须在停机时进行。\n4、机械传动部位安全防护装置、安全保险装置齐全可靠；\n5、通道、梯台、护网（栏）符合标准规定，所有启动和停止装置应有明显标志并易于接近，并有必要的预警信号；\n6、给料或转运料斗及料槽开口位置设防护装置，在无安全措施的条件下严禁人工疏通，严禁输送设施设备运行时进行维护调整、人体接近或触摸运转的部位。\n7、设备液压润滑系统应符合要求，系统压力不得超过最大允许压力。\n8、熟料破碎机运转时加油的，应使用油管延伸至安全地带内才可加油。\n', '417', '水泥企业安全生产标准化评定标准6.2');
INSERT INTO `t_item_tbl` VALUES ('1397', '粉磨设备（包括立式辊磨机、管式球磨机等）：\r\n1、煤磨作业现场必须设置禁止烟火警示标志，安装防静电装置；动火作业前必须申请同意，动火现场必须备好灭火器；\r\n2、煤磨系统必须配备足够的灭火设施和器材（灭火器、消防栓等）；\r\n3、有煤磨系统防火、防爆专项应急预案，防止煤粉制备系统发生爆燃事故。\r\n4、进入磨体内检查必须断开主电源，挂警示牌，使用安全电压照明。\r\n5、磨机机械传动部位防护装置齐全可靠，磨机体周围防护栏警示牌齐全。\r\n6、磨体两侧护栏应齐全，严禁人员从运转磨底穿越靠近磨体。\r\n7、磨机筒体各部件螺丝齐全，牢固可靠，衬板完整无断裂，不漏灰。\r\n8、磨机轴承应根据气候变化及时更换相适应的润滑油，并记录备案。\r\n9、轴承瓦润滑油，冷却水管完好畅通，油泵安装平稳牢固，油管、水管、油箱定期清洗。\r\n10、压力表、温度表保持清洁，安全可靠，并严格遵守在有效期内使用。\r\n11、水泥工厂煤磨袋式收尘器（或煤粉仓）设置温度和一氧化碳监测，或设置气体灭火装置。', '418', '水泥企业安全生产标准化评定标准6.2');
INSERT INTO `t_item_tbl` VALUES ('1398', '烘干设备（回转式烘干机、立筒预热器、悬浮预热器与分解炉等）：\n1、烘干设备周围应设置相应可靠的隔热和防护设施。\n2、烘干设备周围不允许堆放易燃易爆或危险化学品。\n3、应有预热器清堵的专项安全操作规程或作业指导书。\n4、预热器平台、构件、护栏要求完整牢固，检查孔盖牢固，翻板阀灵活好用；预热器周围平台上严禁堆放易燃易爆物品。\n5、悬挂设备下及吊装孔附近应有隔离安全防护等安全设施。\n6、检修状态预热器的翻板阀必须锁紧。\n', '419', '水泥企业安全生产标准化评定标准6.2');
INSERT INTO `t_item_tbl` VALUES ('1399', '回转窑：\r\n1、煤粉输送管路完好无泄漏；燃烧器完好无泄漏，调整机构灵活好用。\r\n2、回转窑窑头、窑尾观察门（盖）完好，平台护栏、测量仪表仪器完好，密封装置完好无脱落。\r\n3、回转窑筒体无阻碍、碰撞物体，检修人孔门固定牢固；筒体冷却装置完好。\r\n4、系统联锁、控制完好，空气炮等气动元件、压力容器工作正常。\r\n5、回转窑传动装置中的高转速联轴器、开式齿轮等传动部件应设置防护罩；冷却水、润滑油供应正常。托轮、挡轮测控仪表完好。\r\n6、回转窑传动装置中，应设置当辅助传动装置启动时能切断主电动机电源的联锁装置，同时辅助传动装置必须另设应急独立动力源。\r\n7、回转窑辅助传动装置必须安装制动装置，以便在使用中切断辅助传动电动机，防止回转窑自行转动。\r\n8、应建立回转窑专项检查制度，其中规定检查的内容以及频次，并定期检查、做好相关运行记录。\r\n9、停窑维护要有相应的安全方案，并严格执行。\r\n10、入窑作业要有相应的安全操作规程或作业指导书，并严格按规程或指导书操作。\r\n11、燃气窑炉设置燃气低压警报器和快速切断阀，或易燃易爆气体聚集区域设置监测报警装置。', '420', '水泥企业安全生产标准化评定标准6.2');
INSERT INTO `t_item_tbl` VALUES ('1400', '应急照明设施并确保完好、有效。', '421', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('1401', '按照国家标准、行业标准配置消防设施、器材，设置消防安全标志，并定期组织检验、维修，确保完好有效。', '422', '《中华人民共和国消防法》第16条');
INSERT INTO `t_item_tbl` VALUES ('1402', '变配电室变压器、高压开关柜、低压开关柜操作面地面应铺设绝缘胶垫。', '423', '参考《变配电室安全管理规范》第5.3条');
INSERT INTO `t_item_tbl` VALUES ('1403', '变配电室出入口应设置高度不低于400mm的挡板。', '424', '参考《变配电室安全管理规范》5.6条');
INSERT INTO `t_item_tbl` VALUES ('1404', '应急照明灯具和疏散指示标志灯的备用充电电源的放电时间不低于20min。', '425', '参考《变配电室安全管理规范》第5.14.1条');
INSERT INTO `t_item_tbl` VALUES ('1405', '通往室外的门应向外开。设备间与附属房间之间的门应向附属房间方向开。高压间与低压间之间的门，应向低压间方向开。配电装置室的中间门应采用双向开启门。', '426', '参考《变配电室安全管理规范》第5.8.3条');
INSERT INTO `t_item_tbl` VALUES ('1406', '通往室外的窗应装有纱窗。', '426', '参考《变配电室安全管理规范》5.9.3');
INSERT INTO `t_item_tbl` VALUES ('1407', '各种安全用具首次使用前应进行试验或检验并定期复检，合格后方可使用。安全用具不应超期使用。', '427', '参考《变配电室安全管理规范》第5.18.3条');
INSERT INTO `t_item_tbl` VALUES ('1408', '电气绝缘安全用具中，绝缘拉杆、绝缘挡板、绝缘罩、绝缘夹钳的试验绝缘周期为每年一次，高压验电器、绝缘手套、绝缘靴、核相器电阻管、绝缘绳的试验绝缘周期为每半年一次。', '427', '参考《变配电室安全管理规范》第5.18.3.1条');
INSERT INTO `t_item_tbl` VALUES ('1409', '具有架空进出线的变配电室应备有登高工具，如：（安全带、脚扣、升降板、紧线器、竹（木）梯、尼龙绳等），除每年试验检查一次外，每次使用前均应进行检查。', '427', '参考《变配电室安全管理规范》第5.18.3.2条');
INSERT INTO `t_item_tbl` VALUES ('1410', '安全用具使用完毕后应妥善保管，存放在干燥通风的处所。并应符合下列要求：\n（一)绝缘拉杆应悬挂或架在支架上，不应与墙接触；\n（二)绝缘手套、绝缘靴应存放在密闭的橱内，并与其他工具仪表分别存放，绝缘靴不应代替一般雨靴使用，绝缘工具不合格的不得存放在工作现场；\n(三)绝缘垫和绝缘台应经常保持清洁、无损伤；\n（四)高压验电器应存放在防潮的匣内，并将匣放在干燥的地方；\n（五)安全用具不允许当作其它工具使用；\n（六)安全用具不合格的不得存放在工作现场。\n', '427', '参考《变配电室安全管理规范》第5.18.5条');
INSERT INTO `t_item_tbl` VALUES ('1411', '禁止类：禁止合闸、禁止攀登、高压危险；警告类：止步、高压危险；准许类：从此上下、在此工作', '428', '参考《变配电室安全管理规范》第5.16.1条');
INSERT INTO `t_item_tbl` VALUES ('1412', '变配电室内环境整洁，场地平整，设备间不应存放与运行无关的闲散器材和私人物品，禁止无关人员进入场地；', '429', '参考《变配电室安全管理规范》第6.8.1条');
INSERT INTO `t_item_tbl` VALUES ('1413', '主控制室、高压配电室不应带入食物及储放粮食，值班室不应设置、使用寝具、灶具，并应有防止小动物的安全措施；', '429', '参考《变配电室安全管理规范》第6.8.4条');
INSERT INTO `t_item_tbl` VALUES ('1414', '非变配电室从业人员因工作需要进入变配电室时，应经值班人员许可，并办理登记手续。当需要进入设备区时，应有值班人员监护', '430', '参考《变配电室安全管理规范》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('1415', '柜、屏、台、箱、盘的金属框架及基础型钢必须接地（PE）或接零（PEN）可靠；装有电器的可开启门与框架的接地端子间应用裸线编织铜线连接，作好标识。', '431', '参考《变配电室安全管理规范》第7.3条');
INSERT INTO `t_item_tbl` VALUES ('1416', '箱盘内开关灵活可靠。带有漏电保护的回路，漏电保护装置动作电流和动作时间按设计要求。', '432', '《配电柜、成套控制柜（屏、台）和动力、照明配电箱（盘）安装施工工艺标准》第4.2.7.2条');
INSERT INTO `t_item_tbl` VALUES ('1417', '剩余电流保护装置投入运行后，运行管理单位应建立相应的管理制度，并建立动作记录。', '432', '《剩余电流动作保护装置安装和运行》第7.1条');
INSERT INTO `t_item_tbl` VALUES ('1418', '剩余电流保护装置投入运行后，必须定期操作实验按钮，检查其动作特性是否正常。雷击活动期和用电高峰期应增加实验次数。', '432', '《剩余电流动作保护装置安装和运行》第7.2条');
INSERT INTO `t_item_tbl` VALUES ('1419', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。', '433', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('1420', '直敷布线可用于正常环境的屋内场所，应采用护套绝缘导线。当导线垂直敷设至地面低于1．8m时，应穿管保护。', '434', '《低压配电设计规范》第5.2.1条');
INSERT INTO `t_item_tbl` VALUES ('1421', '（一）设备的金属外壳应采取防漏电保护接地\n（二）PE线若明设时，应选用不小于4平方毫米的铜芯线，不得使用铝芯线\n（三）PE线若随穿线管接入设备本体时，应选用不小于2.5平方毫米的铜芯线或不小于4平方毫米的铝芯线\n（四）PE线不得搭接或串接，接线规范、接触可靠\n（五）明设的应沿管道或设备外壳敷设，暗设的在接线处外部应有接地标志\n（六）PE线接线间不得涂漆或加绝缘垫\n', '435', 'GB19517-2004《国家电气设备安全技术规范》第2.2.4条');
INSERT INTO `t_item_tbl` VALUES ('1422', '应采用橡皮护套绝缘软线。与电源联接，应采用开关、插头座。严禁用导线直接插入插座，或挂在电源线上使用。', '436', '《电气安全工作规程》第88条');
INSERT INTO `t_item_tbl` VALUES ('1423', '工具必须由专职人员每年至少检查一次，并记录', '437', '《手持式电动工具的管理、使用、检查和维修安全技术规程》第6.3条');
INSERT INTO `t_item_tbl` VALUES ('1424', '在使用移动式的I类设备时，应先确认其金属外壳或构架已可靠接地，使用带保护接地极的插座，同时宜装设漏电保护器，禁止使用无保护线插头插座。', '438', '《用电安全导则》第4.16条');
INSERT INTO `t_item_tbl` VALUES ('1425', '露天使用的用电设备、配电装置应采取防雨、防雪、防雾和防尘的措施。', '439', '《用电安全导则》第4.25条');
INSERT INTO `t_item_tbl` VALUES ('1426', '插座回路均应设置剩余电流动作保护装置。宜装设漏电保护器，禁止使用无保护线插头插座。', '440', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('1427', '用电设备和电气线路的周围应留有足够的安全通道和工作空间。电气装置附近不应堆放易燃、易爆和腐蚀性物品。禁止在架空线上放置或悬挂物品。不应从带插座灯头上引接电源供给电器。', '441', '《用电安全导则》第4.8条');
INSERT INTO `t_item_tbl` VALUES ('1428', '临时线路使用必须经过审批，一般使用期限为过15天，最长不超过一个月', '442', '《用电安全导则》第4.2条');
INSERT INTO `t_item_tbl` VALUES ('1429', '临时用电的电气设备，必须安装剩余电流保护装置。', '443', '《剩余电流动作保护装置安装和运行》第4．5条');
INSERT INTO `t_item_tbl` VALUES ('1430', '线路敷设应符合电气装置设置及安装规范，室内不低于2.5m，室外不低于4.5m，道路上方不低于6m。', '444', '《电气安全管理规程》第20条（1）');
INSERT INTO `t_item_tbl` VALUES ('1431', '（一）手提式照明使用36伏（二）危险环境携带式电动工具使用36伏（三）金属容器内使用24伏(四)室内灯具离地面低于2.4m，手持照明灯具，一般潮湿作业场所（地下室、潮湿室内、潮湿楼梯、隧道、人防工程以及有高温、导电灰尘等）的照明，电源电压应不大于36V。\n（五）在潮湿和易触及带电体场所的照明电源电压，应不大于24V。\n（六）在特别潮湿的场所，锅炉或金属容器内，导电良好的地面使用手持照明灯具等，照明电源电压不得大于12V。\n', '445', '《安全电压标准》');
INSERT INTO `t_item_tbl` VALUES ('1432', '（一）危险化学品应根据分类，分区、分库存放\n（二）爆炸物品、自身能形成爆炸的危险品、腐蚀性物品应单独存放\n（三）相互接触或混合后能引起燃烧或爆炸的物品不能同库存放\n（四）灭火方式不同的物质不得同库存放\n（五）遇火、遇热、遇潮能引起燃烧、爆炸或发生化学反应，产生有毒气体的化学危险品不得露天或在潮湿、积水的建筑中存放\n', '446', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('1433', '开关、灯具、线路、用电设施均应符合防爆要求', '447', '《爆炸危险场所安全规定》第17条');
INSERT INTO `t_item_tbl` VALUES ('1434', '作业现场不得储存、堆放其它易燃易爆物品，易燃易爆废料及时从现场清理', '448', 'GB14444-2006《喷漆室安全技术规定》');
INSERT INTO `t_item_tbl` VALUES ('1435', '物品堆放整齐、稳固，高度不超过2米，易挥发可燃物品桶盖随时拧紧盖严', '448', 'GB/3723-1999');
INSERT INTO `t_item_tbl` VALUES ('1436', '易燃易爆用品现场存量最好为1个班的用量', '448', 'GB/3723-1999');
INSERT INTO `t_item_tbl` VALUES ('1437', '通道保持畅通，出、入口处不得堆放易燃易爆物品', '448', 'GB/3723-1999');
INSERT INTO `t_item_tbl` VALUES ('1438', '危险化学品储存及有限空间应保持良好的通风', '448', 'GB/3723-1999');
INSERT INTO `t_item_tbl` VALUES ('1439', '作业区域应采取自然通风或机械通风，保持通风良好', '448', '《喷漆室安全技术规定》第5.3条（GB14444-2006）');
INSERT INTO `t_item_tbl` VALUES ('1440', '不得使易燃易爆和有毒有害气体在现场积存', '448', '《涂装作业安全规程涂漆工艺安全及其通风净化》第15.5条（GB6514-1995）');
INSERT INTO `t_item_tbl` VALUES ('1441', '封闭式易燃易爆物品使用场所，必须安装自动报警或抑爆系统', '448', '《涂装作业安全规程涂层烘干室安全技术规定》第4.4.1.1条');
INSERT INTO `t_item_tbl` VALUES ('1442', '易燃易爆作业场所区域内所有金属部件、构件都应可靠接地', '448', '《涂装作业安全规程涂屋烘干室安全技术规定》第4.1.3.2条');
INSERT INTO `t_item_tbl` VALUES ('1443', '易燃易爆作业场所不得从事动火作业，严禁人员吸烟', '448', '《喷漆室安全技术规定》第6.1.5条');
INSERT INTO `t_item_tbl` VALUES ('1444', '专人管理，并有防潮防雨淋设施', '448', '《危险化学品安全管理条例》第16条');
INSERT INTO `t_item_tbl` VALUES ('1445', '设置安全标志', '449', '《安全标志》');
INSERT INTO `t_item_tbl` VALUES ('1446', '车间、库房等作业场所应设置防静电装置', '450', '《危险化学品安全管理条例条》第16条');
INSERT INTO `t_item_tbl` VALUES ('1447', '应优先采用职业危害小的技术和工艺，积极使用无毒或低毒原（辅）料，以无毒代替有毒、以低毒代替高毒。', '451', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1448', '应优先采用自动化高、噪声低的生产设备，加强密闭，减少职业危害。', '452', '《中华人民共和国职业病防治法》第20条');
INSERT INTO `t_item_tbl` VALUES ('1449', '应根据生产工艺和职业危害特性，设置通风、排毒、除尘等职业危害防护设施，使作业场所职业危害因素的浓度和强度达到GBZ2.1/2.2《工作场所有害因素职业接触限值》的要求。', '453', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1450', '应对生产过程中产生的粉尘、有毒有害物质采取通风措施，降低作业场所的粉尘、有毒有害物质浓度。如锯、刨、铣、磨床、砂轮机等生产设备的产尘部位，应设局部排风除尘装置；喷漆作业；焊接作业；除锈或清除旧漆作业；粉末静电喷涂作业等', '454', '《中华人民共和国职业病防治法》第24条《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1451', '容易造成职业危害的工序应隔离设置。', '455', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1452', '应当在醒目位置设置公告栏，公布有关职业危害防治的规章制度、操作规程、职业危害事故应急救援措施和工作场所职业危害因素检测结果。', '456', '《中华人民共和国职业病防治法》第25条');
INSERT INTO `t_item_tbl` VALUES ('1453', '应在可产生职业危害的工作场所和设备上，按GBZ158《工作场所职业病危害警示标识》和GBZ/T203《高度物品作业岗位职业病危害告知规范》的要求设置职业危害警示标识。', '457', '《中华人民共和国职业病防治法》第22条');
INSERT INTO `t_item_tbl` VALUES ('1454', '职业危害的作业场所不得住人。劳动者不得在毒物作业区饮水、进食和休息。', '458', '《使用有毒物品作业场所劳动保护条例》第11条');
INSERT INTO `t_item_tbl` VALUES ('1455', '企业外包、分包等劳务合作时，不得转嫁职业病危害的作业给不具备职业病防护条件的单位和个人。加强劳务派遣用工和临时用工的职业卫生管理，落实劳动合同告知、职业健康监护与个体防护用品发放等措施。', '459', '《中华人民共和国职业病防治法》第32条');
INSERT INTO `t_item_tbl` VALUES ('1456', '（一）厂区内实行定置管理：厂区有定置图，对各类厂房建筑、物料堆放点、交通道路等应注明\n（二）厂区内按照定置管理的要求实现定置摆放，厂区内无杂物、无图物不符等状况\n（三）工业垃圾和生活垃圾分开定点存放，有防吹散、防污染措施\n（四）危险固体废料应有专门存放地点，存放点有防渗漏措施，且符合国家规定妥善处理\n（五）厂区大门开关灵活、方便，迅速，无卡死现象\n', '460', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1457', '（一）厂区双向主干道宽度不小于5米，单向主干道宽度不小于3米，且为环形\n（二）路面排水良好，路面平整，无台阶、无坑沟，盖板齐全\n（三）厂区主干道在平原地区应小于6度，山区应小于8度\n（四）厂区大门、危险路段的车速应限制在每小时5公里；人员稠密地段、下坡路、转弯路、交叉路口、装卸作业区限速每小时15公里，并设置限速标志\n（五）交叉路口若有视线盲区，应设立反光镜，反光镜无破损，角度和高度应便于观察道路盲区\n（六）厂区道路应有明显的人、车分流线，人行道宽度不小于1米，分流线清晰，宽度大于10厘米\n', '461', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1458', '（一）利用主干道堆放物品或作为停车位置，应有划线标志，通行部分宽度必须不小于5米\n（二）不得在转弯处或道路两侧堆放物品或停置车辆\n（三）道路施工应有警示牌或护栏，夜间要有红灯警示\n（四）厂区主干道占道率应小于道路长度的5%\n（五）不得将厂区主干道横向全部堵死\n', '462', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1459', '（一）照明灯布置合理，无照明盲区\n（二）照明灯具完好率达100%\n', '463', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1460', '（一）室外消防栓应合理配置，低压消防栓布置间距不超过120米，应沿道路两侧和路口设置，消防栓距路面边不超过2米，距建筑物外墙不小于5米\n（二）有明显的漆色标志，其1米范围内无障碍物\n（三）手提式灭火器的配置数量符合规定\n（四）所有消防器材应完好，在有效期限内使用\n', '464', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1461', '（一）搬运、安装、其他一些作业和高压线保持安全距离；（二）厂房的装修、维修、做防水等和高压线保持安全距离；（三）高压线下严禁码放材料和有建筑物。', '465', '《中华人民共和国电力行业标准》');
INSERT INTO `t_item_tbl` VALUES ('1462', '（一）车间通道根据生产要求，宽度标准如下：\n通道只行人通道，宽度不小于1米；\n通过电瓶车、叉车的通道，宽度不小于1.8米；\n通行汽车的车间通道，宽度应不小于3米；\n如有铁路线，车间通道，宽度应不小于5米\n（二）通道线应明显清晰，宽度不小于10厘米\n', '466', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1463', '（一）通道路面应平整，无台阶、无坑、沟\n（二）无积油、积水，无绊脚物\n', '467', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1464', '（一）任何堆放在通道标记线内和压住安全通道标记线的物件，都判定为占道\n（二）车间占道率应低于5%\n（三）不得有物件将通道横向全部堵死\n', '468', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1465', '（一）车行道上方的悬挂物高度不小于4米，人行道上方悬挂物高度不小于2.5米\n（二）悬挂应牢固可靠\n', '469', '《企业安全生产标准化基本规范》');
INSERT INTO `t_item_tbl` VALUES ('1466', '（一）仓库通道的宽度应满足以下要求：\n车行道宽度不小于3.5米\n人行道宽度不小于1米\n（二）路面平坦，无积油积水，无绊脚物\n（三）占道率应小于5%\n', '470', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1467', '（一）照明灯具完好率达100%\n（二）库房内不得使用移动式照明灯具\n', '471', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1468', '（一）库房内不得存在临时线\n（二）库房内敷设的电气线路必须，穿金属管或非燃硬塑料管\n（三）每个库房应当在库房外单独安装开关箱\n（四）甲、乙类物品和丙类液体库房的电气装置，必须安装防爆型电气装置，储存丙类固体物品的仓库，不得使用碘钨灯或超过60瓦以上的白炽灯等高温照明灯具\n', '472', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1469', '（一）防火制度、消防设施标志、防火标志齐全\n（二）按标准配备消防器材的数量和种类\n（三）防火通道畅通，无封死和堵塞现象\n', '473', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1470', '以下仓库应安装火灾报警装置：\n1、甲、乙、丙类物品储备库；\n2、专业性仓库\n3、大型物资仓库\n4、耐火等级为三、四级的仓库\n', '474', '');
INSERT INTO `t_item_tbl` VALUES ('1471', '（一）物品包装应牢固，不得有严重破损、残缺、变形、物品变质、分解等现象\n（二）甲、乙类物品的包装应严密，不得有跑、冒]滴、漏现象\n', '475', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('1472', '杜绝违章指挥、违章操作、违反劳动纪律的行为', '476', '国家安监总局15号令第44条');
INSERT INTO `t_item_tbl` VALUES ('1473', '液氨储罐应设置不燃烧体防护围堤或隔离设施、设备，并密实闭合。', '103', '');
INSERT INTO `t_item_tbl` VALUES ('1474', '在液氨场所应提供充分的局部排风和全面通风，严禁吸烟。', '104', '');
INSERT INTO `t_item_tbl` VALUES ('1475', '粉尘爆炸危险作业场所的厂房，必须满足相关规定要求，并通过消防建筑设计审核，严禁设置在违规多层厂房内。\n粉尘爆炸危险作业场所严禁设置在居民区，严禁与员工宿舍在同一座建筑物内，严禁存放汽油、煤油、苯等易燃物。粉尘爆炸危险场所设置在非框架结构的多层建构筑物内，或与居民区、员工宿舍、会议室等人员密集场所安全距离不足。', '105', '');
INSERT INTO `t_item_tbl` VALUES ('1476', '企业应根据本单位粉尘爆炸危险场所的具体情况，正确介定爆炸危险区域，根据不同的防爆等级，采用相应的防爆电器。电气设备的铭牌标识清楚，有防爆标志、防爆合格证号，外壳无裂缝、损伤，电机不得漏油。\r\n粉尘爆炸危险场所的20区使用防爆电气设备设施.', '106', '');
INSERT INTO `t_item_tbl` VALUES ('1479', '1.粉尘爆炸危险作业场所的厂房（建构筑物）应按规定设置防雷系统，并可靠接地。                                                             2.粉尘爆炸危险作业场所应对所有金属设备、装置外壳、金属管道、支架、构件、部件等，应按规定采取防静电接地。所有金属管道连接处(如法兰)应进行跨接。除尘系统静电接地必须独立设置。', '107', '');
INSERT INTO `t_item_tbl` VALUES ('1481', '处理有爆炸危险粉尘的除尘器、排风机的设置应符合下列规定：\r\n1）应与其它普通型的风机、除尘器分开设置；\r\n2）应按生产工艺分片（分区域）设置相对独立的除尘系统，并保证除尘系统有足够的风量，风管中不应有粉尘沉降。\r\n可燃性粉尘与可燃气体等易加剧爆炸危险的介质不能共用一套除尘系统，不同防火分区的除尘系统不能互联互通。', '108', '');
INSERT INTO `t_item_tbl` VALUES ('1486', '1.企业应当在粉尘爆炸危险场所和设施、设备上设置明显的安全警示标志，并对作业场所的安全风险进行公告。                                     2.在粉尘爆炸危险场所作业前，要先检查各类仪器、仪表、装置是否正常，并将检查情况如实记录；粉尘除、排尘系统的排风风机运行要先开启（运行10分钟）后停止（作业完全停止后运行15分钟）。', '109', '');
INSERT INTO `t_item_tbl` VALUES ('1488', '粉尘爆炸危险作业场所严禁明火作业，确需要明火作业遵守下列规定:\n1）停产撤离作业人员，有安全负责人批准并取得动火证；\n2）明火作业开始前，应清除明火作业场所的可燃粉尘并配备充足的灭火器材，金属粉尘应使用D类干粉灭火器；\n3）进行明火作业的区段应与其他区段分开或隔开；\n4）进行明火作业期间和作业完成后的冷却期间，不应有粉尘进入明火作业场所。', '110', '');
INSERT INTO `t_item_tbl` VALUES ('1489', '检维修作业：生产系统应完全停止、现场积尘清理干净后，方可进行检维修作业，应使用防爆工具。严禁非维修人员进入现场；检维修作业如涉及有限空间作业应严格遵守“先通风、再检测、后作业”的原则，确定符合安全要求后，方可进入。', '111', '');
INSERT INTO `t_item_tbl` VALUES ('1490', '作业场所清洁应当遵守以下规定：\n1）定期对生产场所残留的粉尘进行清理，清洁所有可能积累粉尘的场所，包括地面、墙角墙面、设备表面和横梁等，并及时对除尘器、吸排尘管道等设备的粉尘进行清理；\n2）应当采用不产生火花、静电、扬尘等方法进行粉尘清理。               2.应严格按照作业规程进行清扫作业，严禁使用压缩空气正压吹扫粉尘。', '112', '');
INSERT INTO `t_item_tbl` VALUES ('1492', '1.企业应为可燃粉尘作业人员配备防尘口罩、防静电手套、防静电鞋、防静电服或棉布工作服、防尘服、阻燃防护服等个体防护装备。禁止穿化纤类易产生静电的工装。                                                       2.企业配备防爆电气维护人员，具备防爆电气知识，负责防爆电气的日常检查和维护。                                                            3.粉尘环境爆炸危险区应按GB 500140规定要求配备专用灭火器和室外消防栓。', '113', '');
INSERT INTO `t_item_tbl` VALUES ('1495', '起重吊运是否有专人指挥。', '114', '');
INSERT INTO `t_item_tbl` VALUES ('1496', '吊运钢包是否检查确认挂钩、脱钩可靠，方可通知司机起吊。', '114', '');
INSERT INTO `t_item_tbl` VALUES ('1497', '吊运装有铁水、钢水、液渣的罐，应与邻近设备或建、构筑物是否保持大于1.5m的净空距离。', '114', '');
INSERT INTO `t_item_tbl` VALUES ('1498', '龙门钩挂重铁水罐时是否有专人对两边进行检查确认的。', '114', '');
INSERT INTO `t_item_tbl` VALUES ('1499', '吊起的铁水罐在等待往转炉兑铁水期间，是否做到不提前挂上倾翻铁水罐的小钩。', '114', '');
INSERT INTO `t_item_tbl` VALUES ('1500', '外部运输使用铁路线运输的，尽头铁路线末端，是否设车挡与车挡指示器。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1501', '铁水运输是否使用专线，不应与其他交通工具混行。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1502', '铁水运输混行是否制定严格安全措施方案。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1503', '厂外公路铁水运输的安全措施方案是否报当地有关部门备案。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1504', '平交道口是否符合《工业企业铁路道口安全标准》（GB 6389）的规定。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1505', '进出炼钢生产厂房的铁路出入口或道口是否按规定设置报警装置。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1506', '在煤气附近的操作室或有人员作业的场所是否安装煤气报警器（安装高度1.0～2.0米之间）。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1507', '在煤气释放点（阀门、水封等）附近是否安装煤气报警器（高度在1米左右）。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1508', '铁路线室内车挡后6m、露天车挡后15m范围内，是否设置建筑物与设备。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1509', '采用抱罐汽车运输液体渣罐时，罐内液渣与罐沿是否保持安全距离。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1510', '抱罐汽车司机室的顶部与背面是否加设防护装置。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1511', '外部运输使用柴油车运输的，载运炽热物体是否使用专用的柴油车，其油箱应采取隔热措施。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1512', '锭坯车、铁水罐车、钢水罐车、渣罐车或运渣车、废钢料篮车及其他料车是否有无关人员乘坐。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1513', '煤气或氧气管道、电缆通廊、管架等下方是否有停留运输炽热物体的车辆。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1514', '铁路运输重罐车的行驶速度，是否大于10km/h；在高炉下行驶或倒罐时，是否大于5km/h。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1515', '高炉炉下、铁水倒罐坑、道口等行走液体金属的铁路线是否进行不定期清理，保持铁路上无杂物。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1516', '铁水钢水运输线上是否设置了紧急停靠避难线。', '115', '');
INSERT INTO `t_item_tbl` VALUES ('1517', '熔融金属吊运和生产区域是否未设置会议室、员工休息室等。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1518', '是否设置安全通道，设置警示标志，防止无关人员进入熔融金属吊运和生产区域。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1519', '铁水倒罐坑和铁水接受坑内是否保持干燥。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1520', '正常生产和吊运时铁水倒罐坑和铁水接受坑内是否有人。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1521', '易受高温辐射、液渣喷溅危害的建，构筑物，是否有防护措施。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1522', '易受钢水与液渣喷溅的平台工作面，是否采用铸铁板或钢板贴面混凝土块(耐火材料)铺设。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1523', '运载铁水罐、渣罐等高温物品的过跨车运载物的外表面距楼板和厂房（平台）柱的外表面的距离是否不小于0.8米，且楼板和柱应有绝热保护。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1524', '炼钢炉、钢水与液渣运输线、钢水吊运通道与浇注区及其附近的地表与地下，是否未设置水管(专用渗水管除外)、电缆等管线。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1525', '所有与钢水、铁水、液渣接触的罐、槽、工具及其作业区域，是否有冰雪、积水，或堆放潮湿物品和其他易燃、易爆物品。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1526', '废钢配料间应部分是否有房屋遮盖，以供雨、雪天配料。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1527', '熔融金属作业和通行地坪是否保持干燥，无有水或潮湿物品。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1528', '熔融金属生产时是否有防喷溅措施的。', '116', '');
INSERT INTO `t_item_tbl` VALUES ('1529', '供电应是否有两路独立的高压电源。', '117', '');
INSERT INTO `t_item_tbl` VALUES ('1530', '当一路电源发生故障或检修时，另一路电源是否能保证车间正常生产的用电负荷。', '117', '');
INSERT INTO `t_item_tbl` VALUES ('1531', '供电与电气设备使用的电缆不应架设在热力与燃气管道上，是否远离高温、火源与液渣喷溅区。', '117', '');
INSERT INTO `t_item_tbl` VALUES ('1532', '电缆必须通过或邻近这些区域时，是否采取可靠的防护措施。', '117', '');
INSERT INTO `t_item_tbl` VALUES ('1533', '供水系统是否设两路独立电源供电。', '118', '');
INSERT INTO `t_item_tbl` VALUES ('1534', '供水泵是否设置备用水泵。', '118', '');
INSERT INTO `t_item_tbl` VALUES ('1535', '高炉、转炉、电炉等是否有事故供水设施。', '118', '');
INSERT INTO `t_item_tbl` VALUES ('1536', '供水量是否满足生产的需要。', '118', '');
INSERT INTO `t_item_tbl` VALUES ('1537', '渣罐使用前是否进行检查。', '119', '');
INSERT INTO `t_item_tbl` VALUES ('1538', '罐内是否有无水、积雪或其他潮湿物料。', '119', '');
INSERT INTO `t_item_tbl` VALUES ('1539', '倒渣或渣罐地坑内是否保持干燥无积水。', '119', '');
INSERT INTO `t_item_tbl` VALUES ('1540', '采用钢渣水淬工艺，发现冲渣水量小于规定值时，是否停止水淬，以防爆炸。', '119', '');
INSERT INTO `t_item_tbl` VALUES ('1541', '罐口结壳及翻渣后罐内结壳，是否使用打渣壳机和撞罐机处理。', '120', '');
INSERT INTO `t_item_tbl` VALUES ('1542', '铁水罐、钢水罐内的铁水、钢水有凝盖时，是否禁止用其他铁水罐、钢水罐压凝盖或人工使用管状物撞击凝盖。', '120', '');
INSERT INTO `t_item_tbl` VALUES ('1543', '高炉内衬耐火材料、填料、泥浆等是否符合设计要求。', '121', '');
INSERT INTO `t_item_tbl` VALUES ('1544', ' 热电偶应对整个炉底进行自动、连续测温，其结果是否正确显示于中控室（值班室）。', '122', '');
INSERT INTO `t_item_tbl` VALUES ('1545', '采用强制通风冷却炉底时，炉基温度是否不高于250℃。', '123', '');
INSERT INTO `t_item_tbl` VALUES ('1546', '应有备用鼓风机，鼓风机运转情况是否显示于高炉中控室。', '123', '');
INSERT INTO `t_item_tbl` VALUES ('1547', '采用水冷却炉底时，炉基温度是否不高于200℃。', '124', '');
INSERT INTO `t_item_tbl` VALUES ('1548', '出铁场平台是否有防止屋面漏水和天窗飘雨的措施。', '125', '');
INSERT INTO `t_item_tbl` VALUES ('1549', '天棚顶有清灰装置时，天棚顶的坡度可为1/12；无清灰装置时，其倾斜角是否小于45℃。', '125', '');
INSERT INTO `t_item_tbl` VALUES ('1550', '渣、铁沟是否有钢盖板或供横跨用的活动小桥。', '126', '');
INSERT INTO `t_item_tbl` VALUES ('1551', '撇渣器上是否设防护罩。', '126', '');
INSERT INTO `t_item_tbl` VALUES ('1552', '渣口正前方是否设挡渣墙。', '126', '');
INSERT INTO `t_item_tbl` VALUES ('1553', '渣口和渣铁罐上面，是否设防雨棚和排烟罩。', '126', '');
INSERT INTO `t_item_tbl` VALUES ('1554', '水冲渣是否有备用电源和备用水泵。', '127', '');
INSERT INTO `t_item_tbl` VALUES ('1555', '冲渣喷口的水压，是否不低于0.25MPa。', '127', '');
INSERT INTO `t_item_tbl` VALUES ('1556', '靠近炉台的水渣沟，其流嘴前是否有活动护栏，或净空尺寸不大于200mm的活动栏网。', '127', '');
INSERT INTO `t_item_tbl` VALUES ('1557', '水冲渣发生故障时，是否有改向渣罐放渣或向干渣坑放渣的备用设施。', '127', '');
INSERT INTO `t_item_tbl` VALUES ('1558', '系统正常运转时，其粒化水量、吹扫空气量、清扫水量、粒化器的最高水温及渣流量，是否达到设计规定值。', '128', '');
INSERT INTO `t_item_tbl` VALUES ('1559', '系统运转时，事故水位是否正常。', '128', '');
INSERT INTO `t_item_tbl` VALUES ('1560', '是否有严防液态渣进入粒化系统。', '128', '');
INSERT INTO `t_item_tbl` VALUES ('1561', '采用轮法冲渣工艺时，是否在粒化轮附近设安全防护网。', '128', '');
INSERT INTO `t_item_tbl` VALUES ('1562', '渣罐倾翻装置应能自锁，倾翻渣罐的倾翻角度是否小于116°（丝杆剩5～6扣）。', '129', '');
INSERT INTO `t_item_tbl` VALUES ('1563', '翻罐供电，是否采用隐蔽插头的软电缆，并在离罐30m以外操作开关。', '129', '');
INSERT INTO `t_item_tbl` VALUES ('1564', '铸铁车间的铁罐道两侧，是否设带栏杆的人行道，行人应在线界以外行走。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1565', '铸铁机主厂房是否有排气天窗，小型铸铁机车间至少应有防雨棚。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1566', '铸铁机厂房的主要操作室及工作间，是否有通风除尘设施。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1567', '铸铁机操作室，是否能清楚地观察到翻罐、铁水溜槽及前半部铸模的工作情况。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1568', '操作室窗户是否采用耐热玻璃，并设有两个方向相对、通往安全地点的出入口', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1569', '铸铁机工作台是否采用耐火砖砌筑，宽度应大于5m。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1570', '铸铁机下不应通行，需要通行时，是否设置专用的安全通道。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1571', '铸铁机地坑内是否无积水。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1572', '铸铁机链带下面（有人出入的地方），是否设置防护格网，以防止没脱模的铁块突然下落伤人。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1573', '翻罐提升机和移动小车，是否有电动极限控制装置。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1574', '铁水溜槽坡度是否为3％左右。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1575', '铸铁时铁水流是否均匀，炉前铸铁是否使用铁口缓冲包，缓冲包在出铁前是否烘干。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1576', '铸模内是否无水，模耳磨损是否不应大于5％，是否不使用开裂及内表面有缺陷的铸模。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1577', '铁水罐对位、复位是否准确，防止偏位和移位。', '130', '');
INSERT INTO `t_item_tbl` VALUES ('1578', '高炉炉顶压力不断增高又无法控制时，是否按照制定的预案，及时减风，并打开炉顶放散阀，找出原因，排除故障，方可恢复工作。', '131', '');
INSERT INTO `t_item_tbl` VALUES ('1579', '开、停炉及计划检修期间，是否有煤气专业防护人员监护。', '131', '');
INSERT INTO `t_item_tbl` VALUES ('1580', '负责指挥开、停炉，是否制定开停炉方案和安全技术措施，并组成以生产厂长或总工程师为首的领导小组。', '131', '');
INSERT INTO `t_item_tbl` VALUES ('1581', '高炉突然断风，是否按紧急休风程序休风，同时出净炉内的渣和铁。', '131', '');
INSERT INTO `t_item_tbl` VALUES ('1582', '高炉生产系统（包括鼓风机）突然停电时，是否按紧急休风程序处理。', '131', '');
INSERT INTO `t_item_tbl` VALUES ('1583', '人员进入高炉炉缸作业前，是否拆除所有直吹管，有效切断煤气、氧气、氮气等危险气源，并认真做好监护、检测和通风措施，是否锁死炉顶布料等相关装置。', '131', '');
INSERT INTO `t_item_tbl` VALUES ('1584', '炉皮开裂的护炉方案，是否制定有保护人员和设备安全的安全措施。', '131', '');
INSERT INTO `t_item_tbl` VALUES ('1585', '非电气信号倒罐的，是否建立渣、铁罐使用牌制度。', '132', '');
INSERT INTO `t_item_tbl` VALUES ('1586', '出铁、出渣以前，是否做好准备工作，并发出出铁、出渣或停止的声响信号。', '132', '');
INSERT INTO `t_item_tbl` VALUES ('1587', '无渣、铁罐使用牌，不应倒罐，高炉不应出铁、出渣。', '132', '');
INSERT INTO `t_item_tbl` VALUES ('1588', '水冲渣的高炉，在出铁前是否先开动冲渣水泵（或打开冲渣水阀门）。', '132', '');
INSERT INTO `t_item_tbl` VALUES ('1589', '维护铁口和渣口作业前，是否先点燃煤气，防止中毒。', '133', '');
INSERT INTO `t_item_tbl` VALUES ('1590', '摆动溜嘴往两边受铁罐受铁时，摆动角度是否保证铁水流入铁水罐口的中心。', '134', '');
INSERT INTO `t_item_tbl` VALUES ('1591', '接班时是否认真检查开关、机械传动部分、电机、减速机、溜嘴工作层等，发现异常及时处理。', '134', '');
INSERT INTO `t_item_tbl` VALUES ('1592', '停电时是否按规定操作摆动溜嘴。', '134', '');
INSERT INTO `t_item_tbl` VALUES ('1593', '转鼓渣过滤系统运转前的检查是否做到：设备无异常，粒化头无堵塞，接受槽格栅无渣块，高低沟、渣闸正常，热水槽无积渣，地坑无积水，管道阀门无泄漏，胶带运行平稳、无偏离，事故水位正常。', '135', '');
INSERT INTO `t_item_tbl` VALUES ('1594', '正常生产时，系统设备的运转是否实行自动控制。', '135', '');
INSERT INTO `t_item_tbl` VALUES ('1595', '转鼓渣过滤系统各种联锁、保护装置的调整，是否经主管部门同意，并报主管厂长批准；调整应记录存档。', '135', '');
INSERT INTO `t_item_tbl` VALUES ('1596', '是否有专人（穿好黄马甲、带好对讲机、手势准确明了）负责加废钢、兑铁水', '136', '');
INSERT INTO `t_item_tbl` VALUES ('1597', '兑铁水时120度扇形内是否禁止有人。', '136', '');
INSERT INTO `t_item_tbl` VALUES ('1598', '主控室防爆门在兑铁水、加补炉料前是否关闭。', '136', '');
INSERT INTO `t_item_tbl` VALUES ('1599', '兑铁水前是否确认炉内无稀渣，严禁留稀渣兑铁水。', '136', '');
INSERT INTO `t_item_tbl` VALUES ('1600', '转炉倾动设备是否设有可靠的事故断电紧急开关；氧枪、副枪驱动，设有事故电源(直流驱动采用蓄电池，交流驱动采用UPS电源)。', '137', '');
INSERT INTO `t_item_tbl` VALUES ('1601', '倒炉开始和过程中，是否做到不长时间正对炉口站立、操作。', '137', '');
INSERT INTO `t_item_tbl` VALUES ('1602', '补炉第一炉出钢，是否严禁正对炉口站立、操作。', '137', '');
INSERT INTO `t_item_tbl` VALUES ('1603', '炉下漏钢坑是否按防水要求设计施工,有防积水的措施。', '138', '');
INSERT INTO `t_item_tbl` VALUES ('1604', '炉下钢水罐车、渣罐车运行区域，地面是否保持干燥。', '138', '');
INSERT INTO `t_item_tbl` VALUES ('1605', '炉区、连铸区域厂房的屋面是否有防漏雨设施、下水道是否保持畅通。', '138', '');
INSERT INTO `t_item_tbl` VALUES ('1606', '炉下热泼渣区地坪应防止积水，周围是否设防护结构。', '138', '');
INSERT INTO `t_item_tbl` VALUES ('1607', '大喷事故可能威胁转炉和电炉主控室是否采取必要的安全防护措施。', '139', '');
INSERT INTO `t_item_tbl` VALUES ('1608', '连铸主控室是否不正对中间罐。', '139', '');
INSERT INTO `t_item_tbl` VALUES ('1609', '转炉炉旁操作室是否采取隔热防喷溅措施。', '139', '');
INSERT INTO `t_item_tbl` VALUES ('1610', '电炉炉后出钢操作室，是否不正对出钢方向开门，其窗户应采取防喷溅措施。', '139', '');
INSERT INTO `t_item_tbl` VALUES ('1611', '具有爆炸和自燃危险的物料，如CaC2粉剂、镁粉、煤粉、直接还原铁（DRI）等是否贮存于密闭贮仓内，必要时用氮气保护。', '140', '');
INSERT INTO `t_item_tbl` VALUES ('1612', '入炉物料是否保持干燥。', '140', '');
INSERT INTO `t_item_tbl` VALUES ('1613', '入炉废钢是否有拣选措施，入炉废钢内严禁夹带有密封、易爆物。', '140', '');
INSERT INTO `t_item_tbl` VALUES ('1614', '地下料仓、高位料仓的受料口，是否设置格栅板并按照受限空间管控。', '140', '');
INSERT INTO `t_item_tbl` VALUES ('1615', '大包回转台是否配置安全制动与停电事故驱动装置。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1616', '连铸浇注区是否设置事故钢水包。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1617', '连铸浇注区是否设置溢流槽。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1618', '连铸浇注区是否中间溢流罐。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1619', '连铸浇注之前，是否检查各介质参数符合要求。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1620', '是否确认结晶器无漏水、渗水、冷却水满足工艺要求，事故槽内不允许有水或潮湿物。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1621', '是否禁止使用有缺陷的中包，中包上线使用前检查中包内衬工作层是否完好；包口座砖按装时是否对中；中包外观检查，发现中包外壁发红，则立即关闭大包，停机换包。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1622', '连铸钢包或中间罐滑动水口开启时，是否做到滑动水口正面无人。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1623', '检查煤气管道接口是否有漏气现象；保证使用的冷料干燥。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1624', '连铸浇注时二次冷却区无人，严禁进入扇形段，并严禁进入密封室及冲渣沟是否有管控措施。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1625', '出现结晶器冷却水减少报警时，是否立即停止浇注。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1626', '大包回转台旋转过程中，旋转区域内是否无人。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1627', '收尾坯中包液面200mm走车，严禁下渣；是否佩戴好防护面罩。', '141', '');
INSERT INTO `t_item_tbl` VALUES ('1628', '采用放射源控制结晶器液面时，是否建立严格的管理和检测制度，放射源的装、卸、运输和存放，使用专用工具。', '141', '');
