INSERT INTO `t_item_tbl` VALUES ('1741', '新建管道计算压力等于或大于105Pa是否进行强度试验，合格后再进行气密性试验。', '161', '');
INSERT INTO `t_item_tbl` VALUES ('1742', '新建管道计算压力小于105Pa时，是否进行气密性试验，试验泄漏率不得大于1%。', '161', '');
