INSERT INTO `t_item_tbl` VALUES ('1750', '电捕焦油器沉淀管间是否设带阀门的连接管。', '162', '');
INSERT INTO `t_item_tbl` VALUES ('1751', '抽气机出口与电捕焦油器之间是否设避震器。', '162', '');
