INSERT INTO `t_item_tbl` VALUES ('1760', '布袋除尘器向外界卸灰时，是否有防止煤气外泄的措施。', '164', '');
INSERT INTO `t_item_tbl` VALUES ('1761', '管理室是否装设二次检测仪表及调节装置。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1762', '管理室是否装有电话。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1763', '一次仪表室是否设强制通风装置。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1764', '加压站内是否设有CO报警装置。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1765', '加压站所是否两路供电。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1766', '有人值班的机械房、加压站、混合站、抽气机房内的值班人员是否不少于二人。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1767', '加压站房内主机之间以及主机与墙壁之间的净距是否不小于1.3m。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1768', '两条引入混合的煤气管道的净距是否不小于800 mm。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1769', '两条引入混合的煤气管道的敷设坡度是否不小于0.5％。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1770', '引入混合站的两条混合管道，在引入的起始端是否设可靠的隔断装置。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1771', '每台加压机、抽气机前后是否设可靠的隔断装置。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1772', '每台设备、阀门、放散管和管道支架应编号管理。', '165', '');
INSERT INTO `t_item_tbl` VALUES ('1773', '气柜值班室应设有煤气压力、流量、气柜高度、气柜倾斜度和柜内CO检测仪表等监控仪表。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1774', '稀油密封柜要有油沟、油位自动检测仪表，并灵敏可靠，有运行记录。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1775', '柜区内应设专用消防栓，水压符合要求，并随时能投入使用，配备的消防器材齐全有效。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1776', '煤气柜出入口管道上是否设隔断装置。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1777', '煤气柜出人口管道最低处是否设排水器。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1778', '煤气柜上是否有容积指示装置。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1779', '煤气柜是否设有自动控制装备（柜位达到上限时关闭煤气入口阀，并设有放散设施；煤气柜位降到下限时，自动停止向外输出煤气或自动充压的装置）。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1780', '干式煤气柜是否设内、外部电梯，供检修及检查时载人用。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1781', '电梯应设最终位置极限开关、升降异常灯。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1782', '电梯内部是否设安全开关、安全扣和联络电话。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1783', '电梯活塞上部是否有一氧化碳检测报警装置。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1784', '人员进行电梯活塞上部是否备正压式空气呼吸器。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1785', '干式柜除生产照明外是否设有事故照明、检修照明、楼梯及过道照明、各种检测仪表照明以及外部升降机上、下出入口照明。', '166', '');
INSERT INTO `t_item_tbl` VALUES ('1786', '调度室、休息室是否为无爆炸危险房屋，与有爆炸危险的房屋分开。', '167', '');
INSERT INTO `t_item_tbl` VALUES ('1787', '休息室是否避免建设在煤气等有毒气体环境介质中。', '167', '');
INSERT INTO `t_item_tbl` VALUES ('1788', '其他有专人值守的场所，如果必须建在煤气区域，是否设有不少于2个对开的安全出口，且装设固定式煤气声光报警器。', '167', '');
INSERT INTO `t_item_tbl` VALUES ('1789', '煤气管道下方是否没有设置操作室、休息室等有人设施。', '167', '');
INSERT INTO `t_item_tbl` VALUES ('1790', '煤气区域内的建构筑物，是否按《建筑物防雷设计规范》（GB50057）的规定设置避雷针、避雷网、避雷器等防雷设施。', '168', '');
INSERT INTO `t_item_tbl` VALUES ('1791', '煤气管道及相关设备设施是否采取消除静电的措施。', '168', '');
INSERT INTO `t_item_tbl` VALUES ('1792', '煤气管道及相关设备设施是否每半年检测一次，确保防雷设施完好，检测报告是否可查。', '168', '');
INSERT INTO `t_item_tbl` VALUES ('1793', '在爆炸性气体环境、爆炸性粉尘环境、火灾危险环境中危险区域划分、危险区域范围确定、电气设备选型是否符合《爆炸和火灾危险环境电力装置设计规范》（GB50058）的要求。', '169', '');
INSERT INTO `t_item_tbl` VALUES ('1794', '厂房的照明，是否符合《建筑采光设计标准》（GB50033）和《建筑照明设计标准》（GB50034）的规定。', '169', '');
INSERT INTO `t_item_tbl` VALUES ('1795', '有爆炸和火灾危险的场所，是否按其危险等级选用相应的照明器材。', '169', '');
INSERT INTO `t_item_tbl` VALUES ('1796', '是否按照CO含量与工作时间的要求进行作业（煤气区域一氧化碳含量不超过24ppm时，可连续工作；一氧化碳含量不超过40ppm时连续工作时间不应超过1小时；一氧化碳含量不超过80ppm时，连续工作时间不应超过30分钟；一氧化碳含量不超过160ppm时，连续工作时间8应超过15分钟）。', '170', '');
INSERT INTO `t_item_tbl` VALUES ('1797', '在煤气设施内作业，除检测的CO含量外，是否检测氧含量不小于19.5%。', '170', '');
INSERT INTO `t_item_tbl` VALUES ('1798', '当现场检测数据不满足规定要求时，作业人员是否使用呼吸器，是否有专人监护。', '170', '');
INSERT INTO `t_item_tbl` VALUES ('1799', '调度室、煤气操作室、值班室是否设有各类煤气主管压力，流量和必要的安全报警装置。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1800', '调度室、煤气操作室、值班室是否配备与生产煤气厂（车间）、煤气防护站和主要用户的直通电话。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1801', '凡开、闭时冒出煤气的隔断装置盲板、眼睛阀或扇型阀及敞开式插板阀等，是否未安装在厂房内或通风不良之处，离明火设备距离是否不少于40米。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1802', '是否有置换、吹扫、加压需要的设备和管道应设置可拆卸式的蒸气或氮气接头。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1803', '煤气吹扫装置使用后是否将接头与煤气设施断开，或堵盲板。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1804', '设备裸露的转动或快速移动部分，是否设有结构可靠的安全防护罩、防护栏杆或防护挡板。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1805', '在易发生爆炸的煤气设备部位应安装泄爆装置，其应保持严密，泄爆口不应正对建筑物的门窗。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1806', '站所和气柜在醒目位置处是否设风险告知牌、危险化学品告知牌和职业健康告知牌、安全警示牌、平面布置图。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1807', '气柜区域和加压机域内照明、电气、电机是否使用防爆型设施。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1808', '气柜周围消防通道是否畅通无阻，柜区内是否设置两个以上安全疏散通道。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1809', '气柜区域和加压机域内照明、电气、电机是否使用防爆型设施。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1810', '在有较大危险因素的作业场所或有关设备上，是否设置符合《安全标志及其使用导则》（GB2894）和《安全色》（GB2893）规定的安全警示标志和安全色。', '171', '');
INSERT INTO `t_item_tbl` VALUES ('1811', '高炉和焦炉厂区边缘与居民区边缘相距是否在1000m以上。', '171', '');
