INSERT INTO `t_item_tbl` VALUES ('2801', '生产、储存剧毒化学品、易制爆危险化学品的单位，应当设置治安保卫机构,配备专职治安保卫人员。', '11', '《危险化学品安全管理条例》第二十三条');
INSERT INTO `t_item_tbl` VALUES ('2967', '（二）甲、乙类物品的包装应严密，不得有跑、冒]滴、漏现象', '250', '《国家储备仓库安全保卫规范化管理与保安员国家职业标准及设施强制性条文》');
INSERT INTO `t_item_tbl` VALUES ('2969', '用于商场、餐饮、文化娱乐等人员聚集场所应设置能够覆盖本地下空间区域的应急广播，其他人员聚集场所可设置电铃等警报设施。在安全出入口、主要通道应安装闭路电视监控设备，并保证闭路电视监控设备在营业期间正常运行，并将录像资料留存30日备查。', '874', '');
INSERT INTO `t_item_tbl` VALUES ('6127', '在爆炸性粉尘环境内，应尽量减少插座和局部照明灯具的数量。如需采用时，插座宜布置在爆炸性粉尘不易积聚的地点，局部宜布置在事故气流不易冲击的位置。插座开口的一面应朝下，且与垂直面的角度不应大于60度。', '106', '');
INSERT INTO `t_item_tbl` VALUES ('6128', '电气布线应敷设在钢管中；管线穿墙及楼板时，孔洞应采用非可燃性填料严密堵塞。', '106', '');
INSERT INTO `t_item_tbl` VALUES ('6129', '所有产尘点均应按规定要求设置吸尘罩，风口保证有足够的吸尘风量风速应大于1m/s，主管道系统垂直风管设计风速应大于19m/s，水平风管设计风速应大于23m/s，满足作业岗位安全和职业卫生要求。', '108', '');
INSERT INTO `t_item_tbl` VALUES ('6130', '处理有爆炸危险粉尘和碎屑的除尘器、过滤器、管道均应设置泄压装置，泄爆口应按规定设置。净化有爆炸危险粉尘的干式除尘器和过滤器应布置在系统的负压段上。除尘器的进出口风管应设风压监测装置。\r\n干式除尘系统应规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', '108', '');
INSERT INTO `t_item_tbl` VALUES ('6131', '除尘器宜安装在室外；如安装在室内，其泄爆管应直通室外，且长度小于3m，并根据粉尘属性设立隔（阻）爆装置。排风设备不应布置在地下、半地下建筑（室）中。 \r\n除尘系统采用粉尘沉降室除尘，或者采用干式巷道式构筑物作为除尘风道。', '108', '');
INSERT INTO `t_item_tbl` VALUES ('6132', '除尘器应采用具有连续卸灰或自动周期卸灰功能的灰斗锁气卸灰装置，收尘器箱体内不应有明显结灰。\r\n袋式除尘器应采用脉冲喷吹等强力清灰方式进行可靠清灰。并根据除尘器类型、清灰方式、过滤风速、入口粉尘浓度等确定合理清理周期，清灰气源应符合产品说明书规定要求，并详细记录。铝镁等金属粉尘生产、收集、贮存必须按照GB15577要求配备防水防潮设施。\r\n干式除尘系统应规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', '108', '');
INSERT INTO `t_item_tbl` VALUES ('6133', '厂区内按照定置管理的要求实现定置摆放，厂区内无杂物、无图物不符等状况', '234', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('6134', '纱、线、织物加工的烧毛、开幅、烘干等热定型工艺的汽化室、燃气贮罐、储油罐、热媒炉等未与生产加工、人员密集场所明确分开或单独设置。', '234', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('6135', '工业垃圾和生活垃圾分开定点存放，有防吹散、防污染措施', '234', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('6136', '危险固体废料应有专门存放地点，存放点有防渗漏措施，且符合国家规定妥善处理', '234', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('6137', '厂区大门开关灵活、方便，迅速，无卡死现象', '234', '《现场标准化实施及考核办法》');
INSERT INTO `t_item_tbl` VALUES ('6138', '按照国家标准制定动火、进入受限空间等特殊作业管理制度，或者制度未有效执行。', '3231', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）；');
INSERT INTO `t_item_tbl` VALUES ('6139', '新开发的危险化学品生产工艺经小试、中试、工业化试验直接进行工业化生产；国内首次使用的化工工艺经过省级人民政府有关部门组织的安全可靠性论证；新建装置制定试生产方案投料开车；精细化工企业按规范性文件要求开展反应安全风险评估。', '656', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('6140', '化工生产装置按国家标准要求设置双重电源供电，自动化控制系统设置不间断电源。', '658', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('6141', '按国家标准分区分类储存危险化学品，超量、超品种储存危险化学品，相互禁配物质混放混存。', '659', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
INSERT INTO `t_item_tbl` VALUES ('6142', '按国家标准分区分类储存危险化学品，超量、超品种储存危险化学品，相互禁配物质混放混存。', '659', '《关于印发<江苏省危险化学品企业安全生产标准化评审标准（试行）>的通知》（苏安监﹝2011﹞202号）');
