INSERT INTO `t_item_tbl` VALUES ('6150', '存在可燃性气体和爆炸性粉尘，通风、检测、照明、通讯设备须符合有关防爆安全要求。', '153', '');
INSERT INTO `t_item_tbl` VALUES ('6151', '落实作业审批制度，未经审批不得擅自进入有限空间作业', '153', '');
INSERT INTO `t_item_tbl` VALUES ('6152', '液氨储罐等压力容器和设备应设置安全阀、压力表、液位计、温度计，并应装有带压力、液位、温度远传记录和报警功能的安全装置，设置整流装置与压力机、动力电源、管线压力、通风设施或相应的吸收装置的联锁装置。液氨储罐需设置紧急切断装置。', '103', '');
INSERT INTO `t_item_tbl` VALUES ('6153', '氨分解车间应设固定式可燃气体（氢）泄漏检测报警仪。在液氨场所（包括储存场所）应设固定式有毒气体（氨）泄漏检测报警仪。', '103', '');
INSERT INTO `t_item_tbl` VALUES ('6154', '防雷装置检测应当每年一次，液氨场所的防雷装置应当每半年检测一次。所有防静电设施（设备）要定期检查、维修，并建立档案。', '103', '');
INSERT INTO `t_item_tbl` VALUES ('6155', '有毒/可燃气体报警仪应由法定计量检定机构定期检定合格。', '103', '');
