INSERT INTO `t_item_tbl` VALUES ('6162', '在液氨场所应配备紧急淋浴和洗眼器', '104', '');
INSERT INTO `t_item_tbl` VALUES ('6163', '在液氨场所（包括装置、罐区、装卸区、管道）等危险场所的醒目位置，是否设置符合GB2894规定的安全标志；是否设置明显的安全告知牌，安全告知牌应注明液氨特性、危害防护、处置措施、报警电话等内容。', '104', '');
INSERT INTO `t_item_tbl` VALUES ('6164', '在液氨场所是否在醒目位置设置公告栏，公布有关职业病防治的规章制度、操作规程、职业病危害事故应急救援措施和工作场所职业病危害因素检测结果。对产生严重职业病危害的作业岗位，应当在其醒目位置，设置符合GBZ158的警示标识和中文警示说明', '104', '');
INSERT INTO `t_item_tbl` VALUES ('6165', '包装间、分割间、产品整理间等人员较多生产场所的空调系统采用氨直接蒸发制冷系统。', '104', '');
INSERT INTO `t_item_tbl` VALUES ('6166', '在液氨场所作业时，从业人员是否戴化学安全防护眼镜，穿防静电工作服，戴橡胶手套。', '104', '');
INSERT INTO `t_item_tbl` VALUES ('6167', '快速冻结装置未设置在单独的作业间内，且作业间内作业人员数量超过9人。', '104', '');
