mapper.select:
select
  T1.id, T1.user_id userId, T1.type, T1.report_time reportTime, T1.expiration_time expirationTime, T1.remark,
  T1.jcd, T1.whys, T1.jcjg, T1.jcdw, T1.jcbh, T1.ba,
  T2.name companyName, T2.village_name villageName
from detection_tbl T1
left join company_view T2 on T1.user_id = T2.user_id

    T2.is_del = 0
   and T1.user_id = #{userId}
   and T2.village_id = #{villageId}
   and T2.town_id = #{townId}
   and T2.district_id = #{districtId}

     and T1.user_id IN ( SELECT uid FROM trade_company_tbl WHERE tid = #{tradeId} )


     and T1.type = #{deType}


     and T2.village_name LIKE CONCAT('%', #{villageName},'%')


     and T2.name LIKE CONCAT('%', #{companyName},'%')


     and T1.expiration_time <= #{isTime}


    and T1.expiration_time >= #{now}


order by T1.id desc

mapper.insert:
insert into detection_tbl
(user_id,type,report_time,expiration_time,remark, jcd, whys, jcjg, jcdw, jcbh, ba )
values

    (${userId},#{item.type},#{item.reportTime},#{item.expirationTime},#{item.remark}
    ,#{item.jcd},#{item.whys},#{item.jcjg},#{item.jcdw},#{item.jcbh},#{item.ba}
    )

mapper.select:
	select
		T1.id, T1.user_id userId, T1.type, T1.report_time reportTime, T1.expiration_time expirationTime, T1.remark,
        T1.jcd, T1.whys, T1.jcjg, T1.jcdw, T1.jcbh, T1.ba
  from detection_tbl T1

  	T1.user_id = #{tradeId}

         and T1.type = #{deType}


      	and T1.expiration_time <= #{isTime}


      	and T1.expiration_time >= #{now}


  order by T1.id desc
