mapper.select:
select
  T1.id, T1.user_id userId, T1.flag, T1.type, T1.model, T1.equipped_position equippedPosition, T1.amount, 
  T1.expiry_date expiryDate, T1.remark, T2.name companyName, T3.name villageName
from facilities_tbl T1
left join company_view T2 on T1.user_id = T2.user_id
left join village_tbl T3 on T2.village_id = T3.user_id
 T2.is_del= 0

    and T1.user_id = #{userId} 


    and T1.flag = #{flag} 

   and T2.village_id = #{villageId}
     and T2.town_id = #{townId}
     and T2.district_id = #{districtId}

      and T1.type =#{faType}


     and T3.name LIKE CONCAT('%', #{villageName},'%')


     and T2.name LIKE CONCAT('%', #{companyName},'%')
