mapper.update:

update fc_naturaldanger_study_t
set uid = #{uid,jdbcType=INTEGER},
  checker = #{checker,jdbcType=VARCHAR},
  del = #{del,jdbcType=INTEGER},
  ctime = #{ctime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select T1.uid, T1.checker, T1.del, T1.ctime, T2.id dangeritemid, T2.dangerid, T2.dangerstudyid, T2.checkret, T2.img, T2.checkret_bigd checkretBigd, T3.id,T3.dmid, T3.level1, T3.level2, T3.level3, T3.factors, T3.type, T3.measures, T3.reference, T3.flag, T3.level, T3.issys, T3.ctime, T3.level, T3.gkcs, T3.gkzt, T3.fjgkfzr, T3.material, T3.gy, T3.hxys, T3.wlbs,T3.gwzy,T3.lnglat from fc_naturaldanger_study_t T1 left join fc_naturaldanger_item_t T2 on T1.id = T2.dangerstudyid left join a_company_manual_tbl T3 on T2.dangerid = T3.id where T1.del = 0
and T1.id = #{id}
and T1.uid = #{uid}
and T2.checkret = #{checkret}
and T3.flag in (${flag})
and T3.flag2 = #{flag2}
and T3.level = #{level}
and T3.level in
#{item}
order by find_in_set(T3.level,'红色,橙色,黄色,蓝色')
order by T3.id desc
