mapper.select:
SELECT 
	T1.uid, T1.del,T2.name,count(T1.uid) num
FROM fc_promise_t T1
LEFT JOIN company_view T2 on T1.uid = T2.user_id
where T1.del = 0 and T1.uid is not null

	and T2.village_id = #{villageId,jdbcType=INTEGER}


	and T2.town_id = #{townId,jdbcType=INTEGER}


	and T2.district_id = #{districtId,jdbcType=INTEGER}

group by T1.uid

SELECT T1.uid, T1.del,T2.name,count(T1.uid) TotalNum,IFNULL(T3.num,0) MonthNum FROM fc_promise_t T1 LEFT JOIN company_view T2 ON T1.uid = T2.user_id LEFT JOIN ( SELECT uid,count(uid) num FROM fc_promise_t WHERE del = 0 AND uid is not null AND DATE_FORMAT(ctime, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m') ) T3 ON T2.user_id = T3.uid where T1.del = 0 and T1.uid is not null
and T2.village_id = #{villageId,jdbcType=INTEGER}
and T2.town_id = #{townId,jdbcType=INTEGER}
and T2.district_id = #{districtId,jdbcType=INTEGER}
group by T1.uid
