mapper.select:
	select 
    T1.id, T1.name, T1.lib_id libId, T1.next_date nextDate,
    T1.work, T1.cycle, T1.number, T1.memo, T2.name companyName
	from pequipment_tbl T1
	left join company_view T2 ON T1.user_id=T2.user_id
	 T2.is_del= 0

          and T1.user_id = #{userId} 

       and T2.village_id = #{villageId}
       and T2.town_id = #{townId}
       and T2.district_id = #{districtId}

		and T1.lib_id = #{libId}


		and T2.name LIKE CONCAT('%', #{companyName},'%')


		and T1.name LIKE CONCAT('%', #{name},'%')


  order by T1.id desc
