mapper.select:
  select 
	  T1.id, T1.user_id userId, T1.project, T1.description, T1.material, T1.material_specifications materialSpecifications, 
	  T1.personnel_requirements personnelRequirements, T1.inspection_standards inspectionStandards,
	  T1.operating_rules operatingRules, T1.equipment, T1.doc_name docName, T1.doc_url docUrl
 from production_process_diagram_tbl T1
 left join company_view T2 on T1.user_id = T2.user_id

   T2.is_del = 0

     and T1.user_id = #{userId}


     and T1.project LIKE CONCAT('%', #{project},'%')

    and T2.village_id = #{villageId}
    and T2.town_id = #{townId}
    and T2.district_id = #{districtId}

 order by id desc
