select
     T1.id, T1.user_id userId, T1.se_name seName, T1.type type, T1.file_number fileNumber,
     T1.amount, T1.detection_time detectionTime,
     T1.expiration_time expirationTime, T1.remark, T2.name companyName, T2.village_name villageName
from sequipment_tbl T1
left join company_view T2 on T1.user_id = T2.user_id
   T2.is_del = 0

        and T1.user_id = #{userId}

     and T2.village_id = #{villageId}
     and T2.town_id = #{townId}
     and T2.district_id = #{districtId}

        and T1.user_id IN ( SELECT uid FROM trade_company_tbl WHERE tid = #{tradeId} )


          and T1.se_name LIKE CONCAT('%', #{seName},'%')


       and T2.village_name LIKE CONCAT('%', #{villageName},'%')


       and T2.name LIKE CONCAT('%', #{companyName},'%')


       and T1.expiration_time <= #{isTime}


    and T1.expiration_time >= #{now}


     order by T1.id desc

    limit #{start},#{count}

	select
		T1.id, T1.user_id userId, T1.se_name seName, T1.type type, T1.file_number fileNumber, 
       T1.amount, T1.detection_time detectionTime,T1.expiration_time expirationTime, T1.remark
  from sequipment_tbl T1

  	T1.user_id = #{tradeId}

           and T1.se_name LIKE CONCAT('%', #{seName},'%')


      and T1.expiration_time <= #{isTime}


      and T1.expiration_time >= #{now}


  order by T1.id desc
