mapper.insert:


  SELECT LAST_INSERT_ID()

insert into standard_tbl (user_id, type, img, 
  certificate_number, time_get, time_exp
  )
values (#{userId,jdbcType=INTEGER}, #{type,jdbcType=VARCHAR}, #{img,jdbcType=VARCHAR}, 
  #{certificateNumber,jdbcType=VARCHAR}, #{timeGet,jdbcType=VARCHAR}, #{timeExp,jdbcType=VARCHAR}
  )

mapper.select:
  select 
	  T1.id, T1.user_id userId, T1.type, T1.img, 
   T1.certificate_number certificateNumber, T1.time_get timeGet, T1.time_exp timeExp,
   T2.name companyName, T2.village_name villageName
 from standard_tbl T1
 left join company_view T2 on T1.user_id = T2.user_id

   T2.is_del = 0

     and T1.type = #{type}


         and T1.user_id = #{userId} 

    and T2.village_id = #{villageId}
    and T2.town_id = #{townId}
    and T2.district_id = #{districtId}

       and T1.user_id IN ( SELECT uid FROM trade_company_tbl WHERE tid = #{tradeId} )


      and T2.name LIKE CONCAT('%', #{companyName},'%')


      and T2.village_name LIKE CONCAT('%', #{villageName},'%')


      and T1.certificate_number LIKE CONCAT('%', #{certificateNumber},'%')


    and T1.time_exp <= #{isTime}


     and T1.time_exp >= #{now}


 order by T1.id desc

select 

from standard_tbl
where user_id = ${userId} and type = ${type}
