mapper.insert:


    SELECT LAST_INSERT_ID()

insert into zzjg_personnel_tbl (ctime, utime, del,
name, mobile, position,
cid, dpid, did, uid
)
values (#{ctime,jdbcType=TIMESTAMP}, #{utime,jdbcType=TIMESTAMP}, #{del,jdbcType=INTEGER},
#{name,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}, #{position,jdbcType=VARCHAR},
#{cid,jdbcType=INTEGER}, #{dpid,jdbcType=INTEGER}, #{did,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER},
#{password,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}
)

mapper.select:
select T1.id, T1.name, T1.mobile, T1.did, T1.dpid, T1.position,T1.status,T1.password, (select name from zzjg_company_tbl where id = T1.cid) companyName, T3.name dname, T4.name dpname, T3.level from zzjg_personnel_tbl T1 left join zzjg_department_tbl T3 on T1.did = T3.id left join zzjg_department_tbl T4 on T1.dpid = T4.id
T1.del = 0
and T1.cid = #{cid}
and T1.uid = #{uid}
and T1.dpid = #{dpid}
and T1.did = #{did}
and T1.name like concat('%',#{name},'%')
order by T1.id desc

select T1.id, T1.name, T1.mobile, T1.did, T1.dpid, T1.position, (select name from zzjg_company_tbl where id = T1.cid) companyName, T3.name dname, T4.name dpname, T3.level from zzjg_personnel_tbl T1 left join zzjg_department_tbl T3 on T1.did = T3.id left join zzjg_department_tbl T4 on T1.dpid = T4.id
T1.del = 0
and T1.cid = #{cid}
and T1.uid = #{uid}
and T1.dpid = #{dpid}
and T1.did = #{did}
and T1.name like concat('%',#{name},'%')
order by T1.id desc limit #{page},#{limit}

select count(*) from zzjg_personnel_tbl T1 left join zzjg_department_tbl T3 on T1.did = T3.id left join zzjg_department_tbl T4 on T1.dpid = T4.id
T1.del = 0
and T1.cid = #{cid}
and T1.uid = #{uid}
and T1.dpid = #{dpid}
and T1.did = #{did}
and T1.name like concat('%',#{name},'%')

select T2.name, count(*) count, T2.user_id userId from zzjg_personnel_tbl T1 right join trade_tbl T2 on T2.user_id = T1.uid
T1.del = 0 and T1.uid = #{tradeId}
and T2.name like concat('%',#{companyName},'%')
